		[3123] = {"Das Gefäß wird getestet",nil, {"Reist ins Hinterland und spürt die Wildekin auf. Schaltet 10 davon aus und benutzt dann das Muisekgefäß, um die gefallenen Wildekin zu schrumpfen und einzufangen. Bringt anschließend 10 Einheiten Muisek von Wildekin und das Muisekgefäß zu Hexendoktor Uzer'i in Feralas."}},
		[3124] = {"Muisek von Hippogryphen",nil, {"Schaltet 10 Fransenfederhippogryphen eines beliebigen Typs aus und benutzt dann das Muisekgefäß, um sie zu schrumpfen und einzufangen."}},
		[3125] = {"Muisek von Feendrachen",nil, {"Schaltet 8 Grimmlingflitzer oder Grimmlingdrachen aus. Benutzt das Muisekgefäß, um die gefallenen Feendrachen zu schrumpfen und einzufangen."}},
		[3126] = {"Muisek von Treants",nil, {"Schaltet 3 umherwandernde Waldgänger aus. Benutzt das Muisekgefäß, um die gefallenen Treants zu schrumpfen und einzufangen."}},
		[3127] = {"Muisek von Bergriesen",nil, {"Schaltet 7 Landgänger oder Klippenriesen aus. Benutzt das Muisekgefäß, um die gefallenen Bergriesen zu schrumpfen und einzufangen."}},
