		[3372] = {"Erlöst sie",nil, {"Sucht das geheimnisvolle Artefakt, das im nordwestlichen Teil der Sengenden Schlucht von den Götzendienern des Schattenhammerklans bewacht wird, und bringt es zum Altar von Suntara in der Schlackengrube."}},
		[3373] = {"Die Essenz des Eranikus",nil, {"Legt die Essenz von Eranikus in den Essenzborn, der sich in dem Versunkenen Tempel in seinem Unterschlupf befindet."}},
		[3374] = {"Die Essenz des Eranikus",nil, {"Bringt den Schwurstein von Yseras Drachenschwarm sowie die gebundene Essenz des Eranikus zu Itharius in die Sümpfe des Elends. Dort werdet Ihr Eure Entscheidung treffen, ob Ihr Yseras Drachenschwarm helfen wollt oder nicht."}},
		[3375] = {"Ersatzphiole",nil, {"Bringt einen Manatrank, Kohle und Süßen Nektar zu Talvash del Kissel in Eisenschmiede, um einen Ersatz für Talvashs Wahrsagephiole zu bekommen."}},
