		[3382] = {"Eine Mannschaft in Bedrängnis",nil, {"Verteidigt Kapitän Vanessa Beltis, ihre Mannschaft und die Passagiere der Herrin des Horizonts gegen den Angriff der Naga. Kapitän Vanessa Beltis muss überleben."}},
		[3385] = {"Der Untermarkt",nil, {"Erschlagt Handelsmeister Kovic und seinen Diener Clunk. Nehmt den Händlerranzen von seinem Leichnam und bringt ihn zu Nilith."}},
		[3402] = {"Der Untermarkt",nil, {"Sprecht mit Vizzklick in Gadgetzan."}},
		[3421] = {"Rückreise",nil, {"Ich werde Euch jetzt zu Sanaths Lager zurückbringen. Macht Euch nichts aus seinen schlechten Manieren - er ist zu allen unhöflich, außer zum Meister. Vielleicht wird er seine Lektion eines Tages lernen."}},
		[3441] = {"Göttliche Vergeltung",nil, {"Lasst Kalaran Windklinge seine Geschichte erzählen."}},
		[3442] = {"Die fehlerlose Flamme",nil, {"Sammelt 4 Herzen der Flamme und 4 Kugeln Golemöl und bringt die Gegenstände zu Kalaran Windklinge."}},
		[3443] = {"Das Schmieden des Schafts",nil, {"Bringt 8 mit Thorium beschichtete Dolche zu Kalaran Windklinge."}},
		[3444] = {"Der Steinreif",nil, {"Holt den Steinreif aus Marvon Nietensuchers Werkstatt in Ratschet."}},
		[3445] = {"Der Versunkene Tempel",nil, {"Sucht Marvon Nietensucher in Tanaris."}},
		[3446] = {"In die Tiefen",nil, {"Sucht den Altar von Hakkar im Versunkenen Tempel in den Sümpfen des Elends."}},
		[3447] = {"Das Geheimnis des Kreises",nil, {"Reist zum Versunkenen Tempel und enthüllt das Geheimnis, das sich in dem Kreis der Statuen verbirgt."}},
		[3448] = {"Die Bürde weitergeben",nil, {"Sprecht mit Magier Tymor in Eisenschmiede."}},
		[3449] = {"Arkane Runen",nil, {"Verwendet das Abschriftenset, um Abpausbilder von der Rune von Beth'Amara, der Rune von Jin'yael, der Rune von Markri und der Rune von Sael'hai herzustellen, bevor Ihr Euch zu der kleinen Insel vor der südlichen Halbinsel von Azshara aufmacht und dem Piloten Xiggs Luntenzünder signalisiert, dass er sie abholen kann."}},
		[3450] = {"Eine einfache Ladung",nil, {"Sprecht mit Xiggs Luntenzünder in Eisenschmiede."}},
		[3451] = {"Signal zum Abholen",nil, {"Sprecht mit Xiggs Luntenzünder in Eisenschmiede und erhaltet von ihm eine Standardleuchtpistole."}},
		[3452] = {"Die Ummantelung der Flamme",nil, {"Bringt ein Symbol von Ragnaros zu Kalaran Windklinge."}},
		[3453] = {"Die Fackel der Vergeltung",nil, {"Wartet bis Kalaran Windklinge die Fackel der Vergeltung vollendet hat."}},
		[3454] = {"Die Fackel der Vergeltung",nil, {"Nehmt die Fackel der Vergeltung."}},
		[3461] = {"Rückkehr zu Magier Tymor",nil, {"Bringt Magier Tymors Abschriftenset zu ihm zurück nach Eisenschmiede."}},
		[3462] = {"Knappe Malzdarr",nil, {"Sprecht mit dem Knappen Malzdarr."}},
		[3463] = {"Setzt sie in Brand!",nil, {"Steckt die Wachpostentürme im Norden, Süden, Osten und Westen in Brand, indem Ihr die Fackel der Vergeltung innerhalb der einzelnen Gebäude benutzt."}},
		[3481] = {"Plunder...",nil, {"Öffnet die Truhe."}},
		[3482] = {"<NYI> <TXT> The Pocked Black Box",nil, {"Bringt diesen Flugschreiber zu Kravel."}},
		[3483] = {"Signal zum Abholen",nil, {"Ah, noch eine Leuchtfeuerwaffe, hm? Die kann ich Euch geben. Und vergesst nicht, Südspitze von Azshara. Es ist eine kleine Insel, die in eine Landungsplattform verwandelt wurde. Ihr könnt sie gar nicht verfehlen, wenn Ihr an der richtigen Stelle sucht."}},
		[3501] = {"Im Dutzend besser",nil, {"Bringt Kum'isha unvollständige Draenethystfragmente und Ihr werdet für jedes, das Ihr bringt, belohnt."}},
		[3502] = {"Des einen Draeneis Plunder...",nil, {"Bringt Kum'isha unvollständige Draenethystfragmente. Jede Art von Monster in den verwüsteten Landen könnte ein Fragment bei sich tragen."}},
		[3503] = {"Treffen mit dem Meister",nil, {"Es gab Zeiten, in denen ich alles von denen, die meinen Meister aufsuchen wollten, verlangen konnte. Heute ist es anders, Ihr müsst nur nach ihm fragen und ich schicke Euch zu ihm. Es ist ärgerlich für mich, aber ich stelle seine Befehle nicht in Frage. Ich kann mich lediglich an Eurer lächerlichen Aufmachung oder Eurem jämmerlichen Gesichtsausdruck erfreuen. Selbst das langweilt mich... Sogar dieses Geschwafel langweilt mich! Möchtet Ihr meinen Meister sehen oder nicht?"}},
		[3504] = {"Verraten",nil, {"Bringt den versiegelten Brief zu Ag'tor Blutfaust nach Azshara."}},
		[3505] = {"Verraten",nil, {"Tötet 10 Blutelfeneintreiber und 10 Blutelfenfeldmesser, bevor Ihr Magus Rimtoris Lager sucht. Sobald Ihr es gefunden habt, sucht nach etwas, das für sie von Bedeutung ist und das wir nehmen können, um sie in einen Kampf zu locken."}},
		[3506] = {"Verraten",nil, {"Zerstört den arkanen Fokussierkristall, der an Magus Rimtori gebunden ist, und tötet sie danach. Bringt dann den Kopf von Magus Rimtori zu Ag'tor Blutfaust nach Azshara."}},
		[3507] = {"Verraten",nil, {"Sprecht mit Belgrom Felshammer in Orgrimmar."}},
		[3508] = {"Den Zauberschutz brechen",nil, {"Wartet, bis Loramus den Erleuchtungszauber vollendet hat."}},
		[3509] = {"Der Name der Bestie",nil, {"Sprecht mit Lord Arkkoroc in Azshara. Beginnt Eure Suche am Rand des Wassers, um den Tempel von Arkkoran zu finden."}},
		[3510] = {"Der Name der Bestie",nil, {"Erschlagt Hetaera und bringt den blutigen Kopf Hetaeras, den geschlagenen Kopf Hetaeras und den verwundeten Kopf Hetaeras zu Lord Arkkoroc."}},
		[3511] = {"Der Name der Bestie",nil, {"Bringt den Härter zurück zu Loramus Thalipedes."}},
		[3512] = {"Mit Eranikus' eigenen Worten",nil, {"Reist in den östlichen Teil von Winterquell und sprecht mit Umbranse, dem Geistsprecher."}},
		[3513] = {"Die runenverzierte Rolle",nil, {"Bringt die runenverzierte Rolle zum nördlichen Wachturm des Brachlandes."}},
		[3514] = {"Hordenpräsenz",nil, {"Tötet die Anführerin des Krallenbaus, Rynthariel die Schlüsselmeisterin."}},
		[3517] = {"Wissen stehlen",nil, {"Sucht die Schrifttafel von Beth'Amara, die Schrifttafel von Jin'yael, die Schrifttafel von Markri und die Schrifttafel von Sael'hai, bevor Ihr zu Jediga in Azshara zurückkehrt."}},
		[3518] = {"Lieferung an Magatha",nil, {"Bringt die Schrifttafel von Beth'Amara zu Magatha in Donnerfels."}},
		[3519] = {"Ein Freund in Not",nil, {"Sprecht mit Dirania Silberschein im Laubschattental."}},
		[3520] = {"Kreischergeister",nil, {"Fangt die Geister von 3 Kreischern in Feralas ein und kehrt dann zu Yeh'kinya beim Dampfdruckpier zurück."}},
		[3521] = {"Iverrons Gegengift",nil, {"Sammelt 7 Hyazinthpilze, 4 Mondblütenlilien und 1 Waldwebersekret für Dirania Silberschein im Laubschattental."}},
		[3522] = {"Iverrons Gegengift",nil, {"Bringt Iverrons Gegengift zu Iverron, bevor die Zeit um ist. Ihr findet Iverron bei der Höhle im Norden."}},
		[3523] = {"Geißel der Niederungen",nil, {"Wenn Ihr Belnistrasz helfen wollt, sprecht noch einmal mit ihm und übergebt ihm den Schwurstein, den er Euch gab."}},
		[3524] = {"An Land gespült",nil, {"Sammelt Meereskreaturknochen von der gestrandeten Meereskreatur unmittelbar südlich von Auberdine und kehrt dann mit ihnen zu Gwennyth Bly'Leggonde nach Auberdine zurück."}},
		[3525] = {"Ausschalten des Götzen",nil, {"Begleitet Belnistrasz zum Götzen der Stacheleber in den Hügeln der Klingenhauer. Beschützt Belnistrasz, während er das Ritual durchführt, um den Götzen auszuschalten."}},
		[3526] = {"Gobliningenieurskunst",nil, {"Wenn Ihr etwas über die Gobliningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Nixx Sprossfeder nach Gadgetzan."}},
		[3527] = {"Die Prophezeiung von Mosh'aru",nil, {"Bringt die erste und die zweite Schrifttafel von Mosh'aru zu Yeh'kinya nach Tanaris."}},
		[3528] = {"Der Gott Hakkar",nil, {"Bringt das gefüllte Ei von Hakkar zu Yeh'kinya nach Tanaris."}},
		[3541] = {"Lieferung an Jes'rimon",nil, {"Bringt die Schrifttafel von Jin'yael zu Jes'rimon nach Orgrimmar."}},
		[3542] = {"Lieferung an Andron Gant",nil, {"Bringt die Schrifttafel von Markri zu Andron Gant nach Unterstadt."}},
		[3561] = {"Lieferung für Erzmagier Xylem",nil, {"Bringt die Schrifttafel von Sael'hai zu Erzmagier Xylem in Azshara."}},
		[3562] = {"Magathas Bezahlung für Jediga",nil, {"Bringt Magathas Notiz zu Jediga in Azshara."}},
		[3563] = {"Jes'rimons Bezahlung für Jediga",nil, {"Bringt Jes'rimons Notiz zu Jediga in Azshara."}},
		[3564] = {"Androns Bezahlung für Jediga",nil, {"Bringt Androns Notiz zu Jediga in Azshara."}},
		[3565] = {"Xylems Bezahlung für Jediga",nil, {"Bringt Xylems Notiz zu Jediga in Azshara."}},
		[3566] = {"Erhebt Euch, Obsidion!",nil, {"Tötet Lathoric den Schwarzen und Obsidion. Kehrt zurück zu Thorius nach Eisenschmiede mit dem Kopf von Lathoric dem Schwarzen und dem Herzen von Obsidion."}},
		[3567] = {"An die Spitze",nil, {"Würdet Ihr den Ausblick von einem der Zwillingskolosse bevorzugen? Ich kann Euch dorthin schicken, <Volk>. Aber überlegt, wie Ihr wieder herunterkommt. Ich kann Euch nur nach oben schicken."}},
		[3568] = {"Verbreitete Verderbnis",nil, {"Füllt alle 4 leeren Phiolen an den Gezeitenteichen entlang der Küste der Ruinen von Eldarath in Azshara und kehrt dann zu Chemiker Miesmut zurück."}},
		[3569] = {"Verbreitete Verderbnis",nil, {"Gebt Miesmuts Elixier Thersa Windweise in Unterstadt."}},
		[3570] = {"Verbreitete Verderbnis",nil, {"Ich hätte mir nie träumen lassen, dass das Elixier so schnell Wirkung zeigen würde. Danke, <Name>. Ich werde mich sofort an die Untersuchung der restlichen Proben machen. Bestimmt werden sie für Lady Silvanas und die Verlassenen schon bald von Nutzen sein."}},
		[3601] = {"Kim'jael, fürwahr!",nil, {"Durchsucht den thalassischen Stützpunkt nach Kim'jaels Kompass, Zielfernrohr, ausgestopftem Huhn und Knobelwizz und bringt ihm dann alles nach Azshara zurück."}},
		[3602] = {"Azsharit",nil, {"Sammelt 20 kristallisierte Azsharite im südlichen Azshara. Ihr könnt den Teufelshund benutzen, damit er Euch beim 'Erschnüffeln' der gut versteckten Ablagerungen behilflich ist. Kehrt zu Loramus zurück, sobald Ihr die Aufgabe erfüllt habt."}},
		[3621] = {"Die Herstellung von Teufelsbann",nil, {"Bringt die Lieferung zu Galvan dem Alten im Schlingendorntal."}},
		[3625] = {"Verzauberte Azsharitteufelswaffe",nil, {"Wartet, bis Galvan die Waffen fertig geschmiedet hat."}},
		[3626] = {"Rückkehr in die verwüsteten Lande",nil, {"Kehrt zum Gefallenen Helden der Horde zurück."}},
		[3627] = {"Das zerbrochene Amulett vereinen",nil, {"Tötet Grol den Zerstörer, Lady Sevine und Erzmagier Allistarj. Nehmt das Amulett von Grol, das Amulett von Sevine und das Amulett von Allistarj von ihren Leichen. Denkt daran, Eure Verzauberte Azsharitteufelswaffe zu BENUTZEN, um die Triade zu schwächen. Auf eine andere Weise können sie nicht getötet werden - aber verwendet sie weise..."}},
		[3628] = {"Ihr seid Rakh'likh, Dämon",nil, {"Vernichtet Razelikh den Entweiher und bringt das abgetrennte Horn des Entweihers zum Gefallenen Helden der Horde. Ihr müsst außerdem den Zauberschutz des Entweihers mit zurückbringen, damit der Gefallene Held ihn zerstören und somit verhindern kann, dass er jemals in die falschen Hände fällt."}},
		[3629] = {"Gobliningenieurskunst",nil, {"Wenn Ihr etwas über die Gobliningenieurskunst erlernen wollt, dann bringt das Handbuch der Ingenieurskunstdisziplinen zu Nixx Sprossfeder nach Gadgetzan."}},
		[3630] = {"Gnomeningenieurskunst",nil, {"Wenn Ihr etwas über die Gnomeningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Tüftlermeister Oberfunks nach Eisenschmiede."}},
		[3631] = {"Teufelsross beschwören",nil, {"Sprecht mit Strahad Farsan im Brachland."}},
		[3632] = {"Gnomeningenieurskunst",nil, {"Wenn Ihr etwas über die Gnomeningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Tüftlermeister Oberfunks nach Eisenschmiede."}},
		[3633] = {"Gobliningenieurskunst",nil, {"Wenn Ihr etwas über die Gobliningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Nixx Sprossfeder nach Gadgetzan."}},
		[3634] = {"Gnomeningenieurskunst",nil, {"Wenn Ihr etwas über die Gnomeningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Tüftlermeister Oberfunks nach Eisenschmiede."}},
		[3635] = {"Gnomeningenieurskunst",nil, {"Wenn Ihr mehr über die Gnomeningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Glotz Widrikus nach Beutebucht."}},
		[3636] = {"Das Licht bringen",nil, {"Erzbischof Benedictus will, dass Ihr Amnennar den Kältebringer in den Hügeln der Klingenhauer tötet."}},
		[3637] = {"Gnomeningenieurskunst",nil, {"Wenn Ihr mehr über die Gnomeningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Glotz Widrikus nach Beutebucht."}},
		[3638] = {"Der Schwur der Geheimhaltung",nil, {"Wenn Ihr ein Gobliningenieur werden wollt, dann müsst Ihr auf den Schwur der Geheimhaltung rechtsklicken und erneut mit Nixx Sprossfeder in Gadgetzan sprechen."}},
		[3639] = {"Zeigt Eure Arbeit",nil, {"Bringt 20 große Eisenbomben, 20 robuste Dynamitstangen und 5 explosive Schafe zu Nixx Sprossfeder nach Gadgetzan."}},
		[3640] = {"Der Schwur der Geheimhaltung",nil, {"Wenn Ihr ein Gnomeningenieur werden wollt, dann müsst Ihr auf den Schwur der Geheimhaltung rechtsklicken und erneut mit Tüftlermeister Oberfunks in Eisenschmiede sprechen."}},
		[3641] = {"Zeigt Eure Arbeit",nil, {"Bringt 6 Mithrilrohre, 1 genaues Zielfernrohr und 2 hochentwickelte Zielattrappen zu Tüftlermeister Oberfunks in Eisenschmiede."}},
		[3642] = {"Der Schwur der Geheimhaltung",nil, {"Wenn Ihr ein Gnomeningenieur werden wollt, dann müsst Ihr auf den Schwur der Geheimhaltung rechtsklicken und erneut mit Glotz Widrikus in Beutebucht sprechen."}},
		[3643] = {"Zeigt Eure Arbeit",nil, {"Bringt 6 Mithrilrohre, 1 genaues Zielfernrohr und 2 hochentwickelte Zielattrappen zu Glotz Widrikus in Beutebucht."}},
		[3644] = {"Mitgliedskarten-Erneuerung",nil, {"Um Eure Mitgliedskarte zu erneuern, müsst Ihr die Erneuerungsgebühr in Höhe von zwei Goldstücken zahlen."}},
		[3645] = {"Mitgliedskarten-Erneuerung",nil, {"Um Eure Mitgliedskarte zu erneuern, müsst Ihr die Erneuerungsgebühr in Höhe von zwei Goldstücken zahlen."}},
		[3646] = {"Mitgliedskarten-Erneuerung",nil, {"Um Eure Mitgliedskarte zu erneuern, müsst Ihr die Erneuerungsgebühr in Höhe von zwei Goldstücken zahlen."}},
		[3647] = {"Mitgliedskarten-Erneuerung",nil, {"Um Eure Mitgliedskarte zu erneuern, müsst Ihr die Erneuerungsgebühr in Höhe von zwei Goldstücken zahlen."}},
		[3661] = {"Elunes Lieblinge?",nil, {"Sammelt 15 Wildekinfedern aus dem Hinterland für Erelas Himmelsbern in Rut'theran."}},
		[3681] = {"Foliant der Offenbarung",nil, {"Sprecht mit Duthorian Rall in Sturmwind."}},
		[3701] = {"Die glimmenden Ruinen von Thaurissan",nil, {"Begebt Euch zu den Ruinen von Thaurissan in der brennenden Steppe und sammelt Informationen über die Relikte von Thaurissan. Kehrt zur königlichen Historikerin Archesonus zurück, wenn Ihr die Informationen besorgt habt."}},
		[3702] = {"Die glimmenden Ruinen von Thaurissan",nil, {"Hört die Erzählung der königlichen Historikerin Archesonus über die Geschichte von Thaurissan."}},
		[3721] = {"Ein eigener OOX",nil, {"Ihr habt drei meiner OOX-Leitroboter gefunden! Ihr habt viel mehr als das getan, worum ich Euch gebeten hatte. Daher will ich Euch etwas Besonderes für Eure Hilfe geben. Dies ist eine Miniaturversion jenes Roboters, den Ihr jetzt kennt und liebt, den OOX!nnIch würde normalerweise niemandem solch ein potenziell tödliches Gerät anvertrauen, aber Ihr wart eine wirklich große Hilfe. Außerdem wurden die Kampfmechanismen und die Leitlogik dieses Exemplars deaktiviert. Aber abgesehen davon ist das ein echter OOX!"}},
		[3741] = {"Hilarys Halskette",nil, {"Sucht Hilarys Halskette und bringt sie Hilary in Seenhain zurück."}},
		[3761] = {"Erde von Un'Goro",nil, {"Bringt 20 Erdproben von Un'Goro zu Ghede auf der Anhöhe der Ältesten in Donnerfels."}},
		[3762] = {"Erzdruide Runentotem helfen",nil, {"Sprecht in Donnerfels mit Erzdruide Hamuul Runentotem."}},
		[3763] = {"Erzdruide Hirschhaupt helfen",nil, {"Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus."}},
		[3764] = {"Erde von Un'Goro",nil, {"Bringt 20 Erdproben von Un'Goro zu Jenal in der Enklave des Cenarius in Darnassus."}},
		[3765] = {"Verderbnis in der Fremde",nil, {"Reist zu Gershala Nachtraunen in Auberdine."}},
		[3781] = {"Morgenkornforschung",nil, {"Bringt den Samenkorngutschein des Erzdruiden zu Mathrengyl Bärenfährte in der Enklave des Cenarius des Darnassus."}},
		[3782] = {"Morgenkornforschung",nil, {"Bringt den Samenkorngutschein zu Bashana Runentotem in Donnerfels."}},
		[3783] = {"Sind wir endlich da, Yeti?",nil, {"Sammelt 10 dicke Yetifelle für Umi Rumpelkicher in der Ewigen Warte."}},
		[3784] = {"Erzdruide Runentotem helfen",nil, {"Sprecht in Donnerfels mit Erzdruide Hamuul Runentotem."}},
		[3785] = {"Morgenkornforschung",nil, {"Benutzt einen Immergrünbeutel mit einem Paket Tharlendrissamenkörner sowie zwei Erdproben von Un'Goro, um Morgenkorn zu kultivieren. Bringt 10 Einheiten Morgenkorn zu Mathrengyl Bärenfährte in Darnassus."}},
		[3786] = {"Morgenkornforschung",nil, {"Benutzt einen Immergrünbeutel mit einem Paket Tharlendrissamenkörner sowie zwei Erdproben von Un'Goro, um Morgenkorn zu kultivieren. Bringt 10 Einheiten Morgenkorn zu Bashana Runentotem in Donnerfels."}},
		[3787] = {"Lebenshands Bitte",nil, {"Sprecht mit Quintis Lebenshand in der Mondfederfeste."}},
		[3788] = {"Lebenshands Bitte",nil, {"Sprecht mit Quintis Lebenshand in der Mondfederfeste."}},
		[3789] = {"Erzdruide Hirschhaupt helfen",nil, {"Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus."}},
		[3790] = {"Erzdruide Hirschhaupt helfen",nil, {"Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus."}},
		[3791] = {"Das Geheimnis des Morgenkorns",nil, {"Bringt 10 Einheiten Morgenkorn zu Quintis Lebenshand in der Mondfederfeste."}},
		[3792] = {"Morgenkorn für die Mondfederfeste",nil, {"Abermals hallo, <Name>. Meine Forschung macht gute Fortschritte, aber wenn Ihr fünf weitere Einheiten Morgenkorn habt, würde ich Euch die sehr gern abnehmen. Na ja... 'gern' ist nicht wirklich der richtige Ausdruck dafür; je länger ich mich in seiner Umgebung befinde, desto unwohler fühle ich mich. Aber für meine Forschungen ist es nun mal nötig, dass ich mich zusammenreiße und weiterarbeite."}},
		[3801] = {"Dunkeleisenerbe",nil, {"Sprecht mit Franclorn Schmiedevater, wenn Ihr daran interessiert seid, einen Schlüssel für die Hauptstadt zu erhalten."}},
		[3802] = {"Dunkeleisenerbe",nil, {"Erschlagt Fineous Dunkelader und bergt den großen Hammer Eisenhölle. Bringt Eisenhölle zum Schrein von Thaurissan und legt ihn auf die Statue von Franclorn Schmiedevater."}},
		[3803] = {"Morgenkorn nach Darnassus",nil, {"Der Erzdruide sucht ständig nach mehr Morgenkorn für die weiteren Forschungsarbeiten des Zirkels des Cenarius. Wir nehmen jeweils zehn Einheiten ab und belohnen Euch mit umfangreichen Dingen, die Ihr nützlich finden werdet. Wenn Ihr zehn Einheiten habt, lasst es mich wissen, und ich werde Euch entsprechend belohnen."}},
		[3804] = {"Morgenkorn nach Donnerfels",nil, {"Der Erzdruide sucht ständig nach mehr Morgenkorn für das Forschungsprojekt des Zirkels des Cenarius über den Un'Goro Krater. Wir nehmen jeweils zehn Einheiten ab und belohnen Euch mit umfangreichen Dingen, die Ihr nützlich finden werdet. Wenn Ihr zehn Einheiten habt, lasst es mich wissen, und ich werde Euch entsprechend belohnen."}},
		[3821] = {"Schreckensfels",nil, {"Begebt Euch in die brennende Steppe und findet heraus, was aus Sha'ni Ehrenhauer geworden ist."}},
		[3822] = {"Krom'Grul",nil, {"Holt Sha'nis Nasenring von Krom'Grul zurück und bringt ihn zu Thal'trak in Kargath. Durchsucht die verschiedenen Höhlen und Minen im Schreckensfels."}},
		[3823] = {"Die Feuermagenoger auslöschen",nil, {"Tötet 15 Ogermagier der Feuermägen, 7 Oger der Feuermägen sowie 7 Schläger der Feuermägen und kehrt zu Oralius zurück, wenn Ihr fertig seid."}},
		[3824] = {"Gor'tesh der Schläger-Lord",nil, {"Sucht Gor'tesh irgendwo im Westen des Schreckensfelsens. Tötet Gor'tesh und bringt dann seinen abgehackten Kopf zu Oralius in der brennenden Steppe zurück."}},
		[3825] = {"Ogerkopf auf einem Pfahl = Party",nil, {"Nehmt Gor'teshs abgehackten Kopf und platziert ihn auf dem Gipfel des Schreckensfelsens. Sucht nach einem weichen Erdhügel, um den Pfahl aufzustellen."}},
		[3841] = {"Waise sucht Zuhause",nil, {"Sprecht mit Quentin in Tausend Nadeln."}},
		[3842] = {"Kurz vor dem Schlüpfen",nil, {"Bringt 2 Elixiere der Seelenstärke zu Quentin in Tausend Nadeln."}},
		[3843] = {"Das neueste Mitglied der Familie",nil, {"Bringt Euer empfindliches Grimmlingflitzerei ins Hinterland und sprecht mit Agnar Tiermeister, aber beeilt Euch. Ihr habt nur eine Stunde Zeit, ihn zu finden. Dann stirbt das Jungtier in dem Ei."}},
		[3844] = {"Es ist für jeden ein Geheimnis",nil, {"Sucht in dem Tümpel nach einem Hinweis darauf, wem dieses havarierte Floß gehören könnte."}},
		[3845] = {"Es ist für jeden ein Geheimnis",nil, {"Untersucht die Gegenstände in dem Bündel und bringt sie zu ihrem Besitzer."}},
		[3861] = {"GLUCK!",nil, {"Beschafft Euch etwas Spezialhühnerfutter und kehrt zu dem Huhn zurück, das sich mit Euch angefreundet hat. Wenn Ihr es gefunden habt, dann benutzt erst /cheer, ehe Ihr dem Huhn das Spezialhühnerfutter gebt."}},
		[3881] = {"Rettungsexpedition",nil, {"Bringt eine Kiste mit Nahrungsmitteln und Forscherausrüstung zu Williden Marschall im Krater von Un'Goro."}},
		[3882] = {"Knochenklappern",nil, {"Bringt 8 Dinosaurierknochen zu Funks Nilminer im Krater von Un'Goro."}},
		[3883] = {"Eine fremdartige Ökologie",nil, {"Verwendet die Kratzphiole, um damit eine Schwarmbauwandprobe von einer der Schwarmbrutstätten der Gorishi im Krater von Un'Goro zu holen. Sucht nach den Kammern mit der hängenden Larvenbrut. Bringt die Schwarmbauwandprobe zu Hol'anyee Marschall im Krater von Un'Goro."}},
		[3884] = {"Willidens Tagebuch",nil, {"Bringt das Tagebuch zu Williden Marschall zurück. Er befindet sich irgendwo im Krater von Un'Goro."}},
		[3901] = {"Klappernde Skelette",nil, {"Tötet 12 klappernde Skelette und kehrt zu Schattenpriester Sarvis in Todesend zurück, wenn Ihr fertig seid."}},
		[3902] = {"Beutesuche in Todesend",nil, {"Durchsucht Todesend und Umgebung nach 6 Stücken aufgestöberter Ware und bringt sie zu Todeswache Saltain zurück."}},
		[3903] = {"Milly Geltmark",nil, {"Sprecht mit Milly Geltmark."}},
		[3904] = {"Millys Ernte",nil, {"Bringt 8 Kisten mit Millys Ernte zu Milly Geltmark in der Nähe der Abtei von Nordhain."}},
		[3905] = {"Traubenliste",nil, {"Bringt die Traubenliste zu Bruder Neals in der Abtei von Nordhain."}},
		[3906] = {"Disharmonie der Flamme",nil, {"Reist zum Steinbruch am Schwarzfels und tötet Übermeister Pyron. Kehrt zu Donnerherz zurück, sobald Ihr den Auftrag erledigt habt."}},
		[3907] = {"Disharmonie des Feuers",nil, {"Betretet die Schwarzfelstiefen und spürt Lord Incendius auf. Tötet ihn und bringt jegliche Informationsquelle, die Ihr finden könnt, zu Donnerherz."}},
		[3908] = {"Es ist für jeden ein Geheimnis",nil, {"Bringt Linkens Schwert zu Donova Schneeweh in Winterquell."}},
		[3909] = {"Das Videre-Elixier",nil, {"Sucht nach Gregan Hopfenspei im nördlichen Feralas. Lasst Euch von ihm erklären, wie Ihr Euch das Videre-Elixier beschaffen könnt, und kehrt dann zu Donova Schneeweh nach Winterquell zurück."}},
		[3911] = {"Das letzte Element",nil, {"Begebt Euch in die Schwarzfelstiefen und beschafft 10 Essenzen der Elemente. Euer erster Gedanke ist, die Golems und die Schöpfer der Golems zu suchen. Doch Ihr erinnert Euch, dass Vivian Lagrave auch etwas von Elementaren vor sich hin gemurmelt hat."}},
		[3912] = {"Treffen am Grab",nil, {"Geht zum Friedhof außerhalb von Gadgetzan und benutzt dort das Videre-Elixier. Befolgt Donovas übrige Anweisungen, um Gaeriyan zu finden."}},
		[3913] = {"Grabesstille",nil, {"Wenn Ihr auferstanden seid, sucht nach dem Grabstein, von dem Gaeriyan Euch erzählt hat."}},
		[3914] = {"Linkens Schwert",nil, {"Liefert Linkens überragendes Schwert bei Linken im Krater von Un'Goro ab."}},
		[3921] = {"Wenikee Kübelbolz",nil, {"Bringt das zerbrochene Samophlang zu Wenikee Kübelbolz."}},
		[3922] = {"Schraubnieten",nil, {"Bringt 15 Schraubnieten zu Wenikee Kübelbolz im Brachland."}},
		[3923] = {"Rilli Schmierklump",nil, {"Bringt das zerbrochene und ramponierte Samophlang nach Orgrimmar zu Rilli Schmierklump."}},
		[3924] = {"Samophlanghandbuch",nil, {"Bringt das Samophlanghandbuch nach Orgrimmar zu Rilli Schmierklump."}},
		[3941] = {"Die Hilfe des Gnoms",nil, {"Sucht J.D. Collie in der Höhle in Marschalls Zuflucht auf."}},
		[3942] = {"Linkens Andenken",nil, {"Sucht Eridan Blauwind im südlichen Teil des Teufelswalds."}},
		[3961] = {"Linkens Abenteuer",nil, {"Bringt das Silbertotem von Aquementas zu Linken in Marschalls Zuflucht."}},
		[3962] = {"Allein ist es gefährlich",nil, {"Begebt Euch zum Feuersäulengrat südlich von Marschalls Zuflucht. Sucht Sturmflamme und verwendet das Totem, um die schützende Aura, die ihn umgibt, zu entfernen. Sobald er besiegt ist, holt die Goldene Flamme aus der Höhle."}},
		[3981] = {"Kommandant Gor'shak",nil, {"Sucht Kommandant Gor'shak in den Schwarzfelstiefen. Ihr erinnert Euch, dass auf dem primitiv gezeichneten Bild des Orcs auch Gitter vor dem Gesicht zu sehen waren. Vielleicht solltet Ihr nach einer Art Gefängnis suchen."}},
		[3982] = {"Was ist los?",nil, {"Verteidigt Gor'shak."}},
		[4001] = {"Was ist los?",nil, {"Sprecht mit Kharan Hammermacht und sammelt Informationen über die Entführung von Prinzessin Moira Bronzebart. Bringt diese Informationen dann zu Thrall nach Orgrimmar. Ihr erinnert Euch, dass Gor'shak gesagt hat, Kharan werde in einer Zelle in seiner Nähe festgehalten."}},
		[4002] = {"Das östliche Königreich",nil, {"Sprecht mit Thrall, wenn Ihr bereit seid, die von ihm geplante Mission zu übernehmen."}},
		[4003] = {"Die königliche Rettung",nil, {"Tötet Imperator Dagran Thaurissan und befreit Prinzessin Moira Bronzebart von seinem bösen Zauber."}},
		[4004] = {"Ist die Prinzessin gerettet?",nil, {"Kehrt zu Thrall zurück!"}},
		[4005] = {"Aquementas",nil, {"Bringt das Buch von Aquor, die silbrigen Klauen und das Eisenstammherz nach Tanaris. Benutzt das Buch von Aquor, um Aquementas zu beschwören. Wenn Ihr das vollständige Totem habt, bringt es zu J.D. Collie in Marschalls Zuflucht zurück."}},
		[4021] = {"Gegenangriff!",nil, {"Bringt ein Teil von Krom'zars Flagge zu Regthar Totenfurt, westlich des Wegekreuzes."}},
		[4022] = {"Eine Kostprobe der Flamme",nil, {"Zeigt Cyrus Therepentous die abgestreifte Haut des schwarzen Drachenschwarms, die Ihr von Kalaran Windklinge erhalten habt."}},
		[4023] = {"Eine Kostprobe der Flamme",nil, {"Zeigt Cyrus Therepentous einen Beweis für Eure Nützlichkeit. Ihr habt so ein Gefühl, dass Cyrus bereits weiß, dass Ihr unwürdig seid."}},
		[4024] = {"Eine Kostprobe der Flamme",nil, {"Begebt Euch in die Schwarzfelstiefen und tötet Bael'Gar. Ihr wisst lediglich, dass der Riese irgendwo in den Schwarzfelstiefen haust. Vergesst nicht, die veränderte Haut des schwarzen Drachenschwarms mit Bael'Gars Überresten zu benutzen und damit die feurige Essenz einzufangen. Bringt die eingeschlossene feurige Essenz zu Cyrus Therepentous zurück."}},
		[4041] = {"Das Videre-Elixier",nil, {"Klar, wenn Ihr mir eine Evowurzel bringt, dann kann ich Euch auch das Videre-Elixier machen. Ich weiß alle möglichen Dinge über Kräuter und Alchimie... Ich könnte Euch Geschichten erzählen... zum Beispiel, wie ich mal Gold aus dem Käse gemacht habe, den ich eigentlich zum Mittagessen... he! Wo wollt Ihr hin?"}},
		[4061] = {"Aufstieg der Maschinen",nil, {"Begebt Euch in die brennende Steppe und beschafft für Theodora Mulvadania 10 gerissene Elementarsplitter. Euch fällt ein, dass Theodora gesagt hat, die Golems und Elementare in der Gegend seien eine gute Quelle für solche Splitter."}},
		[4062] = {"Aufstieg der Maschinen",nil, {"Bringt die Elementarsplitterprobe zu Lotwil Veriatus. Ihr erinnert Euch, dass Theodora sagte, Lotwil sei in einem Lager im Osten stationiert."}},
		[4063] = {"Aufstieg der Maschinen",nil, {"Sucht und tötet Golemlord Argelmach. Bringt Lotwil seinen Kopf. Außerdem müsst Ihr 10 intakte Elementarkerne von den Wuthäschergolems und Kriegshetzerkonstrukten, die Argelmach beschützen, beschaffen. Das wisst Ihr, weil Ihr übernatürliche Fähigkeiten habt."}},
		[4081] = {"SOFORT TÖTEN: Dunkeleisenzwerge",nil, {"Begebt Euch in die Schwarzfelstiefen und vernichtet die üblen Aggressoren!nnKriegsherr Bluthauer möchte, dass Ihr 15 Gardisten der Zorneshämmer, 10 Aufseher der Zorneshämmer und 5 Fußsoldaten der Zorneshämmer tötet. Kehrt zu ihm zurück, sobald Ihr die Aufgabe erfüllt habt."}},
		[4082] = {"SOFORT TÖTEN: Hochrangige Führungskräfte der Dunkeleisenzwerge",nil, {"Begebt Euch in die Schwarzfelstiefen und vernichtet die üblen Aggressoren!nnKriegsherr Bluthauer möchte, dass Ihr 10 Sanitäter der Zorneshämmer, 10 Soldaten der Zorneshämmer und 10 Offiziere der Zorneshämmer tötet. Kehrt zu ihm zurück, sobald Ihr die Aufgabe erfüllt habt."}},
		[4083] = {"Der spektrale Kelch",nil, {"Der spektrale Kelch schwebt in der Luft, leicht ansteigend und dann absinkend, so, als bewege er sich im Takt eines sterbenden Herzens."}},
		[4084] = {"Silberherz",nil, {"Beschafft 11 silbrige Klauen und 1 Eisenstammherz für Eridan Blauwind im Teufelswald."}},
		[4101] = {"Den Teufelswald säubern",nil, {"Bringt 15 Einheiten Blutharz zu Arathandris Silberwolke im Teufelswald."}},
		[4102] = {"Den Teufelswald säubern",nil, {"Bringt 15 Einheiten Blutharz zu Maybess Flussbrise im Teufelswald."}},
		[4103] = {"Salbe durch Jagd",nil, {"Wer ein cenarisches Leuchtfeuer besitzt, kann mit diesem die verderbten Seelensplitter in den besudelten Bestien sehen, die zum Wohl der Allgemeinheit vernichtet werden. Ich schleife Splitter zu einem nutzbaren Reagenz, das für die Herstellung von cenarischer Pflanzensalbe verwendet wird. Wir werden diese Salbe benutzen, um verderbte Pflanzen wieder in gesunde zu verwandeln. Im Austausch für diese Splitter werde ich Euch etwas cenarische Pflanzensalbe geben, die ich bereits vorbereitet habe."}},
		[4104] = {"Salbe durch Bergbau",nil, {"Wenn Ihr im Besitz eines cenarischen Leuchtfeuers seid, während Ihr im Teufelswald Bergbau betreibt, könnt Ihr das besudelte Vitriol sehen, von dem eine Ader Rohmetall verderbt wird. Ich weiß, wie man dieses Vitriol läutert und zu einem Reagenz verarbeitet, aus dem cenarische Pflanzensalbe hergestellt wird. Diese Salbe verwendet Ihr dann an Pflanzen, die Ihr draußen in der Wildnis findet und die gerettet werden müssen. Wenn Ihr verderbtes Vitriol gesammelt habt, werde ich Euch etwas cenarische Pflanzensalbe geben, die ich bereits vorbereitet habe."}},
		[4105] = {"Salbe durch Sammlung",nil, {"Mit einem cenarischen Leuchtfeuer können erfahrene Kräuterkundige die heimtückische Teufelsranke erkennen, ein bösartiges Produkt der Verderbnis des Teufelswalds. Dank meiner Arbeit verfüge ich über das Wissen, die Teufelsranke zu einem Säuberungsreagenz verarbeiten zu können. Dieses verwenden wir für etwas, das wir jetzt cenarische Pflanzensalbe nennen. Wenn Ihr im Teufelswald Kräuter sammelt, dann solltet Ihr, solange Ihr das Leuchtfeuer bei Euch habt, auf Teufelsranken stoßen. Im Austausch gegen das, was Ihr mir bringt, werde ich Euch fertige cenarische Pflanzensalbe geben."}},
		[4106] = {"Salbe durch Kürschnerei",nil, {"Mit einem cenarischen Leuchtfeuer können gut ausgebildete Kürschner die Flecken besudelter Haut auf den im Teufelswald erbeuteten Fellen erkennen. Ich wandle diese scheinbar nutzlosen Flecken in eine dicke Paste um, mit der die cenarische Pflanzensalbe aktiviert wird. Wenn Ihr hier im Teufelswald Eure Beute häutet, dann solltet Ihr unbedingt immer ein Leuchtfeuer bei Euch haben. Sobald Ihr besudelte Flecken gesammelt habt, bringt sie mir. Ich gebe Euch etwas cenarische Pflanzensalbe dafür, die ich bereits hergestellt habe."}},
		[4107] = {"Salbe durch Entzauberung",nil, {"Die Macht eines entzauberten magischen Gegenstandes ist in der von ihm geschaffenen Essenz spürbar. Ich habe einen Weg gefunden, um die rohe Essenz, die von Verzauberern verwendet wird, in ein geeignetes Reagenz für die cenarische Pflanzensalbe umzuwandeln. Dazu braucht Ihr kein cenarisches Leuchtfeuer; jeder angemessen entzauberte Gegenstand, der geringe Netheressenzen abgibt, ist dafür geeignet. Ich brauche nur eine einzige geringe Netheressenz; im Austausch dafür werde ich Euch cenarische Pflanzensalben geben. Die Essenz ist hochwirksam - ganz wenig davon genügt, um viel zu erreichen."}},
		[4108] = {"Salbe durch Jagd",nil, {"Wer ein cenarisches Leuchtfeuer besitzt, kann mit diesem die verderbten Seelensplitter in den besudelten Bestien sehen, die zum Wohl der Allgemeinheit vernichtet werden. Ich schleife Splitter zu einem nutzbaren Reagenz, das für die Herstellung von cenarischer Pflanzensalbe verwendet wird. Wir werden diese Salbe benutzen, um verderbte Pflanzen wieder in gesunde zu verwandeln. Im Austausch für diese Splitter werde ich Euch etwas cenarische Pflanzensalbe geben, die ich bereits vorbereitet habe."}},
