		[4112] = {"Salbe durch Entzauberung",nil, {"Die Macht eines entzauberten magischen Gegenstandes ist in der von ihm geschaffenen Essenz spürbar. Ich habe einen Weg gefunden, um die rohe Essenz, die von Verzauberern verwendet wird, in ein geeignetes Reagenz für die cenarische Pflanzensalbe umzuwandeln. Dazu braucht Ihr kein cenarisches Leuchtfeuer; jeder angemessen entzauberte Gegenstand, der geringe Netheressenzen abgibt, ist dafür geeignet. Ich brauche nur eine einzige geringe Netheressenz; im Austausch dafür werde ich Euch cenarische Pflanzensalben geben. Die Essenz ist hochwirksam - ganz wenig davon genügt, um viel zu erreichen."}},
		[4113] = {"Verderbte Liedblume",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, was wie eine Liedblumenpflanze aussieht. Ein stechender, ungesunder Geruch geht von der Pflanze aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
		[4114] = {"Verderbte Liedblume",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, das wie eine Liedblumenpflanze aussieht. Ein stechender, ungesunder Geruch geht von der Pflanze aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
		[4115] = {"Verderbte Windblüte",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, was wie eine Windblütenpflanze aussieht. Die Beeren, die von der Pflanze herabhängen, sehen verfault und giftig aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
		[4116] = {"Verderbte Liedblume",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, das wie eine Liedblumenpflanze aussieht. Ein stechender, ungesunder Geruch geht von der Pflanze aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
		[4117] = {"Verderbte Peitscherwurzel",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, was wie eine Peitscherwurzelpflanze aussieht. Die sichtbaren Wurzeln der Pflanze sehen zäh und ledrig aus und ein schleimiger Film tropft von der Pflanze. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
		[4118] = {"Verderbte Liedblume",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, das wie eine Liedblumenpflanze aussieht. Ein stechender, ungesunder Geruch geht von der Pflanze aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
		[4119] = {"Verderbter Nachtdrache",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, was wie eine Nachtdrachenpflanze aussieht. Die Früchte, die von der Pflanze herabhängen, sehen verfault und giftig aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
		[4120] = {"Die Kraft der Verderbnis",nil, {"Talo Dornhuf im Camp Mojache in Feralas möchte, dass Ihr 12 Wutklauengrizzlys und 12 Teufelspfotenverheerer im Teufelswald erledigt."}},
		[4121] = {"Gefährliche Zwickmühle",nil, {"Eskortiert Euren Gefangenen, Grark Lorkrub, durch die brennende Steppe und durch den Schwarzfels in die sengende Schlucht. Euch fällt ein, dass Lexlort gesagt hat, seine Männer würden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen. Ihr werdet außerdem Eure Thoriumfesseln an Lexlort übergeben müssen."}},
		[4122] = {"Grark Lorkrub",nil, {"Begebt Euch in die brennende Steppe und sucht Grark Lorkrub. Ihr erinnert Euch, dass Lexlort gesagt hat, er sei zuletzt in einer großen Festung des Schwarzfelsklans gesehen worden. Wenn Ihr Grark Lorkrub gefunden habt, verwendet die Thoriumfesseln, um ihn zu binden, und bringt ihn dann über den Schwarzfels in die sengende Schlucht zurück. Lexlorts Männer werden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen."}},
		[4123] = {"Das Herz des Berges",nil, {"Bringt das 'Herz des Berges' zu Maxwort Funkelglanz in der brennenden Steppe."}},
		[4124] = {"Der vermisste Kurier",nil, {"Sprecht mit Ginro Herdfeuer in der Mondfederfeste."}},
		[4125] = {"Der vermisste Kurier",nil, {"Sucht das Boot des Kuriers irgendwo entlang der Küste von Feralas."}},
		[4126] = {"Hurley Pestatem",nil, {"Bringt Ragnar Donnerbräu in Kharanos das verlorene Donnerbräurezept."}},
		[4127] = {"Havarie",nil, {"Kehrt zur Mondfederfeste zurück und zeigt Ginro Herdfeuer das gefundene Messer."}},
		[4128] = {"Ragnar Donnerbräu",nil, {"Sprecht mit Ragnar Donnerbräu."}},
		[4129] = {"Das wieder aufgetauchte Messer",nil, {"Bringt Quintis Lebenshand in der Mondfederfeste das gefundene Messer."}},
		[4130] = {"Psychometrie-Sitzung",nil, {"Sprecht mit Ginro Herdfeuer in der Mondfederfeste."}},
		[4131] = {"Die Waldpfotengnolle",nil, {"Durchsucht die Lager der Waldpfotengnolle im Süden von Feralas nach Hinweisen auf den Verbleib des Kuriers oder seiner Habseligkeiten."}},
		[4132] = {"Operation: Tod dem Zornesschmied",nil, {"Begebt Euch zu den Schwarzfelstiefen und eliminiert General Zornesschmied! Kehrt zum Kriegsherrn Bluthauer zurück, sobald Ihr diese Aufgabe erledigt habt."}},
		[4133] = {"Vivian Lagrave",nil, {"Sprecht mit Schattenmeisterin Vivian Lagrave in Kargath."}},
		[4134] = {"Verlorenes Donnerbräurezept",nil, {"Bringt Vivian Lagrave in Kargath das verlorene Donnerbräurezept."}},
		[4135] = {"Die verwundene Tiefe",nil, {"Da Ihr jetzt Raschals letzten bekannten Aufenthaltsort in Erfahrung gebracht habt, setzt Ihr die Suche nach ihm oder seinen sterblichen Überresten in der verwundenen Tiefe fort. Laut Notiz soll dieser Ort südlich der Lager der Waldpfotengnolle liegen."}},
		[4136] = {"Ribbly Schraubstutz",nil, {"Bringt Yuka Schraubstutz in der brennenden Steppe Ribblys Kopf."}},
		[4141] = {"Muigin und Larion",nil, {"Sammelt 15 Blutblüten und kehrt zu Muigin zurück."}},
		[4142] = {"Ein Besuch bei Gregan",nil, {"Bringt Gregan Hopfenspei in Feralas eine Blutblüte."}},
		[4143] = {"Der Dunst des Bösen",nil, {"Sammelt 5 Proben Dunst der Atal'ai und bringt sie Muigin im Krater von Un'Goro."}},
		[4144] = {"Blutblütensprösslinge",nil, {"Das ist mein Plan, <Name>: Zieht los und sammelt ein paar Blutblütensprösslinge - Ihr wisst schon, das sind Pflanzen, die noch nicht ganz ausgetrieben haben, und bringt sie mir. Dann werde ich sehen, was man mit dem Dunst der Atal'ai anfangen kann. Ihr haltet mich vielleicht für rachsüchtig ... Und damit liegt Ihr absolut richtig! nnDie Wahrheit ist, ich weiß, wie man hier wegkommt, aber solange Larion nicht zugibt, dass ich Recht habe, rühre ich mich nicht von der Stelle!nnUnd nun hinfort mit Euch. Sucht diese Blutblütensprösslinge, dann gönnen wir uns ein bisschen Spaß!"}},
		[4145] = {"Larion und Muigin",nil, {"Jagt 5 Blutblütenpeitscher, 5 Blutblütendrescher, 5 Blutblütenschinder und 5 Blutblütenfallen für Larion in Marschalls Zuflucht."}},
		[4146] = {"Schrumpftreibstoff",nil, {"Bringt Larion in Marschalls Zuflucht den ungeladenen Schrumpfer und 5 Proben Dunst der Atal'ai."}},
		[4147] = {"Marvons Werkstatt",nil, {"Reist zu Marvon Nietensuchers Werkstatt in Ratschet."}},
		[4148] = {"Blutblütenschrumpfer",nil, {"Damit der Schrumpfer funktioniert, brauchen wir ein Exemplar der Kreaturen, für die er eingestellt werden soll. Geht zum Krater und holt einen Blutblütensprössling. Dann kann ich ihn so einstellen, dass er bei Muigins kleinen Freunden funktioniert... Wenn der Typ nicht so stur wäre, könnten wir alle schon längst auf dem Heimweg sein. Aber jetzt muss ich ihm erst einmal beweisen, dass mir seine Faxen nicht das Geringste ausmachen!"}},
		[4161] = {"Rezept der Kaldorei",nil, {"Sammelt 7 kleine Spinnenbeine für Zarrin in Dolanaar."}},
		[4181] = {"Gobliningenieurskunst",nil, {"Wenn Ihr mehr über die Gobliningenieurskunst wissen wollt, dann bringt das Handbuch der Ingenieurskunstdisziplinen zu Nixx Sprossfeder nach Gadgetzan."}},
		[4182] = {"Drachkin-Bedrohung",nil, {"Erschlagt 15 schwarze Brutlinge, 10 Exemplare der schwarzen Großdrachenbrut, 4 Schwarzwyrmkins und 1 Schwarzdrachen. Kehrt zu Helendis Flusshorn zurück, sobald die Aufgabe erledigt ist."}},
		[4183] = {"Die wahren Meister",nil, {"Reist nach Seenhain und händigt Magistrat Solomon Helendis Flusshorns Brief aus."}},
		[4184] = {"Die wahren Meister",nil, {"Reist nach Sturmwind und tragt Solomons Hilfegesuch dem Hochlord Bolvar Fordragon vor. Bolvar residiert in der Burg Sturmwind."}},
		[4185] = {"Die wahren Meister",nil, {"Sprecht zuerst mit Lady Katrana Prestor, dann mit Hochlord Bolvar Fordragon."}},
		[4186] = {"Die wahren Meister",nil, {"Händigt Bolvars Erlass an Magistrat Solomon in Seenhain aus."}},
		[4201] = {"Der Liebestrank",nil, {"Bringt 4 Gromsblut-Kräuter, 10 Riesensilbervenen und Nagmaras gefüllte Phiole zu Herrin Nagmara in den Schwarzfelstiefen."}},
		[4221] = {"Verderbte Windblüte",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, das wie eine Windblütenpflanze aussieht. Die Beeren, die von der Pflanze herabhängen, sehen verfault und giftig aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
		[4222] = {"Verderbte Windblüte",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, das wie eine Windblütenpflanze aussieht. Die Beeren, die von der Pflanze herabhängen, sehen verfault und giftig aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
		[4223] = {"Die wahren Meister",nil, {"Sprecht mit Marschall Maxwell in der brennenden Steppe."}},
		[4224] = {"Die wahren Meister",nil, {"Sprecht mit dem struppigen John, um etwas über Marschall Windsors Schicksal zu erfahren, und kehrt dann zu Marschall Maxwell zurück, wenn Ihr diese Aufgabe erledigt habt. Ihr erinnert Euch daran, dass Marschall Maxwell Euch geraten hat, ihn in einer Höhle im Norden zu suchen."}},
		[4241] = {"Marschall Windsor",nil, {"Reist zum Schwarzfels im Nordwesten und dann weiter zu den Schwarzfelstiefen. Findet heraus, was aus Marschall Windsor geworden ist. Ihr erinnert Euch daran, dass der struppige John sagte, man hätte Windsor in ein Gefängnis verschleppt."}},
		[4242] = {"Verlorene Hoffnung",nil, {"Überbringt Marschall Maxwell die schlechten Neuigkeiten."}},
		[4243] = {"Chasing A-Me 01",nil, {"Sucht im Krater von Un'Goro nach A-Me 01."}},
		[4244] = {"Chasing A-Me 01",nil, {"Sucht ein Mithrilgehäuse und bringt es zu A-Me 01 im Krater von Un'Goro."}},
		[4245] = {"Chasing A-Me 01",nil, {"Begleitet A-Me 01 zu Karna Wirrbart im Krater von Un'Goro."}},
		[4261] = {"Alter Geist",nil, {"Bringt Arei in Sicherheit und sprecht dann mit Kayneth Stillwind in Waldeslied, im Osten des Eschentals."}},
		[4262] = {"Übermeister Pyron",nil, {"Erschlagt Übermeister Pyron und kehrt dann zu Jalinda Sprig zurück. Ihr erinnert Euch an Jalindas Worte, dass Pyron den Steinbruch bewachen würde. Vielleicht solltet Ihr dort nachsehen?"}},
		[4263] = {"Incendius!",nil, {"Sucht Lord Incendius in den Schwarzfelstiefen und vernichtet ihn!"}},
		[4264] = {"Eine zusammengeknüllte Notiz",nil, {"Soeben seid Ihr auf etwas gestoßen, das Marschall Windsor mit Sicherheit sehr interessiert. Vielleicht besteht ja doch noch Hoffnung."}},
		[4265] = {"Flucht aus dem Schwarmbau",nil, {"Begleitet Raschal sicher aus dem Schwarmbau der Zukk'ash hinaus. Kehrt zu Ginro Herdfeuer in der Mondfederfeste zurück und lasst ihn wissen, dass Raschal am Leben ist und dass es ihm gut geht."}},
		[4266] = {"Der Empfang eines Helden",nil, {"Sprecht mit Shandris Mondfeder in der Mondfederfeste."}},
		[4267] = {"Aufstieg der Silithiden",nil, {"Bringt Raschals Bericht zu Gracina Glaubensmacht in Darnassus."}},
		[4281] = {"Thalanaar-Lieferung",nil, {"Bringt das nicht ausgelieferte Paket zu Falfindel Wegeshut in Thalanaar."}},
		[4282] = {"Ein Funken Hoffnung",nil, {"Holt Marschall Windsors verlorene Informationen zurück. Marschall Windsor glaubt, dass sich die Informationen in den Händen des Golemlords Argelmach und des Generals Zornesschmied befinden."}},
		[4283] = {"FÜNFZIG! Jawoll!",nil, {"Oralius will 50 Medaillons des Schwarzfelsklans haben. Die Chancen stehen gut, dass man die Medaillons bei den Orcs in der brennenden Steppe findet."}},
		[4284] = {"Machtkristalle",nil, {"Sammelt 7 Machtkristalle von jeder Farbe: rot, blau, gelb und grün. Bringt sie zu J. D. Collie in Marschalls Zuflucht."}},
		[4285] = {"Der Nordpylon",nil, {"Findet den Standort des nördlichen Kristallpylonen im Krater von Un'Goro."}},
		[4286] = {"Feine Sachen",nil, {"Reist in die Schwarzfelstiefen und holt 20 Dunkeleisengürteltaschen. Kehrt zu Oralius zurück, sobald die Aufgabe erledigt ist. Ihr nehmt an, dass die Dunkeleisenzwerge in den Schwarzfelstiefen diese 'Gürteltaschen'-Dinger tragen."}},
		[4287] = {"Der Ostpylon",nil, {"Findet den Standort des östlichen Kristallpylonen im Krater von Un'Goro."}},
		[4288] = {"Der Westpylon",nil, {"Findet den Standort des westlichen Kristallpylonen."}},
		[4289] = {"Die Affen von Un'Goro",nil, {"Bringt Torwa Pfadfinder am Eingang zum Krater von Un'Goro 2 Gorillapelze von Un'Goro, 2 Stampferpelze von Un'Goro und 2 Donnererpelze von Un'Goro."}},
		[4290] = {"Lar'korwis Mahl",nil, {"Sucht das Gerippe von Lar'korwis letzter Jagdbeute und stehlt einen Teil eines Dreschadongerippes; kehrt dann zu Torwa Pfadfinder im Krater von Un'Goro zurück."}},
		[4291] = {"Lar'korwis Fährte",nil, {"Bringt Torwa Pfadfinder in der Nähe des Eingangs zum Krater von Un'Goro 2 Pheromondrüsen eines Ravasaurus."}},
		[4292] = {"Ein Köder für Lar'korwi",nil, {"Beschwört Lar'korwi mit dem Inhalt von Torwas Beutel und besiegt ihn. Bringt dann Lar'korwis Kopf zu Torwa Pfadfinder in der Nähe des Eingangs zum Krater von Un'Goro."}},
		[4293] = {"Eine Brühschleimerprobe...",nil, {"Sammelt Teufelswaldschleimproben von den Brühschlammern im Teufelswald. Verwendet dann die Testausrüstung neben Chemiker Brenner in Unterstadt, um zu sehen, welche Eurer Proben am verderbtesten sind. Bringt 5 der verderbten Teufelswaldproben, die Ihr findet, zu Chemiker Brenner."}},
		[4294] = {"...und eine Menge Brühschlammer",nil, {"Sammelt Brühschleimerprobe von Un'Goro von jedem der Brühschleimer im Krater von Un'Goro. Verwendet dann die Testausrüstung neben Chemiker Brenner in Unterstadt, um zu sehen, welche Eurer Proben am reinsten sind. Bringt 5 der reinen Proben von Un'Goro, die Ihr findet, zu Chemiker Brenner."}},
		[4295] = {"Rocknots Bier",nil, {"Oh, Bier! Was würde ich nicht für etwas Dunkeleisenbier geben! Schnell, ich werde langsam nüchtern! Das weiß ich, weil mir die Nüchternheit die Sinne trübt... und weil es Euch aussehen lässt wie der <Volk>, den ich letzte Woche erschlagen habe!"}},
		[4296] = {"Schrifttafel der Sieben",nil, {"Bringt Maxwort Funkelglanz in der brennenden Steppe die Abschrift der Schrifttafel."}},
		[4297] = {"Essen für das Baby",nil, {"Bringt Agnar Tiermeister im Hinterland 5 Silbermähnenpirscherflanken."}},
		[4298] = {"Elternfreuden",nil, {"Sprecht mit Agnar Tiermeister im Hinterland."}},
