		[4740] = {"GESUCHT: Gurgelbacke!",nil, {"Sucht und tötet den Murloc namens Gurgelbacke. Man nimmt an, dass die Kreatur die Hütten der Murlocs am Strand südlich von Auberdine beschützt. Informiert Schildwache Glynda Nal'Shea über Gurgelbackes Tod."}},
		[4741] = {"Wilde Wächter",nil, {"Trull Scheiterbann im Teufelswald möchte, dass Ihr in Winterquell 13 mondbestrahlte Eulenbestien jagt."}},
		[4742] = {"Siegel des Aufstiegs",nil, {"Sucht die drei Edelsteine der Befehlsgewalt: den Edelstein der Gluthauer, den Edelstein der Felsspitzoger und den Edelstein der Blutäxte. Bringt sie zusammen mit dem unverzierten Siegel des Aufstiegs zu Vaelan zurück. Vaelan nannte Euch auch die Namen der drei Generäle: Kriegsmeister Voone von den Gluthauern, Hochlord Omokk von den Felsspitzogern und Oberanführer Wyrmthalak von den Blutäxten."}},
		[4743] = {"Siegel des Aufstiegs",nil, {"Reist zum Drachensumpf in den Düstermarschen. Sucht den alten Drachen Aschenschwinge auf und bekämpft ihn ohne Gnade, bis sein Wille gebrochen ist. In genau diesem Moment müsst Ihr das ungeschmiedete Siegel des Aufstiegs vor der großen Bestie hinlegen. Dann müsst Ihr schnell die Kugel der drachischen Energie auf seine geschwächte Gestalt anwenden und die Kontrolle über seine geistigen Kräfte übernehmen. Kontrolliert die Bestie und zwingt die Flammen des schwarzen Drachenschwarms auf das ungeschmiedete Siegel des Aufstiegs!"}},
		[4761] = {"Thundris Windwirker",nil, {"Sprecht mit Thundris Windwirker in Auberdine."}},
		[4762] = {"Der Klippenquell",nil, {"Geht von Auberdine aus nach Norden zum ersten Wasserfall des Klippenquell und entnehmt aus dem Teich dort eine Probe. Kehrt mit der Probe des Klippenquells zu Thundris Windwirker nach Auberdine zurück."}},
		[4763] = {"Die verderbten Schwarzfelle",nil, {"Füllt die leere Säuberungsschale am Mondbrunnen in Auberdine. Holt jeweils eine Probe von Früchten, Nüssen und Getreide aus dem Lager der Schwarzfellfurbolgs im Norden. Mischt alles in der Schale und stellt diese bei dem Freudenfeuer neben dem Klippenquell auf Höhe des nördlichen Lagers ab und lockt dadurch den Satyrverderber an. Nehmt den Talisman der Verderbnis und bringt ihn zu Thundris Windwirker in Auberdine."}},
		[4764] = {"Doomriggers Schnalle",nil, {"Bringt Mayara Wolkenglanz in der brennenden Steppe Doomriggers Schnalle."}},
		[4765] = {"Lieferung an Kronenbrunn",nil, {"Bringt Kronenbrunns Kiste zu Remington Kronenbrunn nach Sturmwind."}},
		[4766] = {"Mayara Wolkenglanz",nil, {"Sprecht mit Mayara Wolkenglanz in der brennenden Steppe."}},
		[4767] = {"Windreiter",nil, {"Bringt Elu am Freiwindposten 10 Eier von Steilhangflügeldrachen."}},
		[4768] = {"Die Dunkelsteinschrifttafel",nil, {"Bringt der Schattenmagierin Vivian Lagrave in Kargath die Dunkelsteinschrifttafel."}},
		[4769] = {"Vivian Lagrave und die Dunkelsteinschrifttafel",nil, {"Sprecht mit Schattenmagierin Vivian Lagrave."}},
		[4770] = {"Auf der Heimreise",nil, {"Begleitet Pao'ka Bergrausch aus dem Steilhang heraus und sprecht dann am Weißgipfelposten mit Motega Feuermähne."}},
		[4771] = {"Dämmerungstrickfalle",nil, {"Legt die Dämmerungstrickfalle in den Vorführraum von Scholomance. Besiegt Vectus und kehrt dann zu Betina Moppelzink zurück."}},
		[4781] = {"Komponenten für die verzauberte Goldblutrobe",nil, {"Bringt Xizk Gutstich im Schlingendorntal einen Goldbarren."}},
		[4782] = {"Komponenten für die verzauberte Goldblutrobe",nil, {"Kehrt zu Menara Leerbringer im Brachland zurück."}},
		[4783] = {"Komponenten für die verzauberte Goldblutrobe",nil, {"Bringt Menara Leerbringer im Brachland 10 Phiolen Zornsäerblut und 1 geringen Höllenstein."}},
		[4784] = {"Komponenten für die verzauberte Goldblutrobe",nil, {"Bringt Menara Leerbringer im Brachland feinen Goldfaden, 2 glimmende Kohlen und einen Seelensplitter."}},
		[4785] = {"Feiner Goldfaden",nil, {"Bitte sehr, <Name>. Und bitte richtet doch Menara Grüße von uns aus. Wir erwarten große Dinge von Euch. Wenn die Legion in die Knie gezwungen ist, wird man bestimmt Euren Namen rufen und Eure Kraft preisen. Ich kann es kaum erwarten!"}},
		[4786] = {"Die fertiggestellte Robe",nil, {"Wartet, bis Menara Leerbringer Eure Robe fertiggestellt hat, und sprecht dann noch einmal mit ihr."}},
		[4787] = {"Das uralte Ei",nil, {"Bringt das uralte Ei zu Yeh'kinya nach Tanaris."}},
		[4788] = {"Die letzten Schrifttafeln",nil, {"Bringt Ausgrabungsleiter Eisenschuh in Tanaris die fünfte und sechste Schrifttafel von Mosh'aru."}},
		[4801] = {"E'ko der Frostsäbler",nil, {"In Winterspring da werdeta gegen große Katzens kämpfen müssen, das sin' de Frostsäblers. Diese Biesties sin' unglaublich schnell un' gerissen. Wenn Ihr auch so schnell wie 'n Frostsäbler sein wollt, dann bringt mir das E'ko, das Ihr von ihnen kriegt. Nich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
		[4802] = {"E'ko der Winterfelle",nil, {"In Winterspring da werdeta gegen fehlgeleitete Kreaturens kämpfen, das sin' de Winterfellfurbolgse. Weil ihr Anführer das so wollt', ham die unglaubliche Kraft gekriegt. Wenn Ihr auch so stark wie 'n Winterfell sein wollt, dann bringt mir das E'ko, das Ihr von ihnen kriegt. Nich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
		[4803] = {"E'ko eines Splitterzahns",nil, {"In Winterspring, da sin' große Bärens mit extrem dicken Bälgens. Diese Splitterzahnbären kriegen davon so viel Schutz, dasse unheimlich viel Zeugs widerstehen könn'. Wenn Ihr gegen Feuer auch so widerstandsfähig sein wollt, dann bringt mir das E'ko, das Ihr von 'nem Splitterzahn kriegt. Nich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
		[4804] = {"E'ko der Eiswindschimären",nil, {"In Winterspring, da müssta gegen große fliegende Kreaturens kämpfen, das sin' de Eiswindschimären. Diese Biesties könn' Frostmagie wirken. Wenn Ihr gegen Frost auch so widerstandsfähig sein wollt, dann bringt mir das E'ko, das Ihr von 'ner Eiswindschimäre kriegt. Nich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
		[4805] = {"E'ko der Eisdistelyetis",nil, {"In Winterspring, da müssta gegen große Kreaturens kämpfen, das sin' de Eisdistelyetis. Diese Viecher bewegen sich nur langsam, aber se könn' Dank dem dicken Fell 'ne Menge Prügel einstecken. Wenn Ihr auch so gut wie die Angriffen ausweich'n wollt, dann bringt mir das E'ko, das Ihr von ihnen kriegt. Nich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
		[4806] = {"E'ko der Frosthagelriesen",nil, {"In Winterspring habta gegen de großen Eisriesens gekämpft, de Frosthagel. Die verlassen sich auf rohe Gewalt und Kraft. Wenn Ihr auch so stark wie 'n Frosthagelriese sein wollt, dann bringt mir das E'ko, das Ihr von ihnen kriegt. Nich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
		[4807] = {"E'ko der Wildekin",nil, {"In Winterspring, da müssta gegen Kreaturens kämpfen, die ham anscheinend angeborene magische Fertigkeitens. Diese Wildekinses könn' echt starke Magie anwenden. Wenn Ihr Euren Intellekt erweitern wollt, dann bringt mir das E'ko von allen denen Eulenbiesters, die Ihr dort findet. Nich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
		[4808] = {"Felnok Stahlfeder",nil, {"Gebt Felnok Stahlfeder Tinkees Brief."}},
		[4809] = {"Hörner der Eiswindschimären",nil, {"Bringt Felnok Stahlfeder 8 unbeschädigte Hörner einer Eiswindschimäre."}},
		[4810] = {"Rückkehr zu Tinkee",nil, {"Bringt Tinkee Kesseldampf in der brennenden Steppe Felnoks Paket."}},
		[4811] = {"Der rote Kristall",nil, {"Reist von Auberdine nach Osten und sucht nach einem großen, roten Kristall entlang der östlichen Bergkette von Dunkelküste. Informiert Schildwache Glynda Nal'Shea in Auberdine über Eure Erkenntnisse."}},
		[4812] = {"Wenn das Wasser fließt",nil, {"Füllt die leere Wasserröhre am Mondbrunnen von Auberdine und untersucht dann den roten Kristall an der östlichen Bergkette von Dunkelküste."}},
		[4813] = {"Die Fragmente im Inneren",nil, {"Informiert Schildwache Glynda Nal'Shea in Auberdine über Eure Erkenntnisse."}},
		[4821] = {"Fremdartiges Ei",nil, {"Bringt Hagar Blitzhuf am Freiwindposten das fremdartige Ei."}},
		[4822] = {"Lecker Eis",nil, {"Besorgt für Euren Schützling etwas Erdbeereis. Der kleine scheint die Marke 'Tigule und Forors Eiscreme' zu bevorzugen."}},
		[4841] = {"Befriedung der Zentauren",nil, {"Tötet 12 Späher der Galak, 10 Zänker der Galak sowie 6 Windjäger der Galak und kehrt dann zu Klippenbehüter Langhorn am Freiwindposten zurück."}},
		[4842] = {"Sonderbare Quellen",nil, {"Befolgt Donova Schneewehs Anweisungen und meldet Euch dann wieder bei ihr."}},
		[4861] = {"Wütende Wildekin",nil, {"Findet Jaron Steinschleifers ruiniertes Lager samt dessen Vorräte im südlichen Winterquell."}},
		[4862] = {"Be-Öh-Es-Eh",nil, {"Reist zur Schwarzfelsspitze und sammelt 15 Spitzenspinneneier für Kibler. Logischerweise müsstet Ihr diese Eier in der Nähe von Spinnen finden."}},
		[4863] = {"Wütende Wildekin",nil, {"Sucht Jaron Steinschleifers zerstörten Wagen."}},
		[4864] = {"Wütende Wildekin",nil, {"Klaubt Jarons Ladung aus dem Schnee und sucht dann die Wildekin, die das Amulett gestohlen haben, von dem Jaron gesprochen hatte. Bringt Jaron Steinschleifer in der Lodge in Winterquell dessen Ladung und das blaugefiederte Amulett."}},
		[4865] = {"Wilde Schlange",nil, {"Berichtet Motega Feuermähne von Euren Entdeckungen."}},
		[4866] = {"Muttermilch",nil, {"Ihr findet Mutter Glimmernetz im Herzen der Schwarzfelsspitze. Kämpft mit ihr und bringt sie dazu, Euch zu vergiften. Es kann gut sein, dass Ihr sie sogar töten müsst. Kehrt zum struppigen John zurück, sobald Ihr vergiftet seid, damit er Euch 'melken' kann."}},
		[4867] = {"Urok Schreckensbote",nil, {"Lest Waroshs Rolle. Bringt Waroshs Mojo zu Warosh."}},
		[4881] = {"Auftragsmordplan",nil, {"Warnt Kanati Grauwolke am Weißgipfelposten, dass man vorhat, ihn zu töten."}},
		[4882] = {"Geheimnisse bewachen",nil, {"Bringt Trull Scheiterbann beim Blutgiftposten das blaugefiederte Amulett."}},
		[4883] = {"Geheimnisse bewahren",nil, {"Bringt Nara Wildmähne das blaugefiederte Amulett."}},
		[4901] = {"Wächter des Altars",nil, {"Schützt Ranshalla, während sie versucht, den Altar von Elune wieder zu aktivieren. Berichtet Erelas Himmelsbern in Rut'theran von Eurem Fund."}},
		[4902] = {"Wildekin von Elune",nil, {"Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus."}},
		[4903] = {"Befehl des Kriegsherrn",nil, {"Tötet Hochlord Omokk, Kriegsmeister Voone und Oberanführer Wyrmthalak. Findet die wichtigen Schwarzfelsdokumente. Kehrt zum Kriegsherrn Bluthauer nach Kargath zurück, sobald Ihr diese Mission erledigt habt."}},
		[4904] = {"Endlich frei",nil, {"Geleitet Lakota Windweise vom Düsterwolkengipfel hinab und sprecht dann im Freiwindposten mit Thalia Bernhaut."}},
		[4906] = {"Weitere Verderbnis",nil, {"Eridan Blauwind im Teufelswald möchte, dass Ihr Xavaric sowie 8 Jadefeuerhöllenrufer, 8 Jadefeuerverräter und 8 Jadefeuerschwindler tötet. Kehrt zu Eridan zurück, sobald Ihr die Aufgabe erfüllt habt."}},
		[4907] = {"Tinkee Kesseldampf",nil, {"Sprecht mit Tinkee Kesseldampf."}},
		[4921] = {"In der Schlacht vermisst",nil, {"Sucht Mankriks Frau und kehrt dann zu ihm in die Siedlung beim Wegekreuz zurück."}},
		[4941] = {"Etriggs Weisheit",nil, {"Sprecht mit Etrigg in Orgrimmar. Wenn Ihr die Angelegenheit mit Etrigg besprochen habt, bittet Thrall um Rat. Ihr erinnert Euch, dass Ihr Etrigg in Thralls Gemächern gesehen habt."}},
		[4961] = {"Die Säuberung der Kugel von Orahil",nil, {"Tötet den Dämon der Kugel und sprecht dann mit Tabetha."}},
		[4962] = {"Splitter eines Teufelshundes",nil, {"Nehmt den Teufelsrubin und benutzt ihn bei einem der Teufelshunde von Desolace. Wenn Ihr Erfolg hattet, bringt Menara Leerbringer im Brachland den eingekerkerten Teufelshundgeist und den Teufelsrubin."}},
		[4963] = {"Splitter einer Höllenbestie",nil, {"Nehmt den Infussmaragd und benutzt ihn bei einer der Höllenbestien von Desolace. Wenn Ihr Erfolg hattet, bringt Menara Leerbringer im Brachland den eingekerkerten Höllenbestiengeist und den Infussmaragd."}},
		[4964] = {"Die fertiggestellte Kugel von Dar'Orahil",nil, {"Wartet, bis Menara Leerbringer die Kugel von Dar'Orahil fertig gestellt hat, und sprecht dann noch einmal mit ihr."}},
		[4965] = {"Wissen der Kugel von Orahil",nil, {"Sprecht mit Menara Leerbringer im Brachland."}},
		[4966] = {"Kanati Grauwolke schützen",nil, {"Beschützt Kanati Grauwolke vor den Angriffen der Zentauren."}},
		[4967] = {"Wissen der Kugel von Orahil",nil, {"Sprecht mit Menara Leerbringer im Brachland."}},
		[4968] = {"Wissen der Kugel von Orahil",nil, {"Sprecht mit Menara Leerbringer im Brachland."}},
		[4969] = {"Wissen der Kugel von Orahil",nil, {"Sprecht mit Menara Leerbringer im Brachland."}},
		[4970] = {"Frostsäblerverpflegung",nil, {"Sammelt 5 Splitterzahnfleischstücke und 5 Fleischstücke einer Eiswindschimäre für Rivern Frostwind in Winterquell."}},
		[4971] = {"Eine Frage der Zeit",nil, {"Benutzt den Temporalversetzer in der Nähe eines der Silos von Andorhal, um die temporalen Parasiten aufzuspüren. Tötet 10 temporale Parasiten und bringt dann den Temporalversetzer zu Chromie im Gasthaus Andorhal in den westlichen Pestländern."}},
		[4972] = {"Tempus fugit",nil, {"Sucht 5 Uhren von Andorhal, die in Schließkassetten überall im Schutt der Stadt zu finden sind. Kehrt mit ihnen zu Chromie ins Gasthaus Andorhal in den westlichen Pestländern zurück."}},
		[4973] = {"Tempus fugit",nil, {"Sucht 5 Uhren von Andorhal in den Ruinen der Stadt und bringt sie Chromie im Gasthaus Andorhal in den westlichen Pestländern."}},
		[4974] = {"Für die Horde!",nil, {"Begebt Euch zur Schwarzfelsspitze und tötet den Kriegshäuptling Rend Schwarzfaust. Nehmt seinen Kopf und kehrt nach Orgrimmar zurück."}},
		[4975] = {"Die fertiggestellte Kugel von Noh'Orahil",nil, {"Wartet, bis Menara Leerbringer die Kugel von Noh'Orahil fertig gestellt hat, und sprecht dann noch einmal mit ihr."}},
		[4976] = {"Ablieferung der gesäuberten Kugel",nil, {"Bringt die gesäuberte Höllenkugel zu Menara Leerbringer ins Brachland."}},
		[4981] = {"Agentin Bijou",nil, {"Begebt Euch zur Schwarzfelsspitze und findet heraus, was aus Bijou geworden ist."}},
		[4982] = {"Bijous Habseligkeiten",nil, {"Sucht Bijous Habseligkeiten und bringt sie ihr. Ihr erinnert Euch daran, dass sie erwähnte, ihre Sachen auf der untersten Ebene der Stadt versteckt zu haben."}},
		[4983] = {"Bijous Aufklärungsbericht",nil, {"Bringt Bijous Aufklärungsbericht zu Großmeister Lexlort nach Kargath."}},
		[4984] = {"Die Tierwelt leidet auch",nil, {"Tötet 8 kranke Wölfe und kehrt dann zu Mulgris Tiefenfurt auf dem trostlosen Feld in den westlichen Pestländern zurück."}},
		[4985] = {"Die Tierwelt leidet auch",nil, {"Tötet 8 kranke Grizzlys und kehrt dann zu Mulgris Tiefenfurt auf dem trostlosen Feld in den westlichen Pestländern zurück."}},
		[4986] = {"Glyphenverzierter Eichenast",nil, {"Nach der Magie zu urteilen, mit der der glyphenverzierte Eichenast erfüllt ist, hatte der Taurendruide wohl gewollt, dass Ihr den Ast zum Zirkel des Cenarius nach Darnassus bringt. Bittet einen der Druiden dort um Hilfe."}},
		[4987] = {"Glyphenverzierter Eichenast",nil, {"Nach der Magie zu urteilen, mit der der glyphenverzierte Eichenast erfüllt ist, hatte der Taurendruide wohl gewollt, dass Ihr den Ast zum Zirkel des Cenarius nach Donnerfels bringt. Bittet einen der Druiden dort um Hilfe."}},
		[5001] = {"Bijous Habseligkeiten",nil, {"Sucht Bijous Habseligkeiten und bringt sie ihr. Viel Glück!"}},
		[5002] = {"Nachricht an Maxwell",nil, {"Begebt Euch in die brennende Steppe und leitet Bijous Informationen an Marschall Maxwell weiter."}},
		[5021] = {"Besser spät als nie",nil, {"Das wilde Gebrabbel der Geisterfrau deutete darauf hin, dass sie ein Paket ausliefern musste. Sie behauptete, dass es dort war, wo auch ihr Pferd war. Darüber, wo das Pferd jetzt ist oder wohin das Paket ausgeliefert werden muss, sagt der Geist nichts."}},
		[5022] = {"Besser spät als nie",nil, {"Wendet Euch an die königlichen Disponenten von Sturmwind, um etwas über den Aufenthaltsort von Emma Teufelsstein herauszufinden. Normalerweise findet sich ein Volkszählungsbeamter im Rathaus."}},
		[5023] = {"Besser spät als nie",nil, {"Wendet Euch an die königlichen Vorarbeiter von Unterstadt, um etwas über den Aufenthaltsort von Jeremiah Teufelsstein herauszufinden. Normalerweise findet sich ein Volkszählungsbeamter in der Nähe der Gilden- und Wappenrock-Registrierung."}},
		[5041] = {"Vorräte für das Wegekreuz",nil, {"Findet die Vorratskisten des Wegekreuzes und bringt sie zu Thork im Brachland zurück."}},
		[5042] = {"Agammagans Kraft",nil, {"Ich benötige 4 Blutsplitter, wenn Ihr Agammagans Kraft wünscht, <Volk>. Eure Feinde werden vor Eurer Kraft erzittern, wenn Ihr über diesen Segen verfügen würdet. nnDie größten Krieger der Grimmhauer sind mit solcher Stärke ausgestattet!"}},
		[5043] = {"Agammagans Beweglichkeit",nil, {"Man sagt, dass die größten Späher meines Stammes in ihrer Genauigkeit unübertroffen seien und kein Schurke <schnaubt> ihrer Schnelligkeit im Kampf standhalten könne. Bringt mir 4 Blutsplitter und ich werde Euch noch flinker machen als jeder lebende Stacheleber."}},
		[5044] = {"Weisheit von Agammagan",nil, {"Die Schamanen und Geomanten der Stacheleberstämme <schnaubt> nutzen die Kraft von Agammagan im Kampf und bei ihren Ritualen, <Volk>. Wenn auch Ihr die zusätzliche Macht von Agammagans Segen für Eure Zauber wünscht, dann bringt mir 4 Blutsplitter, und Quetschzahn wird Euch helfen."}},
		[5045] = {"Steigerung des Geistes",nil, {"Krieger aller Art <schnaubt> benötigen einen starken Geist. Er treibt Euch zu größeren Leistungen, wenn alle Hoffnung verloren ist. Das ist den Grimmhauern nicht <schnaubt> unbekannt. Wir sind ein spiritueller Stamm... wir sind stolzer auf unsere Verbindung zu Agammagan als auf unsere Leistungsfähigkeit <schnaubt> im Kampf. Bringt mir 4 Blutsplitter und ich werde Euch für den Kampf segnen. <schnaubt>"}},
		[5046] = {"Klingenhaut",nil, {"Alle, die <schnaubt> in den Kampf eintreten, benötigen Schutz. Ihre Knochen müssen stark sein. Ihre Haut zäh. <schnaubt> Mein Stamm weiß das auch und um den natürlichen Schutz zu verstärken, den wir durch unsere Stacheln und unsere Haut erhalten, <schnaubt> rufen wir Agammagan an, uns vor Schaden zu schützen, bevor wir in den Krieg ziehen. Bringt mir 4 Blutsplitter <schnaubt> und ich werde Euch den gleichen Schutz gewähren."}},
		[5047] = {"Finkle Einhorn, zu Euren Diensten!",nil, {"Sprecht mit Malyfous Düsterhammer in der Ewigen Warte."}},
		[5048] = {"Emma die Nette",nil, {"Sucht die alte Emma in Sturmwind und findet heraus, ob sie tatsächlich Emma Teufelsstein ist. Wenn sie es ist, dann möchte sie vielleicht das Paket haben, das Janice Teufelsstein für sie gepackt hat."}},
		[5049] = {"Der Jammer des Jeremiah",nil, {"Sucht Jeremiah Lohnson in Unterstadt und findet heraus, ob er tatsächlich Jeremiah Teufelsstein ist. Wenn er es ist, dann möchte er vielleicht das Paket haben, das Janice Teufelsstein für ihn gepackt hat."}},
		[5050] = {"Glücksbringer",nil, {"Bringt Janice Teufelsstein in den westlichen Pestländern die Hälfte ihres Glücksbringers zurück."}},
		[5051] = {"Zwei Hälften vereint",nil, {"Sucht auf dem Teufelssteinfeld in den westlichen Pestländern nach der anderen Hälfte des Glücksbringers und setzt ihn wieder zusammen. Gebt den Glücksbringer Janice Teufelsstein auf dem Teufelssteinfeld in den westlichen Pestländern."}},
		[5052] = {"Blutsplitter von Agammagan",nil, {"Bringt Quetschzahn im Camp Taurajo im Brachland einen Blutsplitter. Sammelt weitere Blutsplitter, wenn Ihr von ihm mit Agammagans Macht gesegnet werden möchtet."}},
		[5054] = {"Ursius von den Splitterzahnbären",nil, {"Storm Schattenhuf in der Ewigen Warte in Winterquell möchte, dass Ihr Ursius tötet."}},
		[5055] = {"Brumeran von den Eiswindschimären",nil, {"Storm Schattenhuf in der Ewigen Warte in Winterquell möchte, dass Ihr Brumeran tötet."}},
		[5056] = {"Shy-Rotam",nil, {"Storm Schattenhuf in der Ewigen Warte in Winterquell möchte, dass Ihr Shy-Rotam besiegt."}},
		[5057] = {"Frühere Abenteuer",nil, {"Bringt Storm Schattenhufs Gutschein zu Melor Steinhuf in Donnerfels."}},
		[5058] = {"Frau Dalsons Tagebuch",nil, {"Ein Tagebuch mit vergilbten Seiten liegt aufgeschlagen da, sodass man den letzten Eintrag lesen kann:nn\"Ich weiß nicht, was passieren wird, jetzt, wo Harold sich verwandelt hat. Es hat mich meine ganze Kraft gekostet, ihn zu dem einzigen Ort zu schleppen, den ich finden konnte, in dem er sicher eingesperrt ist und nicht herauskommen kann. Den Schlüssel habe ich bei mir und ich kann nur beten, dass es funktionieren wird ... Ich hoffe, ich schaffe es, hier herauszukommen, aber dazu brauche ich Geld. Unsere ganzen Sachen sind alle im Schrank, glaube ich ... und jetzt, wo ich darüber nachdenke - trägt Harold den Schlüssel dazu nicht bei sich?nnIch muss darüber schlafen. Im Augenblick bin ich todmüde.\""}},
		[5059] = {"Weggeschlossen",nil, {"Der von Splittern gezeichnete Türrahmen des Plumpsklos sieht aus, als ob wiederholt und unablässig versucht worden wäre, die Tür gewaltsam zu öffnen. Sie ist derzeit von außen fest verschlossen. Als Ihr an der Tür rüttelt, hört Ihr aus dem Inneren ein kehliges Stöhnen! Was auch immer sich im Inneren befindet, es klingt nicht freundlich."}},
		[5060] = {"Weggeschlossen",nil, {"Vor Euch steht ein sehr stabiler Schrank, sicher verschlossen mit einem eingebauten Schloss. Die Vorderseite des Schranks ist mit tiefen Kratzern und Riefen übersät. Offensichtlich sind das die Zeichen zahlreicher und gewaltsamer fehlgeschlagener Versuche, die Geheimnisse zu enthüllen, die der Schrank derzeit verbirgt. Wenn Ihr diesen Schrank öffnen wollt, müsst Ihr den Schlüssel finden."}},
		[5061] = {"Wassergestalt",nil, {"Kehrt nach Darnassus zurück und zeigt Mathrengyl Bärenfährte den Anhänger des Seelöwen."}},
		[5062] = {"Hochheiliges Feuer",nil, {"Sammelt 10 Scheffel Brandpulveragaven und beratet Euch dann mit Magatha Grimmtotem auf der Anhöhe der Ältesten in Donnerfels."}},
		[5063] = {"Kappe des scharlachroten Wissenden",nil, {"Um die Kappe des scharlachroten Wissenden herzustellen, müsst Ihr die folgenden Dinge zu Malyfous Düsterhammer bringen: 1 makelloser Balg der Bestie; 5 ausgefranste Monstrositätenstickereien; 8 Arkankristalle; 5 verzauberte scharlachrote Fäden. Ihr müsst außerdem 50 Goldstücke für die Herstellung des Gegenstandes zahlen."}},
		[5064] = {"Grimmtotemspionage",nil, {"Sucht die drei geheimen Notizen im Düsterwolkengipfel und bringt sie zurück."}},
		[5065] = {"Die verlorenen Schrifttafeln von Mosh'aru",nil, {"Bringt Ausgrabungsleiter Eisenschuh in Tanaris die dritte und vierte Schrifttafel von Mosh'aru."}},
		[5066] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Kommandant Ashlam Ehrenschlag auf. Sein Stützpunkt befindet sich am Zugwindlager, nördlich des Alteracgebirges."}},
		[5067] = {"Gamaschen von Arcana",nil, {"Um die Gamaschen von Arcana herzustellen, müsst Ihr die folgenden Dinge zu Malyfous Düsterhammer bringen: 1 makelloser Balg der Bestie; 5 ausgefranste Monstrositätenstickereien; 5 Arkanitbarren; 5 Einheiten Frostraunens Balsamierungsflüssigkeit. Ihr müsst außerdem 50 Goldstücke für die Herstellung des Gegenstandes zahlen."}},
		[5068] = {"Brustplatte des Blutdurstes",nil, {"Um die Brustplatte des Blutdurstes herzustellen, müsst Ihr die folgenden Dinge zu Malyfous Düsterhammer bringen: 1 makelloser Balg der Bestie; 10 ausgefranste Monstrositätenstickereien; 5 Arkanitbarren; 5 Häute des Schattens. Ihr müsst außerdem 50 Goldstücke für die Herstellung des Gegenstandes zahlen."}},
		[5081] = {"Maxwells Mission",nil, {"Reist zur Schwarzfelsspitze und schaltet Kriegsmeister Voone, Hochlord Omokk und Oberanführer Wyrmthalak aus. Kehrt zu Marschall Maxwell zurück, wenn Eure Aufgabe erledigt ist."}},
		[5082] = {"Bedrohung durch die Winterfelle",nil, {"Donova Schneeweh in Winterquell möchte, dass Ihr 8 Pfadfinder der Winterfelle, 8 Höhlenbehüter der Winterfelle und 8 Totemiker der Winterfelle erledigt."}},
		[5083] = {"Feuerwasser der Winterfelle",nil, {"Bringt das leere Feuerwasserfläschchen zu Donova Schneeweh in Winterquell."}},
		[5084] = {"Der Verderbnis erliegen",nil, {"Durchsucht das Furbolglager der Totenwaldfelle im nördlichen Teil des Teufelswalds nach einem Hinweis."}},
		[5085] = {"Geheimnisvoller Glibber",nil, {"Berichtet Donova Schneeweh von Euren Entdeckungen."}},
		[5086] = {"Toxinschrecken",nil, {"Sammelt 3 Toxinschreckentropfen für Donova Schneeweh in Winterquell."}},
		[5087] = {"Läufer der Winterfelle",nil, {"Sucht die Läufer der Winterfelle und hindert sie daran, ihre Lieferung zum Lager der Winterfelle zu bringen."}},
		[5088] = {"Arikara",nil, {"Erschlagt Arikara. Bringt ihre Überreste und das Brandpulver zu Motega Feuermähne im Weißgipfelposten als Beweis für Eure Tat."}},
		[5089] = {"General Drakkisaths Befehl",nil, {"Bringt den Befehl von General Drakkisath zu Marschall Maxwell in der brennenden Steppe."}},
		[5090] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Kommandant Ashlam Ehrenschlag auf. Sein Stützpunkt befindet sich am Zugwindlager, nördlich des Alteracgebirges."}},
		[5091] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Kommandant Ashlam Ehrenschlag auf. Sein Stützpunkt befindet sich am Zugwindlager, nördlich des Alteracgebirges."}},
		[5092] = {"Den Weg räumen",nil, {"Tötet 10 Skelettschinder und 10 sabbernde Ghuls in Trauerhügel. Kehrt zu Kommandant Ashlam Ehrenschlag am Zugwindlager, westliche Pestländer, zurück."}},
		[5093] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Hochexekutor Derrington auf. Sein Stützpunk befindet sich am Bollwerk, östlich von Tirisfal und Unterstadt."}},
		[5094] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Hochexekutor Derrington auf. Sein Stützpunk befindet sich am Bollwerk, östlich von Tirisfal und Unterstadt."}},
		[5095] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Hochexekutor Derrington auf. Sein Stützpunk befindet sich am Bollwerk, östlich von Tirisfal und Unterstadt."}},
		[5096] = {"Scharlachrote Ablenkung",nil, {"Zieht zum Basislager des Scharlachroten Kreuzzugs zwischen dem Teufelssteinfeld und Dalsons Tränenfeld und zerstört sein Kommandozelt. Lasst das Banner der Geißel im Lager wehen und kehrt dann zu Hochexekutor Derrington am Bollwerk in den westlichen Pestländern zurück."}},
		[5097] = {"Turm um Turm",nil, {"Markiert mit der Signalfackel jeden Turm in Andorhal; Ihr müsst im Eingang jedes Turmes stehen, um ihn erfolgreich zu markieren. Wenn alle vier Türme markiert sind, bringt Ihr die Signalfackel zu Kommandant Ashlam Ehrenschlag am Zugwindlager, westliche Pestländer, zurück."}},
		[5098] = {"Turm um Turm",nil, {"Markiert mit der Signalfackel jeden Turm in Andorhal; Ihr müsst im Eingang jedes Turmes stehen, um ihn erfolgreich zu markieren. Wenn alle vier Türme markiert sind, bringt Ihr die Signalfackel zu Hochexekutor Derrington am Bollwerk, westliche Pestländer, zurück."}},
		[5101] = {"Lee's Ultimate Test Quest... of Doom!",nil, {"Tötet 6 Schafe für Benny... und dann nehmt die Füße in die Hand und rennt weg, was das Zeug hält!nnnnnWar nur 'n Scherz... was das Rennen angeht."}},
		[5102] = {"General Drakkisaths Niedergang",nil, {"Begebt Euch zur Schwarzfelsspitze und schaltet General Drakkisath aus. Kehrt zu Marschall Maxwell zurück, wenn Eure Aufgabe erledigt ist."}},
		[5103] = {"Heißer, feuriger Tod",nil, {"Jemand auf dieser Welt muss doch wissen, was mit diesen Stulpen zu tun ist. Viel Glück!"}},
		[5121] = {"Oberhäuptling der Winterfelle",nil, {"Donova Schneeweh in Winterquell möchte, dass Ihr den Oberhäuptling der Winterfelle besiegt."}},
		[5122] = {"Das Medaillon des Glaubens",nil, {"Die Macht dieser Kapelle gibt mir die Kraft, der Verderbnis in meinem Herzen zu widerstehen. Wenn ich sie verlasse, werde ich zu einem Todesritter. Aber es gibt ein Medaillon, das Medaillon des Glaubens, das mir so viel Kraft geben wird, dass ich trotzdem gehen kann. Bitte, <Name>, findet das Medaillon für mich. Es wird von Malor dem Eifrigen bewacht, einem wahnsinnigen Mitglied der Scharlachroten Bruderschaft. Er lebt tief in der scharlachroten Bastion im westlichen Stratholme."}},
		[5123] = {"Das letzte Stück",nil, {"Bringt das kunstlos geschriebene Logbuch zu Donova Schneeweh in Winterquell."}},
		[5124] = {"Feurige Plattenstulpen",nil, {"Bringt Malyfous Düsterhammer 6 verzauberte Thoriumbarren, 2 Essenzen des Feuers und 4 Sternrubine. Ihr müsst außerdem Eure ungebrannten Plattenstulpen abgeben."}},
		[5125] = {"Aurius' Abrechnung",nil, {"<Name>, ich sterbe... aber meine Seele ist gerettet. Dafür schulde ich Euch mehr, als man mit Worten ausdrücken kann. Bitte, nehmt dies... und kämpft weiter so mutig, <Klasse>!"}},
		[5126] = {"Lorax' Geschichte",nil, {"Sprecht mit Lorax. Hört, was er zu sagen hat."}},
		[5127] = {"Die Dämonenschmiede",nil, {"Begebt Euch zur Schwarzfelsspitze und sucht Goraluk Hammerbruch. Erschlagt ihn und wendet dann die blutbefleckte Pike auf seine Leiche an. Nachdem seine Seele abgesaugt wurde, wird die Pike seelenbefleckt sein. Ihr müsst außerdem die ungeschmiedete runenbedeckte Brustplatte finden. Bringt die seelenbefleckte Pike und die ungeschmiedete runenbedeckte Brustplate zu Lorax in Winterquell."}},
		[5128] = {"Worte des Oberhäuptlings",nil, {"Bringt das kunstlos geschriebene Logbuch zu Kelek Himmelshüter im südlichen Teil des Teufelswalds."}},
		[5141] = {"Drachenlederverarbeitung",nil, {"Bringt 2 feste Skorpidbrustplatten, 2 feste Skorpidhandschuhe und 10 Drachenschuppen zu Peter Galen in Azshara. Wenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Drachenlederverarbeitung erhalten. Ihr könnt dann allerdings weder Elementarlederverarbeitung noch Stammeslederverarbeitung erlernen; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt."}},
		[5142] = {"Klein-Pamela",nil, {"Sucht Pamela Rotpfad."}},
		[5143] = {"Stammeslederverarbeitung",nil, {"Bringt eine Wildlederweste und einen Wildlederhelm zu Caryssia Jägermond in Feralas. Wenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Stammeslederverarbeitung erhalten. Wenn Ihr diese Aufgabe abschließt, werdet Ihr allerdings weder Drachenlederverarbeitung noch Elementarlederverarbeitung erlernen können; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt."}},
		[5144] = {"Elementarlederverarbeitung",nil, {"Bringt 2 Herzen des Feuers, 2 Kugeln des Wassers, 2 Erdenkerne und 2 Odem des Windes zu Sarah Gerber in die sengende Schlucht. Wenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Elementarlederverarbeitung erhalten. Wenn Ihr diese Aufgabe abschließt, werdet Ihr allerdings weder Drachenlederverarbeitung noch Stammeslederverarbeitung erlernen können; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt."}},
		[5145] = {"Drachenlederverarbeitung",nil, {"Bringt 2 feste Skorpidbrustplatten, 2 feste Skorpidhandschuhe und 10 Drachenschuppen zu Thorkaf Drachenaug im Ödland. Wenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Drachenlederverarbeitung erhalten. Ihr könnt dann allerdings weder Elementarlederverarbeitung noch Stammeslederverarbeitung erlernen; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt."}},
		[5146] = {"Elementarlederverarbeitung",nil, {"Bringt 2 Herzen des Feuers, 2 Kugeln des Wassers, 2 Erdenkerne und 2 Odem des Windes zu Brumn Winterhuf in den Arathibergen. Wenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Elementarlederverarbeitung erhalten. Wenn Ihr diese Aufgabe abschließt, werdet Ihr allerdings weder Drachenlederverarbeitung noch Stammeslederverarbeitung erlernen können; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt."}},
		[5147] = {"GESUCHT: Arnak Grimmtotem",nil, {"Erschlagt Arnak Grimmtotem und bringt den Beweis Eurer Tat zu Klippenbehüter Langhorn im Freiwindposten."}},
		[5148] = {"Stammeslederverarbeitung",nil, {"Bringt eine Wildlederweste und einen Wildlederhelm zu Se'Jib im Schlingendorntal. Wenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Stammeslederverarbeitung erhalten. Wenn Ihr diese Aufgabe abschließt, werdet Ihr allerdings weder Drachenlederverarbeitung noch Elementarlederverarbeitung erlernen können; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt."}},
		[5149] = {"Pamelas Puppe",nil, {"Sucht Pamelas Puppe."}},
		[5150] = {"Dadanga ist hungrig!",nil, {"Dadanga sieht zu Euch auf, während Ihr auf sie zu geht. Sie scheint auf etwas zu hoffen... Sie hat wahrscheinlich Hunger!nnIm Krater muss irgendetwas wachsen, was die perfekte Leckerei für einen riesigen Pflanzenfresser ist. Dadanga ist ein großes Mädchen; was immer Ihr ihr bringt... es muss viel davon sein!"}},
		[5151] = {"Hyperkondensator-Dingsda",nil, {"Erschlagt die Bestie und bringt den Hyperkondensator sowie den Pantherkäfigschlüssel zu Wizlo Lagerputzer zurück."}},
		[5152] = {"Tante Marlene",nil, {"Sprecht mit Marlene, südlich der Ruinen von Andorhal."}},
		[5153] = {"Ein seltsamer Historiker",nil, {"Bringt Josephs Ehering zu Chromie."}},
		[5154] = {"Die Annalen von Darroheim",nil, {"Bringt die 'Annalen von Darroheim' zu Chromie in Andorhal."}},
		[5155] = {"Streitmacht von Jaedenar",nil, {"Betretet Jaedenar und erschlagt 4 Hunde von Jaedenar, 4 Gardisten von Jaedenar, 6 Adepten von Jaedenar und 6 Kultisten von Jaedenar, bevor Ihr zu Greta Mooshuf im Teufelswald zurückkehrt."}},
		[5156] = {"Die Verderbnis überprüfen",nil, {"Sucht und zerstört 2 entropische Bestien und 2 entropische Schrecken, während Ihr den Narbengrund erforscht. Nachdem Ihr überprüft habt, ob die Krater immer noch mit verderbtem Wasser gefüllt sind, kehrt zu Taronn Rotfeder im Teufelswald zurück."}},
		[5157] = {"Verderbtes Wasser sammeln",nil, {"Bringt die leere Feldflasche zum Mondbrunnen im Zentrum von Jaedenar und füllt sie, bevor Ihr zu Greta Mooshuf im Teufelswald zurückkehrt."}},
		[5158] = {"Spirituelle Hilfe suchen",nil, {"Bringt das verderbte Mondbrunnenwasser in das Brachland zu Islen Wasserdeuter."}},
		[5159] = {"Gesäubertes Wasser wird zum Teufelswald gebracht",nil, {"Bringt das geläuterte Mondbrunnenwasser zu Greta Mooshuf im Teufelswald."}},
		[5160] = {"Die oberste Beschützerin",nil, {"Begebt Euch nach Winterquell und sucht Haleh. Gebt ihr Awbees Schuppe."}},
		[5161] = {"Der Zorn des blauen Drachenschwarms",nil, {"Sprecht mit Haleh, wenn Ihr weitermachen wollt."}},
		[5162] = {"Der Zorn des blauen Drachenschwarms",nil, {"Sprecht mit Jeziba in den Pestländern. Er wohnt in Andorhal."}},
		[5163] = {"Sind wir endlich da, Yeti?",nil, {"Nehmt Umis mechanischen Yeti und erschreckt ihre Freunde damit:nnLegacki in der Ewigen Warte (Winterquell)nSprinkel in Gadgetzan (Tanaris)nQuixxil in Marschalls Zuflucht (Krater von Un'Goro)nnWenn Ihr damit fertig seid, bringt den mechanischen Yeti zu Umi zurück."}},
		[5164] = {"Verzeichnis der Abtrünnigen",nil, {"Lest das 'Verzeichnis der Abtrünnigen'."}},
		[5165] = {"Übergießen der Flammen des Schutzes",nil, {"Übergießt die Flammen der vier Kohlenpfannen des Schutzes in der Schattenfeste im Teufelswald mit dem geläuterten Mondbrunnenwasser und kehrt danach zu Greta Mooshuf im Smaragdrefugium zurück."}},
		[5166] = {"Brustplatte des chromatischen Drachenschwarms",nil, {"Um die Brustplatte des chromatischen Drachenschwarms zu schmieden, müsst Ihr die folgenden Gegenstände zu Jeziba dem 'Bildhauer' bringen:nn1 chromatischen Knochenpanzer. 10 glänzende chromatische Schuppen. 10 Einheiten Blut von Helden. 10 ausgefranste Monstrositätenstickereien."}},
		[5167] = {"Beinplatten des chromatischen Widersetzers",nil, {"Um die Beinplatten des chromatischen Widersetzers zu schmieden, müsst Ihr die folgenden Gegenstände zu Jeziba dem 'Bildhauer' bringen:nn1 chromatischer Knochenpanzer. 10 glänzende chromatische Schuppen. 10 Einheiten Blut von Helden. 5 Häute des Schattens."}},
		[5168] = {"Helden von Darroheim",nil, {"Bringt Davils Buchband und Rotpfads Schild zu Carlin Rotpfad."}},
		[5181] = {"Bösewichte von Darroheim",nil, {"Bringt den Schädel von Horgus und das zertrümmerte Schwert von Marduk zu Carlin Rotpfad in der Kapelle des hoffnungsvollen Lichts."}},
		[5201] = {"Eindringlinge der Winterfelle",nil, {"Rivern Frostwind möchte, dass Ihr 5 Schamanen der Winterfelle und 5 Ursa der Winterfelle erschlagt."}},
		[5202] = {"Ein merkwürdiger roter Schlüssel",nil, {"Durchsucht Jaedenar, um herauszufinden, wofür der blutrote Schlüssel verwendet wird."}},
		[5203] = {"Rettung aus Jaedenar",nil, {"Bringt Arko'narin dorthin, wo Lichtschmied und ihre Ausrüstung aufbewahrt werden, und beschützt sie dann bei eurer Flucht aus der Schattenfeste. Sucht danach Jessir Mondsichel im Teufelswald auf und lasst sie wissen, was mit ihren Freunden geschehen ist."}},
		[5204] = {"Vergeltung des Lichts",nil, {"Kehrt nach Jaedenar zurück und tötet den Sukkubus Rakaiah, bevor Ihr die Überreste von Trey Lichtschmied sucht."}},
		[5206] = {"Marodeure von Darroheim",nil, {"Bringt 5 widerhallende Schädel und den Mystikerkristall zu Carlin Rotpfad in den östlichen Pestländern."}},
		[5210] = {"Bruder Carlin",nil, {"Bringt die erweiterten 'Annalen von Darroheim' zu Carlin Rotpfad in den östlichen Pestländern."}},
		[5211] = {"Verteidiger von Darroheim",nil, {"Befreit 15 Geister von Darroheim und kehrt dann zu Carlin Rotpfad zurück."}},
		[5212] = {"Das Fleisch lügt nicht",nil, {"Sammelt 10 verseuchte Fleischproben in Stratholme und bringt sie zu Betina Moppelzink zurück. Ihr vermutet, dass Ihr besagte Fleischproben bei jeder Kreatur in Stratholme finden könnt."}},
		[5213] = {"Der aktive Wirkstoff",nil, {"Reist nach Stratholme und durchsucht die Ziggurats. Sucht neue Geißeldaten und bringt sie zu Betina Moppelzink zurück."}},
		[5214] = {"Der große Fras Siabi",nil, {"Sucht Fras Siabis Raucherladen in Stratholme und bergt einen Kasten von Siabis Tollem Tabak. Kehrt zu Smokey LaRue zurück, wenn Eure Aufgabe erledigt ist."}},
		[5215] = {"Die Geißelkessel",nil, {"Sprecht mit der Hohepriesterin MacDonnell an der Zugwindspitze, westliche Pestländer."}},
		[5216] = {"Ziel: Teufelssteinfeld",nil, {"Geht zum Teufelssteinfeld in den westlichen Pestländern. Sucht nach dem dortigen Kessellord und besiegt ihn. Er könnte einen Schlüssel haben, der den Zugang zu dem Kessel ermöglicht. Ihr müsst die leere Flasche: Teufelssteinfeld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet."}},
		[5217] = {"Rückkehr zum Zugwindlager",nil, {"Bringt die gefüllte Flasche: Teufelssteinfeld zur Hohepriesterin MacDonnell am Zugwindlager, westliche Pestländer."}},
		[5218] = {"Der Kessel des Teufelssteinfelds",nil, {"Der Seuchenkessel der Geißel steht vor Euch und speit große Mengen Toxin in die Luft. Eine kleine, unauffällige Zugangsklappe befindet sich in der großen Plattform des Kessels. Da Ihr den Kessellord bereits besiegt und seinen Schlüssel an Euch genommen habt, könnt Ihr die geeigneten Reagenzien in den Kessel geben, um ein Gegenmittel gegen die Seuche zu schaffen."}},
		[5219] = {"Ziel: Dalsons Tränenfeld",nil, {"Begebt Euch zu Dalsons Tränenfeld in den westlichen Pestländern, um den dortigen Kessellord zu suchen und zu besiegen. Verwendet dann seinen Schlüssel, um Zugang zum Kessel zu erhalten. Ihr müsst die leere Flasche: Dalsons Tränenfeld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet."}},
		[5220] = {"Rückkehr zum Zugwindlager",nil, {"Bringt die gefüllte Flasche: Dalsons Tränenfeld zur Hohepriesterin MacDonnell am Zugwindlager, westliche Pestländer."}},
		[5221] = {"Der Kessel von Dalsons Tränenfeld",nil, {"Der Seuchenkessel der Geißel steht vor Euch und speit große Mengen Toxin in die Luft. Eine kleine, unauffällige Zugangsklappe befindet sich in der großen Plattform des Kessels. Da Ihr den Kessellord bereits besiegt und seinen Schlüssel an Euch genommen habt, könnt Ihr die geeigneten Reagenzien in den Kessel geben, um ein Gegenmittel gegen die Seuche zu schaffen."}},
		[5222] = {"Ziel: Das trostlose Feld",nil, {"Begebt Euch zum trostlosen Feld in den westlichen Pestländern, um den dortigen Kessellord zu suchen und zu besiegen. Verwendet dann seinen Schlüssel, um Zugang zum Kessel zu erhalten. Ihr müsst die leere Flasche: Das trostlose Feld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet."}},
		[5223] = {"Rückkehr zum Zugwindlager",nil, {"Bringt die gefüllte Flasche: Das trostlose Feld zur Hohepriesterin MacDonnell am Zugwindlager, westliche Pestländer."}},
		[5224] = {"Der Kessel des trostlosen Felds",nil, {"Der Seuchenkessel der Geißel steht vor Euch und speit große Mengen Toxin in die Luft. Eine kleine, unauffällige Zugangsklappe befindet sich in der großen Plattform des Kessels. Da Ihr den Kessellord bereits besiegt und seinen Schlüssel an Euch genommen habt, könnt Ihr die geeigneten Reagenzien in den Kessel geben, um ein Gegenmittel gegen die Seuche zu schaffen."}},
		[5225] = {"Ziel: Gahrrons Trauerfeld",nil, {"Begebt Euch zu Gahrrons Trauerfeld in den westlichen Pestländern, um den dortigen Kessellord zu suchen und zu besiegen. Verwendet dann seinen Schlüssel, um Zugang zum Kessel zu erhalten. Ihr müsst die leere Flasche: Gahrrons Trauerfeld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet."}},
		[5226] = {"Rückkehr zum Zugwindlager",nil, {"Bringt die gefüllte Flasche: Gahrrons Trauerfeld zur Hohepriesterin MacDonnell am Zugwindlager, westliche Pestländer."}},
		[5227] = {"Der Kessel von Gahrrons Trauerfeld",nil, {"Der Seuchenkessel der Geißel steht vor Euch und speit große Mengen Toxin in die Luft. Eine kleine, unauffällige Zugangsklappe befindet sich in der großen Plattform des Kessels. Da Ihr den Kessellord bereits besiegt und seinen Schlüssel an Euch genommen habt, könnt Ihr die geeigneten Reagenzien in den Kessel geben, um ein Gegenmittel gegen die Seuche zu schaffen."}},
		[5228] = {"Die Geißelkessel",nil, {"Sprecht mit der Schattenpriesterin Vandis am Bollwerk, westliche Pestländer."}},
		[5229] = {"Ziel: Teufelssteinfeld",nil, {"Geht zum Teufelssteinfeld in den westlichen Pestländern. Sucht nach dem dortigen Kessellord und besiegt ihn. Er könnte einen Schlüssel haben, der den Zugang zu dem Kessel ermöglicht. Ihr müsst die leere Flasche: Teufelssteinfeld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet."}},
		[5230] = {"Rückkehr zum Bollwerk",nil, {"Bringt die gefüllte Flasche: Teufelssteinfeld zur Schattenpriesterin Vandis am Bollwerk, westliche Pestländer."}},
		[5231] = {"Ziel: Dalsons Tränenfeld",nil, {"Begebt Euch zu Dalsons Tränenfeld in den westlichen Pestländern, um den dortigen Kessellord zu suchen und zu besiegen. Verwendet dann seinen Schlüssel, um Zugang zum Kessel zu erhalten. Ihr müsst die leere Flasche: Dalsons Tränenfeld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet."}},
		[5232] = {"Rückkehr zum Bollwerk",nil, {"Bringt die gefüllte Flasche: Dalsons Tränenfeld zur Schattenpriesterin Vandis am Bollwerk, westliche Pestländer."}},
		[5233] = {"Ziel: Das trostlose Feld",nil, {"Begebt Euch zum trostlosen Feld in den westlichen Pestländern, um den dortigen Kessellord zu suchen und zu besiegen. Verwendet dann seinen Schlüssel, um Zugang zum Kessel zu erhalten. Ihr müsst die leere Flasche: Das trostlose Feld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet."}},
		[5234] = {"Rückkehr zum Bollwerk",nil, {"Bringt die gefüllte Flasche: Das trostlose Feld zur Schattenpriesterin Vandis am Bollwerk, westliche Pestländer."}},
		[5235] = {"Ziel: Gahrrons Trauerfeld",nil, {"Begebt Euch zu Gahrrons Trauerfeld in den westlichen Pestländern, um den dortigen Kessellord zu suchen und zu besiegen. Verwendet dann seinen Schlüssel, um Zugang zum Kessel zu erhalten. Ihr müsst die leere Flasche: Gahrrons Trauerfeld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet."}},
		[5236] = {"Rückkehr zum Bollwerk",nil, {"Bringt die gefüllte Flasche: Gahrrons Trauerfeld zur Schattenpriesterin Vandis am Bollwerk, westliche Pestländer."}},
		[5237] = {"Mission erfüllt!",nil, {"<Name>, Eure erfolgreiche Umarbeitung der Kessel ist ein überwältigender Sieg für uns!nnnnAlle, die sich hier in meinem Umfeld befinden, mögen Folgendes hören: Für Dienste am Königreich Stormwind und der Allianz, die über die gebotenen Pflichten hinausgingen - trotz wenig Erfolg versprechender Chancen und im Angesicht unglaublicher Gefahren, möchte ich <Name> dies geben und ihn unserer unvergänglichen Dankbarkeit als wahren Helden der Allianz versichern. Danke, sage ich!"}},
		[5238] = {"Mission erfüllt!",nil, {"<Name>, Eure erfolgreiche Umarbeitung der Kessel ist ein überwältigender Sieg für uns!nnnnAlle, die sich hier in meinem Umfeld befinden, mögen Folgendes hören: Für Dienste an den Verlassenen und an der Horde, die weit über die erwarteten Pflichten hinausgingen - trotz wenig Erfolg versprechender Chancen und im Angesicht unglaublicher Gefahren, möchte ich <Name> dies geben und ihn unserer unvergänglichen Dankbarkeit als wahren Helden der Horde versichern. Hussah!"}},
		[5241] = {"Onkel Carlin",nil, {"Sucht Carlin Rotpfad."}},
		[5242] = {"Der endgültige Schlag",nil, {"Tötet Moora und Salia und bringt den Kopf von Schattenlord Fel'dan zu Greta Mooshuf im Teufelswald."}},
		[5243] = {"Häuser der Heiligen",nil, {"Begebt Euch nach Stratholme im Norden. Durchsucht die Vorratskisten, die über die Stadt verstreut sind, und holt 5 Einheiten Weihwasser von Stratholme. Kehrt zu Leonidas Bartholomäus dem Geachteten zurück, wenn Ihr genug der gesegneten Flüssigkeit gesammelt habt."}},
		[5244] = {"Die Ruinen von Kel'Theril",nil, {"Sprecht mit Jaron Steinschleifer in Sternfall."}},
		[5245] = {"Die gequälten Seelen von Kel'Theril",nil, {"Benutzt Jarons Hacke, um die vier Fragmente des Hochgeborenenrelikts zu suchen. Bringt sie zu Aurora Himmelsrufer in den östlichen Pestländern."}},
		[5246] = {"Fragmente der Vergangenheit",nil, {"Sucht die hochheiligen Schriften der Hochgeborenen im Tempel von Zin-Malor in Azshara und bringt sie zu Aurora Himmelsrufer beim Nordpassturm in den östlichen Pestländern."}},
		[5247] = {"Fragmente der Vergangenheit",nil, {"Bringt einen verzauberten Thoriumbarren, 5 Kristallflicker und eine Phiole mit Düsterwasser zu Aurora Himmelsrufer beim Nordpassturm in den östlichen Pestländern."}},
		[5248] = {"Gequält von der Vergangenheit",nil, {"Bringt den Kristall von Zin-Malor zum Kel'Therilsee in Winterquell und sucht einen Geist eines Hochgeborenen, der mit Euch sprechen will."}},
		[5249] = {"Nach Winterquell!",nil, {"Sucht Sternfall in Winterquell und sprecht mit Wynd Nachtjagd."}},
		[5250] = {"Sternfall",nil, {"Sucht die Siedlung Sternfall in Winterquell und sprecht mit Wynd Nachtjagd."}},
		[5251] = {"Der Archivar",nil, {"Reist nach Stratholme und sucht Archivar Galford vom Scharlachroten Kreuzzug. Vernichtet ihn und verbrennt das Scharlachrote Archiv."}},
		[5252] = {"Reuevolle Hochgeborene",nil, {"Sprecht mit Wynd Nachtjagd in Sternfall und fragt nach dem richtigen Ort für den Kristall von Zin-Malor."}},
		[5253] = {"Der Kristall von Zin-Malor",nil, {"Überbringt dem Erzdruiden Fandral Hirschhaupt in Darnassus den Kristall von Zin-Malor."}},
		[5261] = {"Eagan Pelzschneider",nil, {"Sprecht mit Eagan Pelzschneider."}},
		[5262] = {"Die Wahrheit zeigt sich mit Macht",nil, {"Bringt den Kopf von Balnazzar zu Fürst Nicholas Zverenhoff in den östlichen Pestländern."}},
		[5263] = {"Übertroffen",nil, {"Zieht nach Stratholme und vernichtet Baron Totenschwur. Nehmt seinen Kopf und kehrt zu Fürst Nicholas Zverenhoff zurück."}},
		[5264] = {"Lord Maxwell Tyrosus",nil, {"Sprecht mit Lord Maxwell Tyrosus. Er befindet sich in der Kirche."}},
		[5265] = {"Der Argentumtresor",nil, {"Öffnet den Argentumtresor und nehmt Euch Eure Belohnung."}},
		[5281] = {"Die ruhelosen Seelen",nil, {"Sucht Egan. Ihr wisst nur, dass er zuletzt in der Nähe von Stratholme gesehen wurde."}},
		[5282] = {"Die ruhelosen Seelen",nil, {"Wendet Egans Blaster auf die geisterhaften und spektralen Bürger von Stratholme an. Wenn die ruhelosen Geister ihre geisterhaften Hüllen sprengen, wendet den Blaster erneut an - dann sind sie endlich frei!nnBefreit 15 ruhelose Seelen und kehrt zu Egan zurück."}},
		[5283] = {"Die Kunst des Rüstungsschmiedes",nil, {"Um Rüstungsschmied zu werden, müsst Ihr die folgenden Gegenstände herstellen und zu Grumnus bringen: 4 verschnörkelte Mithrilhelme, 2 verschnörkelte Mithrilstiefel, 1 verschnörkelte Mithrilbrustplatte."}},
		[5284] = {"Der Weg des Waffenschmieds...",nil, {"Um Waffenschmied zu werden, müsst Ihr die folgenden Gegenstände herstellen und zu Ironus bringen: 4 Mondstahlbreitschwerter, 4 massive Eisenäxte, 2 schwere Mithriläxte und 2 große schwarze Streitkolben."}},
		[5301] = {"Die Kunst des Rüstungsschmiedes",nil, {"Um Rüstungsschmied zu werden, müsst Ihr die folgenden Gegenstände herstellen und zu Okothos bringen: 4 verschnörkelte Mithrilhelme, 2 verschnörkelte Mithrilstiefel, 1 verschnörkelte Mithrilbrustplatte."}},
		[5302] = {"Der Weg des Waffenschmieds...",nil, {"Um Waffenschmied zu werden, müsst Ihr die folgenden Gegenstände herstellen und zu Borgosh bringen: 4 Mondstahlbreitschwerter, 4 massive Eisenäxte, 2 schwere Mithriläxte und 2 große schwarze Streitkolben."}},
		[5305] = {"Süße Beschaulichkeit",nil, {"Begebt Euch nach Stratholme und tötet den purpurroten Hammerschmied. Nehmt die Schürze des purpurroten Hammerschmiedes und kehrt zu Lilith zurück."}},
		[5306] = {"Schlangenstein der Schattenjägerin",nil, {"Begebt Euch zur Schwarzfelsspitze und erschlagt Schattenjägerin Vosh'gajin. Holt Vosh'gajins Schlangenstein und kehrt zu Kilram zurück."}},
		[5307] = {"Verderbnis",nil, {"Findet den Schwertschmied der schwarzen Wache in Stratholme und vernichtet ihn. Holt die Insignien der schwarzen Wache und kehrt zu Seril Geißelbann zurück."}},
		[5321] = {"Der Schläfer ist erwacht",nil, {"Begleitet Kerlonian Schattenspiel zu Liladris Mondbach in Maestras Posten im Eschental."}},
		[5341] = {"Das Familienvermögen der Barovs",nil, {"Begebt Euch zur Scholomance und holt das Familienvermögen der Barovs zurück. Dieses Vermögen besteht aus vier Besitzurkunden: Es sind die Besitzurkunde für Darrowehr, die Besitzurkunde für Brill, die Besitzurkunde für Tarrens Mühle und die Besitzurkunde für Süderstade. Kehrt zu Alexi Barov zurück, sobald die Aufgabe erledigt ist."}},
		[5342] = {"Der letzte Barov",nil, {"Begebt Euch zum Zugwindlager - Territorium der Allianz - und ermordet Weldon Barov. Nehmt seinen Kopf und kehrt zu Alexi Barov zurück. Seid gewarnt, Weldon wird wahrscheinlich gut von Abschaum der Allianz geschützt."}},
		[5343] = {"Das Familienvermögen der Barovs",nil, {"Begebt Euch zur Scholomance und holt das Familienvermögen der Barovs zurück. Dieses Vermögen besteht aus vier Besitzurkunden: Es sind die Besitzurkunde für Darrowehr, die Besitzurkunde für Brill, die Besitzurkunde für Tarrens Mühle und die Besitzurkunde für Süderstade. Kehrt zu Weldon Barov zurück, sobald die Aufgabe erledigt ist."}},
		[5344] = {"Der letzte Barov",nil, {"Begebt Euch zum Bollwerk - Territorium der Horde - und ermordet Alexi Barov. Nehmt seinen Kopf und kehrt zu Weldon Barov zurück. Seid gewarnt, Alexi wird wahrscheinlich gut durch Abschaum der Horde geschützt."}},
		[5361] = {"Stammbaum",nil, {"Bringt den Bericht von Klippenbehüter Langhorn zu Nataka Langhorn am Geistwandlerposten in Desolace."}},
		[5381] = {"Hand von Iruxos",nil, {"Holt den Dämonenkasten aus der Festung Donneraxt und kehrt damit zu Taiga Klugmähne nach Schattenflucht zurück."}},
		[5382] = {"Doktor Theolen Krastinov, der Schlächter",nil, {"Sucht Doktor Theolen Krastinov in der Scholomance. Vernichtet ihn, verbrennt dann die Überreste von Eva Sarkhoff und die Überreste von Lucien Sarkhoff. Kehrt zu Eva Sarkhoff zurück, sobald Ihr die Aufgabe erfüllt habt."}},
		[5383] = {"Krastinovs Tasche der Schrecken",nil, {"Bringt die Tasche der Schrecken zu Eva Sarkhoff in Darrowehr."}},
		[5384] = {"Kirtonos der Herold",nil, {"Kehrt mit dem Blut Unschuldiger zur Scholomance zurück. Sucht die Veranda und legt das Blut der Unschuldigen in die Kohlenpfanne. Kirtonos wird kommen, um sich von Eurer Seele zu nähren. Kämpft tapfer, gebt keinen Fußbreit nach! Vernichtet Kirtonos und kehrt zu Eva Sarkhoff zurück."}},
		[5385] = {"Die Überreste von Trey Lichtschmied",nil, {"Bringt die Überreste von Trey Lichtschmied zu Jessir Mondsichel im Teufelswald."}},
		[5386] = {"Der Fang des Tages",nil, {"Sprecht mit Jinar'Zillen in Schattenflucht und bringt Nataka Langhorn dann 2 Blutbauchfische."}},
		[5401] = {"Anstecknadel der Argentumdämmerung",nil, {"Alle, die für die Argentumdämmerung das Böse bekämpfen, müssen unsere Anstecknadel tragen. Wenn Ihr eine solche tragt, erhaltet Ihr das Recht, Belohnungen in Anspruch zu nehmen, die unsere Rüstmeister anzubieten haben. Während Ihr die Nadel tragt, könnt Ihr Euch Geißelsteine von erschlagenen Mitgliedern der Geißel holen; das sind Insignien, die sie manchmal besitzen. Beschafft Euch diese Insignien und bringt sie mir. Für alle, die Ihr abgebt, erhaltet Ihr unsere Marke des Heldentums - die Ehrenmarke der Argentumdämmerung. Erfüllt Euer Schicksal, <Klasse> ... stürzt Euch noch heute in den Kampf!"}},
		[5402] = {"Geißelsteine des Dieners",nil, {"Der Geißelstein des Dieners ist die Insignie des Fußvolkes der Geißel. Man findet sie manchmal bei den schwächeren Truppen, die in den Pestländern stationiert sind, obwohl der Begriff 'schwächere' natürlich relativ betrachtet werden muss. Die Geißel sollte, ganz gleich wo sie sich befindet, niemals unterschätzt werden. Die Argentumdämmerung bietet derzeit eine Ehrenmarke im Tausch für zwanzig dieser Insignien - ein Tausch, den ich freudig ausführe, vorausgesetzt, Ihr habt genug Geißelsteine!"}},
		[5403] = {"Geißelsteine des Eindringlings",nil, {"Der Geißelstein des Eindringlings ist die Insignie der Elitetruppen der Geißel. Man findet sie manchmal bei den stärkeren Truppen, die in den Pestländern stationiert sind. Das Erschlagen einer solchen Kreatur ist wahrlich eine Herausforderung, aber eine, die jemand, der sich den Sieg über das Böse auf die Fahne geschrieben hat, mit Freude erfüllen sollte. Die Argentumdämmerung bietet derzeit eine Ehrenmarke im Tausch für zehn dieser Insignien. Bringt sie mir und ich werde sicherstellen, dass Ihr Eure gerechte Belohnung erhaltet."}},
		[5404] = {"Geißelsteine des Verderbers",nil, {"Der Geißelstein des Verderbers ist die Insignie der Oberanführer der Geißel. Ein mächtiges Wesen, das eine Autoritätsposition über der Geißel innehat, wird immer einen besitzen. Eine solch bösartige Kreatur zu erschlagen, würde die Sache der Argentumdämmerung und alle guten Sachen sicherlich voranbringen!nnIm Namen der Argentumdämmerung werde ich Euch eine Ehrenmarke im Tausch für jeweils eine dieser Insignien geben. Seid vorsichtig, wenn Ihr diese Insignien holt, <Name> ... mit solch mächtigen Wesen ist nicht zu spaßen."}},
		[5405] = {"Anstecknadel der Argentumdämmerung",nil, {"Alle, die für die Argentumdämmerung das Böse bekämpfen, müssen unsere Anstecknadel tragen. Wenn Ihr eine solche tragt, erhaltet Ihr das Recht, Belohnungen in Anspruch zu nehmen, die unsere Rüstmeister anzubieten haben. Während Ihr die Nadel tragt, könnt Ihr Euch Geißelsteine von erschlagenen Mitgliedern der Geißel holen; das sind Insignien, die sie manchmal besitzen. Beschafft Euch diese Insignien und bringt sie mir. Für alle, die Ihr abgebt, erhaltet Ihr unsere Marke des Heldentums - die Ehrenmarke der Argentumdämmerung. Erfüllt Euer Schicksal, <Klasse> ... stürzt Euch noch heute in den Kampf!"}},
		[5406] = {"Geißelsteine des Verderbers",nil, {"Der Geißelstein des Verderbers ist die Insignie der Oberanführer der Geißel. Ein mächtiges Wesen, das eine Autoritätsposition über der Geißel innehat, wird immer einen besitzen. Eine solch bösartige Kreatur zu erschlagen, würde die Sache der Argentumdämmerung und alle guten Sachen sicherlich voranbringen!nnIm Namen der Argentumdämmerung werde ich Euch eine Ehrenmarke im Tausch für jeweils eine dieser Insignien geben. Seid vorsichtig, wenn Ihr diese Insignien holt, <Name> ... mit solch mächtigen Wesen ist nicht zu spaßen."}},
		[5407] = {"Geißelsteine des Eindringlings",nil, {"Der Geißelstein des Eindringlings ist die Insignie der Elitetruppen der Geißel. Man findet sie manchmal bei den stärkeren Truppen, die in den Pestländern stationiert sind. Das Erschlagen einer solchen Kreatur ist wahrlich eine Herausforderung, aber eine, die jemand, der sich den Sieg über das Böse auf die Fahne geschrieben hat, mit Freude erfüllen sollte. Die Argentumdämmerung bietet derzeit eine Ehrenmarke im Tausch für zehn dieser Insignien. Bringt sie mir und ich werde sicherstellen, dass Ihr Eure gerechte Belohnung erhaltet."}},
		[5408] = {"Geißelsteine des Dieners",nil, {"Der Geißelstein des Dieners ist die Insignie des Fußvolkes der Geißel. Man findet sie manchmal bei den schwächeren Truppen, die in den Pestländern stationiert sind, obwohl der Begriff 'schwächere' natürlich relativ betrachtet werden muss. Die Geißel sollte, ganz gleich wo sie sich befindet, niemals unterschätzt werden. Die Argentumdämmerung bietet derzeit eine Ehrenmarke im Tausch für zwanzig dieser Insignien - ein Tausch, den ich freudig ausführe, vorausgesetzt, Ihr habt genug Geißelsteine!"}},
		[5421] = {"Fisch im Eimer",nil, {"Ich werde jeden belohnen, der mein Leben ein bisschen einfacher macht... Ich genieße es, so wenig wie möglich zu tun, Mann!nnUnter den Docks hier in Shadowprey habe ich viele Schalentierfallen. Wenn Ihr so freundlich wärt, meine Arbeit zu tun und meine Schalentiere einzusammeln, dann werde ich Euch mit etwas belohnen, das Ihr wollt... Ihr wisst schon, was ich meine, eh? Für jeweils fünf Schalentiere, die Ihr mir bringt, gebe ich Euch einen feinen Blutbauchfisch!"}},
		[5441] = {"Faule Peons",nil, {"Benutzt den Großknechttotschläger an faulen Peons, wenn sie schlafen. Weckt 5 Peons auf und bringt dann den Großknechttotschläger zu Großknecht Thazz'ril ins Tal der Prüfungen zurück."}},
		[5461] = {"Der Mensch Ras Frostraunen",nil, {"Reist in das Arathihochland zu den Ruinen von Stromgarde. Durchsucht Stromgarde nach einem Andenken der Erinnerung. Wenn Ihr einen solchen Gegenstand findet, kehrt mit ihm zu Magistrat Marduk zurück."}},
		[5462] = {"Der Sterbende Ras Frostraunen",nil, {"Reist zur Hoffnung des Lichts in den östlichen Pestländern und sucht Leonidas Bartholomäus den Geachteten auf. Zeigt ihm das Andenken der Erinnerung und berichtet ihm alles, was Ihr entdeckt habt."}},
		[5463] = {"Menethils Geschenk",nil, {"Begebt Euch nach Stratholme und sucht Menethils Geschenk. Platziert das Andenken der Erinnerung auf dem unheiligen Boden."}},
		[5464] = {"Menethils Geschenk",nil, {"Bringt das seelengebundene Andenken zu Leonidas Bartholomäus in den östlichen Pestländern."}},
		[5465] = {"Seelengebundenes Andenken",nil, {"Kehrt zu Magistrat Marduk in Darrowehr zurück. Zeigt ihm das seelengebundene Andenken."}},
		[5466] = {"Der Lich Ras Frostraunen",nil, {"Sucht Ras Frostraunen in der Scholomance. Wenn Ihr ihn gefunden habt, wendet das seelengebundene Andenken auf sein untotes Antlitz an. Solltet Ihr ihn erfolgreich in einen Sterblichen zurückverwandeln können, dann schlagt ihn nieder und nehmt den menschlichen Kopf von Ras Frostraunen an Euch. Bringt den Kopf zu Magistrat Marduk."}},
		[5481] = {"Gordos Aufgabe",nil, {"Sammelt 3 Düsterkräuter und bringt sie zu Jungapotheker Holland auf den Friedhof von Brill."}},
		[5482] = {"Verdammniskraut",nil, {"Sammelt 10 Verdammniskräuter und bringt sie zu Jungapotheker Holland."}},
		[5501] = {"Knochenjäger",nil, {"Bringt Bibbly F'utzbuckel bei Kormeks Hütte 10 Kodoknochen vom Kodofriedhof."}},
		[5502] = {"Tageseltern der Horde",nil, {"Bringt Euren Schützling in das Waisenhaus von Orgrimmar zurück, indem Ihr die Pfeife des Orcwaisenkindes an Waisenmatrone Schlachterbe im Tal der Ehre übergebt."}},
		[5503] = {"Anstecknadel der Argentumdämmerung",nil, {"Alle, die für die Argentumdämmerung das Böse bekämpfen, müssen unsere Anstecknadel tragen. Wenn Ihr eine solche tragt, erhaltet Ihr das Recht, Belohnungen in Anspruch zu nehmen, die unsere Rüstmeister anzubieten haben. Während Ihr die Nadel tragt, könnt Ihr Euch Geißelsteine von erschlagenen Mitgliedern der Geißel holen; das sind Insignien, die sie manchmal besitzen. Beschafft Euch diese Insignien und bringt sie mir. Für alle, die Ihr abgebt, erhaltet Ihr unsere Marke des Heldentums - die Ehrenmarke der Argentumdämmerung. Erfüllt Euer Schicksal, <Klasse> ... stürzt Euch noch heute in den Kampf!"}},
		[5504] = {"Mäntel der Dämmerung",nil, {"Bringt 10 Ehrenmarken der Argentumdämmerung zu Rüstmeister Hasana ins Bollwerk in den westlichen Pestländern."}},
		[5505] = {"Der Schlüssel zur Scholomance",nil, {"Tja, da ist er, <Name>, der fertige Skelettschlüssel. Ich bin eigentlich absolut sicher, dass dieser Schlüssel Euch Zutritt zur Scholomance verschaffen wird. Es gibt aber nur eine Möglichkeit, um absolut sicher zu sein, dass er wirklich funktioniert. Verschwendet keine Zeit darauf, es auszuprobieren, sage ich. Viel Glück. Möget Ihr auch weiterhin Licht in die Dunkelheit bringen!"}},
		[5507] = {"Mäntel der Dämmerung",nil, {"Bringt 10 Ehrenmarken der Argentumdämmerung zu Rüstmeister Funkellicht im Zugwindlager in den westlichen Pestländern."}},
		[5508] = {"Geißelsteine des Verderbers",nil, {"Der Geißelstein des Verderbers ist die Insignie der Oberanführer der Geißel. Ein mächtiges Wesen, das eine Autoritätsposition über der Geißel innehat, wird immer einen besitzen. Eine solch bösartige Kreatur zu erschlagen, würde die Sache der Argentumdämmerung und alle guten Zwecke sicherlich voranbringen!nnIm Namen der Argentumdämmerung werde ich Euch eine Ehrenmarke im Tausch für jeweils eine dieser Insignien geben. Seid vorsichtig, wenn Ihr diese Insignien holt, <Name>... mit solch mächtigen Wesen ist nicht zu spaßen."}},
		[5509] = {"Geißelsteine des Eindringlings",nil, {"Der Geißelstein des Eindringlings ist die Insignie der Elitetruppen der Geißel. Man findet sie manchmal bei den stärkeren Truppen, die in den Pestländern stationiert sind. Das Erschlagen einer solchen Kreatur ist wahrlich eine Herausforderung, aber eine, die jemand, der sich den Sieg über das Böse auf die Fahne geschrieben hat, mit Freude erfüllen sollte. Die Argentumdämmerung bietet derzeit eine Ehrenmarke im Tausch für zehn dieser Insignien. Bringt sie mir und ich werde sicherstellen, dass Ihr Eure gerechte Belohnung erhaltet."}},
		[5510] = {"Geißelsteine des Dieners",nil, {"Der Geißelstein des Dieners ist die Insignie des Fußvolkes der Geißel. Man findet sie manchmal bei den schwächeren Truppen, die in den Pestländern stationiert sind, obwohl der Begriff 'schwächere' natürlich relativ betrachtet werden muss. Die Geißel sollte, ganz gleich wo sie sich befindet, niemals unterschätzt werden. Die Argentumdämmerung bietet derzeit eine Ehrenmarke im Tausch für zwanzig dieser Insignien - ein Tausch, den ich freudig ausführe, vorausgesetzt, Ihr habt genug Geißelsteine!"}},
		[5511] = {"Der Schlüssel zur Scholomance",nil, {"Tja, da ist er, <Name>, der fertige Skelettschlüssel. Ich bin eigentlich absolut sicher, dass dieser Schlüssel Euch Zutritt zur Scholomance verschaffen wird. Es gibt aber nur eine Möglichkeit, um absolut sicher zu sein, dass er wirklich funktioniert. Verschwendet keine Zeit darauf, es auszuprobieren, sage ich. Viel Glück. Mögen Eure Siege Euch auch weiterhin Ruhm und Macht bringen, während Ihr die Scholomance in Schutt und Asche legt!"}},
		[5513] = {"Mäntel der Dämmerung",nil, {"Bringt 10 Ehrenmarken der Argentumdämmerung zu Rüstmeister Knackschloss an der Kapelle des hoffnungsvollen Lichts, östliche Pestländer."}},
		[5514] = {"Sold reimt sich auf...",nil, {"Bringt die magieerfüllten Skelettfragmente sowie 15 Goldstücke zu Krinkle Goldstahl in Gadgetzan."}},
		[5515] = {"Krastinovs Tasche der Schrecken",nil, {"Sucht nach Jandice Barov in der Scholomance und vernichtet sie. Entnehmt ihrer Leiche Krastinovs Tasche der Schrecken. Bringt die Tasche zu Eva Sarkhoff."}},
		[5517] = {"Der Chromatische Mantel der Dämmerung",nil, {"Bringt 25 Ehrenmarken der Argentumdämmerung zu Rüstmeister Knackschloss an der Kapelle des hoffnungsvollen Lichts, östliche Pestländer."}},
		[5518] = {"Der Ogeranzug der Gordok",nil, {"Bringt 4 Runenstoffballen, 8 Stücke unverwüstliches Leder, 2 Runenfaden und etwas Ogergerbemittel zu Knot Zwingschraub. Momentan ist er im Gordokflügel von Düsterbruch angekettet."}},
		[5519] = {"Der Ogeranzug der Gordok",nil, {"Ihr braucht einen weiteren Ogeranzug, richtig? Da ich wohl so schnell nirgendwo hingehe - bringt mir einfach die gleichen Sachen, um die ich Euch zuvor gebeten habe und ich fertige Euch einen weiteren Ogeranzug an."}},
		[5521] = {"Der Chromatische Mantel der Dämmerung",nil, {"Bringt 25 Ehrenmarken der Argentumdämmerung zu Rüstmeister Funkellicht im Zugwindlager in den westlichen Pestländern."}},
		[5522] = {"Leonidas Bartholomäus",nil, {"Bringt die gefrorenen Eier zu Leonidas Bartholomäus in den östlichen Pestländern."}},
		[5524] = {"Der Chromatische Mantel der Dämmerung",nil, {"Bringt 25 Ehrenmarken der Argentumdämmerung zu Rüstmeister Hasana ins Bollwerk in den westlichen Pestländern."}},
		[5525] = {"Befreit Knot!",nil, {"Sie nennen ihn den Gordokfesselschlüssel - Naja, zumindest tun es diese \"Tonnenweise Spaß auf zwei Beinen\"-Oger. Ich denke einer der Oger müsste ihn haben, leider weiß ich nicht genau welcher. Möglicherweise ist die Chance größer, dass einer der Wachen ihn hat... Was erzähle ich da? - sicher habt Ihr ihn schon, oder? Ist es der da??!"}},
		[5526] = {"Die Splitter der Teufelsranke",nil, {"Sucht die Teufelsranke in Düsterbruch und nehmt einen Teufelsrankensplitter mit Euch. Aller Wahrscheinlichkeit nach, werdet Ihr Alzzin den Wildformer töten müssen, um an die Teufelsranke zu gelangen. Benutzt das Requiliar der Reinheit, um darin den Splitter sicher zu versiegeln, und bringt das versiegelte Reliquiar zu Rabine Saturna in Nachthafen, Mondlichtung."}},
		[5527] = {"Ein Reliquiar der Reinheit",nil, {"Reist nach Silithus und sucht in den Ruinen des Südwindpostens nach einem Reliquiar der Reinheit. Sollte Eure Suche Erfolg haben, kehrt zu Rabine Saturna in Nachthafen auf der Mondlichtung zurück."}},
		[5528] = {"Der Gordokgeschmackstest",nil, {"Ihr jetzt de' Big Boss hier. Also kriegen freie Kostproben meiner besseren Gebräue! Diese Tränke werden Euch verstärken UND Euch Schädel weghauen! Das ist da' guta Zeug, Chef... Ihr versuchen und dann Kreeg glauben!"}},
		[5529] = {"Verseuchte Jungtiere",nil, {"Tötet 20 verseuchte Jungtiere und kehrt dann zu Betina Moppelzink bei der Kapelle des hoffnungsvollen Lichts zurück."}},
		[5531] = {"Betina Moppelzink",nil, {"Bringt die gefrorenen Eier zu Betina Moppelzink."}},
		[5533] = {"Scholomance",nil, {"Sprecht mit Alchimist Arbington an der Zugwindspitze, westliche Pestländer."}},
		[5534] = {"Kim'jaels 'fehlende' Ausrüstung",nil, {"Sucht, in den Ruinen von Eldarath irgendeine Rune, die die Naga in ihrem Besitz haben, und bringt sie Kim'jael in Azshara."}},
		[5535] = {"Spirituelle Unruhe",nil, {"Vernichtet 6 Erscheinungen einer Hochgeborenen und 6 Lichlinge eines Hochgeborenen, bevor Ihr zu Loh'atu in Azshara zurückkehrt."}},
		[5536] = {"Ein Land voller Hass",nil, {"Tötet 6 Satyrn der Haldarr, 2 Schwindler der Haldarr sowie 2 Teufelsanbeter der Haldarr und kehrt dann zu Loh'atu an der Grenze zum Eschental zurück."}},
		[5537] = {"Skelettfragmente",nil, {"Bringt 15 Skelettfragmente zu Alchimist Arbington an der Zugwindspitze, westliche Pestländer."}},
		[5538] = {"Sold reimt sich auf...",nil, {"Bringt die magieerfüllten Skelettfragmente sowie 15 Goldstücke zu Krinkle Goldstahl in Gadgetzan."}},
		[5541] = {"Munition für Schlingerschuss",nil, {"Bringt Schlingerschuss' Munition zu Hegnar Schlingerschuss in Dun Morogh."}},
		[5542] = {"Dämonenhunde",nil, {"Erschlagt 20 kleine Seuchenhunde, 5 Seuchenhunde und 5 rasende Seuchenhunde. Kehrt zu Tirion Fordring zurück, sobald Ihr die Aufgabe erfüllt habt."}},
		[5543] = {"Blutgefärbter Himmel",nil, {"Erschlagt 30 Seuchenfledermäuse und kehrt zu Tirion Fordring zurück."}},
		[5544] = {"Aassammlung",nil, {"Sucht nach den Aaslarven und Aasverschlingern dieses Gebiets. Erschlagt sie und sammelt ihr Fleisch. Kehrt zu Tirion Fordring zurück, wenn Ihr 15 Scheiben Aaswurmfleisch gesammelt habt."}},
		[5545] = {"Ein Bündel Probleme",nil, {"Bringt 8 Holzbündel zu Raelen beim Holzfällerlager des Osttals."}},
		[5561] = {"Kodozusammentrieb",nil, {"Bringt fünf gezähmte Kodos sowie den Kodokombobulator zu Smeed Schraubenbuddel bei Schraubenbuddels Lager."}},
		[5581] = {"Portale der Legion",nil, {"Verbannt 6 Portale beim Zirkel der Mannoroc und kehrt dann zu Taiga Klugmähne nach Schattenflucht zurück."}},
		[5582] = {"Gesunde Großdrachenschuppe",nil, {"Bringt die gesunde Großdrachenschuppe zu Betina Moppelzink bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern."}},
		[5601] = {"Schwester Pamela",nil, {"Sucht Pamela Rotpfad in Darroheim."}},
		[5621] = {"Gewänder des Mondes",nil, {"Sucht die Schildwache Shaya und heilt ihre Wunden mit Hilfe von 'Geringes Heilen' (Rang 2). Belegt sie danach mit 'Machtwort: Seelenstärke' und kehrt dann zu Laurna Morgenglanz in Dolanaar zurück."}},
		[5622] = {"Elune geneigt",nil, {"Sprecht mit Laurna Morgenglanz in Teldrassil."}},
		[5623] = {"Dem Licht geneigt",nil, {"Sprecht mit der Priesterin Josetta im Wald von Elwynn."}},
		[5624] = {"Gewänder des Lichts",nil, {"Sucht die Wache Roberts und heilt seine Wunden mit Hilfe von 'Geringes Heilen' (Rang 2). Belegt ihn danach mit 'Machtwort: Seelenstärke' und kehrt dann zu Priesterin Josetta in Goldhain zurück."}},
		[5625] = {"Gewänder des Lichts",nil, {"Sucht den Gebirgsjäger Dolf und heilt seine Wunden mit Hilfe von 'Geringes Heilen' (Rang 2). Belegt ihn danach mit 'Machtwort: Seelenstärke' und kehrt dann zu Maxan Anvol in Kharanos zurück."}},
		[5626] = {"Dem Licht geneigt",nil, {"Sprecht mit Maxan Anvol in Dun Morogh."}},
