		[5648] = {"Gewänder der Spiritualität",nil, {"Findet die Grunzerin Kor'ja und heilt ihre Wunden mit Hilfe von 'Geringes Heilen' (Rang 2). Belegt sie danach mit 'Machtwort: Seelenstärke' und kehrt dann zu Tai'jin in Klingenhügel zurück."}},
		[5649] = {"Der Spiritualität geneigt",nil, {"Sprecht mit Tai'jin in Durotar."}},
		[5650] = {"Gewänder der Dunkelheit",nil, {"Findet die Todeswache Kel und heilt seine Wunden mit Hilfe von 'Geringes Heilen' (Rang 2). Belegt ihn danach mit 'Machtwort: Seelenstärke' und kehrt zum dunklen Kleriker Beryll in Brill zurück."}},
		[5651] = {"Der Dunkelheit geneigt",nil, {"Sprecht mit dem Dunklen Kleriker Beryll in Tirisfal."}},
		[5652] = {"Verhexung der Schwäche",nil, {"Ihr tätet gut daran, diese Lektion zu beachten. Die Verhexung der Schwäche wird Euch im Kampf von großem Nutzen sein. Später werde ich Prüfungen für Euch bereithalten, mit Hilfe derer Ihr Euch weiterentwickeln und Euren Wert für den Stamm beweisen könnt."}},
		[5654] = {"Verhexung der Schwäche",nil, {"Sprecht mit Ur'kyo in Orgrimmar."}},
		[5655] = {"Verhexung der Schwäche",nil, {"Sprecht mit Ur'kyo in Orgrimmar."}},
		[5656] = {"Verhexung der Schwäche",nil, {"Sprecht mit Ur'kyo in Orgrimmar."}},
		[5657] = {"Verhexung der Schwäche",nil, {"Sprecht mit Ur'kyo in Orgrimmar."}},
		[5658] = {"Berührung der Schwäche",nil, {"Ah, junger <Klasse>, ich bin froh, dass Ihr hierher zurückgekehrt seid. Ich würde Euch gerne eine Lektion lehren, die Ihr besser nicht vergessen solltet. Seid Ihr bereit?"}},
		[5660] = {"Berührung der Schwäche",nil, {"Sprecht mit Aelthalyste in Unterstadt."}},
		[5661] = {"Berührung der Schwäche",nil, {"Sprecht mit Aelthalyste in Unterstadt."}},
		[5662] = {"Berührung der Schwäche",nil, {"Sprecht mit Aelthalyste in Unterstadt."}},
		[5663] = {"Berührung der Schwäche",nil, {"Sprecht mit Aelthalyste in Undercity."}},
		[5672] = {"Elunes Anmut",nil, {"Indem Ihr Euch Elune und unserem Volk widmet, habt Ihr einen Status erreicht, den nicht alle von uns erlangen können. Es ist Tradition, dass Euch nun noch größere Fähigkeiten für Eure Hingabe gelehrt werden sollen. Nehmt Ihr an?"}},
		[5673] = {"Elunes Anmut",nil, {"Sprecht mit der Priesterin Alathea in Darnassus."}},
		[5674] = {"Elunes Anmut",nil, {"Sprecht mit der Priesterin Alathea in Darnassus."}},
		[5675] = {"Elunes Anmut",nil, {"Sprecht mit der Priesterin Alathea in Darnassus."}},
		[5676] = {"Arkane Rückkopplung",nil, {"Wir waren gezwungen, einige Magien zu lernen, um uns vor dem zu schützen, was üblicherweise unser stärkster Feind genannt wird: dem Arkanen. Ich würde Euch einen Zauber beibringen, um Euch und Euren Kameraden bei der Verteidigung gegen diejenigen zu helfen, die diese Macht einsetzen."}},
		[5677] = {"Arkane Rückkopplung",nil, {"Sprecht mit der Hohepriesterin Laurena in Sturmwind."}},
		[5679] = {"Verschlingende Seuche",nil, {"Ich würde Euch 'Verschlingende Seuche' beibringen, wenn Ihr Euch bereit dazu fühlt, <Name>. Dieser Spruch ist nicht gerade von kleiner Macht und wird Euch in den kommenden Schlachten helfen. Übt damit - es wird später Prüfungen dazu geben."}},
		[5680] = {"Schattenwache",nil, {"Eure Riten werden bald kommen. Fürs Erste lehre ich Euch einen Zauber, der Euch von großem Nutzen sein wird, wenn Ihr mit Euren Feinden kämpft."}},
		[5713] = {"Ein Schuss, ein Treffer",nil, {"Schützt die Schildwache Aynasha und sprecht dann mit Onaeya bei Maestras Posten im Eschental."}},
		[5721] = {"Die Schlacht um Darroheim",nil, {"Legt das Reliktbündel auf den Marktplatz von Darroheim. Verhindert Davil Lichterscheins Tod, bis Horgus stirbt. Verhindert Hauptmann Rotpfads Tod, bis Rotpfad der Verderbte erscheint. Besiegt Rotpfad den Verderbten. Sprecht mit dem Geist von Joseph Rotpfad, sobald er auf dem Marktplatz erscheint, und sprecht dann mit Pamela in ihrem Zuhause."}},
		[5722] = {"Die Suche nach dem verloren gegangenen Ranzen",nil, {"Sucht im Flammenschlund nach Maur Grimmtotems Leiche und durchsucht sie nach interessanten Gegenständen."}},
		[5723] = {"Die Kraft des Feindes wird auf die Probe gestellt",nil, {"Sucht in Orgrimmar nach dem Flammenschlund, tötet dann 8 Flammenschlundtroggs und 8 Schamanen der Flammenschlundtroggs und kehrt anschließend zu Rahauro in Donnerfels zurück."}},
		[5724] = {"Wiederbeschaffung des verloren gegangenen Ranzens",nil, {"Bringt den Grimmtotemranzen zu Rahauro nach Donnerfels."}},
		[5725] = {"Die Macht der Zerstörung...",nil, {"Bringt die Bücher 'Schattenzauber' und 'Zauberformeln aus dem Nether' zu Varimathras nach Unterstadt."}},
		[5726] = {"Verborgene Feinde",nil, {"Bringt das Insigne eines Offiziers zu Thrall nach Orgrimmar."}},
		[5727] = {"Verborgene Feinde",nil, {"Bringt Neeru Feuerklinge das Insigne des Offiziers und sprecht mit ihm. Seht, ob er Euch glaubt, dass Ihr ein Mitglied der Brennenden Klinge seid, und kehrt dann zu Thrall nach Orgrimmar zurück."}},
		[5728] = {"Verborgene Feinde",nil, {"Tötet Bazzalan und Jergosh den Herbeirufer, bevor Ihr zu Thrall nach Orgrimmar zurückkehrt."}},
		[5729] = {"Verborgene Feinde",nil, {"Sprecht mit Neeru Feuerklinge in Orgrimmar."}},
		[5730] = {"Verborgene Feinde",nil, {"Sprecht in Orgrimmar mit Thrall und berichtet ihm, was Ihr erfahren habt."}},
		[5741] = {"Szepter des Lichts",nil, {"Besorgt das Szepter des Lichts und bringt es zurück zu Azore Aldamort am Turm in Ethel Rethor."}},
		[5742] = {"Erlösung",nil, {"Hört Euch an, was Tirion Fordring zu sagen hat."}},
		[5761] = {"Vernichtung der Bestie",nil, {"Begebt Euch in den Flammenschlund und erschlagt Taragaman den Hungerleider. Bringt anschließend dessen Herz zu Neeru Feuerklinge nach Orgrimmar."}},
		[5762] = {"Hemet Nesingwary Jr.",nil, {"Bringt Kravels Kiste zu Hemet Nesingwary Jr. ins Schlingendorntal."}},
		[5763] = {"Jagd im Schlingendorntal",nil, {"Bringt Roons Kodohorn zu Hemet Nesingwary Jr. ins Schlingendorntal."}},
		[5781] = {"Von vergessenen Erinnerungen",nil, {"Begebt Euch zum Tiefgewölbe im südlichsten Abschnitt der östlichen Pestländer und sucht nach Taelans Hammer. Kehrt zu Tirion Fordring zurück, sobald Ihr Eure Mission abgeschlossen habt."}},
		[5801] = {"Feuersäulen geschmiedet",nil, {"Bringt die Skelettschlüsselform und 2 Barren Thorium zur Spitze des Feuersäulengrats im Krater von Un'Goro. Benutzt die Skelettschlüsselform am Lavasee, um den unvollendeten Skelettschlüssel zu schmieden. Bringt den unvollendeten Skelettschlüssel dann zum Alchimisten Arbington an der Zugwindspitze in den westlichen Pestländern."}},
		[5802] = {"Feuersäulen geschmiedet",nil, {"Bringt die Skelettschlüsselform und 2 Barren Thorium zur Spitze des Feuersäulengrats im Krater von Un'Goro. Benutzt die Skelettschlüsselform am Lavasee, um den unvollendeten Skelettschlüssel zu schmieden. Bringt den unvollendeten Skelettschlüssel zu Apotheker Zitters im Bollwerk, westliche Pestländer."}},
		[5803] = {"Arajs Skarabäus",nil, {"Zerstört Araj den Beschwörer und bringt Arajs Skarabäus zum Alchimisten Arbington an der Zugwindspitze in den westlichen Pestländern."}},
		[5804] = {"Arajs Skarabäus",nil, {"Vernichtet Araj den Beschwörer und bringt Arajs Skarabäus zum Apotheker Zitters im Bollwerk, in den westlichen Pestländern."}},
		[5805] = {"Willkommen!",nil, {"Bringt Merissa Stillbrunn in Goldhain den Goldhaingeschenkgutschein."}},
		[5821] = {"Leibwache zu mieten",nil, {"Bringt Gizeltons Karawane am Dorf der Kolkarzentauren vorbei. Sprecht in Schraubenbuddels Lager mit Smeed, um Eure Belohnung zu erhalten."}},
		[5841] = {"Willkommen!",nil, {"Bringt Yori Bruchhelm in Kharanos den Geschenkgutschein von Kharanos."}},
		[5842] = {"Willkommen!",nil, {"Bringt Orenthil Wisperwind in Dolanaar den Geschenkgutschein von Dolanaar."}},
		[5843] = {"Willkommen!",nil, {"Bringt Magga auf dem Klingenhügel den Geschenkgutschein des Klingenhügels."}},
		[5844] = {"Willkommen!",nil, {"Bringt Vorn Himmelsdeuter im Dorf der Bluthufe den Geschenkgutschein der Bluthufe."}},
		[5845] = {"Von verlorener Ehre",nil, {"Reist ins Nordtal im nordöstlichsten Gebiet der östlichen Pestländer und sucht nach dem Symbol der verlorenen Ehre. Kehrt zu Tirion Fordring zurück, sobald Ihr Eure Mission abgeschlossen habt."}},
		[5846] = {"Von Liebe und Familie",nil, {"Begebt Euch zur Insel Darrowehr im südlich-zentralen Bereich der Pestländer und sucht nach Hinweisen, wo sich das Gemälde befinden könnte."}},
		[5847] = {"Willkommen!",nil, {"Bringt Claire Weidner in Brill den Geschenkgutschein von Brill."}},
		[5848] = {"Von Liebe und Familie",nil, {"Begebt Euch nach Stratholme im nördlichen Teil der Pestländer. In der scharlachroten Bastion findet Ihr das Gemälde 'Von Liebe und Familie', das zwischen anderen Gemälden versteckt ist und auf dem die Zwillingsmonde unserer Welt abgebildet sind. Bringt das Gemälde zu Tirion Fordring."}},
		[5861] = {"Myranda suchen",nil, {"Begebt Euch zu Uthers Grabmal in den Südlichen Pestländern. Zeigt Myranda der Vettel Tirions Geschenk."}},
		[5862] = {"Scharlachrote Täuschung",nil, {"Sprecht mit Myranda, um die scharlachrote Illusion zu erhalten. Begebt Euch nach Herdweiler, während Ihr unter dem Einfluss der scharlachroten Illusion steht, und übergebt dem Hochlord Taelan Fordring Tirions Geschenk. Herdweiler liegt im Norden, Taelan dürfte sich in der Burg Mardenholde aufhalten."}},
		[5863] = {"Das Truppenlager der Dünenbrecher",nil, {"Andi Lynn in Gadgetzan möchte, dass Ihr das Truppenlager der Dünenbrecher vernichtet, indem Ihr 10 Schläger der Dünenbrecher 10 Vollstrecker der Dünenbrecher sowie Gor'marok den Verheerer tötet."}},
		[5881] = {"Hinzurufen der Reserve",nil, {"Maggran möchte, dass Ihr Grish Fernläufer am großen Aufzug im Brachland einen Brief zustellt."}},
		[5882] = {"Salbe durch Jagd",nil, {"Wer ein cenarisches Leuchtfeuer besitzt, kann mit diesem die verderbten Seelensplitter in den besudelten Bestien sehen, die zum Wohl der Allgemeinheit vernichtet werden. Ich schleife Splitter zu einem nutzbaren Reagenz, das für die Herstellung von cenarischer Pflanzensalbe verwendet wird. Wir werden diese Salbe benutzen, um verderbte Pflanzen wieder in gesunde zu verwandeln. Im Austausch für diese Splitter werde ich Euch etwas cenarische Pflanzensalbe geben, die ich bereits vorbereitet habe."}},
		[5883] = {"Salbe durch Bergbau",nil, {"Wenn Ihr im Besitz eines cenarischen Leuchtfeuers seid, während Ihr im Teufelswald Bergbau betreibt, könnt Ihr das besudelte Vitriol sehen, von dem eine Ader Rohmetall verderbt wird. Ich weiß, wie man dieses Vitriol läutert und zu einem Reagenz verarbeitet, aus dem cenarische Pflanzensalbe hergestellt wird. Diese Salbe verwendet Ihr dann an Pflanzen, die Ihr draußen in der Wildnis findet und die gerettet werden müssen. Wenn Ihr verderbtes Vitriol gesammelt habt, werde ich Euch etwas cenarische Pflanzensalbe geben, die ich bereits vorbereitet habe."}},
		[5884] = {"Salbe durch Sammlung",nil, {"Mit einem cenarischen Leuchtfeuer können erfahrene Kräuterkundige die heimtückische Teufelsranke erkennen, ein bösartiges Produkt der Verderbnis des Teufelswalds. Dank meiner Arbeit verfüge ich über das Wissen, die Teufelsranke zu einem Säuberungsreagenz verarbeiten zu können. Dieses verwenden wir für etwas, das wir jetzt cenarische Pflanzensalbe nennen. Wenn Ihr im Teufelswald Kräuter sammelt, dann solltet Ihr, solange Ihr das Leuchtfeuer bei Euch habt, auf Teufelsranken stoßen. Im Austausch gegen das, was Ihr mir bringt, werde ich Euch fertige cenarische Pflanzensalbe geben."}},
		[5885] = {"Salbe durch Kürschnerei",nil, {"Mit einem cenarischen Leuchtfeuer können gut ausgebildete Kürschner die Flecken besudelter Haut auf den im Teufelswald erbeuteten Fellen erkennen. Ich wandle diese scheinbar nutzlosen Flecken in eine dicke Paste um, mit der die cenarische Pflanzensalbe aktiviert wird. Wenn Ihr hier im Teufelswald Eure Beute häutet, dann solltet Ihr unbedingt immer ein Leuchtfeuer bei Euch haben. Sobald Ihr besudelte Flecken gesammelt habt, bringt sie mir. Ich gebe Euch etwas cenarische Pflanzensalbe dafür, die ich bereits hergestellt habe."}},
		[5886] = {"Salbe durch Entzauberung",nil, {"Die Macht eines entzauberten magischen Gegenstandes ist in der von ihm geschaffenen Essenz spürbar. Ich habe einen Weg gefunden, um die rohe Essenz, die von Verzauberern verwendet wird, in ein geeignetes Reagenz für die cenarische Pflanzensalbe umzuwandeln. Dazu braucht Ihr kein cenarisches Leuchtfeuer; jeder angemessen entzauberte Gegenstand, der geringe Netheressenzen abgibt, ist dafür geeignet. Ich brauche nur eine einzige geringe Netheressenz; im Austausch dafür werde ich Euch cenarische Pflanzensalben geben. Die Essenz ist hochwirksam - ganz wenig davon genügt, um viel zu erreichen."}},
		[5887] = {"Salbe durch Jagd",nil, {"Wer ein cenarisches Leuchtfeuer besitzt, kann mit diesem die verderbten Seelensplitter in den besudelten Bestien sehen, die zum Wohl der Allgemeinheit vernichtet werden. Ich schleife Splitter zu einem nutzbaren Reagenz, das für die Herstellung von cenarischer Pflanzensalbe verwendet wird. Wir werden diese Salbe benutzen, um verderbte Pflanzen wieder in gesunde zu verwandeln. Im Austausch für diese Splitter werde ich Euch etwas cenarische Pflanzensalbe geben, die ich bereits vorbereitet habe."}},
