		[7828] = {"Die Pirsch auf die Pirscher",nil, {"Jäger Markhor aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 10 Silbermähnenpirscher und 10 Silbermähnenheuler tötet. Kehrt zu ihm zurück, sobald Ihr Eure Aufgabe erledigt habt. Markhor erwähnte, dass sich die Wölfe in der Wildnis des Hinterlands verstecken."}},
		[7829] = {"Jagt die Wilden",nil, {"Jäger Markhor aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 20 Wilde Eulenbestien tötet. Kehrt zu ihm zurück, sobald Ihr Eure Aufgabe erledigt habt. Markhor erwähnte, dass sich die Eulenbestien in der Wildnis des Hinterlands befinden."}},
		[7830] = {"Die Gefallenen rächen",nil, {"Jäger Markhor aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr einen Klingenschnabelhimmelsfürsten tötet und dessen Federbusch an Euch nehmt. Kehrt zu ihm zurück sobald Ihr diese Aufgabe erledigt habt. Die Klingenschnabelhimmelsfürsten sind eine recht seltene Spezies und nisten meist in der Wildnis des Hinterlands."}},
