		[7838] = {"Großmeister der Arena",nil, {"Aye, Landratte! Bringt mir 12 Schmuckstücke des hiesigen Meisters der Arena und Ihr seid der neue Großmeister der Arena der Gurabashi! Arrrr!"}},
		[7839] = {"Schläger der Blutfratzen",nil, {"Schmied Aschenbaum aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr seine verlorenen Werkzeuge ausfindig macht und dann zu ihm zurückkehrt. Ihr erinnert Euch an Schmied Aschenbaums Worte, dass die Werkzeuge möglicherweise bei einem der Tempel der Blutfratzen zu finden seien, im Nordosten des Hinterlands. Ihr solltet auch den Lauerfels absuchen."}},
		[7840] = {"Lards vergessene Brotzeit",nil, {"Lard aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr Lards Brotzeit wieder findet. Kehrt nach Erfüllung dieser Aufgabe zu ihm zurück. Lard erwähnte, dass er es auf der Insel im Norden vergessen habe. Achtet auf die Trolle der Blutfratzen."}},
		[7841] = {"Eine Botschaft an die Wildhämmer",nil, {"Otho Moji'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 15 Hochtalkundschafterinnen, 15 Hochtalspäher, 15 Hochtalschützen und 15 Hochtalwaldläuferinnen tötet. Kehrt nach vollendeten Taten zu ihm zurück. Ihr findet die Hochtalelfen bei der Jagdhütte von Quel'Danil im Nordwesten des Hinterlands."}},
		[7842] = {"Eine weitere Botschaft an die Wildhämmer",nil, {"Otho Moji'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr ihm 10 lange elegante Federn von Greifen besorgt, die das Hinterland bewohnen. Kehrt mit den Federn zu ihm zurück. Es ist bekannt, dass Greifen überall im Hinterland zu finden sind."}},
		[7843] = {"Die letzte Botschaft an die Wildhämmer",nil, {"Otho Moji'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr zum Nistgipfel im Westen des Hinterlands reist und die letzte Botschaft an die Wildhämmer beim Brunnen im Stadtzentrum platziert. Kehrt nach Vollendung der Aufgabe zu ihm zurück. Hütet Euch vor zornigen Wildhammerzwergen und niederträchtigen Mitgliedern der Allianz."}},
		[7844] = {"Kannibalistische Vettern",nil, {"Mystikerin Yayo'jin aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 15 Skalpierer der Blutfratzen und 10 Sterndeuter der Blutfratzen tötet. Kehrt nach Vollendung dieser Aufgabe zu ihr zurück. Yayo'jin deutete an, dass man diese Trolle nahe der Tempel von Shaol'watha und Agol'watha finden könnte. Sie liegen im Nordosten des Hinterlands."}},
		[7845] = {"Die Entführung des Ältesten Stumpfhauer",nil, {"Ura Stumpfhauer aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr ihren Gefährten, den Ältesten Stumpfhauer, aus Jintha'Alor befreit. Man sagte Euch, dass er hoch oben in Jintha'Alor gefangen gehalten wird - der Hauptstadt der Blutfratzen im Süden des Hinterlands."}},
		[7846] = {"Erlangt den Schlüssel!",nil, {"Der Älteste Stumpfhauer in Jintha'Alor möchte, dass Ihr Hitah'ya die Bewahrerin tötet, um in den Besitz des Fesselnschlüssels zu kommen. Kehrt zum Ältesten Stumpfhauer zurück, wenn Ihr den Schlüssel habt."}},
		[7847] = {"Rückkehr zu Ura Stumpfhauer",nil, {"Der Älteste Stumpfhauer in Jintha'Alor möchte, dass Ihr Ura Stumpfhauer im Dorf der Bruchhauer die Neuigkeit von seiner Befreiung überbringt."}},
		[7848] = {"Abstimmung mit dem Kern",nil, {"Begebt Euch zum Portal in den Schwarzfelstiefen, das in den geschmolzenen Kern führt, und findet ein Kernfragment. Kehrt mit dem Fragment zu Lothos Felsspalter am Schwarzfels zurück."}},
		[7849] = {"Die getrennten Brüder",nil, {"Jäger Markhor aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr die Überreste seines Zwillingsbruders bergt. Findet den Schädel und die Knochen des Jägers Malkhor und bringt sie zu Jäger Markhor zurück. Markhor erwähnte, dass die Überreste seines Bruders an die Brühschlammer und Wölfe der Blutfratzen verfüttert wurden. Er sagte weiterhin, dass es in Jintha'Alor eine Grube mit Brühschlammern gibt, sowie einen Wolfsbau. Beide liegen in der Nähe der Tempelspitze."}},
		[7850] = {"Dunkle Gefäße",nil, {"Ura Stumpfhauer aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr Euch 10 Gefäße mit besudeltem Blut aus Jintha'Alor beschafft. Kehrt dann damit zu Ura Stumpfhauer zurück."}},
		[7861] = {"GESUCHT: Üble Priesterin Hexx und ihre Diener",nil, {"Ihr habt die Anweisung erhalten die üble Priesterin Hexx und 10 Aman'zasiwachen der Blutfratzen zu töten. Habt Ihr Eure Aufgabe erfüllt, so sprecht mit Ura Stumpfhauer aus dem Dorf der Bruchhauer im Hinterland. Die üble Priesterin Hexx und die Aman'zasiwachen halten sich hoch oben in Jintha'Alor im Hinterland auf."}},
		[7862] = {"Offener Posten: Wachbefehlshaber der Bruchhauer",nil, {"Ihr wurdet mit der Vernichtung von 10 Berserkern der Blutfratzen, 5 Schattenjägern der Blutfratzen, 5 Bluttrinkern der Blutfratzen und 5 Seelenfressern der Blutfratzen beauftragt. Kehrt nach Beendigung dieser Aufgabe zu Ura Stumpfhauer ins Dorf der Bruchhauer im Hinterland zurück."}},
		[7863] = {"Basisüberlebenspaket der Schildwache",nil, nil},
		[7864] = {"Standardüberlebenspaket der Schildwache",nil, nil},
		[7865] = {"Verbessertes Überlebenspaket der Schildwache",nil, {"Ihr habt zweifellos begonnen, Euch bei uns als würdig zu erweisen, <Name>. Bitte, nehmt dieses Überlebenspaket als Gegenleistung an! Ihr findet darin Rationen und Verbände, die nur innerhalb der Warsongschlucht verwendet werden können. Bewährt Ihr Euch noch weiter in der Schlucht, werdet Ihr auch mehr dieser Gegenstände erwerben können. Ihr erhaltet die Rationen, wenn unsere Einstellung Euch gegenüber freundlich ist, sowie Verbände, wenn Ihr unser Wohlwollen erlangt habt."}},
		[7866] = {"Basisüberlebenspaket der Vorhut",nil, nil},
		[7867] = {"Standardüberlebenspaket der Vorhut",nil, nil},
		[7868] = {"Verbessertes Überlebenspaket der Vorhut",nil, {"Ihr habt zweifellos begonnen, Euch bei uns als würdig zu erweisen, <Name>. Bitte, nehmt dieses Überlebenspaket als Gegenleistung an! Ihr findet darin Rationen und Verbände, die nur innerhalb der Warsongschlucht verwendet werden können. Bewährt Ihr Euch noch weiter in der Schlucht, werdet Ihr auch mehr dieser Gegenstände erwerben können. Ihr erhaltet die Rationen, wenn unsere Einstellung Euch gegenüber freundlich ist, sowie Verbände, wenn Ihr unser Wohlwollen erlangt habt."}},
		[7871] = {"Besiegt die Eindringlinge!",nil, {"Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt zur Schildwache Weltensang am Hain der Silberschwingen zurück."}},
		[7872] = {"Besiegt die Eindringlinge!",nil, {"Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt zur Schildwache Weltensang am Hain der Silberschwingen zurück."}},
		[7873] = {"Besiegt die Eindringlinge!",nil, {"Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt zur Schildwache Weltensang am Hain der Silberschwingen zurück."}},
		[7874] = {"Bezwingt die Eindringlinge der Silberschwingen",nil, {"Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Allianz. Erwerbt ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt anschließend zu Hauptmann Schädelbrecher im Stützpunkt Mor'shan zurück."}},
		[7875] = {"Bezwingt die Eindringlinge der Silberschwingen",nil, {"Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Allianz. Erwerbt ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt anschließend zu Hauptmann Schädelbrecher im Stützpunkt Mor'shan zurück."}},
		[7876] = {"Bezwingt die Eindringlinge der Silberschwingen",nil, {"Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Allianz. Erwerbt ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt anschließend zu Hauptmann Schädelbrecher im Stützpunkt Mor'shan zurück."}},
		[7877] = {"Der Schatz der Shen'dralar",nil, {"Kehrt in das Athenaeum zurück und sucht den Schatz der Shen'dralar. Nehmt Euch Eure Belohnung!"}},
		[7881] = {"Jahrmarktsstiefel",nil, nil},
		[7882] = {"Jahrmarktswämser",nil, {"Unsere Arbeiter vom Jahrmarkt machen weitaus mehr als Dreck wegzufegen und Bolzen einzuschlagen! Sie helfen auch den Jahrmarkt auf Reisen zu bewachen und benötigen dafür stets robuste und rüstungsstarke Wämser. Wenn Ihr etwas strapazierfähige Rüstung herstellt und zu mir bringt, dann werde ich dies sicherlich an die richtigen Leute weiterleiten. Ich werde Euch im Austausch dafür auch ein paar Lose des Dunkelmond-Jahrmarkts geben."}},
		[7883] = {"Der größte Gnom der Welt!",nil, nil},
		[7884] = {"Der Krokilisken-Junge und der bärtige Murloc",nil, {"Yebb arbeitet an einer weiteren Attraktion für sein Kuriositätenkabinett. Es handelt sich dabei um eine nasse und sumpfige Behausung für einen Jungen, der von Krokilisken aufgezogen wurde und vielleicht auch für einen Murloc. Wir werden schöne, wasserdichte Beinkleider brauchen, damit wir während dem Auf- und Abbauen nicht pitschnass werden. Schildkrötenschuppengamaschen würden sich dafür bestens eignen. <Name>, könnt Ihr solche für uns anfertigen? Wenn ja, dann wird Euch ein dicker Stapel Lose des Dunkelmond-Jahrmarkts erwarten."}},
		[7885] = {"Rüstungssets",nil, {"Es mag Euch vielleicht nicht bewusst sein, aber die Unterhaltung eines fahrenden Jahrmarkts ist kostspielig! Alles nutzt sich so schnell ab, es ist erstaunlich, dass wir überhaupt Gewinne machen. Wegen all dieser versteckten Kosten, suchen wir immer nach Möglichkeiten, wie wir unsere Ausrüstung so günstig wie möglich instandhalten lassen können. Unsere Zelte reißen zum Beispiel regelmäßig, aber ich habe gemerkt, dass Lederrüstungssets gute Flicken abgeben. Könnt Ihr mir ein paar Rüstungssets besorgen? Das würde uns den Kauf von einigen neuen Zelten ersparen! Wenn Ihr uns damit aushelfen könntet, dann würde Euch eine Hand voller Lose des Dunkelmond-Jahrmarkts erwarten..."}},
		[7886] = {"Verdienstabzeichen",nil, nil},
		[7887] = {"Verdienstabzeichen",nil, nil},
		[7888] = {"Verdienstabzeichen",nil, nil},
		[7889] = {"Grober Gewichtsstein",nil, nil},
		[7890] = {"Schwerer Schleifstein",nil, {"Ich würde gerne einen Stab zwischen schweren Schleifsteinen befestigen und ihn dann über meinen Kopf stemmen, um mein Training zu verbessern. Es ist nicht einfach, aber man wird schließlich nicht durch bloßes Herumsitzen zur stärksten Frau der Welt - so wie ich es bin!nnKönnt Ihr mir helfen? Ich brauche mehr Schleifsteine - bringt mir ein paar und ich tausche sie gegen Lose des Dunkelmond-Jahrmarkts ein."}},
		[7891] = {"Grüne Eisenarmschienen",nil, {"Ich arbeite an einem neuen Kostüm für später, wenn ich einmal meinen eigenen Stand als stärkste Frau der Welt habe! Ich suche gerade nach ein paar Armschienen, die ich tragen könnte. Sie dürfen nicht zu viel bedecken, da die Leute meine Muskeln sehen möchten, aber ich möchte trotzdem etwas Farbe ins Spiel bringen, versteht Ihr?nnWie wäre es mit grünen Eisenarmschienen, <Name>? Könnt Ihr mir solche herstellen? Wenn ja, dann hätte ich einen ganzen Haufen Lose des Dunkelmond-Jahrmarkts für Euch."}},
		[7892] = {"Großer schwarzer Streitkolben",nil, nil},
		[7893] = {"Rituale der Stärke",nil, {"<Name>, in meinem Bestreben die stärkste Frau der Welt zu werden, bin ich auf meinen Reisen bereits weit umher gekommen und habe geheime Trainingstechniken von Gewichthebern, Kraftgurus und uralten Handbüchern erlernt. Ich kenne verbotene Übungen, die Schwache töten und Unerfahrene verkrüppeln könnten!nnDiese Leistung von Stärke und Konditionierung erfordert ein Training mit den schwersten aller Schleifsteine überhaupt. <Name>, könnt Ihr mir diese Steine besorgen oder für mich herstellen? Ich würde Euch dafür auch Lose geben..."}},
		[7894] = {"Kupfermodulator",nil, {"Yebb arbeitet an einer neuen Attraktion für das Kuriositätenkabinett, eine, die die Leute vor Verwunderung und Erstaunen begeistern wird! Es wird allerdings eine Menge an Arbeit und bedeuten und viel Material benötigen... Unter anderem Kupfermodulatoren. Wir brauchen zahlreiche Modulatoren für die mechanischen Teile der Attraktion. Bringt mir Kupfermodulatoren und ich werde sie gegen ein Los des Dunkelmond-Jahrmarkts einlösen."}},
		[7895] = {"Surrendes bronzenes Dingsda",nil, {"Mein neuer Stand wird etwas, das man sehen muss, ja! Etwas, das man sehen muss und... für ein paar Glückliche ist es auch etwas zum Anfassen!nnIch kann schon das mechanische Entzücken der surrenden kleinen Dingsdas hören und sehe schon die Kunden vor mir, wie sie näher an meinen Stand treten, um ihre Fähigkeiten zu testen!nnAh... jetzt brauche ich die Dingsdas selbst, um meinem Traum ein Stückchen näher zu kommen. Bitte, <Name>, bringt mir surrende bronzene Dingsdas... und ein paar Lose des Dunkelmond-Jahrmarkts werden Euer sein!"}},
		[7896] = {"Grünes Feuerwerk",nil, {"Wenn ich meinen Stand endlich fertig habe, möchte ich auch dafür werben! Was würde sich dafür schon besser eignen als Feuerwerk?nnNun, manch einer würde sagen, dass sich der Geruch eines Kessels, in dem die Köpfe unserer Feinde köcheln, besser zum Anziehen von Massen eignet, aber... wir sind eine gemischte Gesellschaft. Es muss also Feuerwerk sein!nnBringt mir grünes Feuerwerk, <Name>, und ich werde Euch eine Hand voller Lose des Dunkelmond-Jahrmarkts geben."}},
		[7897] = {"Mechanische Repariersets",nil, {"Der hauptsächliche Grund, weshalb ich meinen Stand nicht eröffnen kann, ist... weil alles kaputt ist! Ich brauche mechanische Repariersets, um alles wieder herrichten zu können!nnKönnt Ihr mir diese Sets besorgen, <Name>? Ich tausche sie gegen... Sets von Losen des Dunkelmond-Jahrmarkts. Was denkt Ihr?"}},
		[7898] = {"Thoriumapparat",nil, {"Yebb Nebblegear arbeitet wie ich an einer zukünftigen Attraktion. Nebb sagt, dass die Attraktion so aufregend ist, dass es Besucher aus der ganzen Welt anziehen wird...und uns alle sehr, sehr reich!nnEr meint, dass er Thoriumapparate für dieses Projekt benötigt und ich soll sie für ihn sammeln. Na, was sagt Ihr; <Name>? Wisst Ihr, wo man Thoriumapparate bekommt? Solltet ihr Ingenieur sein, könntet Ihr dann welche herstellen? Wollt Ihr sie gegen Lose des Dunkelmond-Jahrmarkts einlösen?"}},
		[7899] = {"Kleine pelzige Tatzen",nil, {"Will man eine große Masse herbeilocken, sollte man vor allem eines wissen: wie man mit kleinen Anreizen und Krimskrams ihr Interesse weckt! Könnt Ihr mir dabei helfen?nnIch brauche kleine pelzige Tatzen, die ich als Preise verwenden kann. Ihr könnt dergleichen von den Katzen des Brachlands oder der Dunkelküste erhalten... Bringt mir ein paar Tatzen und ich tausche sie gegen ein Los des Dunkelmond-Jahrmarkts ein."}},
		[7900] = {"Zerrissene Bärenpelze",nil, {"Kinder lieben Spielsachen! Egal was für Kinder, ich habe herausgefunden, dass alle Kinder pelzige und kuschelige Spielsachen lieben, die aus zerrissenem Bärenpelz angefertigt wurden!nnIhr könnt diese Pelze von Bären aus Ashenvale oder Hillsbrad bekommen. Bringt mir einen Haufen davon und verdient Euch damit eine Menge Lose des Dunkelmond-Jahrmarkts!"}},
		[7901] = {"Weiche buschige Schwänze",nil, {"Es ist unglaublich was Leute so alles schätzen! Wie wäre es zum Beispiel mit einem Stock, an dem man einen weichen buschigen Schwanz hängt. Ihr und ich mögen nun denken, dass derartiges keinerlei Wert hat... aber verpasst man so einem Quatsch einen netten Namen wie \"Wilder Wuschelschwanz\" und packt das Ganze in eine hübsche Schachtel, dann erscheint es den Leuten plötzlich wertvoll. Aus Tand werden heiß begehrte Objekte!nnBesorgt mir ein paar weiche buschige Schwänze von Wölfen aus Desolace und dem Ödland, dann werde ich Euch dafür Lose des Dunkelmond-Jahrmarkts geben."}},
		[7902] = {"Farbenprächtige Federbüsche",nil, {"Das Vorstellungsvermögen einer Person ist etwas Unglaubliches. Es kann aus einem kleinen Hügel einen riesigen Berg machen und aus einem harmlosen Schatten einen furchtbaren Alptraum!nnWir vom Dunkelmond-Jahrmarkt erwecken die Fantasien unserer Besucher zum Leben! Allerdings müssen wir uns manchmal schlaue Tricks einfallen lassen, um ihren Vorstellungen etwas auf die Sprünge zu helfen... Ich brauche farbenprächtige Federbüsche für eine meiner Attraktionen. Ihr könnt sie von den Vögeln der etwas gefährlicheren Gegenden Azeroths bekommen. Bringt mir diese Federn und zahlreiche Lose werden Euer Eigen sein!"}},
		[7903] = {"Böse Fledermausaugen",nil, {"Kommt es vor, dass Ihr spät in der Nacht in einer Höhle umherwandert und nach Abenteuern sucht? Ja? Wirklich? Nun, dann habt Ihr die gruseligen Fledermausaugen gesehen, die Euch von hoch oben anstarren und nur darauf warten, dass Ihr unachtsam werdet, damit sie Euch Euer Blut aussaugen können!nnEin schauriger Gedanke, nicht wahr? Natürlich - Fledermausaugen sind ohne Frage gruselig!nnKönnt Ihr mir ein paar böse Fledermausaugen besorgen, <Name>? Ich brauche sie für... für etwas, das ich entwerfe. Etwas Gruseliges. Ihr könnt sie von den Fledermäusen der östlichen Pestländer bekommen."}},
		[7905] = {"Der Dunkelmond-Jahrmarkt",nil, {"Bringt den Gutschein zu Gelvas Rußgatter, der sich innerhalb des Dunkelmond-Jahrmarkts aufhält."}},
		[7906] = {"Dunkelmond-Karten: Bestien",nil, nil},
		[7907] = {"Bestienkartenset des Dunkelmond-Jahrmarkts",nil, {"Gebt das Bestienkartenset auf dem Dunkelmond-Jahrmarkt ab, sobald dieser wieder in der Stadt ist."}},
		[7908] = {"Meister der Arena",nil, {"Begebt Euch in die Arena des Schlingendorntals und sprecht mit Störtebrecher."}},
		[7921] = {"Verdienstabzeichen",nil, nil},
		[7922] = {"Ehrenabzeichen",nil, nil},
		[7923] = {"Ehrenabzeichen",nil, nil},
		[7924] = {"Ehrenabzeichen",nil, nil},
		[7925] = {"Ehrenabzeichen",nil, nil},
		[7926] = {"Der Dunkelmond-Jahrmarkt",nil, {"Bringt den Gutschein zu Gelvas Rußgatter, der sich innerhalb des Dunkelmond-Jahrmarkts aufhält."}},
		[7927] = {"Portalkartenset des Dunkelmond-Jahrmarkts",nil, {"Gebt das Portalkartenset auf dem Dunkelmond-Jahrmarkt ab, sobald dieser wieder in der Stadt ist."}},
		[7928] = {"Kriegsfürstenkartenset des Dunkelmond-Jahrmarkts",nil, {"Gebt das Kriegsfürstenkartenset auf dem Dunkelmond-Jahrmarkt ab, sobald dieser wieder in der Stadt ist."}},
		[7929] = {"Elementarkartenset des Dunkelmond-Jahrmarkts",nil, {"Gebt das Elementarkartenset auf dem Dunkelmond-Jahrmarkt ab, sobald dieser wieder in der Stadt ist."}},
		[7930] = {"5 Lose - Dunkelmond-Blume",nil, {"Kommt näher, kommt näher! Wenn ihr Lose vom Dunkelmond-Jahrmarkt habt, die ihr einlösen möchtet, dann sagt nur Bescheid! Ihr könnt Lose gegen zahlreiche, wundersame und fantastische Preise einlösen. Scheut euch nicht, probiert es aus!"}},
		[7931] = {"5 Lose - Kleiner Dunkelmond-Preis",nil, {"Kommt näher, kommt näher! Wenn ihr Lose vom Dunkelmond-Jahrmarkt habt, die ihr einlösen möchtet, dann sagt nur Bescheid! Ihr könnt Lose gegen zahlreiche, wundersame und fantastische Preise einlösen. Scheut euch nicht, probiert es aus!"}},
		[7932] = {"12 Lose - Geringer Dunkelmond-Preis",nil, {"Kommt näher, kommt näher! Wenn ihr Lose vom Dunkelmond-Jahrmarkt habt, die ihr einlösen möchtet, dann sagt nur Bescheid! Ihr könnt Lose gegen zahlreiche, wundersame und fantastische Preise einlösen. Scheut euch nicht, probiert es aus!"}},
		[7933] = {"40 Lose - Großer Dunkelmond-Preis",nil, {"Kommt näher, kommt näher! Wenn ihr Lose vom Dunkelmond-Jahrmarkt habt, die ihr einlösen möchtet, dann sagt nur Bescheid! Ihr könnt Lose gegen zahlreiche, wundersame und fantastische Preise einlösen. Scheut euch nicht, probiert es aus!"}},
		[7934] = {"50 Lose - Dunkelmond-Lagerbehälter",nil, nil},
		[7935] = {"10 Lose - Hammel des letzten Monats",nil, nil},
		[7936] = {"50 Lose - Hammel des letzten Jahres",nil, nil},
		[7937] = {"Euer Schicksal erwartet Euch...",nil, {"Reist zum Holzfällerlager des Osttals im Wald von Elwynn und sucht nach Eurem Schicksal."}},
		[7938] = {"Euer Schicksal erwartet Euch...",nil, {"Reist zu den Todesminen in Westfall und sucht nach Eurem Schicksal."}},
		[7939] = {"Mehr verdichtete Schleifsteine",nil, {"<Name>, ich danke Euch für all Eure Hilfe. Ich kann Euch wirklich nicht genug danken!nnIch brauche jetzt zwar nichts mehr wirklich... aber solltet Ihr mir mehr verdichtete Schleifsteine bringen, so habe ich auch noch einige Lose des Dunkelmond-Jahrmarkts für Euch."}},
		[7940] = {"1200 Lose - Kugel des Dunkelmonds",nil, {"Kommt näher, kommt näher! Wenn ihr Lose vom Dunkelmond-Jahrmarkt habt, die ihr einlösen möchtet, dann sagt nur Bescheid! Ihr könnt Lose gegen zahlreiche, wundersame und fantastische Preise einlösen. Scheut euch nicht, probiert es aus!"}},
		[7941] = {"Mehr Rüstungssets",nil, {"<Name>, ich muss mich für all Eure Hilfe bedanken. Ich verfüge jetzt über eine Menge an Vorräten, dank Eurer Bemühungen, aber wenn Ihr mir noch mehr Rüstungssets bringen möchtet, dann werde ich sie gerne gegen ein paar Lose des Dunkelmond-Jahrmarkts eintauschen."}},
		[7942] = {"Mehr Thoriumapparate",nil, {"<Name>, Ihr wart so gut zu uns, indem Ihr uns ohne Unterlass Waren beschafft habt. Ich bin Euch dafür unendlich dankbar!nnAuch wenn wir einen nicht mehr so großen Bedarf an Thoriumapparaten wie zuvor haben, so könnt Ihr mir doch jederzeit weitere bringen. Ich tausche sie dann gegen Lose des Dunkelmond-Jahrmarkts ein."}},
		[7943] = {"Mehr Fledermausaugen",nil, nil},
		[7944] = {"Euer Schicksal erwartet Euch...",nil, {"Reist zu den Höhlen des Wehklagens im Brachland und sucht nach Eurem Schicksal."}},
		[7945] = {"Euer Schicksal erwartet Euch...",nil, {"Reist zum Bleichmähnenfels in Mulgore und sucht nach Eurem Schicksal."}},
		[7946] = {"Jubjubs Nachwuchs",nil, {"Ich bin so froh, dass Jubjub wieder da ist. Die Kleine liebt Dunkeleisenbier so sehr, dass sie, als mein Vorrat zuneige ging, weggerannt ist, um mehr davon zu suchen. Jetzt, da Jubjub zurück ist, versuche ich, soviel Bier wie möglich zu kriegen! Ich möchte nicht, dass sie wieder wegläuft, wisst Ihr?nnHabt Ihr vielleicht etwas Dunkeleisenbier dabei? Wenn ja, gebe ich Euch eines von Jubjubs Froscheiern für einen Krug Dunkeleisenbier..."}},
		[7961] = {"Es ist Hasensaison!",nil, {"Tötet 5 Hasen und kehrt zu Jon LeCraft an der Schmiede auf der Designer-Insel zurück."}},
		[7981] = {"1200 Lose - Amulett des Dunkelmonds",nil, nil},
		[8001] = {"Vorhut des Kriegshymnenklans",nil, nil},
		[8002] = {"Schildwachen der Silberschwingen",nil, nil},
		[8021] = {"iCoke-Gewinngutschein einlösen",nil, nil},
		[8023] = {"iCoke-Gewinngutschein einlösen",nil, nil},
		[8026] = {"iCoke-Gewinngutschein einlösen",nil, nil},
		[8041] = {"Stärke des Bergs Mugamba",nil, {"Seid gegrüßt, <Klasse>. Ich bin Jin'rokh der Zerstörer, Troll von Mugamba. Wir vom Stamm der Zandalar erkennen Eure Leistungen an. Wir begrüßen Eure Hartnäckigkeit und Willenskraft. Tragt diesen Talisman um Euren Hals, <Klasse>. Lasst die Stärke des Bergs Mugamba durch Euch hindurch fließen. Fühlt die Macht, wenn Ihr unsere Feinde niederstreckt!"}},
		[8042] = {"Stärke des Bergs Mugamba",nil, {"Gut zu sehen, dass Ihr wohlauf seid, <Name>. Die glorreichen Schlachten, in denen Ihr Euch geschlagen habt, wirken sich auf das Leben des gesamten Planeten aus. Hakkar darf die Grenzen von Zul'Gurub niemals überschreiten, da die Welt seinem immensen Hunger mit Sicherheit nicht standhalten könnte. Gebt mir Euren Talisman und ich werde seine Stärke noch mächtiger machen. Setzt die Stärke von Mugamba zum Bestrafen unserer Feinde ein!"}},
		[8043] = {"Stärke des Bergs Mugamba",nil, {"Meister <Name>, Ihr kehrt zurück! Molthor hat bereits Kunde von Euren Taten zum König geschickt. Euer Name ist sogar in den südlichen Meeren bekannt. Vielleicht werdet Ihr eines Tages ein legendärer <Klasse> sein... eine Legende für die Ewigkeit. Gebt mir Euren Talisman, damit ich seine Macht noch einmal verstärken kann."}},
		[8044] = {"Die Wut Mugambas",nil, {"König Rastakhan sendet Euch seine Grüße, Meister <Name>. Eine solche Ehre wurde noch nie jemanden zuteil, der nicht zum Stamm der Zandalar gehört. nnGebt mir Euren Talisman, damit ich ihn noch einmal verstärken kann. Ihr sollt wissen, dass die Wut Mugambas jetzt untrennbar mit Eurer Seele verbunden ist."}},
		[8045] = {"Das Emblem des Ungläubigen",nil, {"Wisset, Ungläubiger: eine Katastrophe hat uns vereint. Eure heidnischen Wege werden von den Zandalarianern weder verstanden, noch geschätzt, aber wir sind ein faires Volk. Tragt dieses Emblem um Euren Hals. Es soll unseren Feinden als Warnung und den Zandalarianern als Zeichen der Freundschaft dienen. Dient auch weiterhin unserem Stamm und ich werde die Macht des Emblems verstärken."}},
		[8046] = {"Das Emblem des Ungläubigen",nil, {"Ich war vielleicht etwas zu vorschnell mit meinem Urteil, <Klasse>. Es scheint, als wäre Euer blindes Vertrauen in das Licht nach all dem doch noch von Wert. Gebt mir Euer Emblem, damit ich seine Macht verstärken kann."}},
		[8047] = {"Das Emblem des Ungläubigen",nil, {"Ich muss gestehen, dass ich nicht daran geglaubt habe, dass Ihr die Entschlossenheit besitzt, um dieser Sache Herr zu werden, <Klasse>. Für Zandalar seid Ihr jetzt ein Verbündeter von unschätzbarem Wert. Gut gemacht, in der Tat. Gebt mir Euer Emblem und und ich werde seine Macht noch einmal verstärken."}},
		[8048] = {"Das Emblem des Helden",nil, {"Der König nimmt Eure Existenz zur Kenntnis, <Klasse>. Ihr seid der erste Eurer Art, der von Rastakhan als wahrer Verbündeter der Zandalarianer akzeptiert wird. Ihr werdet für Eure Bemühungen reichlich belohnt werden. Zeigt Euer Emblem vor und ich werde auch noch dessen letzte Verstärkung vornehmen."}},
		[8049] = {"Das Auge von Zuldazar",nil, {"Nehmt diesen Talisman als Zeichen unserer Wertschätzung an, <Klasse>. Das Auge von Zuldazar wird umso mächtiger, je stärker Euer Bund mit den Trollen von Zandalar wird. Kommt von Zeit zu Zeit zu mir zurück und ich werde das Juwel mit Macht erfüllen."}},
		[8050] = {"Das Auge von Zuldazar",nil, {"Willkommen <Klasse>. Das Auge von Zuldazar pulsiert voller Energie. Ihr seid für die nächste Stufe bereit. Überreicht mir den Talisman, damit ich ihn mit Macht erfüllen kann."}},
		[8051] = {"Das Auge von Zuldazar",nil, {"Überall verbreiten sich Geschichten von Euren Schlachtzügen gegen die Gurubashi, <Klasse>. Sogar im Königshaus wird jetzt von Euren Heldentaten gesprochen. Man sagt, dass König Rastakhan sogar Euren Namen erwähnt hat. Nun ist es an der Zeit das Auge zu erfüllen. Reicht mir den Talisman und ich werde Euch diesen Dienst erweisen."}},
		[8052] = {"Das allsehende Auge von Zuldazar",nil, {"Wenn wir nur zehntausend mehr wie Euch hätten, <Name>. Die Armeen Hakkars würden in kürzester Zeit niedergeschmettert werden. nnAch, das Auge ist nun für die Erfüllung seines wahren Potentials bereit. Gebt es mir."}},
		[8053] = {"Symbole der Macht: Die Armschützer des Freidenkers",nil, {"Bringt urzeitliche Hakkaribindungen zu Jin'rokh dem Zerstörer. Die Einstellung des Stammes der Zandalar muss Euch gegenüber wenigstens freundlich oder höher sein. Jin'rokh der Zerstörer befindet sich auf der Insel Yojamba im Schlingendorntal."}},
