		[8080] = {"Die Ressourcen des Arathibeckens",nil, {"Gewinnt die Schlacht um das Arathibecken, raubt eine Arathivorratskiste und bringt sie Unteroffizier Maclear in der Zuflucht."}},
		[8081] = {"Noch mehr Vorratskisten",nil, nil},
		[8101] = {"Der Stein des Kajaro",nil, {"<Volk>, der Stein, der von diesem Talisman umrahmt wird, kommt vom Berg Kajaro in den südlichen Meeren. Das Gebiet rund um den Berg Kajaro wird von Unbeständigkeit geplagt; insbesondere durch heftige und oft auch magische Vulkanausbrüche. Je stärker Euer Bund mit den Trollen Zandalars wird, umso mächtiger wird dieser Stein. Nutzt die Macht des Steins, um unsere Feinde zu vernichten. Seid wie der Berg: schnell, explosiv, tödlich..."}},
		[8102] = {"Der Stein des Kajaro",nil, {"Willkommen zurück, <Klasse>. Ich kann sehen, dass der Stein häufig Anwendung fand. Viele unserer Feinde wurden Opfer Eurer jenseitigen Fähigkeiten. Ihr Leiden war groß. Es ist an der Zeit, dass ich die Macht des Steins verstärke. Gebt ihn mir."}},
		[8103] = {"Der Stein des Kajaro",nil, {"Die Hakkari fürchten Euch, <Name>. Sie sprechen von Grausamkeiten, die sie bis jetzt nur ihrem gesichtslosen Gott zutrauten. Ihr habt sie mit der Macht des Kajaro gezeichnet. Sie kennen jetzt die beiden Seiten der Furcht. Es ist an der Zeit, den Stein noch weiter zu stärken. Gebt mir den Talisman."}},
		[8104] = {"Das Juwel von Kajaro",nil, {"Gebt mir den Talisman, <Name>. Ich muss Euch etwas zeigen."}},
		[8105] = {"Die Schlacht um das Arathibecken!",nil, {"Erobert die Mine, das Sägewerk, die Schmiede und die Farm und meldet Euch anschließend bei Feldmarschall Lichtmark in der Zuflucht."}},
		[8106] = {"Kezans Schmach",nil, {"Bitte nehmt diesen Talisman, <Klasse>. Nutzt seine Macht, um unsere gemeinsamen Feinde niederzuschmettern. Seid Euch bewusst, dass Ihr ein Stück Geschichte um Euren Hals tragt. Kezan war einst ein großes Territorium des Imperiums von Zandalar. Doch im Laufe der Zeit zwang der schändliche Einfluss der Goblin-Handelsfürsten meine stolzen Brüder und Schwestern, Kezan den Rücken zu kehren. Kezan beherbergt jetzt Undermine, die Hauptstadt der Goblins; doch wisset, dass sie einst wahre Größe ausstrahlte. Noch heute gilt Kezan meinem Volk als heiliges Land."}},
		[8107] = {"Kezans Schmach",nil, {"Ihr habt Euch bei den Trollen Zandalars Ehre verdient, <Name>. Mit Ehre kommt auch Entlohnung, Freundschaft, Verbundenheit... Gebt mir den Talisman, damit ich seine Macht verstärken kann."}},
		[8108] = {"Kezans Schmach",nil, {"Ich bin für euch bereit, <Klasse>. Noch einmal werde ich Euren Talisman verstärken. Ich bitte Euch nur mit Eurer Zerstörung von Hakkar und seinen Dienern fortzufahren. Gebt mir den Talisman."}},
		[8109] = {"Kezans unaufhaltsame Schmach",nil, {"Ruft Eure Dämonen hervor, <Klasse>. Lasst sie in der Energie von Kezans Schmach schwelgen. Führt sie auf's Schlachtfeld! Vernichtet den Feind!nnJa... die Zeit ist gekommen, um Kezans Schmach endgültig zu verstärken. Gebt mir den Talisman."}},
		[8110] = {"Verzauberter Südmeertang",nil, {"Unglaublich! Ihr habt sie gefunden! Ihr seid ein wahrer Held, <Name>!nnDas ist eine gute Sache, denn ich habe die Schrifttafeln gelesen, die Ihr mir zuvor gebracht habt, und nach dem, was ich herausgefunden habe... tja, da werden wir wohl einen Helden brauchen, der aufhält, was kommt..."}},
		[8111] = {"Verzauberter Südmeertang",nil, {"Hey Ihr! Gut Euch wieder zu sehen. Der Stamm spricht von Eurer Vernichtung der Hakkari und all dem. Molthor hat mir zugesichert, dass ich Eurem Talisman mehr Mojo hinzufügen kann. Gebt ihn einfach her und Maywiki wird ihn besser machen."}},
		[8112] = {"Verzauberter Südmeertang",nil, {"Wie ist der Talisman? Es sieht aus, als wäre es an der Zeit dem Tang etwas mehr Magie und Mojo zuzufügen. Er benötigt eine kleine Auffrischung. Gebt in her und Maywiki wird's schon machen."}},
		[8113] = {"Reiner verzauberter Südmeertang",nil, nil},
		[8114] = {"Kontrolliert vier Stützpunkte",nil, {"Betretet das Arathibecken, erobert und kontrolliert gleichzeitig vier Stützpunkte im Arathibecken und kehrt danach zu Feldmarschall Lichtmark in der Zuflucht zurück."}},
		[8115] = {"Kontrolliert fünf Stützpunkte",nil, {"Kontrolliert gleichzeitig fünf Stützpunkte im Arathibecken und kehrt danach zu Feldmarschall Lichtmark in der Zuflucht zurück."}},
		[8116] = {"Vision von Voodress",nil, {"Ihr und Maywiki haben etwas gemeinsam. Wir sind beide Schamanen! Auch wenn Maywiki eher geheimnisvoll ist und Ihr vielmehr grob daher kommt. Trotzdem, wir wissen beide von der Macht der Geister. Ich gebe Euch diese Kugel als Geschenk, tragt sie um den Hals. Schaut tief in sie hinein und seht die Stadt von Voodress in den südlichen Meeren! Lasst sie Eure Geister beflügeln!"}},
		[8117] = {"Vision von Voodress",nil, {"Sha-mann! Maywiki macht Witze!nn<Maywiki lacht.>nnSo, Ihr habt also die Geister und Elemente gegen unsere Feinde in Zul'Gurub eingesetzt, hä? Die Geister seien gelobt!nnMaywiki wird Eure Kugel aufpolieren. Gebt sie mir, <Name>."}},
		[8118] = {"Vision von Voodress",nil, {"Gute Neuigkeiten, <Name>. Molthor hat Lob über Euch verkündet. Er erzählte, wie Ihr Euch in einen wahren Helden der Zandalar verwandelt habt. Er wies mich auch an, Eure Kugel noch etwas aufzupolieren! Gebt sie her, damit Maywiki die Vision noch weiter aufhellen kann."}},
		[8119] = {"Die unversehrte Vision von Voodress",nil, {"<Maywiki zeigt auf Euren Talisman.>nnSchaut, <Name>! Er ist zum Leben erwacht. Die Geister tanzen um die Kugel. Gebt ihn Maywiki, damit sie die letzte Anpassung vornehmen kann."}},
		[8120] = {"Die Schlacht um das Arathibecken!",nil, {"Erobert die Mine, das Sägewerk, die Schmiede, die Farm und die Ställe im Arathibecken und kehrt dann zu Todesmeister Dunkels in Hammerfall zurück."}},
		[8121] = {"Erobert vier Stützpunkte",nil, {"Haltet vier Stützpunkte zur selben Zeit im Arathibecken und kehrt danach zu Todesmeister Dunkels nach Hammerfall zurück."}},
		[8122] = {"Erobert fünf Stützpunkte",nil, {"Haltet fünf Stützpunkte zur selben Zeit im Arathibecken und kehrt danach zu Todesmeister Dunkels nach Hammerfall zurück."}},
		[8123] = {"Zerschlagt die Versorgungslinien Arathors",nil, {"Entscheidet die Schlacht um das Arathibecken für Euch, erbeutet eine Arathivorratskiste und bringt sie Todespirscher Mortis in Hammerfall."}},
		[8124] = {"Noch mehr Vorratskisten",nil, nil},
		[8141] = {"Zandalarischattentalisman",nil, {"Ihr macht rasch Fortschritte, <Klasse>. Vielleicht ist es an der Zeit Euch in Shera Ali'kh einzuweihen - zandalarianisch für die Hand des Todes. Tausende von Jahren übernahmen wir die Rolle der Meuchelmörder für das Imperium der Zandalar. Zu lange saßen wir untätig herum und erlaubten es den Hakkari an Macht zu gewinnen. Noch einmal stehen wir Hakkar gegenüber, aber diesmal ohne die Unterstützung einer vereinten Trollfront. Ihr und Eure Verbündeten müssen uns dabei helfen den Blutgott und seine Diener zu vernichten!nnTragt diesen Talisman und lasst ihn Euch bei Euren Taten unterstützen."}},
		[8142] = {"Zandalarischattentalisman",nil, {"Ich spüre, dass Ihr zahlreiche Todesopfer unter den Hakkari gefordert habt, <Name>. Ihr tragt den Geruch ihres Mojos. Ich vermute, dass Euch der Schattentalisman beim Ausmerzen behilflich war? Gebt ihn mir, ich werde seine Macht noch einmal verstärken."}},
		[8143] = {"Zandalarischattentalisman",nil, {"Beeindruckend, <Name>. Ihr habt ein Händchen für's Töten, so etwas ist mir schon seit Jahren nicht mehr untergekommen. Sogar Molthor hat von Euren Taten für Zandalar Notiz genommen. Lasst mich Euren Talisman weiter verstärken, es ist an der Zeit... gebt ihn mir."}},
		[8144] = {"Zandalarischattentalisman der Beherrschung",nil, {"Er sehnt sich nach Wachstum, <Name>. Der Talisman hat all das in sich aufgenommen, was Ihr vollbracht habt. Die unzähligen Trollkadaver, die Zul'Gurub beschmutzen, haben seine Macht angeheizt. Gebt ihn mir und ich werde ihn endgültig verstärken."}},
		[8145] = {"Die Mahlstromranke",nil, {"Unter den Zandalarianern wird der Stand des Jägers hoch verehrt. Die mächtigsten unserer <Klasse> reisen bis an den Rand des Maelstroms, um ihre Entschlossenheit unter Beweis zu stellen. Aus dem tiefsten Herzen der See, ernten sie eine seltene Seetangart, die Maelstromranke. Die Seetangstränge werden in einen Talisman gewoben, welcher anschließend verzaubert wird, um die Treffsicherheit und Macht des Trägers zu verstärken. Ich werde Euch jetzt die erste Maelstromranke in einen Talisman einweben. Wenn Euer Bund mit den Zandalar stärker wird, werde ich Euch mehr Ranken in den Talisman weben und so dessen Macht verstärken."}},
		[8146] = {"Die Mahlstromranke",nil, {"Ah, <Name>, es ist schön Euch mal wieder zu riechen. nnIhr müsst meinen Humor entschuldigen, zuweilen nimmt er abstruse Formen an. Ich fühle, dass Ihr unter unseren Feinden Angst und Schrecken verbreitet habt. Hakkars Gefolgsleute sprechen Euren Namen nur noch im Zorn aus. Hervorragend. Ihr habt es verdient, dass ich Euch eine weitere Ranke in Euren Talisman einwebe. Gebt ihn mir."}},
		[8147] = {"Die Mahlstromranke",nil, {"Selbst jetzt höre ich noch, wie Eure Geschosssalven unter Euren Feinden ein Blutbad anrichten. Ich vernehme das Echo der Todesqualen in ihren Schreien. Ich habt Euch wahrlich einen Namen unter den Trollen der Zandalar gemacht, <Name>. Wir sind Euch für Eure Taten sehr dankbar. Gebt mir Euren Talisman, damit ich eine weitere Ranke hineinweben kann."}},
		[8148] = {"Mahlstroms Zorn",nil, {"<Name>, Molthor brachte uns eine Botschaft von König Rastakhan. Der königliche Hof ist beeindruckt davon, wie sehr Ihr die Hakkaristreitkräfte dezimiert habt. Ich bin ermächtigt worden, die letzte Verstärkung in Euren Talisman einzuweben. Gebt ihn mir."}},
		[8149] = {"Die Ehrung eines Helden",nil, {"Reist zu Uthers Grabmal in den westlichen Pestländern und benutzt Uthers Tribut an seiner Statue. Kehrt zu Wagner Hammerschlag außerhalb von Eisenschmiede zurück, bevor das Erntedankfest endet."}},
		[8150] = {"Die Ehrung eines Helden",nil, {"Reist zu Groms Monument im Dämonensturz im Eschental und benutzt Groms Tribut am Fuße seines Monuments. Kehrt zu Javnir Nashak außerhalb von Orgrimmar zurück, bevor das Erntedankfest endet."}},
		[8151] = {"Der Talisman des Jägers",nil, {"Sprecht mit Ogtinc in Azshara."}},
		[8153] = {"Gehörnte Renner",nil, {"Bringt ein perfektes Rennergeweih zu Ogtinc nach Azshara. Er befindet sich auf den Klippen nordöstlich der Ruinen von Eldarath."}},
		[8154] = {"Die Ressourcen des Arathibeckens",nil, {"Gewinnt die Schlacht um das Arathibecken, raubt eine Arathivorratskiste und bringt sie Unteroffizier Maclear in der Zuflucht."}},
