		[8231] = {"Wellenjagd",nil, {"Bringt 6 Wellenhauerschuppen zu Ogtinc in Azshara. Ogtinc wohnt oberhalb des Kliffs, nordöstlich der Ruinen von Eldarath."}},
		[8232] = {"Der grüne Drache",nil, {"Bringt Morphaz' Zahn zu Ogtinc in Azshara. Ogtinc wohnt oberhalb des Kliffs, nordöstlich der Ruinen von Eldarath."}},
		[8233] = {"Ein simples Anliegen",nil, {"Sprecht mit Lord Jorach Rabenholdt im Rabenholdtanwesen."}},
		[8234] = {"Die versiegelte azurblaue Tasche",nil, {"Erlangt die versiegelte azurblaue Tasche von dem Schamanen der Holzschlundfeste in Azshara zurück. Bringt sie anschließend zum Erzmagier Xylem, der ebenfalls in Azshara zu finden ist."}},
		[8235] = {"Verschlüsselte Fragmente",nil, {"Bringt Erzmagier Xylem in Azshara 10 verschlüsselte Fragmente."}},
		[8236] = {"Der azurblaue Schlüssel",nil, {"Bringt den azurblauen Schlüssel zu Lord Jorach Rabenholdt."}},
		[8238] = {"Münzen der Gurubashi, Blutfratzen und Bleichborken",nil, {"Ich suche nach den Symbolen der Macht, bekannt als Münzen der Stämme. Sie sind die Währung unterschiedlichster Bewohner Zul'Gurubs und eine jede ist mit subtilem, aber mächtigem Mojo erfüllt. Es gibt neun bestimmte Arten, die es zu finden gilt. Manche werden von meinen Landsmänner für unterschiedliche Rüstungen begehrt, die sie zandalarianischen Helden anbieten. Solltet Ihr welche über haben, werde ich Euch eine unserer Ehrenmünzen im tausch gegen drei davon geben. Ihr könnt die Münzen bei Rin'wosho dem Händler verwenden; er bietet unseren Helden spezielle Gegenstände dafür an."}},
		[8239] = {"Münzen der Sandwüter, Schädelspalter und Blutskalpe",nil, {"Ich suche nach den Symbolen der Macht, bekannt als Münzen der Stämme. Sie sind die Währung unterschiedlichster Bewohner Zul'Gurubs und eine jede ist mit subtilem, aber mächtigem Mojo erfüllt. Es gibt neun bestimmte Arten, die es zu finden gilt. Manche werden von meinen Landsmänner für unterschiedliche Rüstungen begehrt, die sie zandalarianischen Helden anbieten. Solltet Ihr welche über haben, werde ich Euch eine unserer Ehrenmünzen im Tausch gegen drei davon geben. Ihr könnt die Münzen bei Rin'wosho dem Händler verwenden; er bietet unseren Helden spezielle Gegenstände dafür an."}},
		[8240] = {"Ein Schmuckstück für Zanza",nil, {"Zerstört jedes Schmuckstück der Hakkari, das Ihr in Zul'Gurub findet, am Altar von Zanza auf der Insel Yojamba. Sobald Ihr dies getan habt, sprecht ganz in der Nähe mit Vinchaxa."}},
		[8241] = {"Nachschub an feurigem Fluxus: Eisen",nil, {"Lasst uns nicht noch mehr meiner kostbaren Zeit mit Geplapper verschwenden, <Volk>. Wir müssen unseren schwindenden Vorrat an feurigen Fluxus wieder aufstocken. Ich brauche Euch, um mir folgendes zu beschaffen:nn*Incendosaurierschuppen. *Eisenbarren. *Kohle. Ich nehme alles was Ihr kriegen könnt!nnUnd seid besser schnell! Ihr wollt es Euch doch nicht mit der Bruderschaft verderben, oder?"}},
		[8242] = {"Nachschub an feurigem Fluxus: Schweres Leder",nil, {"Lasst uns nicht noch mehr meiner kostbaren Zeit mit Geplapper verschwenden, <Volk>. Wir müssen unseren schwindenden Vorrat an feurigen Fluxus wieder aufstocken. Ich brauche Euch, um mir folgendes zu beschaffen:nn*Incendosaurierschuppen. *Schweres Leder. *Kohle. Ich nehme alles was Ihr kriegen könnt!nnUnd seid besser schnell! Ihr wollt es Euch doch nicht mit der Bruderschaft verderben, oder?"}},
		[8243] = {"Zanzas wirksame Tränke",nil, {"Mit Eurem verbesserten Ruf innerhalb unseres Stammes, bekommt Ihr auch Zugang zu einigen unserer wirksamsten Tränke. Nehmt Euch in Acht... diese Gebräue sind stark im Mojo - gesegnet von Zanza und für Abenteurer jeglicher Herkunft gemacht!nnIch lasse Euch einen aus den drei Tränken, die ich anbiete, auswählen; im Gegenzug verlange ich eine zandalarianische Ehrenmünze. Seid Euch im Klaren, dass nur jeweils die Effekte eines Tranks zu einer beliebigen Zeit durch Euren Geist jagen können. Lasst mich wissen, wenn Ihr zum Tauschhandel bereit seid!"}},
		[8246] = {"Das Siegel der Zandalar",nil, {"Für jemanden, der bei den Zandalar so angesehen ist wie Ihr <Name>, habe ich etwas sehr Besonderes. Direkt aus unserer Heimat in den südlichen Meeren... die Siegel der Zandalar! Diese Siegel werden zum Aufwerten eines beliebigen Schultergegenstands, den Ihr besitzt, verwendet. Solltet Ihr nach Macht, Mojo oder Gelassenheit suchen - dann habe ich genau das, was Ihr braucht!nnIch gebe Euch ein Siegel Eurer Wahl im Austausch gegen fünfzehn zandalarianische Ehrenmünzen. Sobald Ihr die Münzen habt, können wir Geschäfte machen!"}},
		[8249] = {"Plunderkisten benötigt",nil, {"Meine Schurken müssen ihre Fertigkeiten im Schlossknacken noch verbessern und ich brauche dafür noch Schlösser mit entsprechendem Schwierigkeitsgrad -- das Zeug, das Zan für uns herstellt, taugt lediglich für totale Anfänger. Ich sag' Euch was, wenn Ihr mir 5 schwere Plunderkisten besorgt, dann gebe ich Euch dafür die feinsten Wurfmesser, die in Schurkenkreisen bekannt sind. Ihr könnt die Kisten öffnen und durchstöbern, aber lasst wenigstens ein paar Münzen übrig... auch ein <Klasse>, der sich in Ausbildung befindet, braucht etwas Motivation."}},
		[8250] = {"Geschick der Magier",nil, {"Sucht nach Sanath Lim-yo und verschafft Euch Zugang zu Erzmagier Xylem."}},
		[8251] = {"Magischer Staub",nil, {"Bringt Erzmagier Xylem in Azshara 10 glitzernde Staubproben."}},
		[8252] = {"Die Koralle der Sirenen",nil, {"Sammelt 6 verzauberte Korallen und bringt sie zum Erzmagier Xylem."}},
		[8253] = {"Vernichtet Morphaz",nil, {"Beschafft den arkanen Splitter von Morphaz' Leichnam und kehrt mit ihm zu Erzmagier Xylem zurück."}},
		[8254] = {"Cenarische Hilfe",nil, {"Sucht Ogtinc in Azshara auf."}},
		[8255] = {"Renner für einen höheren Zweck",nil, {"Beschafft 4 gesunde Rennerdrüsen und bringt sie zu Ogtinc in Azshara. Ihr findet ihn auf den Klippen, nordöstlich der Ruinen von Eldarath."}},
		[8256] = {"Sekret des Untodes",nil, {"Beschafft ein Sekret des Untodes für Ogtinc in Azshara. Ihr findet ihn auf den Klippen, nordöstlich der Ruinen von Eldarath."}},
		[8257] = {"Morphaz' Blut",nil, {"Tötet Morphaz im Versunkenen Tempel von Atal'Hakkar und bringt Greta Mooshuf im Teufelswald sein Blut. Der Eingang zum Versunkenen Tempel liegt in den Sümpfen des Elends."}},
		[8258] = {"Die Bedrohung durch Schattensichel",nil, {"Wendet das Orakel der Anrufung inmitten des Kellergewölbes des großen Ossuariums in Scholomance an. Ihr werdet damit Geister herbeirufen, die es zu bekämpfen gilt. Nach Vernichtung dieser Geister wird der Todesritter Schattensichel auftauchen; besiegt ihn. Bringt Schattensichels Kopf zu Sagorne Gratläufer im Tal der Weisheit in Orgrimmar."}},
		[8259] = {"Eine angemessenere Belohnung",nil, nil},
		[8260] = {"Basisüberlebenspaket von Arathor",nil, nil},
		[8261] = {"Standardüberlebenspaket von Arathor",nil, nil},
		[8262] = {"Verbessertes Überlebenspaket von Arathor",nil, {"Ihr habt zweifellos begonnen, Euch bei uns als würdig zu erweisen, <Name>. Bitte, nehmt dieses Überlebenspaket als Gegenleistung an! Ihr findet darin Rationen und Verbände, die nur innerhalb des Arathibecken verwendet werden können. Bewährt Ihr Euch noch weiter im Arathibecken, werdet Ihr auch mehr dieser Gegenstände erwerben können. Ihr erhaltet die Rationen, wenn unsere Einstellung Euch gegenüber freundlich ist, sowie Verbände, wenn Ihr unser Wohlwollen erlangt habt."}},
		[8263] = {"Basisüberlebenspaket der Entweihten",nil, nil},
		[8264] = {"Standardüberlebenspaket der Entweihten",nil, nil},
		[8265] = {"Verbessertes Überlebenspaket der Entweihten",nil, {"Ihr habt zweifellos begonnen, Euch bei uns als würdig zu erweisen, <Name>. Bitte, nehmt dieses Überlebenspaket als Gegenleistung an! Ihr findet darin Rationen und Verbände, die nur innerhalb des Arathibecken verwendet werden können. Bewährt Ihr Euch noch weiter im Arathibecken, werdet Ihr auch mehr dieser Gegenstände erwerben können. Ihr erhaltet die Rationen, wenn Ihr unser Wohlwollen erlangt habt, sowie Verbände, wenn Ihr Euch unseren Respekt verdient habt."}},
		[8266] = {"Aufopferungsabzeichen",nil, nil},
		[8267] = {"Aufopferungsabzeichen",nil, nil},
		[8268] = {"Aufopferungsabzeichen",nil, nil},
		[8269] = {"Aufopferungsabzeichen",nil, nil},
		[8271] = {"Held der Sturmlanzen",nil, {"Als ein Held der Stormpike, dürft Ihr Euch eines dieser erst kürzlich geplün… äh, wiederentdeckten Schätze, aussuchen."}},
		[8272] = {"Held der Frostwölfe",nil, {"Was ist das für ein Gefühl, <Name>? Wie fühlt es sich an, die Linien des Feindes zu zerschmettern und jeglichen Widerstand auszulöschen? Es ist ein gutes Gefühl, nicht wahr?nnFür Eure Heldentat habt Ihr eine große Belohnung verdient."}},
		[8273] = {"Orans Dankbarkeit",nil, {"Ach das ist ja interessant! Ihr sagt, Ihr habt diese Schrifttafel auf einer kleinen, unbewohnten Insel nahe der Aussichtsklippen im Hinterland gefunden, <Klasse>? Wirklich äußerst interessant. nnIch danke Euch für Eure Bemühungen in dieser Angelegenheit. Unsere dunkle Fürstin wird mit dieser Entdeckung sehr zufrieden sein. Erlaubt mir, Euch angemessen zu belohnen."}},
		[8275] = {"Die Rückeroberung von Silithus",nil, {"Sprecht mit Windruferin Prachthorn auf Burg Cenarius in Silithus."}},
		[8276] = {"Die Rückeroberung von Silithus",nil, {"Sprecht mit Windruferin Prachthorn auf Burg Cenarius in Silithus."}},
		[8277] = {"Tödliches Wüstengift",nil, {"Beetix Wankelhupf von der Burg Cenarius in Silithus möchte, dass Ihr 8 Stachel von Steinpanzerskorpiden und 8 Giftzähne von Sandhuschern für sie sammelt. Kehrt zu ihr zurück, sobald Ihr diese Aufgabe erfüllt habt."}},
		[8278] = {"Noggles letzte Hoffnung",nil, {"Beetix Wankelhupf von der Burg Cenarius in Silithus möchte, dass Ihr je 3 Stachel von Steinpanzerzangen und Steinpanzerschindern, sowie 3 Felspirschergiftzähne für sie sammelt."}},
		[8279] = {"Das Lexikon des Schattenhammerklans",nil, {"Bringt Ortell dem Einsiedler in Silithus die drei Kapitel des Lexikons des Schattenhammerklans."}},
		[8280] = {"Sicherung der Versorgungslinien",nil, {"Windruferin Prachthorn auf Burg Cenarius in Silithus möchte, dass Ihr 15 Baggerschläger tötet."}},
		[8281] = {"Verstärkte Sicherheit",nil, {"Windruferin Prachthorn auf Burg Cenarius in Silithus möchte, dass Ihr 20 Baggerbrecher tötet."}},
		[8282] = {"Noggles verlorener Ranzen",nil, {"Spürt Noggles Ranzen im südlichen Gebirge von Silithus auf und bringt ihn zu ihm, auf Burg Cenarius, zurück."}},
		[8283] = {"GESUCHT: Totenstachel, Terror der Wüste",nil, {"Bringt Totenstachels Zange zu Vish Kozus, Hauptmann der Wache."}},
		[8284] = {"Das Geheimnis des Schattenhammerklans",nil, {"Geologin Lerchenbann auf Burg Cenarius in Silithus möchte, dass Ihr ihr 8 Schrifttafelfragmente des Schattenhammerklans bringt."}},
		[8285] = {"Der Deserteur",nil, {"Bringt die wiederhergestellte Schrifttafel zu Ortell dem Einsiedler in Silithus."}},
		[8286] = {"Was uns morgen erwartet",nil, {"Reist zu den Höhlen der Zeit in Tanaris und findet Anachronos, Nachkomme Nozdormus."}},
		[8287] = {"Ein teuflischer Plan",nil, {"Bringt Kommandant Mar'alith die entschlüsselte Abschrift der Schrifttafel."}},
		[8288] = {"Nur einer kann sich erheben",nil, {"Bringt Brutwächter Dreschbringers Kopf zu Baristolth der Sandstürme in die Burg Cenarius in Silithus."}},
