		[8446] = {"Eingehüllt in Alpträume",nil, {"Sucht nach jemandem, der die Bedeutung des in Alpträume gehüllten Gegenstands entschlüsseln kann. Vielleicht kann Euch ein Druide von großer Macht weiterhelfen."}},
		[8447] = {"Legenden erwachen",nil, {"Wartet, bis Bewahrer Remulos bei der Mondlichtung den von Alpträumen eingehüllten Gegenstand gereinigt hat."}},
		[8460] = {"Verbündeter der Holzschlundfeste",nil, {"Grazle möchte, dass Ihr Euch beweist, indem Ihr 6 Krieger der Totenwaldfelle, 6 Pfadfinder der Totenwaldfelle und 6 Gärtner der Totenwaldfelle besiegt. Kehrt zu ihm in den Süden des Teufelswalds nahe des Smaragdrefugiums zurück, sobald Ihr dies getan habt."}},
		[8461] = {"Totenwaldfelle des Nordens",nil, {"Nafien möchte, dass Ihr 6 Höhlenbehüter der Totenwaldfelle, 6 Rächer der Totenwaldfelle und 6 Schamanen der Totenwaldfelle besiegt. Kehrt danach zu ihm in den Norden des Teufelswalds nahe des Eingangs der Holzschlundfeste zurück."}},
		[8462] = {"Sprecht mit Nafien",nil, {"Reist nach Norden entlang der Hauptstraße des Teufelswalds und sprecht mit einem Furbolg namens Nafien. Er hält außerhalb des Eingangs zur Holzschlundfeste Wache."}},
		[8463] = {"Instabile Manakristalle",nil, {"Bringt Aeldon Sonnenmal auf dem Falkenplatz 6 instabile Manakristalle."}},
		[8464] = {"Aktivität der Winterfelle",nil, {"Salfa möchte, dass Ihr 8 Schamanen der Winterfelle, 8 Höhlenbehüter der Winterfelle und 8 Ursa der Winterfelle erledigt. Salfa befindet sich genau außerhalb des Eingangs zur Holzschlundfeste in Winterquell."}},
		[8465] = {"Sprecht mit Salfa",nil, {"Durchquert die Holzschlundfeste und verlasst sie in Richtung Winterquell. Sprecht mit Salfa, die außerhalb des Eingangs zur Holzschlundfeste Wache steht."}},
		[8466] = {"Federn für Grazle",nil, {"Ihr habt Euch mir gegenüber mehr als bewiesen, doch um das Vertrauen meiner misstrauischen Brüder und Schwestern zu gewinnen, ist es noch ein weiter Weg. Keine Sorge, ich werde Euch dabei behilflich sein, auch diese letzte Hürde zu überwinden. Einige Furbolgs der Totenwaldfelle tragen einen auffälligen Kopfschmuck. Dieser wäre ein ausreichender Beweis, für Euren Kampf gegen unsere verderbten Geschwister. Bringt mir die Federn der von Euch erbeuteten Kopfschmucke. Für jeweils fünf Federn die Ihr bei mir abgebt, wird sich Euer Ruf unter den Einwohnern der Holzschlundfeste verbessern."}},
		[8467] = {"Federn für Nafien",nil, {"Ihr habt Euch mir gegenüber mehr als bewiesen, doch um das Vertrauen meiner misstrauischen Brüder und Schwestern zu gewinnen, ist es noch ein weiter Weg. Keine Sorge, ich werde Euch dabei behilflich sein, auch diese letzte Hürde zu überwinden. Einige Furbolgs der Totenwaldfelle tragen einen auffälligen Kopfschmuck. Dieser wäre ein ausreichender Beweis, für Euren Kampf gegen unsere verderbten Geschwister. Bringt mir die Federn der von Euch erbeuteten Kopfschmucke. Für jeweils fünf Federn die Ihr bei mir abgebt, wird sich Euer Ruf unter den Einwohnern der Holzschlundfeste verbessern."}},
		[8468] = {"Gesucht: Thaelis der Manadurstige",nil, {"Bringt Thaelis Kopf zu Unteroffizier Kan'ren beim Falkenplatz im Immersangwald."}},
		[8469] = {"Perlen für Salfa",nil, {"Ihr habt Euch mein Vertrauen hart erarbeitet, <Name>. Doch bis meine Brüder und Schwestern Euch genauso akzeptieren, wie ich es tue, ist es noch ein weiter Weg. Ihr könntet jedoch etwas für mich tun, um diesen Prozess zu beschleunigen. Die Furbolgs der Winterfelle sind dafür bekannt, dass sie zum Schutz vor dunklen Mächten Geisterperlen bei sich tragen. Aber auch diese mächtigen Artefakte vermochten es nicht, die Verderbnis aufzuhalten und so sind die Winterfelle dieser Macht erlegen. Bringt mir die Geisterperlen gefallener Furbolgs der Winterfelle. Für jeweils fünf Geisterperlen die Ihr bei mir abgebt, wird sich Euer Ruf unter den Einwohnern der Holzschlundfeste verbessern."}},
		[8470] = {"Ritualtotem der Totenwaldfelle",nil, {"Bringt das Ritualtotem in die Holzschlundfeste und findet heraus, ob einer der dortigen Furbolgs eine Verwendung für diesen Gegenstand hat. Die Furlbolgs der Holzschlundfeste werden solange nicht mit Euch sprechen, bis Euer Ruf bei ihnen neutral oder höher ist."}},
		[8471] = {"Ritualtotem der Winterfelle",nil, {"Bringt das Ritualtotem in die Holzschlundfeste und findet heraus, ob einer der dortigen Furbolgs eine Verwendung für diesen Gegenstand hat. Die Furlbolgs der Holzschlundfeste werden solange nicht mit Euch sprechen, bis Euer Ruf bei ihnen neutral oder höher ist."}},
		[8472] = {"Schwere Fehlfunktion",nil, {"Sammelt 6 Arkankerne von den Arkanpatrouillen, die in den Ruinen von Silbermond umherstreifen, und bringt sie Magister Jaronis beim Falkenplatz im Immersangwald."}},
		[8473] = {"Eine traurige Aufgabe",nil, {"Tötet 10 siechende Waldbehüter beim versengten Hain und meldet Euch anschließend bei Larianna Flusswind, die sich im Turm nordwestlich des versengten Hains im Immersangwald aufhält."}},
		[8474] = {"Weißborkes Anhänger",nil, {"Macht die Person ausfindig, die Weißborkes Anhänger angefertigt haben könnte. Ihr könntet Hinweise über den Gegenstand erfahren."}},
		[8475] = {"Die Todesschneise",nil, {"Tötet 8 Seuchenknochenplünderer und kehrt anschließend zu Waldläuferin Jaela in der Todesschneise im Immersangwald zurück."}},
		[8476] = {"Amaniübergriff",nil, {"Leutnant Dämmerflucht bei der Zuflucht der Weltenwanderer im Immersangwald möchte, dass Ihr 5 Berserker der Amani und 5 Axtwerfer der Amani tötet."}},
		[8477] = {"Der Hammer des Speermachers",nil, {"Sucht in Tor'Watha, südöstlich der Zuflucht der Weltenwanderer, nach Speermacher Otembe und bringt Otembes Hammer zu Arathel Sonnenschmied bei der Zuflucht der Weltenwanderer im Immersangwald."}},
		[8478] = {"Wählt Eure Waffe",nil, nil},
		[8479] = {"Zul'Marosh",nil, {"Ven'jashi, der gefangene Troll in Tor'Watha möchte, dass Ihr ihm Häuptling Zul'Maroshs Kopf bringt. Häuptling Zul'Marosh befindet sich in Zeb'Watha, jenseits des Elrendarsees."}},
		[8480] = {"Zurückgelassene Waffen",nil, {"Bringt 8 Waffen der Sin'dorei zu Velendris Weißflor im Immersangwald."}},
		[8481] = {"Die Wurzel alles Bösen",nil, {"Stellt die Fackel zur Dämonenbeschwörung im Eingang der Höhle des Oberhäuptlings der Winterfelle, im Lager der Winterfellfurbolgs auf. Besiegt den Dämon und erlangt die Essenz von Xandivious für Gorn Einauge in der Holzschlundfeste."}},
		[8482] = {"Belastende Dokumente",nil, {"Bringt die belastenden Dokumente zu Aeldon Sonnenmal auf dem Falkenplatz im Immersangwald."}},
		[8483] = {"Der zwergische Spion",nil, {"Bringt den Kopf von Ausgrabungsleiter Ambossel zu Aeldon Sonnenmal beim Falkenplatz im Immersangwald."}},
		[8484] = {"Das Bemühen um Frieden",nil, {"Bringt das Friedensangebot der Holzschlundfeste zu König Magni Bronzebart in Eisenschmiede."}},
		[8485] = {"Das Bemühen um Frieden",nil, {"Bringt das Friedensangebot der Holzschlundfeste zu Thrall in Orgrimmar."}},
		[8486] = {"Arkane Instabilität",nil, {"Leybewahrerin Velania möchte, dass Ihr 5 Managespenster und 5 Manapirscher tötet."}},
		[8487] = {"Besudelte Erde",nil, {"Lehrling Mirveda beim Sanktum des Ostens im Immersangwald möchte, dass Ihr 8 besudelte Erdproben einsammelt."}},
		[8488] = {"Ungewöhnliche Ergebnisse",nil, {"Beschützt Lehrling Mirveda vor dem Angriff der Geißel."}},
		[8489] = {"Ein intakter Wandler",nil, {"Bringt den intakten Arkanwandler zu Leybewahrerin Velania."}},
		[8490] = {"Wiederaufladen der Verteidigung",nil, {"Platziert den energieerfüllten Kristall beim Runenstein des Ostens und verteidigt ihn 1 Minute lang vor der Geißel. Bringt den energieerfüllten Kristall anschließend zu Runenwächter Deryan im Immersangwald, um Eure Belohnung zu erhalten."}},
		[8491] = {"Pelzjagd",nil, {"Velan Lichteiche in Morgenluft im Immersangwald möchte, dass Ihr ihm 6 Bachtatzenfelle bringt."}},
		[8492] = {"Die Allianz braucht Kupferbarren!",nil, {"Bringt 20 Kupferbarren zu Unteroffizier Steinbraue im Militärviertel in Eisenschmiede."}},
		[8493] = {"Die Allianz braucht mehr Kupferbarren!",nil, {"Bringt 20 Kupferbarren zu Unteroffizier Steinbraue beim Landeplatz in Dun Morogh."}},
		[8494] = {"Die Allianz braucht Eisenbarren!",nil, {"Bringt 20 Eisenbarren zu Korporal Schwarz im Militärviertel in Eisenschmiede."}},
		[8495] = {"Die Allianz braucht mehr Eisenbarren!",nil, {"Bringt 20 Eisenbarren zu Korporal Schwarz beim Landeplatz in Dun Morogh."}},
		[8496] = {"Verbände für den Feldeinsatz",nil, {"Bringt 30 schwere Runenstoffverbände, 30 schwere Seidenverbände und 30 schwere Magiestoffverbände zu Windruferin Prachthorn auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz X, um diese Aufgabe abschließen zu können."}},
		[8497] = {"Wüstenüberlebenspaket",nil, {"Bringt 4 Kugeln des Wassers, 4 mächtige Gegengifte und 4 geräucherte Wüstenknödel zu Calandrath in der Taverne von Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz I, um diese Aufgabe abschließen zu können."}},
		[8498] = {"Schlachtordnungen des Schattenhammerklans",nil, {"Beschafft Euch die Schlachtordnungen des Schattenhammerklans und bringt sie zu Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch den taktischen Einsatz X, um diese Aufgabe abschließen zu können."}},
		[8499] = {"Die Allianz braucht Thoriumbarren!",nil, {"Bringt 20 Thoriumbarren zu Dame Doppelzopf im Militärviertel in Eisenschmiede."}},
		[8500] = {"Die Allianz braucht mehr Thoriumbarren!",nil, {"Bringt 20 Thoriumbarren zu Dame Doppelzopf beim Landeplatz in Dun Morogh."}},
		[8501] = {"Ziel: Stecher des Ashischwarms",nil, {"Tötet 30 Stecher des Ashischwarms und erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz XII, um diese Aufgabe abschließen zu können."}},
		[8502] = {"Ziel: Arbeiter des Ashischwarms",nil, {"Tötet 30 Arbeiter des Ashischwarms und erstattet Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz III, um diese Aufgabe abschließen zu können."}},
		[8503] = {"Die Allianz braucht Würgetang!",nil, {"Bringt 20 Stück Würgetang zu dem Gefreiten Drechselknacks im Militärviertel in Eisenschmiede."}},
		[8504] = {"Die Allianz braucht mehr Würgetang!",nil, {"Bringt 20 Stück Würgetang zu dem Gefreiten Drechselknacks beim Landeplatz in Dun Morogh."}},
		[8505] = {"Die Allianz braucht lila Lotus!",nil, {"Bringt 20 lila Lotus zu Meister Nachtweise im Militärviertel in Eisenschmiede."}},
		[8506] = {"Die Allianz braucht mehr lila Lotus!",nil, {"Bringt 20 lila Lotus zu Meister Nachtweise beim Landeplatz in Dun Morogh."}},
		[8507] = {"Felddienst",nil, {"Erstattet am Posten der Eisenschmiedebrigade beim Zoraschwarmbau Bericht. Bereitet Eure nicht unterzeichneten Felddienstpapiere vor, lasst sie von Hauptmann Rußschmied unterzeichnen und kehrt zu Windrufer Kaldon auf Burg Cenarius in Silithus zurück. Hinweis: das Heilen oder Wirken von begünstigenden Zaubern auf ein Mitglied der Eisenschmiedebrigade wird Euch für den PvP-Kampf markieren."}},
		[8508] = {"Felddienstpapiere",nil, {"Ihr wollt diese Papiere also unterzeichnet bekommen, Junge?"}},
		[8509] = {"Die Allianz braucht Arthas' Tränen!",nil, {"Bringt 20 Arthas' Tränen zu Stabsfeldwebel Germaine im Militärviertel in Eisenschmiede."}},
		[8510] = {"Die Allianz braucht mehr Arthas' Tränen!",nil, {"Bringt 20 Arthas' Tränen zu Stabsfeldwebel Germaine beim Landeplatz in Dun Morogh."}},
		[8511] = {"Die Allianz braucht leichtes Leder!",nil, {"Bringt 10 Einheiten leichtes Leder zu Bonnie Steinschinder im Militärviertel in Eisenschmiede."}},
		[8512] = {"Die Allianz braucht mehr leichtes Leder!",nil, {"Bringt 10 Einheiten leichtes Leder zu Bonnie Steinschinder beim Landeplatz in Dun Morogh."}},
		[8513] = {"Die Allianz braucht mittleres Leder!",nil, {"Bringt 10 Einheiten mittleres Leder zu dem Gefreiten Porter im Militärviertel in Eisenschmiede."}},
		[8514] = {"Die Allianz braucht mehr mittleres Leder!",nil, {"Bringt 10 Einheiten mittleres Leder zu dem Gefreiten Porter beim Landeplatz in Dun Morogh."}},
		[8515] = {"Die Allianz braucht dickes Leder!",nil, {"Bringt 10 Einheiten dickes Leder zu Marta Feinspindel im Militärviertel in Eisenschmiede."}},
		[8516] = {"Die Allianz braucht mehr dickes Leder!",nil, {"Bringt 10 Einheiten dickes Leder zu Marta Feinspindel beim Landeplatz in Dun Morogh."}},
		[8517] = {"Die Allianz braucht Leinenverbände!",nil, {"Bringt 20 Leinenverbände zu Schildwache Silberwolke im Militärviertel in Eisenschmiede."}},
		[8518] = {"Die Allianz braucht mehr Leinenverbände!",nil, {"Bringt 20 Leinenverbände zu Schildwache Silberwolke beim Landeplatz in Dun Morogh."}},
		[8519] = {"Eine Spielfigur auf dem Schachbrett der Ewigkeit",nil, {"Lernt so viel Ihr könnt über die Vergangenheit, sprecht dann mit Anachronos in den Höhlen der Zeit in Tanaris."}},
		[8520] = {"Die Allianz braucht Seidenverbände!",nil, {"Bringt 20 Seidenverbände zu Schwester Steinfeld im Militärviertel in Eisenschmiede."}},
		[8521] = {"Die Allianz braucht mehr Seidenverbände!",nil, {"Bringt 20 Seidenverbände zu Schwester Steinfeld beim Landeplatz in Dun Morogh."}},
		[8522] = {"Die Allianz braucht Runenstoffverbände!",nil, {"Bringt 20 Runenstoffverbände zu Bewahrer Mondschatten im Militärviertel in Eisenschmiede."}},
		[8523] = {"Die Allianz braucht mehr Runenstoffverbände!",nil, {"Bringt 20 Runenstoffverbände zu Bewahrer Mondschatten beim Landeplatz in Dun Morogh."}},
		[8524] = {"Die Allianz braucht Regenbogenflossenthunfisch!",nil, {"Bringt 20 Regenbogenflossenthunfische zu Gastronom Glitschko im Militärviertel in Eisenschmiede."}},
		[8525] = {"Die Allianz braucht mehr Regenbogenflossenthunfisch!",nil, {"Bringt 20 Regenbogenflossenthunfische zu Gastronom Glitschko beim Landeplatz in Dun Morogh."}},
		[8526] = {"Die Allianz braucht geröstete Raptoren!",nil, {"Bringt 20 geröstete Raptoren zu Sarah Sorgenpfeifer im Militärviertel in Eisenschmiede."}},
		[8527] = {"Die Allianz braucht mehr geröstete Raptoren!",nil, {"Bringt 20 geröstete Raptoren zu Sarah Sorgenpfeifer beim Landeplatz in Dun Morogh."}},
		[8528] = {"Die Allianz braucht Tüpfelgelbschwänze!",nil, {"Bringt 20 Tüpfelgelbschwänze zu Jägerin Flinkbach im Militärviertel in Eisenschmiede."}},
		[8529] = {"Die Allianz braucht mehr Tüpfelgelbschwänze!",nil, {"Bringt 20 Tüpfelgelbschwänze zu Jägerin Flinkbach beim Landeplatz in Dun Morogh."}},
		[8530] = {"Die Allianz braucht sengende Kernsteine!",nil, {"Bringt 50 sengende Kernsteine zu Kommandant Hammerkraft beim Landeplatz in Dun Morogh."}},
		[8532] = {"Die Horde braucht Kupferbarren!",nil, {"Bringt 20 Kupferbarren zu Minenarbeiter Kromwell im Tal der Geister in Orgrimmar."}},
		[8533] = {"Die Horde braucht mehr Kupferbarren!",nil, {"Bringt 20 Kupferbarren zu Minenarbeiter Kromwell in Durotar."}},
		[8534] = {"Späherbericht des Zoraschwarms",nil, {"Setzt Euch mit der cenarischen Späherin Azenel innerhalb des Zoraschwarmbaus in Verbindung und bringt den Späherbericht des Zoraschwarms zu Windruferin Prachthorn auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz VI, um diese Aufgabe abschließen zu können."}},
		[8535] = {"Weißgrauer Templer",nil, {"Beschwört und vernichtet einen weißgrauen Templer. Erstattet anschließend Bericht bei Bor Wildmähne auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz IV, um diese Aufgabe abschließen zu können."}},
		[8536] = {"Irdener Templer",nil, {"Beschwört und vernichtet einen irdenen Templer. Erstattet anschließend Bericht bei Bor Wildmähne auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz III, um diese Aufgabe abschließen zu können."}},
		[8537] = {"Purpurroter Templer",nil, {"Beschwört und vernichtet einen purpurroten Templer. Erstattet anschließend Bericht bei Bor Wildmähne auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz II, um diese Aufgabe abschließen zu können."}},
		[8538] = {"Die vier Fürsten",nil, {"Findet einen Weg, um den Fürst der Asche, den Fürst der Tiefen, den Fürst der Stürme und den Fürst der Splitter zu beschwören und zu vernichten. Erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz V, um diese Aufgabe abschließen zu können."}},
		[8539] = {"Ziel: Schwestern des Zoraschwarms",nil, {"Tötet 30 Schwestern des Zoraschwarms und erstattet Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz V, um diese Aufgabe abschließen zu können."}},
		[8540] = {"Stiefel für die Wache",nil, {"Bringt 3 Paar verschnörkelte Mithrilstiefel zu Vish Kozus, Hauptmann der Wache von Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz II, um diese Aufgabe abschließen zu können."}},
		[8541] = {"Schleifsteine für die Wache",nil, {"Bringt 10 verdichtete Schleifsteine, 10 robuste Schleifsteine und 10 schwere Schleifsteine zu Vish Kozus, Hauptmann der Wache von Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz III, um diese Aufgabe abschließen zu können."}},
		[8542] = {"Die Horde braucht Zinnbarren!",nil, {"Bringt 20 Zinnbarren zu Grunzer Maug im Tal der Geister in Orgrimmar."}},
		[8543] = {"Die Horde braucht mehr Zinnbarren!",nil, {"Bringt 20 Zinnbarren zu Grunzer Maug in Durotar."}},
		[8544] = {"Schiftung des Eroberers",nil, {"Bringt die Befehlsbindungen der Qiraji, 2 Götzen der Nacht, 5 Steinskarabäen und 5 Tonskarabäen zu Andorgos in Ahn'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen neutralen Ruf bei der Brut Nozdormus."}},
		[8545] = {"Die Horde braucht Mithrilbarren!",nil, {"Bringt 20 Mithrilbarren zu Schlachtrufer T'kelah im Tal der Geister in Orgrimmar."}},
		[8546] = {"Die Horde braucht mehr Mithrilbarren!",nil, nil},
		[8547] = {"Willkommen!",nil, {"Bringt den Geschenkgutschein des Falkenplatzes zu Marsilla Morgenstern auf dem Falkenplatz."}},
		[8548] = {"Schlachtrüstung des Kriegsfreiwilligen",nil, {"Bringt 5 cenarische Gefechtsabzeichen, 3 cenarische Versorgungsabzeichen und 7 cenarische Strategieabzeichen zu Vargus auf Burg Cenarius in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf beim Zirkel des Cenarius."}},
		[8549] = {"Die Horde braucht Friedensblumen!",nil, {"Bringt 20 Friedensblumen zu der Kräuterkundigen Stolzfeder im Tal der Geister in Orgrimmar."}},
		[8550] = {"Die Horde braucht mehr Friedensblumen!",nil, {"Hallo <Name>. Ich bin froh, dass Ihr noch einmal wegen unserer niemals enden wollenden Sammelaktion für Friedensblumen nachfragt. Die Kriegsvorbereitungen gehen weiter und damit auch die Herstellung von Tränken und Elixiren, die in den kommenden Kämpfen helfen sollen. Werdet Ihr uns wieder unterstützen, <Klasse>? Ich benötige noch einmal genauso viele Friedensblumen wie zuvor. Wenn Ihr also ein weiteres Mal diese Pflanzen sammeln und anschließend zu mir zurückbringen könntet, wäre ich Euch äußerst dankbar. Solltet Ihr bereits welche bei Euch haben, umso besser."}},
		[8551] = {"Die Truhe des Kapitäns",nil, {"Bringt Smotts' Truhe zu Hecklebury Smotts nach Beutebucht."}},
		[8552] = {"Die Schärpe mit Monogramm",nil, {"Bringt die Schärpe mit Monogramm zu ihrem Besitzer zurück."}},
		[8553] = {"Der Entersäbel des Kapitäns",nil, {"Sprecht mit Knirpsens."}},
		[8554] = {"Negolash entgegentreten",nil, {"Bringt 10 gegrillte Bussardflügel und 5 Flaschen Dschungelrankenwein zu Käpt'n Smotts Rettungsboot. Tötet Negolash und bringt Smotts' Entersäbel zu Kapitän Smotts ins Schlingendorntal."}},
		[8555] = {"Der Bund der Drachenschwärme",nil, {"Eranikus, Vaelastrasz und Azuregos... zweifellos kennt Ihr diese Drachen, Sterblicher. Es ist daher auch kein Zufall, dass sie eine so wichtige Rolle als Behüter unserer Welt gespielt haben. Unglücklicherweise (und zum Teil ist auch meine eigene Naivität daran schuld) fiel jeder Wächter, sei es durch Beauftragte der alten Götter oder Verrat derer, die sie ihre Freunde nannten, der Tragödie zum Opfer. Das Ausmaß dessen, schürte mein Misstrauen zu Euereins noch mehr an. Sucht nach ihnen... und <Volk>, bereitet Euch auf das Schlimmste vor."}},
		[8556] = {"Siegel der unnachgiebigen Stärke",nil, {"Bringt 1 Gebieterring der Qiraji, 2 züngelnde Götzen, 5 Bronzeskarabäen und 5 Elfenbeinskarabäen zu Windruferin Yessendra in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen wohlwollenden Ruf beim Zirkel des Cenarius."}},
		[8557] = {"Tuch der unnachgiebigen Stärke",nil, {"Bringt 1 Kampftuch der Qiraji, 2 Onyxgötzen, 5 Sillberskarabäen und 5 Knochenskarabäen zu Keyl Flinkklaue in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen respektvollen Ruf beim Zirkel des Cenarius."}},
		[8558] = {"Sichel der unnachgiebigen Stärke",nil, {"Bringt 1 Stachelgriff der Qiraji, 2 Alabastergötzen, 5 Kristallskarabäen und 5 Steinskarabäen zu Aufseher Haro in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen ehrfürchtigen Ruf beim Zirkel des Cenarius."}},
		[8559] = {"Schienbeinschützer des Eroberers",nil, {"Bringt die Befehlsbindungen der Qiraji, 2 Götzen des Krieges, 5 Elfenbeinskarabäen und 5 Goldskarabäen zu Kandrostrasz in Ahn'Qiraj. Diese Aufgabe erfordert auch einen neutralen Ruf bei der Brut Nozdormus."}},
		[8560] = {"Beinschützer des Eroberers",nil, {"Bringt Ouros intakte Haut, 2 Götzen des Todes, 5 Bronzeskarabäen und 5 Elfenbeinskarabäen zu Kandrostrasz innerhalb von Ahn'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf bei der Brut Nozdormus."}},
		[8561] = {"Krone des Eroberers",nil, {"Bringt Vek'nilashs Reif, 2 Götzen der Sonne, 5 Steinskarabäen und 5 Kristallskarabäen zu Andorgos in Ahn'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf bei der Brut Nozdormus."}},
		[8562] = {"Brustplatte des Eroberers",nil, {"Bringt den Knochenpanzer des alten Gottes, 2 Götzen des Krieges, 5 Silberskarabäen und 5 Knochenskarabäen zu Vethsera innerhalb von Ahn'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen wohlwollenden Ruf bei der Brut Nozdormus."}},
		[8563] = {"Ausbildung zum Hexenmeister",nil, {"Sprecht mit Beschwörer Teli'Larien - dem Hexenmeisterlehrer - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer."}},
		[8564] = {"Ausbildung zum Priester",nil, {"Sprecht mit Matrone Arena - der Priesterlehrerin - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer."}},
		[8565] = {"Vergangene Siege im Arathibecken",nil, nil},
		[8566] = {"Vergangene Siege im Arathibecken",nil, nil},
		[8567] = {"Vergangene Siege in der Kriegshymnenschlucht",nil, nil},
		[8568] = {"Vergangene Siege in der Kriegshymnenschlucht",nil, nil},
		[8569] = {"Vergangene Bemühungen in der Kriegshymnenschlucht",nil, nil},
		[8570] = {"Vergangene Bemühungen in der Kriegshymnenschlucht",nil, nil},
		[8572] = {"Schlachtrüstung des Veteranen",nil, {"Bringt 7 cenarische Gefechtsabzeichen, 4 cenarische Versorgungsabzeichen und 4 cenarische Strategieabzeichen zu Vargus auf Burg Cenarius in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen wohlwollenden Ruf beim Zirkel des Cenarius."}},
		[8573] = {"Schlachtrüstung des Feldherrn",nil, {"Bringt 15 cenarische Gefechtsabzeichen, 20 cenarische Versorgungsabzeichen, 20 cenarische Strategieabzeichen und 1 Mal des Cenarius zu Vargus auf Burg Cenarius in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen ehrfürchtigen Ruf beim Zirkel des Cenarius."}},
		[8574] = {"Schlachtrüstung des Gefolgsmanns",nil, {"Bringt 15 cenarische Gefechtsabzeichen, 20 cenarische Versorgungsabzeichen, 17 cenarische Strategieabzeichen und 1 Mal von Remulos zu Vargus auf Burg Cenarius in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen respektvollen Ruf beim Zirkel des Cenarius."}},
		[8575] = {"Azuregos' magisches Buch",nil, {"Bringt Azuregos' magisches Buch zu Narain Pfauentraum in Tanaris."}},
		[8576] = {"Übersetzung des Buchs",nil, {"Eins nach dem anderen, <Name>! Wir müssen herausfinden was Azuregos in dieses Buch geschrieben hat. Er hat Euch also gesagt, dass Ihr eine Arkanitboje herstellen sollt und dass dies hier der Bauplan dafür ist? Komisch, dass er das in Drachisch schreiben würde. Der alte Fuchs weiß doch, dass ich diesen Quatsch nicht lesen kann. Wenn das klappen soll, dann werde ich meine Wahrsagebrille, ein 500 Pfund-Hühnchen und Band 2 von \"Drachisch für Dummies\" benötigen. Nicht unbedingt in dieser Reihenfolge."}},
		[8577] = {"Stewvul, Ex-B.F.F.I.",nil, {"Narain Pfauentraum möchte, dass Ihr seinen Ex-'Besten Freund für immer' (B.F.F.I.) Stewvul ausfindig macht. Nehmt ihm die gestohlene Wahrsagerbrille ab und bringt sie Narain zurück."}},
		[8578] = {"Wahrsagerbrille? Kein Problem!",nil, {"Findet Narains Wahrsagerbrille und bringt sie Narain Pfauentraum in Tanaris."}},
		[8579] = {"Sterbliche Helden",nil, {"Wir spüren die Anwesenheit unserer versklavten Brüder tief im Innern von Ahn'Qiraj; dennoch wäre selbst eine Armee von Drachen nicht genug, um sie zu befreien, <Volk>. Der gleiche Zauber, der sie gefangen hält, würde uns der Gewalt C'Thuns unterwerfen, sobald wir ihm näher kämen. Der alte Gott hat unser Kommen vorhergesehen. All unsere Hoffnung ist dahin. Was könnte ein bloßer Sterblicher wie Ihr daran wohl ändern?"}},
		[8580] = {"Die Horde braucht Feuerblüten!",nil, {"Bringt 20 Feuerblüten zu Fledermausreiter Pele'keiki im Tal der Geister in Orgrimmar."}},
		[8581] = {"Die Horde braucht mehr Feuerblüten!",nil, nil},
		[8582] = {"Die Horde braucht lila Lotus!",nil, {"Bringt 20 lila Lotus zu Apothekerin Jezel im Tal der Geister in Orgrimmar."}},
		[8583] = {"Die Horde braucht mehr lila Lotus!",nil, {"Wie es aussieht, brauchen wir noch mehr lila Lotus, <Klasse>. Während meine eigenen Studien erst einen neuen nützlichen Verwendungszweck für diese Pflanze nachweisen müssen, gibt es bereits bekannte und bewährte Methoden, die angewendet werden können. Ihr müsst noch einmal für mich losziehen und mindestens zwanzig lila Lotus sammeln. Bringt sie dann hierher zu mir."}},
		[8584] = {"Fragt mich nie nach meinen Angelegenheiten",nil, {"Narain Pfauentraum in Tanaris möchte, dass Ihr mit Dirge Schnetzelhack in Gadgetzan sprecht."}},
		[8585] = {"Die Insel des Schreckens!",nil, {"Dirge Schnetzelhack in Tanaris möchte, dass Ihr Lakmaerans Kadaver, sowie 20 Chimaeroklenden beschafft."}},
		[8586] = {"Dirges abgefahrene Chimaerokkoteletts",nil, {"Dirge Schnetzelhack in Gadgetzan möchte, dass Ihr ihm 20 Einheiten Goblinraketentreibstoff und 20 Einheiten Tiefsteinsalz bringt."}},
		[8587] = {"Rückkehr zu Narain",nil, {"Bringt das 500 Pfund-Hühnchen zu Narain Pfauentraum in Tanaris."}},
		[8588] = {"Die Horde braucht schweres Leder!",nil, {"Bringt 10 Einheiten schweres Leder zu Kürschner Jamani im Tal der Geister in Orgrimmar."}},
		[8589] = {"Die Horde braucht mehr schweres Leder!",nil, {"Es ist wahr, <Klasse>, ich benötige noch mehr schweres Leder. Die Nachfrage von den Generalen und ihren Rüstmeistern scheint endlos zu sein - und das ist noch gar nichts im Vergleich zu dem, was die Zeppelinmeister verlangen!nnDie Sammelaktion muss jetzt doppelt so schnell vorangehen. <Name>, bringt mir, so bald Ihr könnt, noch mehr Stapel schweren Leders!"}},
		[8590] = {"Die Horde braucht dickes Leder!",nil, {"Bringt 10 Einheiten dickes Leder zu Unteroffizier Umala im Tal der Geister in Orgrimmar."}},
		[8591] = {"Die Horde braucht mehr dickes Leder!",nil, {"<Klasse>, wie Ihr sehen könnt, brauche ich noch mehr dickes Leder. Noch einmal möchte ich Euch um Eure Hilfe bitten. Ich verspreche Euch, dass Ihr bei Erfüllung Eurer Aufgabe große Anerkennung finden werdet. Die Zeit ist ausschlaggebend! Kehrt mit dem dicken Leder zu mir zurück, damit wir unsere Vorbereitungen abschließen und in den Krieg ziehen können, Held!"}},
		[8592] = {"Tiara des Orakels",nil, {"Bringt Vek'nilashs Reif, 2 Götzen der Weisen, 5 Silberskarabäen und 5 Knochenskarabäen zu Andorgos in Ahn'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf bei der Brut Nozdormus."}},
		[8593] = {"Beinkleider des Orakels",nil, {"Bringt Ouros intakte Haut, 2 Götzen der Wiedergeburt, 5 Goldskarabäen und 5 Tonskarabäen zu Kandrostrasz innerhalb von Ahn'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf bei der Brut Nozdormus."}},
		[8594] = {"Mantel des Orakels",nil, {"Bringt die Befehlsbindungen der Qiraji, 2 Götzen der Wiedergeburt, 5 Silberskarabäen und 5 Elfenbeinskarabäen zu Andorgos in Ahn'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen neutralen Ruf bei der Brut Nozdormus."}},
		[8595] = {"Sterbliche Helden",nil, {"Einen Qirajilord zu töten ist eine bemerkenswerte Tat für einen Sterblichen, <Name>. Allerdings sind sie zahlreich und mächtig. Bezwingt sie auch weiterhin und erweist Euch als unser Held."}},
		[8596] = {"Fußlappen des Orakels",nil, {"Bringt die Befehlsbindungen der Qiraji, 2 Götzen des Todes, 5 Bronzeskarabäen und 5 Goldskarabäen zu Kandrostrasz in Ahn'Qiraj. Diese Aufgabe erfordert auch einen neutralen Ruf bei der Brut Nozdormus."}},
		[8597] = {"Drachisch für Dummies",nil, {"Findet Narain Pfauentraums Buch, das auf einer Insel der südlichen Meere vergraben ist."}},
		[8598] = {"lÖsEGeLd",nil, {"Bringt die Lösegeldforderung zu Narain Pfauentraum in Tanaris."}},
		[8599] = {"Liebesbrief für Narain",nil, {"Bringt Meridiths Liebesbrief zu Narain Pfauentraum in Tanaris."}},
		[8600] = {"Die Horde braucht unverwüstliches Leder!",nil, {"Bringt 10 Einheiten unverwüstliches Leder zu Doktor Serratus im Tal der Geister in Orgrimmar."}},
		[8601] = {"Die Horde braucht mehr unverwüstliches Leder!",nil, {"Die Vorbereitungen für den bevorstehenden Krieg in Ahn'Qiraj machen Fortschritte, <Klasse>. Um sicher zu gehen, dass wir nicht hinterherhängen oder dass es dann zumindest nicht an mir liegt, möchte ich Euch um eine weitere Ladung unverwüstliches Leder bitten. Ein schnelles Handeln Eurerseits wird hoch geschätzt werden, <Name>."}},
		[8602] = {"Schulterstücke des Sturmrufers",nil, {"Bringt die Dominanzbindungen der Qiraji, 2 Götzen des Lebens, 5 Goldskarabäen und 5 Kristallskarabäen zu Andorgos in Ahn'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen neutralen Ruf bei der Brut Nozdormus."}},
		[8603] = {"Gewänder des Orakels",nil, {"Bringt die Hülle des alten Gottes, 2 Götzen des Todes, 5 Steinskarabäen und 5 Kristallskarabäen zu Vethsera innerhalb von Ahn'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen wohlwollenden Ruf bei der Brut Nozdormus."}},
		[8604] = {"Die Horde braucht Wollverbände!",nil, {"Bringt 20 Wollverbände zu Heiler Fernläufer im Tal der Geister in Orgrimmar."}},
		[8605] = {"Die Horde braucht mehr Wollverbände!",nil, {"Schön Euch wiederzusehen, <Klasse>. Wir arbeiten hier noch immer an den Kriegsvorbereitungen und ich kann auch weiterhin all die Hilfe gebrauchen, die Ihr mir hinsichtlich der Sammlung von Wollverbänden geben könnt. Jegliche Unterstützung wird hoch geschätzt werden. Ich hoffe nur, dass unsere Bemühungen ausreichen. Ich habe schon zu viele ehrenhafte Seelen auf den Schlachtfeldern verloren und werde sehen, was ich tun kann, um so etwas noch einmal zu verhindern."}},
		[8606] = {"Lockvogel!",nil, {"Narain Pfauentraum in Tanaris möchte, dass Ihr nach Winterquell reist und die Tasche voller Gold, so wie es die Buchnapper angegeben haben, am Übergabepunkt platziert."}},
		[8607] = {"Die Horde braucht Magiestoffverbände!",nil, {"Bringt 20 Magiestoffverbände zu Lady Kahler im Tal der Geister in Orgrimmar."}},
		[8608] = {"Die Horde braucht mehr Magiestoffverbände!",nil, {"Ihr seid wieder da? Interessant. Wie Ihr sehen könnt, sammle ich noch immer für die Kriegsvorbereitungen von Ahn'Qiraj. Das bedeutet, dass ich Euch noch einmal brauchen werde, um einen weiteren Haufen Magiestoffverbände für mich zu beschaffen. Ich bin schon auf Eure Rückkehr gespannt, <Klasse>."}},
		[8609] = {"Die Horde braucht Runenstoffverbände!",nil, {"Bringt 20 Runenstoffverbände zu Steinwache Lehmhuf im Tal der Geister in Orgrimmar."}},
		[8610] = {"Die Horde braucht mehr Runenstoffverbände!",nil, {"Der Moment, in dem wir uns in die Wogen des Krieges von Ahn'Qiraj stürzen werden, rückt immer näher, <Klasse>. Jedoch sollten wir unsere Kräfte vorerst dazu nutzen, um uns vorzubereiten, damit wir den Angriff auf die Insekten und ihre Meister überleben können. Da Ihr wieder hier seid, bedeutet das bestimmt, dass Ihr uns mit einer weiteren Ladung an Runenstoffverbänden aushelfen wollt. Bitte macht das so bald wie möglich und kehrt dann noch einmal zu mir zurück."}},
		[8611] = {"Die Horde braucht magere Wolfsteaks!",nil, {"Bringt 20 magere Wolfsteaks zu Blutwache Rawtar im Tal der Geister in Orgrimmar."}},
		[8612] = {"Die Horde braucht mehr magere Wolfsteaks!",nil, nil},
		[8613] = {"Die Horde braucht Tüpfelgelbschwänze!",nil, {"Bringt 20 Tüpfelgelbschwänze zu Angler Lin'do im Tal der Geister in Orgrimmar."}},
		[8614] = {"Die Horde braucht mehr Tüpfelgelbschwänze!",nil, {"Ja, in der Tat, wir brauchen hier noch mehr Fisch. Unsere Kämpfer werden in Ahn'Qiraj eine Menge an Essen brauchen. Ich benötige daher alle Tüpfelgelbschwänze, die ich bekommen kann. Fangt sie, kocht sie und bringt sie dann zu mir. Ich werde Euch dafür in meinem und im Namen der Horde danken."}},
		[8615] = {"Die Horde braucht gebackenen Lachs!",nil, {"Bringt 20 Stück gebackenen Lachs zu Häuptling Scharfklaue im Tal der Geister in Orgrimmar."}},
		[8616] = {"Die Horde braucht mehr gebackenen Lachs!",nil, nil},
		[8618] = {"Die Horde braucht mehr sengende Kernsteine!",nil, nil},
		[8619] = {"Urahne Schwermut",nil, {"Es ist gut zu wissen, dass die Bewohner dieser Welt noch immer die alten Völker verehren und sie nicht vergessen haben. Ich erbiete Euch meinen Respekt, <Name>. Nehmt dies als Zeichen meiner Dankbarkeit."}},
		[8620] = {"Der einzige Weg",nil, {"Findet die 8 verlorenen Kapitel von Drachisch für Dummies und vereint sie mit dem magischen Bucheinband. Bringt das vollständige Buch \"Drachisch für Dummies: Band 2\" zu Narain Pfauentraum in Tanaris."}},
