		[8706] = {"Hammer der aufziehenden Stürme",nil, {"Bringt 1 Stachelgriff der Qiraji, 2 Bernsteingötzen, 5 Elfenbeinskarabäen und 5 Bronzeskarabäen zu Aufseher Haro in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen ehrfürchtigen Ruf beim Zirkel des Cenarius."}},
		[8707] = {"Klinge der behüteten Geheimnisse",nil, {"Bringt 1 verschnörkelten Griff der Qiraji, 2 Obsidiangötzen, 5 Silberskarabäen und 5 Knochenskarabäen zu Aufseher Haro in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen ehrfürchtigen Ruf beim Zirkel des Cenarius."}},
		[8708] = {"Streitkolben des endlosen Lebens",nil, {"Bringt 1 verschnörkelten Griff der Qiraji, 2 Jaspisgötzen, 5 Kristallskarabäen und 5 Steinskarabäen zu Aufseher Haro in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen ehrfürchtigen Ruf beim Zirkel des Cenarius."}},
		[8709] = {"Dolch der Schattenschleier",nil, {"Bringt 1 Stachelgriff der Qiraji, 2 Zinnobergötzen, 5 Goldskarabäen und 5 Tonskarabäen zu Aufseher Haro in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen ehrfürchtigen Ruf beim Zirkel des Cenarius."}},
		[8710] = {"Kris der ungesagten Namen",nil, {"Bringt 1 verschnörkelten Griff der Qiraji, 2 Onyxgötzen, 5 Goldskarabäen und 5 Tonskarabäen zu Aufseher Haro in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen ehrfürchtigen Ruf beim Zirkel des Cenarius."}},
		[8711] = {"Klinge der ewigen Gerechtigkeit",nil, {"Bringt 1 Stachelgriff der Qiraji, 2 Bernsteingötzen, 5 Bronzeskarabäen und 5 Elfenbeinskarabäen zu Aufseher Haro in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen ehrfürchtigen Ruf beim Zirkel des Cenarius."}},
		[8712] = {"Sense des unsichtbaren Pfads",nil, {"Bringt 1 Stachelgriff der Qiraji, 2 Azurgötzen, 5 Silberskarabäen und 5 Knochenskarabäen zu Aufseher Haro in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen ehrfürchtigen Ruf beim Zirkel des Cenarius."}},
		[8713] = {"Urahnin Sternensang",nil, {"Der Himmel, mit seinen unzähligen Sternen, bietet die Antworten auf alle irdenen Fragen, <Name>. Mag es sein, dass wenn der Weise und Glückliche zu ihnen aufblickt, er die tiefere Wahrheit findet?"}},
