		[8728] = {"Die gute und die schlechte Nachricht",nil, {"Narain Pfauentraum in Tanaris möchte, dass Ihr ihm 20 Arkanitbarren, 10 Elementiumerz, 10 Azerothische Diamanten und 10 blaue Saphire bringt."}},
		[8729] = {"Der Zorn von Neptulon",nil, {"Benutzt die Arkanitboje am wirbelnden Mahlstrom in der Bucht der Stürme in Azshara."}},
		[8730] = {"Nefarius' Verderbnis",nil, {"Tötet Nefarian und bringt den roten Szeptersplitter wieder in Euren Besitz. Bringt den roten Szeptersplitter zu Anachronos in den Höhlen der Zeit in Tanaris. Euch bleiben 5 Stunden, um diese Aufgabe zu erfüllen."}},
		[8731] = {"Felddienst",nil, {"Erstattet Krug Schädelspalt am Posten der Orgrimmarlegion beim Regalschwarmbau Bericht. Bereitet Eure nicht unterzeichneten Felddienstpapiere vor, lasst sie unterzeichnen und bringt sie dann zu Windrufer Kaldon auf Burg Cenarius. Hinweis: das Heilen oder Wirken von begünstigenden Zaubern auf ein Mitglied der Orgrimmarlegion wird Euch für den PvP-Kampf markieren."}},
		[8732] = {"Felddienstpapiere",nil, {"Felddienstpapiere? Was für ein Schwachsinn ist das, <Name>?"}},
		[8733] = {"Eranikus, der Tyrann des Traums",nil, {"Reist nach Teldrassil und sucht Malfurions Agenten irgendwo außerhalb der Stadtmauern von Darnassus auf."}},
		[8734] = {"Tyrande und Remulos",nil, {"Reist zur Mondlichtung und sprecht mit Bewahrer Remulos."}},
		[8735] = {"Die Verderbnis des Alptraums",nil, {"Reist zu den vier Portalen des smaragdgrünen Traums in Azeroth und sammelt dort jeweils ein Verderbnisfragment des Alptraums ein. Kehrt zu Bewahrer Remulos bei der Mondlichtung zurück, sobald Ihr diese Aufgabe erfüllt habt."}},
		[8736] = {"Der Alptraum offenbart sich",nil, {"Verteidigt Nachthafen gegen Eranikus. Bewahrer Remulos darf dabei nicht ums Leben kommen und Eranikus darf nicht getötet werden. Verteidigt Euch. Erwartet Tyrande."}},
		[8737] = {"Azurblauer Templer",nil, {"Beschwört und vernichtet einen azurblauen Templer. Erstattet anschließend Bericht bei Bor Wildmähne auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz I, um diese Aufgabe abschließen zu können."}},
		[8738] = {"Späherbericht des Regalschwarms",nil, {"Setzt Euch mit dem cenarischen Späher Landion innerhalb des Regalschwarmbaus in Verbindung und bringt den Späherbericht des Regalschwarms zu Windruferin Prachthorn auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz VII, um diese Aufgabe abschließen zu können."}},
		[8739] = {"Späherbericht des Ashischwarms",nil, {"Setzt Euch mit der cenarischen Späherin Jalia innerhalb des Ashischwarmbaus in Verbindung und bringt den Späherbericht des Ashischwarms zu Windruferin Prachthorn auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz VIII, um diese Aufgabe abschließen zu können."}},
		[8740] = {"Marodeure des Schattenhammers",nil, {"Tötet den Marodeur des Schattenhammers Morna und 2 Marodeure des Schattenhammers. Erstattet anschließend Bericht bei Windruferin Prachthorn. Ihr benötigt auch den taktischen Einsatz IX, um diese Aufgabe abschließen zu können."}},
		[8741] = {"Der Held kehrt zurück",nil, {"Bringt den grünen Szeptersplitter zu Anachronos in den Höhlen der Zeit in Tanaris."}},
		[8742] = {"Die Macht von Kalimdor",nil, {"Eintausend Jahre sind vergangen und wie es vorausgesagt wurde, steht die Eine nun vor mir. die Auserwählte, welche Ihr Volk in ein neues Zeitalter führen wird. Spürt Ihr es? Der alte Gott erzittert, <Name>! Oh ja, er fürchtet Eure Bestimmung. Zerschlagt die Prophezeiung von C'thun!nnEr weiß, dass Ihr zu ihm kommen werdet - und mit Euch kommt die Macht von Kalimdor. Lasst es mich wissen, wenn Ihr bereit seid und ich werde Euch das Szepter der Sandstürme anvertrauen."}},
		[8743] = {"Schlagt den Gong!",nil, {"Die Oberfläche des Skarabäusgongs spiegelt bedrohlich das Licht der sengenden Sonne Silithus' wieder. Stählt Euch für das Kommende, denn wurde der Gong erst einmal geschlagen, werden sich die Tore nach Ahn'Qiraj öffnen. Dann wird es kein Zurück mehr gebennnIm züngelnden Antlitz der Bestie lauert nur Chaos und Zerstörung. Verteidigt Euer Volk!"}},
		[8744] = {"Ein sorgfältig verpacktes Geschenk",nil, {"Auf dem Kärtchen and diesem Geschenk steht:nnAn <Name>,nnmöge Euer Winterfest fröhlich und heiter verlaufen!"}},
		[8745] = {"Der Schatz des Zeitlosen",nil, {"Seig gegrüßt, großer Held! Ich bin Jonathan, der Bewahrer des heiligen Gongs und ewige Wächter des bronzenen Drachenschwarms. Der Zeitlose hat mich dazu ermächtigt, Euch aus seinem Schatz einen Gegenstand Eurer Wahl zu gewähren. Möge er Euren Kampf gegen C'Thun erleichtern."}},
		[8746] = {"Metzen das Rentier",nil, {"Findet Metzen das Rentier. Lest Euch die erhaltenen Notizen durch, um Tipps über seinen Aufenthaltsort zu erhalten. Wenn Ihr Metzen findet, solltet Ihr den Beutel mit Rentierstaub dabeihaben. Berieselt Ihr Metzen mit dem Staub, sollte dies ihn aus seiner Gefangenschaft befreien. Bringt den Beutel zu Kaymard Kupferzwick in Orgrimmar zurück, sobald Ihr Metzen befreit habt."}},
		[8747] = {"Der Pfad des Beschützers",nil, {"Beeindruckend, kleine Kreatur. Ihr habt eine Menge durchgemacht, um das Vertrauen des bronzenen Drachenschwarms zu erwerben. Eure Hingabe und Selbstaufopferung wurde zur Kenntnis genommen. Der Siegelring des Beschützers birgt enorme Kräfte in sich, die Euch gegen die Mächte des Bösen helfen werden. Seine Macht wird Eure Verteidigungskraft um ein Vielfaches verstärken. Aber seid gewarnt! Habt Ihr erst einmal Euren Pfad gewählt, gibt es kein Zurück mehr."}},
		[8748] = {"Der Pfad des Beschützers",nil, {"Eure selbstlosen Bemühungen, die Kinder Kalimdors zu verteidigen, wurden zur Kenntnis genommen. Übergebt mir Euren Siegelring, damit ich seine Kräfte verstärken kann."}},
		[8749] = {"Der Pfad des Beschützers",nil, {"Ihr ehrt meinesgleichen, <Name>! Vernichtet die Feinde Kalimdors! Zeigt ihnen, was es heißt, das Land des ewigen Sternenlichts anzugreifen. Ihr habt Euch eine weitere Verstärkung des Rings verdient. Übergebt mir Euren Siegelring, damit ich seine Kräfte erweitern kann."}},
		[8750] = {"Der Pfad des Beschützers",nil, {"Eine derartige Hingabe für eine gerechte Sache ist selten. Ihr habt die Unbeugsamkeit Eures Willens bewiesen, <Name>! Dafür, dass Euer wacher Geist und Euer scharfer Verstand über diese Welt wacht, sollt Ihr reichhaltig belohnt werden. Übergebt mir Euren Siegelring, damit ich seine Kräfte verstärken kann."}},
		[8751] = {"Der Beschützer von Kalimdor",nil, {"Selten habe ich solch eine Hingabe, solch ein Durchhaltevermögen gesehen! Der bronzene Drachenschwarm gewährt Euch die mächtigste Verzauberung. Der Zeitlose selbst, hat es befohlen!nnÜbergebt mir Euren Siegelring, damit ich seine Kräfte verstärken kann."}},
