		[8761] = {"Der große Herbeirufer",nil, {"Selten habe ich solch eine Hingabe, solch ein Durchhaltevermögen gesehen! Der bronzene Drachenschwarm gewährt Euch die mächtigste Verzauberung. Der Zeitlose selbst, hat es befohlen!nnÜbergebt mir Euren Siegelring, damit ich seine Kräfte verstärken kann."}},
		[8762] = {"Metzen das Rentier",nil, {"Findet Metzen das Rentier. Lest Euch die erhaltenen Notizen durch, um Tipps über seinen Aufenthaltsort zu erhalten. Wenn Ihr Metzen findet, solltet Ihr den Beutel mit Rentierstaub dabeihaben. Berieselt Ihr Metzen mit dem Staub, sollte dies ihn aus seiner Gefangenschaft befreien. Bringt den Beutel zu Wulmort Klingeltaschen in Eisenschmiede zurück, sobald Ihr Metzen befreit habt."}},
		[8763] = {"Der Held des Tages",nil, {"Bringt 1 Tiefsteinsalz und 5 Goldstücke zum Tannenzweigkonservierer. Ihr werdet 5 konservierte Tannenzweige erhalten."}},
		[8764] = {"Den Pfad wechseln - Kein Beschützer mehr",nil, nil},
		[8765] = {"Den Pfad wechseln - Kein Herbeirufer mehr",nil, nil},
		[8766] = {"Den Pfad wechseln - Kein Eroberer mehr",nil, nil},
		[8767] = {"Ein leicht geschütteltes Geschenk",nil, {"Dieses Geschenk sieht aus, als wäre es schon ein paar Mal geschüttelt worden. Auf dem Kärtchen steht:BnFür <Name>, einen ganz besonderen Helden."}},
		[8768] = {"Ein fröhlich verpacktes Geschenk",nil, {"Auf diesem festlich verpackten Geschenk steht Euer Name!nn Hat sich das etwa gerade bewegt?"}},
		[8769] = {"Ein tickendes Geschenk",nil, {"Ein wundervolles Winterhauchfest wünschen Euch Eure Freunde bei Kokelwälder."}},
		[8770] = {"Ziel: Verteidiger des Ashischwarms",nil, {"Tötet 30 Verteidiger des Ashischwarms und erstattet Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz I, um diese Aufgabe abschließen zu können."}},
		[8771] = {"Ziel: Sandpirscher des Ashischwarms",nil, {"Tötet 30 Sandpirscher des Ashischwarms und erstattet Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz II, um diese Aufgabe abschließen zu können."}},
		[8772] = {"Ziel: Wegbehüter des Zoraschwarms",nil, {"Tötet 30 Wegbehüter des Zoraschwarms und erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz VI, um diese Aufgabe abschließen zu können."}},
		[8773] = {"Ziel: Häscher des Zoraschwarms",nil, {"Tötet 30 Häscher des Zoraschwarms und erstattet Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz IV, um diese Aufgabe abschließen zu können."}},
		[8774] = {"Ziel: Wegelagerer des Regalschwarms",nil, {"Tötet 30 Wegelagerer des Regalschwarms und erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz VIII, um diese Aufgabe abschließen zu können."}},
		[8775] = {"Ziel: Feuerspucker des Regalschwarms",nil, {"Tötet 30 Feuerspucker des Regalschwarms und erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz IX, um diese Aufgabe abschließen zu können."}},
		[8776] = {"Ziel: Sklavenjäger des Regalschwarms",nil, {"Tötet 30 Sklavenjäger des Regalschwarms und erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz X, um diese Aufgabe abschließen zu können."}},
		[8777] = {"Ziel: Einbuddler des Regalschwarms",nil, {"Tötet 30 Einbuddler des Regalschwarms und erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz XI, um diese Aufgabe abschließen zu können."}},
		[8778] = {"Die Eisenschmiedebrigade braucht Sprengstoff!",nil, {"Bringt Arkanist Sprinkelstaub nahe des Zoraschwarmbaus in Silithus 6 Öle des Feuerbrandes, 5 Einheiten Goblinraketentreibstoff und 10 Einheiten dichtes Sprengpulver. Ihr benötigt auch Logistikeinsatz IV, um diese Aufgabe abschließen zu können."}},
		[8779] = {"Magische Materialien",nil, {"Bringt 1 großen glänzenden Splitter, 1 großen strahlenden Splitter und einen gewaltigen Smaragd zu Geologin Lerchenbann auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz V, um diese Aufgabe abschließen zu können."}},
		[8780] = {"Rüstungssets für den Feldeinsatz",nil, {"Bringt 8 unverwüstliche Rüstungssets und 8 schwere Rüstungsset zu Janela Starkhammer am Posten der Eisenschmiedebrigade beim Zoraschwarmbau in Silithus. Ihr benötigt auch Logistikeinsatz VII, um diese Aufgabe abschließen zu können."}},
		[8781] = {"Waffen für den Feldeinsatz",nil, {"Bringt 2 Mondstahlbreitschwerter zu Janela Starkhammer am Posten der Eisenschmiedebrigade beim Zoraschwarmbau. Ihr benötigt auch Logistikeinsatz VI, um diese Aufgabe abschließen zu können."}},
		[8782] = {"Uniformnachschub",nil, {"Bringt 1 Einheit Mondstoff, 2 Runenstoffballen und 1 Einheit Eisenweberseide zu Windruferin Prachthorn auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz VIII, um diese Aufgabe abschließen zu können."}},
		[8783] = {"Besondere Materialien",nil, {"Bringt Vargus auf Burg Cenarius in Silithus 2 verzauberte Thoriumbarren und 2 Einheiten verzaubertes Leder. Ihr benötigt auch Logistikeinsatz IX, um diese Aufgabe abschließen zu können."}},
		[8784] = {"Geheimnisse der Qiraji",nil, {"Bringt das uralte Qirajiartefakt zu den Drachen, die sich nahe des Tempeleingangs versteckt halten."}},
		[8785] = {"Die Orgrimmarlegion braucht Mojo!",nil, {"Bringt Schattenpriesterin Shai nahe des Regalschwarmbaus in Silithus 6 Einheiten mächtiges Mojo, 6 Fläschchen großes Mojo und 8 Öle des Feuerbrandes. Ihr benötigt auch Logistikeinsatz IV, um diese Aufgabe abschließen zu können."}},
		[8786] = {"Waffen für den Feldeinsatz",nil, {"Bring 3 massive Eisenäxte zu Merok Langschreiter im Lager der Orgrimmarlegion außerhalb des Regalschwarmbaus. Ihr benötigt auch Logistikeinsatz VI, um diese Aufgabe abschließen zu können."}},
		[8787] = {"Rüstungssets für den Feldeinsatz",nil, {"Bringt 8 unverwüstliche Rüstungssets und 8 schwere Rüstungsset zu Merok Langschreiter nahe des Regalschwarmbaus. Ihr benötigt auch Logistikeinsatz VII, um diese Aufgabe abschließen zu können."}},
		[8788] = {"Ein leicht geschütteltes Geschenk",nil, {"Dieses Geschenk sieht aus, als wäre es schon ein paar Mal geschüttelt worden. Auf dem Kärtchen steht:nnFür <Name>, einen ganz besonderen Helden."}},
		[8789] = {"Imperiale Qirajiwaffen",nil, {"Arygos im Tempel von Ahn'Qiraj wird Euch mit Elementium erfüllte Ausrüstung herstellen, wenn Ihr ihm imperiale Qirajiwaffen und 3 Einheiten Elementiumerz bringt."}},
		[8790] = {"Imperiale Qirajiinsignien",nil, {"Merithra des Traums im Tempel von Ahn'Qiraj wird Euch mit Elementium erfüllte Ausrüstung herstellen. Bringt ihr dazu imperiale Qirajiinsignien und 3 Einheiten Elementiumerz."}},
		[8791] = {"Der Untergang von Ossirian",nil, {"Bringt den Kopf von Ossirian dem Narbenlosen zu Kommandant Mar'alith auf Burg Cenarius in Silithus."}},
		--[8792] = {"Die Horde braucht Euch!",nil, {"Sprecht mit Kriegsherr Gorchuk im Tal der Geister in Orgrimmar."}},
