		[8798] = {"Euer eigener Yeti",nil, {"Hey! Wisst Ihr was?! Ihr seid auch Ingenieur! So, da Ihr mir dabei geholfen habt meinen Freunden eine Lektion zu erteilen, werde ich Euch jetzt lehren, wie Ihr Euren eigenen mechanischen Yeti herstellt! Jetzt kann der Spaß für immer weitergehen und Ihr könnt wem immer Ihr wollt einen Schrecken einjagen! Vorausgesetzt Ihr treibt die benötigten Teile dafür auf. Na, was sagt Ihr, <Name>? Wollt Ihr, dass ich Euch in das Geheimnis der Herstellung eines mechanischen Yetis einweihe?"}},
		[8799] = {"Der Held des Tages",nil, {"Bringt 1 Tiefsteinsalz und 5 Goldstücke zum Tannenzweigkonservierer. Ihr werdet 5 konservierte Tannenzweige erhalten."}},
		[8800] = {"Schlachtrüstung des Cenarius",nil, {"Sprecht mit Vargus auf Burg Cenarius in Silithus."}},
		[8801] = {"C'Thuns Vermächnis",nil, {"Bringt Caelastrasz im Tempel von Ahn'Qiraj das Auge von C'Thun."}},
		[8802] = {"Der Retter von Kalimdor",nil, {"Bringt Anachronos in den Höhlen der Zeit das Auge von C'Thun."}},
		[8803] = {"Ein Festtagsgeschenk",nil, {"Auf dem Schildchen des Geschenks steht:nnFür <Name>,nnIch hoffe, dieses Geschenk wird Euch dabei helfen, einen Hauch von Gemütlichkeit und Festtagsfreude in ganz Azeroth zu verbreiten. Altvater Winte"}},
		--[8804] = {"Wüstenüberlebenspaket",nil, {"Bringt 4 Kugeln des Wassers, 4 mächtige Gegengifte und 4 geräucherte Wüstenknödel zu Calandrath in der Taverne von Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz I, um diese Aufgabe abschließen zu können."}},
		--[8805] = {"Stiefel für die Wache",nil, {"Bringt 3 Paar verschnörkelte Mithrilstiefel zu Vish Kozus, Hauptmann der Wache von Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz II, um diese Aufgabe abschließen zu können."}},
		--[8806] = {"Schleifsteine für die Wache",nil, {"Bringt 10 verdichtete Schleifsteine, 10 robuste Schleifsteine und 10 schwere Schleifsteine zu Vish Kozus, Hauptmann der Wache von Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz III, um diese Aufgabe abschließen zu können."}},
		--[8807] = {"Magische Materialien",nil, {"Bringt 1 großen glänzenden Splitter, 1 großen strahlenden Splitter und 1 gewaltigen Smaragd zu Geologin Lerchenbann auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz V, um diese Aufgabe abschließen zu können."}},
		--[8808] = {"Uniformnachschub",nil, {"Bringt 1 Einheit Mondstoff, 2 Runenstoffballen und 1 Einheit Eisenweberseide zu Windruferin Prachthorn auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz VIII, um diese Aufgabe abschließen zu können."}},
		--[8809] = {"Besondere Materialien",nil, {"Bringt Vargus auf Burg Cenarius in Silithus 2 verzauberte Thoriumbarren und 2 Einheiten verzaubertes Leder. Ihr benötigt auch Logistikeinsatz IX, um diese Aufgabe abschließen zu können."}},
		--[8810] = {"Verbände für den Feldeinsatz",nil, {"Bringt 30 schwere Runenstoffverbände, 30 schwere Seidenverbände und 30 schwere Magiestoffverbände zu Windruferin Prachthorn auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz X, um diese Aufgabe abschließen zu können."}},
