		--[8856] = {"Wüstenüberlebenspaket",nil, {"Bringt 4 Kugeln des Wassers, 4 mächtige Gegengifte und 4 gebackene Lachse zu Calandrath in der Taverne von Burg Cenarius in Silithus."}},
		[8857] = {"Geheimnisse des Kolosses - Ashi",nil, {"Bringt das Fragment vom Koloss des Ashischwarms zu Glotz Widrikus in Beutebucht."}},
		[8858] = {"Geheimnisse des Kolosses - Regal",nil, {"Bringt das Fragment vom Koloss des Regalschwarms zu Aufseher Schlickfaust in die sengende Schlucht."}},
		[8859] = {"Geheimnisse des Kolosses - Zora",nil, {"Bringt das Fragment vom Koloss des Zoraschwarms zu Lord Maxwell Tyrosus an der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern."}},
		[8860] = {"Neujahrsfeier!",nil, {"Bringt die Vorräte von Kokelwälder zu Gastwirtin Allison in Sturmwind."}},
		[8861] = {"Neujahrsfeier!",nil, {"Bringt die Vorräte von Kokelwälder zu Gastwirtin Pala in Donnerfels."}},
		[8862] = {"Elunes Kerze",nil, {"Elunes Kerze ist für die Anhänger des Mondfests ein Artefakt der großen Verehrung. Die Kerze beinhaltet wahres Mondlicht, bereit zur Freigabe!nnDieses Licht ist für die meisten Kreaturen harmlos, aber Ihr werdet sehen, dass Omen und seine Diener davon Verbrennungen erleiden und geblendet werden."}},
		[8863] = {"Festtagsklöße",nil, {"Wenn Ihr hungrig seid, dann solltet ihr zu der richtigen Adresse kommen!nnKlöße sind das traditionelle Gericht des Mondfests; man serviert es bereits seit den Zeiten des ersten Mondfests und ich bin stolz, dass ich diese Köstlichkeit während der diesjährigen Feierlichkeiten anbieten kann. Im Austausch gegen eine einzige Münze der Urahnen werde ich Euch von einer Probe dieses unvergleichlich genüsslichen Gerichts kosten lassen!"}},
		[8864] = {"Festtagskleider des Mondfests",nil, {"Ich habe eine Auswahl an Kleidern, die Euch interessieren könnte. Derartige Gewänder wurden in vergangenen Zeiten während des Mondfests getragen. Die Auswahl, die ich heute zu bieten habe, ist vielleicht die beste, die ich jemals gesehen habe. Jedes Stück ist aus den feinsten und geschmeidigsten Materialien handgefertigt worden. Ich lasse Euch im Tausch gegen ein paar Münzen der Urahnen unter den drei Kleidungsstilen einen für Euch selbst auswählen. Ist das annehmbar für Euch?"}},
		[8865] = {"Festliche Hosenanzüge des Mondfests",nil, {"Ich freue mich Euch mitteilen zu können, dass ich über eine feine Auswahl an festlichen Hosenanzügen verfüge, die ich Euch anbieten kann... aber nur im Tausch gegen Münzen der Urahnen. Wenn Ihr Wert auf Eure Erscheinung legt, dann ist dies genau das Richtige für Euch! Ich kann Euch versichern, dass Ihr keine vergleichbaren Hosenanzüge finden werdet. Ich lasse Euch im Tausch gegen ein paar Münzen der Urahnen unter den drei Kleidungsstilen einen für Euch selbst auswählen. Ist das annehmbar für Euch?"}},
		[8866] = {"Urahne Bronzebart",nil, {"Es ist gut zu wissen, dass die Bewohner dieser Welt noch immer die alten Völker verehren und sie nicht vergessen haben. Ich erbiete Euch meinen Respekt, <Name>. Nehmt dies als Zeichen meiner Dankbarkeit."}},
		[8867] = {"Mondfeuerwerk",nil, {"Zündet 8 Raketen und 2 Raketenbündel und kehrt dann zu einer Botin des Mondfests in irgendeiner Hauptstadt zurück."}},
		[8868] = {"Elunes Segen",nil, {"Beschwört Omen, besiegt ihn und erlangt Elunes Segen. Kehrt anschließend zu Valadar Sternensang in Nachthafen zurück."}},
		[8869] = {"Süße Beschaulichkeit",nil, {"Begebt Euch nach Stratholme und tötet den purpurroten Hammerschmied. Nehmt die Schürze des purpurroten Hammerschmiedes und kehrt zu Lilith zurück."}},
		[8870] = {"Das Mondfest",nil, {"Sprecht mit der Botin des Mondfests im Mystikerviertel von Eisenschmiede. Die Boten in den anderen Hauptstädten können Euch ebenfalls weiterhelfen."}},
		[8871] = {"Das Mondfest",nil, {"Sprecht mit der Botin des Mondfests im Parkviertel von Sturmwind. Die Boten in den anderen Hauptstädten können Euch ebenfalls weiterhelfen."}},
		[8872] = {"Das Mondfest",nil, {"Sprecht mit der Botin des Mondfests in der Enklave des Cenarius in Darnassus. Die Boten in den anderen Hauptstädten können Euch ebenfalls weiterhelfen."}},
		[8873] = {"Das Mondfest",nil, {"Sprecht mit der Botin des Mondfests im Tal der Weisheit in Orgrimmar. Die Boten in den anderen Hauptstädten können Euch ebenfalls weiterhelfen."}},
		[8874] = {"Das Mondfest",nil, {"Sprecht mit der Botin des Mondfests am Eingang zur Unterstadt. Die Boten in den anderen Hauptstädten können Euch ebenfalls weiterhelfen."}},
		[8875] = {"Das Mondfest",nil, {"Sprecht mit der Botin des Mondfests auf der Anhöhe der Ältesten in Donnerfels. Die Boten in den anderen Hauptstädten können Euch ebenfalls weiterhelfen."}},
		[8876] = {"Kleine Raketen",nil, {"Ich hüte das Geheimnis zur Herstellung der Mondfestraketen und freue mich, mein Wissen mit den Würdigen zu teilen. Ich kann Euch im Tausch gegen Münzen der Urahnen das Rezept zur Herstellung einer kleinen Rakete anbieten..."}},
		[8877] = {"Zünder für Feuerwerk",nil, {"Auch wenn Raketen wunderbar zum Feiern sind, so müssen sie doch von einem richtigen Zünder abgefeuert werden, damit man ihre wahre Herrlichkeit sehen kann... Bringt mir Münzen der Urahnen und ich lehre Euch die Herstellung von Feuerwerkszündern, wie man sie in Moonglade sieht."}},
		[8878] = {"Festtagsrezepte",nil, {"Die Kleider und Hosenanzüge, die Ihr hier sehen könnt, sind traditionelle Gewänder vergangener Zeiten. Sie werden zu Ehren alter Gebräuche und geliebter Vorfahren getragen. Bringt Ihr mir Münzen der Urahnen, <Name>, werde ich Euch das Muster zur Herstellung traditioneller roter Kleider oder Hosenanzüge geben."}},
		[8879] = {"Große Raketen",nil, {"Große Festtagsraketen erhellen den Himmel sternengleich - nur die Raketenbündel können mit ihrer Schönheit rivalisieren!nnBringt mir Münzen der Urahnen und ich lehre Euch ihre Herstellung."}},
		[8880] = {"Raketenbündel",nil, {"Raketenbündel bestehen aus einer Anzahl unterschiedlicher Raketen. Ihre Schönheit ist überwältigend! Bringt mir Münzen der Urahnen und ich werde das Wissen ihrer Herstellung mit Euch teilen…"}},
		[8881] = {"Große Raketenbündel",nil, {"Die Herstellung großer Raketenbündel erfordert viel Erfahrung und noch mehr Ressourcen, doch ihre Schönheit ist die Mühen wert! Bringt mir Münzen der Urahnen und ich werde das Wissen ihrer Herstellung mit Euch teilen."}},
		[8882] = {"Zünder für Raketenbündel",nil, {"Die schönsten Feuerwerke erfordern ganze Bündel an Feuerwerksraketen! Um diese abzufeuern, benötigt man Zünder für Raketenbündel! Wäre es dann nicht logisch für Euch, wenn Ihr das Geheimnis der Herstellung solcher Zünder von mir erlernen würdet? Bringt mir Münzen der Urahnen und ich werde mein Wissen mit Euch teilen…"}},
		[8883] = {"Valadar Sternensang",nil, {"Verwendet die Mondfesteinladung im Mondlicht und reist somit zur Mondlichtung. Sprecht nach Eurer Ankunft in Nachthafen mit Valadar Sternensang."}},
		[8884] = {"Fischköpfe, Fischköpfe...",nil, {"Sammelt 8 Köpfe von Grimmschuppenmurlocs und bringt sie zu Hathvelion Sonnenblick im Immersangwald auf der Klippe, die die stille Küste überblickt."}},
		[8885] = {"Mmmrrrgggllls Ring",nil, {"Erlangt Mmmrrrgggllls Ring von dem Häuptling der Grimmschuppen und kehrt damit anschließend zu Hathvelion Sonnenblick im Immersangwald auf der Klippe, die die stille Küste überblickt, zurück."}},
		[8886] = {"Piraten der Grimmschuppen!",nil, {"Erlangt 6 Stücke von Kapitän Kelisendras Fracht wieder und bringt sie zu Kapitän Kelisendra beim befestigten Lager direkt östlich des Ankerplatzes der Sonnensegel im Immersangwald zurück."}},
		[8887] = {"Kapitän Kelisendras verlorene Seekarten",nil, {"Bringt Kapitän Kelisendras verlorene Seekarten zu Kapitän Kelisendra beim befestigten Lager direkt östlich des Ankerplatzes der Sonnensegel im Immersangwald zurück."}},
		[8888] = {"Der Lehrling des Magisters",nil, {"Sprecht mit Lehrling Loralthalis nahe des Nachtschimmerturms im Immersangwald."}},
		[8889] = {"Deaktivierung des Turms",nil, {"Deaktiviert 3 Kraftquellen des Nachtschimmerturms im Nachtschimmerturm. Bringt anschließend das Deaktivierungsjuwel zu Lehrling Loralthalis im Immersangwald."}},
		[8890] = {"Nachricht vom Turm",nil, {"Kehrt zu Magister Nachtschimmer bei der Zuflucht der Weltenwanderer im Immersangwald zurück."}},
		[8891] = {"Aufgegebene Untersuchungen",nil, {"Bringt Magister Nachtschimmers Tagebuch zu Magister Nachtschimmer bei der Zuflucht der Weltenwanderer im Immersangwald zurück."}},
		[8892] = {"Vorfall beim Ankerplatz der Sonnensegel",nil, {"Tötet 5 getriebene Rohlinge und 5 getriebene Straßendiebe und erstattet Waldläufer Degolien im Immersangwald Bericht."}},
		[8893] = {"Der Super-Ei-O-Mat",nil, {"Der Super-Ei-O-Mat ist die neueste Erfindung von Curgle Cranklehop. Es ist eine ziemlich beeindruckende Maschine. Der Super-Ei-O-Mat fängt wild an zu spucken und zu zischen, als Ihr Euch der Steuerkonsole nähert."}},
		[8894] = {"Sicherung des Grunds",nil, {"Tötet 6 Manaschlangen und 6 Ätherbestien. Kehrt anschließend zu Grundbewahrer Wyllithen beim Nachtschimmergrund im Immersangwald zurück."}},
		[8895] = {"Lieferung an das Sanktum des Nordens",nil, {"Überbringt den unbeschrifteten Brief an Leybewahrer Caidanis beim Sanktum des Nordens im Immersangwald."}},
		[8896] = {"Der Zwergenspion",nil, {"Findet und tötet Ausgrabungsleiter Ambossel im Sanktum des Nordens und berichtet anschließend Aeldon Sonnenmal im Immersangwald."}},
		[8897] = {"Liebste Colara",nil, {"Bringt Leutnant Heldrics sorgfältig verfasste Notiz zu Colara Dean außerhalb der Bank von Sturmwind."}},
		[8898] = {"Liebste Colara",nil, {"Bringt den zerknitterten Brief zu Colara Dean außerhalb der Bank in Sturmwind."}},
		[8899] = {"Liebste Colara",nil, {"Bringt den makellosen Brief zu Colara Dean außerhalb der Bank von Sturmwind."}},
		[8900] = {"Liebste Elenia",nil, {"Bringt die leicht zerknitterte Notiz zu Elenia Haydon nahe der Bank in Unterstadt."}},
		[8901] = {"Liebste Elenia",nil, {"Bringt den sorgfältig verfassten Brief zu Elenia Haydon nahe der Bank in Unterstadt."}},
		[8902] = {"Liebste Elenia",nil, {"Bringt den liebevoll verfassten Brief zu Elenia Haydon nahe der Bank in Unterstadt."}},
		[8903] = {"Gefährliche Liebschaft",nil, {"Besorgt die Karte einer Wache von Sturmwind und bringt sie zu Aristan Mottar in Sturmwind."}},
