		[8921] = {"Der Ektoplasmadestillierer",nil, {"Kehrt mit einem empfindlichen Arkanitwandler, 4 großen ewigen Essenzen, 10 Flaschen Steinschuppenöl, 25 Einheiten Vulkanasche und 40 Gold im Gepäck zu Mux Manamix in Gadgetzan zurück."}},
		[8922] = {"Ein übernatürlicher Apparat",nil, {"Bringt den versiegelten Blutbehälter zu Mux Manamix in Gadgetzan in Tanaris."}},
		[8923] = {"Ein übernatürlicher Apparat",nil, {"Bringt den versiegelten Giftbehälter zu Mux Manamix in Gadgetzan."}},
		[8924] = {"Ektoplasmajagd",nil, {"Benutzt den Ektoplasmadestillierer in der Nähe von schemenhaften Untoten, um 12 Einheiten versengtes Ektoplasma in Silithus, 12 Einheiten gefrorenes Ektoplasma in Winterquell und 12 Einheiten stabiles Ektoplasma in den östlichen Pestländern zu erhalten. Bringt das Ektoplasma dann mitsamt des Ektoplasmadestillierers zu Mux Manamix in Gadgetzan zurück."}},
		[8925] = {"Eine portable Energiequelle",nil, {"Findet Magmafürst Bokk in der brennenden Steppe, nehmt seinen Magmakern an Euch und bringt ihn zu Mux Manamix in Gadgetzan."}},
