		[8946] = {"Lebensbeweis",nil, {"Bringt Anthion Harmon in den östlichen Pestländern Ysidas Medaillon."}},
		[8947] = {"Anthions merkwürdige Anforderung",nil, {"Bringt Anthion Harmon in den östlichen Pestländern 3 Dunkeleisenbarren, 20 verzaubertes Leder, 3 Mondstoff und 4 geschmeidige unverwüstliche Bälge"}},
		[8948] = {"Anthions alter Freund",nil, {"Bringt Falrin Rankenweber in der Bibliothek von Düsterbruch das unvervollständigte Banner der Provokation."}},
		[8949] = {"Falrins Rachefeldzug",nil, {"Nehmt den Ogern in der Schwarzfelsspitze oder in Düsterbruch 25 Ogerkriegsperlen ab und bringt sie zu Falrin Rankenweber im Athenaeum, der Bibliothek von Düsterbruch."}},
		[8950] = {"Die Verzauberung des Aufhetzers",nil, {"Bringt Falrin Rankenweber in Düsterbruch 1 Essenz eines spöttischen Geisterwesens, 4 Dunkelrunen und 8 große glänzende Splitter."}},
