		[8960] = {"Bodleys trauriges Schicksal",nil, {"Reist zum Schwarzfels und benutzt den extradimensionalen Geisterdetektor, um Bodley nahe der Schwarzfelsspitze zu finden."}},
		[8961] = {"Die drei Könige der Flammen",nil, {"Bringt Bodley im Schwarzfels Incendius' Pyrophor, Glutsehers Glut, die Asche des Fürsten und ein geheiligtes Räuchergefäß."}},
		[8962] = {"Komponenten von großer Wichtigkeit",nil, {"Bringt Bodley im Schwarzfels einige Druidenüberreste."}},
		[8963] = {"Komponenten von großer Wichtigkeit",nil, {"Bringt Bodley im Schwarzfels ein Relikt von Sternenhauch."}},
		[8964] = {"Komponenten von großer Wichtigkeit",nil, {"Bringt Bodley im Schwarzfels ein gleißendes Schwert des Zeloten."}},
