		[9272] = {"Kleider machen Seebären",nil, nil},
		[9274] = {"Geister der Ertrunkenen",nil, {"Waldläufer Krenn'an aus der Enklave der Weltenwanderer in den Geisterlanden möchte, dass Ihr beim nahegelegenen See 8 rachsüchtige Erscheinungen und 8 ruhelose Erscheinungen tötet."}},
		[9275] = {"In der Würze liegt der Tod",nil, {"Vergiftet die Trollnahrungsvorräte in den Lagern von Zeb'Nowa. Kehrt anschließend zu Apotheker Venustus bei der Enklave der Weltenwanderer in den Geisterlanden zurück."}},
		[9276] = {"Angriff auf Zeb'Tela",nil, {"Tötet 8 Schattenzauberer der Waldschattentrolle und 8 Kopfjäger der Waldschattentrolle. Kehrt anschließend zu Weltenwanderin Solanna bei der Enklave der Weltenwanderer in den Geisterlanden zurück."}},
		[9277] = {"Angriff auf Zeb'Nowa",nil, {"Tötet 10 Pantherlords der Waldschattentrolle und 10 Verhexer der Waldschattentrolle. Kehrt anschließend zu Weltenwanderin Solanna bei der Enklave der Weltenwanderer in den Geisterlanden zurück."}},
		[9278] = {"Willkommen!",nil, {"Bringt den Geschenkgutschein der Azurwacht zu Jaeleil in der Azurwacht."}},
		[9279] = {"Ihr habt überlebt!",nil, {"Sprecht mit Proenitus bei der Absturzstelle im Am'mental."}},
		[9280] = {"Wiederaufladen der Heilkristalle",nil, {"Beschafft Euch 8 Phiolen mit Mottenblut von den in der Nähe hausenden Talmotten und bringt sie zu Proenitus bei der Absturzstelle im Am'mental."}},
		[9281] = {"Den Weg frei machen",nil, {"Lehrling Vor'el von Andiliens Grund in den Geisterlanden möchte, dass Ihr 10 Große Spindelweber und 10 Geisterklauenverheerer tötet."}},
		[9282] = {"Die Enklave der Weltenwanderer",nil, {"Magister Kaendris beim Sanktum der Sonne möchte, dass Ihr mit Waldläuferin Vynna bei der Enklave der Weltenwanderer in den Geisterlanden sprecht."}},
		[9283] = {"Rettet die Überlebenden!",nil, {"Wendet Eure Gabe der Naaru an, um einen der Überlebenden der Draenei zu heilen, die über das ganze Am'mental verstreut sind. Kehrt dann zu Zalduun bei der Absturzstelle im Am'mental zurück."}},
		[9284] = {"Aldor Faction Test",nil, nil},
		[9285] = {"Consortium Faction Test",nil, nil},
		[9286] = {"Scryers Faction Test",nil, nil},
		[9287] = {"Ausbildung zum Paladin",nil, nil},
		[9288] = {"Ausbildung zum Jäger",nil, {"Ich bin froh, dass Ihr zu mir gekommen seid. Ich bin schon eine ganze Weile für die Ausbildung junger <Klasse> verantwortlich, eine Aufgabe, die ich auch in dieser neuen Welt noch weiter erfüllen möchte. Ich beobachte Euch bereits seit geraumer Zeit und es wäre eine Ehre für mich, wenn ich Euch die Künste des Jägers lehren dürfte. Sollen wir beginnen?"}},
