		[9290] = {"Ausbildung zum Magier",nil, nil},
		[9291] = {"Ausbildung zum Priester",nil, nil},
		[9292] = {"Gesprungener nekrotischer Kristall",nil, {"Bringt den gesprungenen nekrotischen Kristall zu Leutnant Orrin außerhalb der Tore von Sturmwind."}},
		[9293] = {"Was getan werden muss...",nil, {"Sammelt 10 Peitscherproben ein und bringt sie zu Botanikerin Taerix bei der Absturzstelle im Am'mental."}},
		[9294] = {"Die Heilung des Sees",nil, {"Verwendet den Neutralisierungswirkstoff bei dem strahlenden Energiekristall und kehrt anschließend zu Botanikerin Taerix bei der Absturzstelle im Am'mental zurück."}},
		[9295] = {"Brief von der Front",nil, {"Bringt den zerfledderten Brief zum Bewahrer der Schriften bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern."}},
