		[9302] = {"Schreiben von der Front",nil, {"Bringt das zerknitterte Schreiben zum Bewahrer der Schriften bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern."}},
		[9303] = {"Immunisierung",nil, {"Wendet den Immunisierungskristall auf 6 Nisteleulkin an. Kehrt anschließend zu Verteidiger Aldar bei der Absturzstelle im Am'mental zurück."}},
		[9304] = {"Dokument von der Front",nil, {"Bringt das verschmierte Dokument zum Bewahrer der Schriften bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern."}},
