		[9671] = {"Dringende Nachricht",nil, {"Seid Ihr <Name>? Es wurde auch Zeit! Ich war gerade dabei, die Suche nach Euch aufzugeben!nnBeim Briefkasten wartet eine dringende Nachricht auf Euch. Die Person, die sie mir übergeben hatte, war ein Admiral der Menschen von der Azurmythosinsel. Admiral Odesy-irgendwas... der Name ist mir entfallen. Er sagte, Ihr würde wissen, wer er sei."}},
		[9672] = {"Das Vermächtnis des Blutfluchs",nil, {"Admiral Odesyus hat Euch gebeten, nach Kapitän Edward Hanes zu suchen und mit ihm zu sprechen."}},
		[9673] = {"Wildtierausbildung",nil, {"Sprecht mit Halthenis in Silbermond."}},
		[9674] = {"Die Naga des Blutfluchs",nil, {"Kapitän Edward Hanes auf der Insel Drachenfels möchte, dass Ihr 10 Naga des Blutfluchs tötet."}},
		[9675] = {"Wildtierausbildung",nil, {"Sprecht mit Ganaar bei der Händlertreppe der Exodar."}},
		[9676] = {"Ausbildung zum Paladin",nil, {"Sprecht mit Jesthenis Sonnensturm - dem Paladinlehrer - im Sonnenturm auf der Insel der Sonnenwanderer."}},
		[9677] = {"Aufforderung des Ritterfürsten Heldenblut",nil, {"Sprecht mit Ritterfürst Heldenblut beim Platz der Weltenwanderer in Silbermond."}},
		[9678] = {"Die erste Prüfung",nil, {"Reist zu der Insel, die Euch Ritterfürst Heldenblut beschrieben hat, sucht die Höhle und entzündet die Kohlepfanne. Kehrt, nachdem Ihr die erste Prüfung bestanden habt, zu Ritterfürst Heldenblut in Silbermond zurück."}},
		[9680] = {"Die Vergangenheit aufwühlen",nil, {"Erzmagier Alturus möchte, dass Ihr zu den Bergen südlich von Karazhan im Gebirgspass der Totenwinde geht und ein verkohltes Knochenfragment besorgt."}},
		[9681] = {"Eine Studie der Macht",nil, {"Sprecht mit Magister Astalor Blutschwur in der Geheimen Kammer unter dem Hauptquartier der Blutelfen."}},
		[9682] = {"Die Hoffnungslosen...",nil, {"Kapitän Edward Hanes auf der Insel Drachenfels möchte, dass Ihr 4 Seelen des Blutfluchs zu ihm bringt."}},
		[9683] = {"Beendigung des Blutfluchs",nil, {"Kapitän Edward Hanes auf der Insel Drachenfels möchte, dass Ihr die Statue der Königin Azshara zerstört und Atoph des Blutfluchs tötet."}},
		[9684] = {"Die Beherrschung des Lichts",nil, {"Wendet das schimmernde Gefäß auf die Magister der Blutelfen an, um es zu füllen und kehrt anschließend zu Ritterfürst Heldenblut in Silbermond zurück."}},
		[9685] = {"Die Erlösung der Toten",nil, {"Bringt das gefüllte schimmernde Gefäß zum Gasthaus von Silbermond und verwendet seine Magie, um Sangrias Stillstreich wiederzubeleben."}},
		[9686] = {"Die zweite Prüfung",nil, {"Besteht die zweite Prüfung und besiegt Champion Rachblut, Champion Lichtrend, Champion Schnellklinge und Champion Sonnensturm im Pavillon in der Nähe von Meister Kelerun Blutpein."}},
		[9687] = {"Wiederherstellung der Heiligkeit",nil, {"Prinz Toreth auf der Insel Drachenfels möchte, dass Ihr 8 Drachenknochen wiedererlangt."}},
		[9688] = {"Im Traum",nil, {"Prinz Toreth auf der Insel Drachenfels hat von Euch verlangt, dass Ihr 5 Viridianwelpen und 5 Viridianbrutlinge tötet."}},
		[9689] = {"Messerrachen",nil, {"Prinz Toreth auf der Insel Drachenfels hat von Euch verlangt, dass Ihr Messerrachen tötet."}},
		[9690] = {"Die zweite Prüfung",nil, {"Meldet Euch bei Meister Kelerun Blutpein."}},
		[9691] = {"Rückkehr nach Silbermond",nil, {"Kehrt zu Ritterfürst Heldenblut nach Silbermond zurück."}},
		[9692] = {"Der Pfad des Adepten",nil, {"Bringt Ritterfürst Heldenblut in Silbermond einen verderbten Koredelstein, eine Kiste mit blutgeschmiedeten Blöcken, etwas Blut des Hasserfüllten und ein Insigne der Blutritter."}},
		[9693] = {"Was Argus für mich bedeutet",nil, {"Exarch Admetius bei der Blutwacht wies Euch an, mit Verteidiger Boros zu sprechen."}},
		[9694] = {"Blutwacht",nil, {"Verteidiger Boros bei der Blutwacht möchte, dass Ihr 10 Spione der Sonnenfalken tötet."}},
		[9696] = {"Übersetzungen...",nil, {"Bringt das Schreiben der Sonnenfalken zur Vernehmungsbeauftragten Elysia bei der Blutwacht."}},
		[9697] = {"Behüterin Leesa'oh",nil, {"Sprecht mit Behüterin Leesa'oh im Wachposten des Cenarius in den Zangarmarschen."}},
		[9698] = {"Audienz beim Propheten",nil, {"Bringt das übersetzte Schreiben der Sonnenfalken zum Propheten Velen bei der Exodar."}},
		[9699] = {"Was ist wahr?",nil, {"Bringt Velens Anordnungen zu Verteidiger Boros bei der Blutwacht."}},
		[9700] = {"Licht ins Dunkel bringen",nil, {"Verteidiger Boros bei der Blutwacht möchte, dass Ihr die Lage des Sonnenportals ausfindig macht und 5 Anomalien der Leere tötet."}},
		[9701] = {"Sporlinge beobachten",nil, {"Untersucht das Pilzgeflecht in den Zangarmarschen und berichtet danach Behüterin Leesa'oh im Wachposten des Cenarius in den Zangarmarschen von Euren Beobachtungen."}},
		[9702] = {"Eine Frage der Fressgier",nil, {"Sammelt 10 Stück liegengelassene Nahrung und kehrt dann zu Behüterin Leesa'oh im Wachposten des Cenarius in den Zangarmarschen zurück."}},
		[9703] = {"Der Kryokern",nil, {"Verteidiger Kuros bei der Blutwacht möchte, dass Ihr 12 Kisten mit Heilvorräten sichert."}},
		[9704] = {"Von den Getriebenen getötet",nil, {"Geht nach Süden zu den Ruinen von Silbermond und haltet nach der getöteten Kundschafterin beim Dämmerweg Ausschau."}},
		[9705] = {"Bergung des Päckchens",nil, {"Kehrt mit Eronas Päckchen zu Kundschafterin Alarion zurück."}},
		[9706] = {"Galaens Tagebuch - Das Schicksal von Verteidiger Saruan",nil, {"Bringt Galaens Tagebuch zu Verteidiger Kuros bei der Blutwacht."}},
		[9707] = {"Das Schmieden der Waffe",nil, {"Bringt die Kiste mit Rohmaterialien zu Bemarrin in Silbermond."}},
		[9708] = {"Bekannter Pilz",nil, {"Sammelt 15 Pilzproben und kehrt dann zu Behüterin Leesa'oh im Wachposten des Cenarius in den Zangarmarschen zurück."}},
		[9709] = {"Die Pilze zurückstehlen",nil, {"Sammelt 10 Kisten Pilze und kehrt dann zu Behüterin Leesa'oh im Wachposten des Cenarius in den Zangarmarschen zurück."}},
		[9710] = {"Der blutgehärtete Ranseur",nil, {"Wartet, während Bemarrin Eure Waffe schmiedet."}},
		[9711] = {"Matis der Grausame",nil, {"Verteidiger Kuros bei der Blutwacht möchte, dass Ihr Matis den Grausamen gefangen nehmt."}},
		[9712] = {"Das thalassische Schlachtross",nil, {"Sprecht mit Ritterfürst Heldenblut, um zu erfahren, wie Ihr Euer thalassisches Schlachtross beschwören könnt."}},
		[9713] = {"Glowcap Harvesting Enabling Flag",nil, nil},
		[9714] = {"Bringt mir ein weiteres Gebüsch!",nil, {"Wir können immer noch mehr Bluthibiskus von den Schlurfern, Sumpfriesen und Tiefenfledermäusen im Echsenkessel gebrauchen. Bringt sie mir in Bündeln zu je fünf, und ich werde Euch sehr dankbar sein."}},
		[9715] = {"Bringt mir ein Gebüsch!",nil, {"Sammelt 5 Stück Bluthibiskus und bringt sie zu Gzhun'tt in Sporeggar in den Zangarmarschen."}},
		[9716] = {"Anomalie am Umbrafennsee",nil, {"Untersucht den Grund für die sinkenden Wasserspiegel am Umbrafennsee. Kehrt dann zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen zurück."}},
		[9717] = {"Los geht's!",nil, {"Besorgt einen Tiefensporenfarn und bringt ihn zu T'shu in Sporeggar in den Zangarmarschen."}},
		[9718] = {"Der Flug der Krähe",nil, {"Ysiel Windsänger möchte, dass Ihr das Sturmkrähenamulett benutzt, um die größeren Seen in den Zangarmarschen zu erkunden."}},
		[9719] = {"Tötet die Mutter",nil, {"Bringt das Gehirn der Schattenmutter zu Khn'nix in Sporeggar in den Zangarmarschen."}},
		[9720] = {"Das Gleichgewicht muss erhalten bleiben",nil, {"Ysiel Windsänger möchte, dass Ihr die Eisenrankensamenkörner benutzt, um die Dampfpumpensteuerungen am Schlangensee, Umbrafennsee, Sumpflichtsee und der Lagune zu zerstören. Kehrt danach mit den übrigen Samen zu ihr in die Zuflucht des Cenarius in den Zangarmarschen zurück."}},
		[9721] = {"Aufforderung von Lord Solanar",nil, {"Sprecht mit Lord Solanar Blutzorn in Silbermond."}},
		[9722] = {"Der Pfad des Meisters",nil, {"Sprecht nochmals mit Lord Solanar Blutzorn, um seine angebotene Unterstützung für Eure Aufnahme zu akzeptieren."}},
		[9723] = {"Eine Geste der Hingabe",nil, {"Bringt 40 Einheiten Runenstoff, 6 Arkanitbarren, 10 Einheiten Sonnengras, 5 Dunkelrunen und 150 Goldstücke zu Lord Solanar Blutzorn in Silbermond."}},
		[9724] = {"Den Zirkel des Cenarius warnen",nil, {"Sprecht mit Amythiel Nebelgänger beim Posten des Cenarius auf der Höllenfeuerhalbinsel."}},
		[9725] = {"Eine Demonstration Eurer Loyalität",nil, {"Lord Solanar Blutzorn in Silbermond möchte, dass Ihr 3 Fleischwagen der Geißel zerstört und 15 Belagerungsingenieure der Geißel tötet."}},
		[9726] = {"Jetzt, da wir Freunde sind...",nil, {"Tötet 12 Sklaventreiber der Blutschuppen und 6 Verzauberinnen der Blutschuppen und kehrt dann zu Gzhun'tt in Sporeggar in den Zangarmarschen zurück."}},
		[9727] = {"Jetzt, da wir immer noch Freunde sind...",nil, {"Tötet 12 Sklaventreiber der Blutschuppen und 6 Verzauberinnen der Blutschuppen und kehrt dann zu Gzhun'tt in Sporeggar in den Zangarmarschen zurück."}},
		[9728] = {"Ein herzliches Willkommen",nil, {"Bringt 30 Nagaklauen zu Aufseher Hamuht in der Zuflucht des Cenarius."}},
		[9729] = {"Fhwoor zerschmettern!",nil, {"Begleitet Fwoohr in das Gebiet bei der Dampfpumpe am Sumpflichtsee, um die Lade von Ssslith zu holen. Bringt Fhwoor und die Lade sicher zurück und meldet Euch bei Gzhun'tt in Sporeggar in den Zangarmarschen."}},
		[9730] = {"Anführer der Dunkelkämme",nil, {"Tötet Rajah Haghazed bei der Lagune und kehrt danach zu Aufseher Hamuht bei der Expedition des Cenarius in den Zangarmarschen zurück, um eine Belohnung zu erhalten."}},
		[9731] = {"Kanalisationspläne",nil, {"Durchsucht den Grund des Schlangensees nach einem Ablauf. Kehrt mit Neuigkeiten über Eure Entdeckung zu Ysiel Windsänger in der Zuflucht des Cenarius zurück."}},
		[9732] = {"Rückkehr in die Marschen",nil, {"Kehrt zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen zurück."}},
		[9733] = {"Warnung an den Zirkel des Cenarius",nil, {"Sprecht mit Mahuram Starkhuf in Thrallmar auf der Höllenfeuerhalbinsel."}},
		[9734] = {"Rückkehr in die Marschen",nil, {"Kehrt zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen zurück."}},
		[9735] = {"Wahre Meister des Lichts",nil, {"Bringt Lord Solanar Blutzorn in Silbermond eine Phiole Weihwasser von Tyrs Hand."}},
		[9736] = {"Wahre Meister des Lichts",nil, {"Bringt Lord Solanar Blutzorn in Silbermond 1 arkanen Katalysator, 1 Einheit krepuskulares Pulver, 1 azerothischen Diamanten und 1 makellosen schwarzen Diamanten."}},
		[9737] = {"Wahre Meister des Lichts",nil, {"Verwendet die Löschmischung in der Nähe der ewigen Flamme in der Alonsuskapelle, um den Schutz des Lichts zu brechen. Seid bereit, jeden zu töten, der versucht, die Kapelle zu verteidigen."}},
		[9738] = {"Bei der Arbeit verschollen",nil, {"Findet heraus, was mit Erdbinder Rayge, Naturalist Biss, Jäter Gründaum und Windrufer Klaue geschehen ist. Kehrt dann zu Behüterin Jhang im Echsenkessel in den Zangarmarschen zurück."}},
		[9739] = {"Die Misere der Sporlinge",nil, {"Fahssn im Pilzgeflecht möchte, dass Ihr 10 reife Sporenbeutel sammelt."}},
		[9740] = {"Das Sonnentor",nil, {"Verteidiger Aesom bei der Blutwacht möchte, dass Ihr das Sonnentor zerstört."}},
		[9741] = {"Geschöpfe aus der Leere",nil, {"Tötet 12 Geschöpfe der Leere und erstattet Verteidiger Aesom bei der Blutwacht Bericht."}},
		[9742] = {"Noch ein paar Sporensäcke",nil, {"Habt Ihr noch mehr Sporensäcke gerettet, <Volk>?"}},
		[9743] = {"Natürliche Feinde",nil, {"Bringt 6 Sumpflordranken zu Fahssn im Pilzgeflecht in den Zangarmarschen."}},
		[9744] = {"Mehr Ranken!",nil, {"Ihr habt viele Riesen getötet, aber es sind immer noch viele übrig. Und sie greifen immer noch unsere Sporenbeutel an!"}},
		[9746] = {"Grenze der Erschöpfung",nil, {"Verteidiger Aesom bei der Blutwacht möchte, dass Ihr 10 Pyromanten der Sonnenfalken und 10 Verteidiger der Sonnenfalken tötet."}},
		[9747] = {"Der Umbrafennstamm",nil, {"Ikeyen in der Zuflucht des Cenarius möchte, dass Ihr nach Umbrafenn reist und Kataru, 8 Seher von Umbrafenn, 6 Hexendoktoren von Umbrafenn und 6 Orakel von Umbrafenn tötet."}},
		[9748] = {"Trinkt nicht das Wasser",nil, {"Verteidiger Aesom bei der Blutwacht möchte, dass Ihr den Wasserprobenbehälter verwendet, um eine Wasserprobe der Blutmythosinsel zu entnehmen."}},
		[9749] = {"Sie leben! Vielleicht...",nil, {"Forscher Cornelius hat Euch darum gebeten, 5 Expeditionsforscher zu befreien."}},
		[9750] = {"UNUSED Urgent Delivery",nil, nil},
		[9751] = {"Das Vermächtnis des Blutfluchs",nil, {"Admiral Odesyus hat Euch gebeten, nach Kapitän Edward Hanes zu suchen und mit ihm zu sprechen."}},
		[9752] = {"Flucht aus Umbrafenn",nil, {"Begleitet Kayra Langmähne zur Zuflucht des Cenarius in den Zangarmarschen. Gebt Ysiel Windsänger Bescheid, sobald Ihr diese Aufgabe erledigt habt."}},
		[9753] = {"Was wir wissen...",nil, {"Sprecht mit Exarch Admetius bei der Blutwacht."}},
		[9756] = {"Was wir nicht wissen...",nil, {"Sprecht mit einem gefangenen Agenten der Sonnenfalken bei der Blutwacht und eignet Euch Informationen der Sonnenfalken an. Kehrt zu Exarch Admetius zurück, sobald Ihr Eure Aufgabe erfüllt habt."}},
		[9757] = {"Sucht Jägerin Kella Nachtbogen auf",nil, {"Sprecht mit Jägerin Kella Nachtbogen nahe Valaars Steg."}},
		[9758] = {"Rückkehr zu Arkanist Vandril",nil, {"Kehrt zu Arkanist Vandril in Tristessa in den Geisterlanden zurück."}},
		[9759] = {"Ihre Welt enden lassen",nil, {"Folgt und beschützt Demolierer Legoso, während er versucht, die Vektorspule zu zerstören und tötet Sironas. Kehrt zu Exarch Admetius bei der Blutwacht zurück, sobald Ihr Eure Aufgabe erfüllt habt."}},
		[9760] = {"Verteidigers Ruh'",nil, {"Sprecht mit Verteidigerin Corin bei Verteidigers Ruh' auf der Blutmythosinsel."}},
		[9761] = {"Den Weg frei machen",nil, {"Verteidigerin Corin bei Verteidigers Ruh' möchte, dass Ihr 8 Saboteure der Sonnenfalken und 8 Agenten der Sonnenfalken tötet."}},
		[9762] = {"Die ungeschriebene Prophezeiung",nil, {"Diese Inseln werden für Tausende ein Zuhause sein. Die meisten werden ihr gesamtes Leben hier verbringen. Ihr könntet das auch, aber es ist nicht, was Ihr wollt, oder <Name>?nnnnIhr werdet in Eurem Leben noch viele großartige Taten vollbringen. Ihr müsst Euer Schicksal umarmen und es willkommen heißen!nnBevor Ihr geht, möchte ich Euch noch zwei Geschenke überreichen: Dieser Wappenrock wird nur von denen getragen, die den höchsten Respekt der Hand von Argus genießen; und nur die Weisesten besitzen diesen Siegelring. Tragt beides mit Stolz, <Name>."}},
		[9763] = {"Das Versteck des Kriegsherren",nil, {"Behüterin Jhang möchte, dass Ihr Kriegsherr Kalithresh im Echsenkessel findet und tötet."}},
		[9764] = {"Befehle von Lady Vashj",nil, {"Bringt die Befehle von Lady Vashj zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen."}},
		[9765] = {"Kriegsvorbereitungen",nil, {"Besorgt die Waffen des Echsenkessels von den Naga im Echsenkessel und kehrt dann zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen zurück."}},
		[9766] = {"Waffen des Echsenkessels",nil, {"Wir werden den eigenen Stahl der Naga in Waffen gegen sie umschmieden. Bringt mir alle Waffen, die Ihr aus dem Echsenkessel besorgen könnt."}},
		[9767] = {"Den Feind kennen",nil, {"Besorgt ein Zauberbuch des Echsenkessels von den Naga im Echsenkessel und bringt es zu Ashyen in der Zuflucht des Cenarius in den Zangarmarschen."}},
		[9769] = {"Mode kann man nicht erklären",nil, {"Bringt 8 lichtdurchlässige Flügel von einer beliebigen Glühwürmchenart der Zangarmarschen zu Magasha im Sumpfrattenposten."}},
		[9770] = {"Bedrohliche Marschenfänge",nil, {"Reavij im Sumpfrattenposten möchte, dass Ihr 10 Marschfangreißer tötet."}},
		[9771] = {"Auf der Suche nach Späher Jyoba",nil, {"Sucht Späher Jyoba."}},
		[9772] = {"Jyobas Bericht",nil, {"Besorgt Späher Jyobas Bericht von den siechenden Riesen und kehrt dann zu Zurai im Sumpfrattenposten zurück."}},
		[9773] = {"Keine Pilze mehr!",nil, {"Bringt 8 Aalfilets von Umbrafennaalen zu Magasha im Sumpfrattenposten."}},
		[9774] = {"Dicke Hydraschuppen",nil, {"Bringt 12 dicke Hydraschuppen zu Zurai im Sumpfrattenposten."}},
		[9775] = {"Bericht an Schattenjäger Denjai",nil, {"Bringt Zurais Bericht zu Schattenjäger Denjai in Zabra'jin."}},
		[9776] = {"Die Oreborzuflucht",nil, {"Sprecht mit Ikuti in der Oreborzuflucht."}},
		[9777] = {"Fulgorsporen",nil, {"Bringt 6 Fulgorsporen zu Ruam in Telredor."}},
		[9778] = {"Aufseher Hamuht",nil, {"Meldet Euch bei Aufseher Hamuht in der Zuflucht des Cenarius in den Zangarmarschen."}},
		[9779] = {"Abfangen der Nachricht",nil, {"Tötet die Elfen der Sonnenfalken, bis Ihr das Schreiben der Sonnenfalken findet, und bringt es zu Verteidiger Boros bei der Blutwacht."}},
		[9780] = {"Aalfilets von Umbrafenn",nil, {"Bringt 8 Aalfilets zu Noraani in Telredor."}},
		[9781] = {"Zu viele Mäuler zu stopfen",nil, {"Tötet 10 Morasthydras und kehrt zu Haalrun in Telredor zurück."}},
		[9782] = {"Das Todesmoor",nil, {"Bringt eine Erdprobe vom Todesmoor zu Verteidiger Idaar in Telredor."}},
		[9783] = {"Eine unnatürliche Dürre",nil, {"Verteidiger Idaar in Telredor möchte, dass Ihr 12 siechende Riesen tötet."}},
		[9784] = {"Pflanzenteile identifizieren",nil, {"Ich könnte noch mehr Pflanzenteile brauchen. Jetzt, da die Seen ihr Wasser verlieren, befürchte ich, dass die fremden Arten zunehmen werden. Ich brauche aber mehr Beweise, um meine Theorie zu untermauern."}},
		[9785] = {"Der Segen der Uralten",nil, {"Windruferin Schwarzhuf möchte, dass Ihr mit den Urtumen Ashyen und Keleth in der Zuflucht des Cenarius sprecht und um ihren Segen bittet."}},
		[9786] = {"Die Ruinen von Boha'mu",nil, {"Anachoret Ahuurn in Telredor möchte, dass Ihr die Ruinen von Boha'mu erforscht."}},
		[9787] = {"Götzen des Wildfennstamms",nil, {"Sammelt 6 Wildfenngötzen und kehrt dann zu Anachoret Ahuurn in Telredor zurück."}},
		[9788] = {"Ein feuchter, düsterer Ort",nil, {"Sucht nach Ikeyens Habseligkeiten in einer Höhle südlich von Umbrafenn. Bringt sie zurück zu Ikeyen in der Zuflucht des Cenarius in den Zangarmarschen."}},
		[9789] = {"Beherrschen der Grollhufjagd",nil, {"Erlegt 30 Grollhufe und kehrt anschließend zu Hemet Nesingwary bei Nesingwarys Safari in Nagrand zurück, um Euch zu beweisen."}},
		[9790] = {"Lichtdurchlässige Flügel",nil, {"Bringt 8 lichtdurchlässige Flügel von einer beliebigen Glühwürmchenart der Zangarmarschen zu Haalrun in Telredor."}},
		[9791] = {"Bedrohliche Marschenfänge",nil, {"Tötet 10 Marschenfangreißer und kehrt dann zu Noraani in Telredor zurück."}},
		[9792] = {"Eine Botschaft für Telaar",nil, {"Sprecht mit Nahuud in Telaar in Nagrand."}},
		[9793] = {"Das Schicksal von Tuurem",nil, {"Bringt Verteidiger Idaars Brief zu Andarl in Allerias Feste in den Wäldern von Terokkar."}},
		[9794] = {"Keine Zeit für Neugier",nil, {"Bringt Timothys Brief zu Kialon in Sylvanaar im Schergrat."}},
		[9795] = {"Die Ogerbedrohung",nil, {"Reist zur Donnerfeste im Schergrat und sprecht mit Nekthar."}},
		[9796] = {"Nachricht aus den Zangarmarschen",nil, {"Sprecht mit Mokasa in der Steinbrecherfeste in den Wäldern von Terokkar."}},
		[9797] = {"Verstärkung für Garadar",nil, {"Sprecht mit Hauptmann Kroghan in Garadar in Nagrand."}},
		[9798] = {"Blutelfenpläne",nil, {"Bringt die Blutelfenpläne zu Verteidiger Aldar bei der Absturzstelle im Am'mental."}},
		[9799] = {"Botanische Hilfsarbeit",nil, {"Sammelt 3 verderbte Blumen und kehrt zu Lehrling Vishael bei der Absturzstelle im Am'mental zurück."}},
		[9800] = {"Eine seltene Bohne",nil, {"Bringt 15 verdaute Caracolibohnen zu Elementarist Lo'ap am Thron der Elemente in Nagrand."}},
		[9801] = {"Die Reagenzien sammeln",nil, {"Anachoret Ahuurn in Telredor möchte, dass Ihr ihm 4 Sporensegleraugen und 4 Fennschreitertentakel bringt."}},
		[9802] = {"Pflanzen der Zangarmarschen",nil, {"Bringt 10 unbekannte Pflanzenteile zu Lauranna Thar'well in der Zuflucht des Cenarius in den Zangarmarschen."}},
		[9803] = {"Botschaft für den Wildfennstamm",nil, {"Trinkt Ahuurns Elixier in den Ruinen von Boha'mu. Sprecht mit dem Ältesten Kuruti in der Form eines Arakkoas."}},
		[9804] = {"Aufgebrachte Geister des Himmelsweisensees",nil, {"Elementarist Lo'ap am Thron der Elemente in Nagrand möchte, dass Ihr 12 Seegeister tötet."}},
		[9805] = {"Segen des Incineratus",nil, {"Elementarist Lo'ap am Thron der Elemente in Nagrand möchte, dass Ihr die große Hütte, die westliche Hütte, die südliche Hütte und die östliche Hütte in Schilftanz zerstört. Bringt das beseelte Feuer zurück zu Elementarist Lo'ap, wenn Ihr fertig seid."}},
		[9806] = {"Fruchtbare Sporen",nil, {"Sammelt 6 fruchtbare Sporen von den Sporenseglern und den Marschenläufern der Zangarmarschen und kehrt danach zu Gshaff in Sporeggar zurück."}},
		[9807] = {"Noch mehr fruchtbare Sporen",nil, nil},
		[9808] = {"Glühkappenpilze",nil, {"Bringt 10 Glühkappen zu Msshi'fn in Sporeggar in den Zangarmarschen."}},
