		[10082] = {"Die aufgebrachten Ahnen",nil, {"Mutter Kashur möchte, dass Ihr 15 aufgebrachte Orcgeister tötet."}},
		[10084] = {"Angriff auf Mageddon",nil, {"Tötet Nachtlord Malphas im Konstruktionslager: Mageddon und meldet Euch dann bei Truppenkommandant Danath Trollbann in der Ehrenfeste."}},
		[10085] = {"Ein Besuch bei den Ahnen",nil, {"Mutter Kashur im Ahnengrund in Nagrand möchte, dass Ihr den Sonnenwindposten, die Ruinen des Lachenden Schädels, Garadar und die Ruinen des Blutenden Auges besucht."}},
		[10086] = {"Ich arbeite... für die Horde!",nil, {"Bringt 8 Stück gebrauchtes Metall und 8 Stück gebrauchtes Holz zu Megzeg Knaxknöchel in Thrallmar."}},
		[10087] = {"Zündet es an... für die Horde!",nil, {"Zündet die östliche und die westliche Kanone der Allianz mit der flammenden Fackel an und kehrt danach zu Megzeg Knaxknöchel in Thrallmar zurück."}},
		[10088] = {"Bringt diese Mine zum Beben!",nil, {"Tötet 12 Pioniere der Gan'arg und kehrt dann zu Großknecht Razelcraz zurück."}},
		[10089] = {"Konstruktionslager der Legion",nil, {"Tötet 10 Diener der Gan'arg, 5 Legionäre des Konstruktionslagers und 5 Schwestern des Leids. Durchsucht danach des Konstruktionslager: Zorn nach einem Hinweis auf die Absichten der Legion."}},
		[10090] = {"Die Pläne der Legion",nil, {"Besorgt die Pläne des Konstruktionslagers: Bosheit und bringt sie zu Oberanführer Hun Stümmelfaust im Häschersturz."}},
		[10091] = {"Seeleninstrumente",nil, {"Stehlt 5 Seeleninstrumente und bringt sie zu Spionagemeisterin Mehlisah Hochkrone auf der Terrasse des Lichts in Shattrath."}},
		[10092] = {"Angriff auf Mageddon",nil, {"Tötet Nachtlord Malphas im Konstruktionslager: Mageddon und meldet Euch dann bei Oberanführer Hun Stümmelfaust im Häschersturz."}},
		[10093] = {"Der Tempel von Telhamat",nil, {"Sprecht mit Amaan dem Weisen beim Tempel von Telhamat im Westen der Höllenfeuerhalbinsel."}},
		[10094] = {"Der Kodex des Blutes",nil, {"Lest den Kodex des Blutes im Schattenlabyrinth von Auchindoun."}},
		[10095] = {"In das Herz des Labyrinths",nil, {"Vernichtet Murmur und informiert danach Spionagemeisterin Mehlisah Hochkrone auf der Terrasse des Lichts in Shattrath über die Geschehnisse im Schattenlabyrinth."}},
		[10096] = {"Rettung der Sporloks",nil, {"Tötet 10 Marschengräber und 10 Marschenlauerer und kehrt dann zu Lauranna Thar'well in der Zuflucht des Cenarius in den Zangarmarschen zurück."}},
		[10097] = {"Bruder gegen Bruder",nil, {"Tötet Dunkelwirker Syth in den Sethekkhallen und befreit Lakka. Kehrt zu Isfar außerhalb der Sethekkhallen zurück, wenn Ihr die Aufgabe abgeschlossen habt."}},
		[10098] = {"Terokks Erbe",nil, {"Bringt Terokks Maske, Terokks Federkiel und die Sage von Terokk zu Isfar außerhalb der Sethekkhallen."}},
		[10099] = {"Das Genie",nil, {"Sucht und tötet Z'kral in den Minen unter der Ehrenfeste und meldet Euch dann bei Großknecht Biggums zurück."}},
		[10100] = {"Das Genie",nil, {"Sucht und tötet Urga'zz in den Minen unter Thrallmar und kehrt dann zu Großknecht Razelcraz zurück."}},
		[10101] = {"Wenn Geister sprechen",nil, {"Mutter Kashur im Ahnengrund in Nagrand bittet Euch, Eurer Vision auf den Grund zu gehen. Findet K'ure in Oshu'gun."}},
		[10102] = {"Ein gelüftetes Geheimnis",nil, {"Sprecht mit A'dal in Shattrath."}},
		[10103] = {"Bericht an Zurai",nil, {"Meldet Euch bei Zurai im Sumpfrattenposten in den Zangarmarschen."}},
		[10104] = {"Sorgen um Tuurem",nil, {"Sprecht mit Andarl in Allerias Feste in den Wäldern von Terokkar."}},
		[10105] = {"Nachrichten für Rakoria",nil, {"Sprecht mit Rakoria in der Steinbrecherfeste in den Wäldern von Terokkar."}},
		[10106] = {"Höllenfeuerbefestigungen",nil, {"Nehmt den Rundblick, das Stadion und die Trümmerbastion ein, und kehrt dann zu Stabsfeldwebel Tracy Stolzbrunn in der Ehrenfeste auf der Höllenfeuerhalbinsel zurück."}},
		[10107] = {"Diplomatische Maßnahmen",nil, {"Jorin Totauge in Garadar in Nagrand möchte, dass Ihr Lantresor von der Klinge in den Ruinen der Brennenden Klinge findet und mit ihm sprecht."}},
		[10108] = {"Diplomatische Maßnahmen",nil, {"Mo'mor der Brecher in Telaar in Nagrand möchte, dass Ihr Lantresor von der Klinge in den Ruinen der Brennenden Klinge findet und mit ihm sprecht."}},
		[10109] = {"Ich muss sie haben!",nil, {"Wazdaz gegenüber der Klanwacht im Süden von Nagrand möchte, dass Ihr ihm 3 Einheiten Luftelementargas bringt."}},
		[10110] = {"Höllenfeuerbefestigungen",nil, {"Nehmt den Rundblick, das Stadion und die Trümmerbastion ein, und kehrt dann zu Kriegsherold Blauauge in Thrallmar zurück."}},
		[10111] = {"Bringt mir das Ei!",nil, {"Wazdaz in Nagrand möchte, das Ihr den Spring-o-Mat 4000 benutzt, um in das Nest in dem nahe gelegenen Baum zu springen und das geheimnisvolle Ei zu stehlen."}},
		[10112] = {"Ein persönlicher Gefallen",nil, {"Holt 5 von Lathrais gestohlenen Waren zurück und bringt sie zu Windhändler Lathrai bei der Taverne Weltenend im unteren Viertel in Shattrath."}},
		[10113] = {"Nesingwarys Safari",nil, {"\"Die Flinte\" Jones möchte, dass Ihr mit Hemet Nesingwary in Nesingwarys Safari am nordöstlichen Rand von Nagrand sprecht."}},
		[10114] = {"Nesingwarys Safari",nil, {"Ohlorn Fernreiser möchte, dass Ihr mit Shado 'Fitz' Fernreiser in Nesingwarys Safari in Nagrand sprecht."}},
		[10115] = {"Dolchfennkrise",nil, {"Tötet 3 Auftragsmörder von Dolchfenn und 15 Matschkrabbler von Dolchfenn."}},
		[10116] = {"Gesucht: Häuptling Mummaki",nil, {"Bringt Häuptling Mummakis Totem zu Ikuti in der Oreborzuflucht."}},
		[10117] = {"Gesucht: Häuptling Mummaki",nil, {"Bringt Häuptling Mummakis Totem zu Schattenjäger Denjai."}},
		[10118] = {"Nachricht für Dolchfenn",nil, {"Tötet 3 Auftragsmörder von Dolchfenn und 15 Matschkrabbler von Dolchfenn und kehrt dann zu Schattenjäger Denjai in Zabra'jin zurück."}},
		[10119] = {"Durch das Dunkle Portal",nil, {"Reist durch das Dunkle Portal und sucht Kommandant Duron. Er wartet auf der anderen Seite auf Eure Ankunft - in der Scherbenwelt."}},
		[10120] = {"Ankunft in der Scherbenwelt",nil, {"Generalleutnant Orion beim Dunklen Portal auf der Höllenfeuerhalbinsel möchte, dass Ihr Orions Bericht zu Vlagga Freyfeder bringt."}},
		[10121] = {"Die Brennende Legion ausrotten",nil, {"Sprecht mit Unteroffizier Schädelbrecher bei der Versorgungskarawane außerhalb von Thrallmar im Osten."}},
		[10122] = {"Die Erstreckung der Zitadelle",nil, {"Durchsucht das nördlichste Lager entlang des Südlichen Bollwerks nach einer Spur von Gefreiter Imarion."}},
		[10123] = {"Teufelsfunkenklamm",nil, {"Unteroffizier Schädelbrecher bei der Versorgungskarawane östlich von Thrallmar auf der Höllenfeuerhalbinsel möchte, dass Ihr 4 Flammenschürerwichtel, 6 höllische Kriegshetzer und 1 Schreckensrufer tötet."}},
		[10124] = {"Vorposten: Häschersturz",nil, {"Meldet Euch bei Vorpostenkommandant To'arch im Häschersturz."}},
		[10125] = {"Mission: Die Kommunikation unterbrechen",nil, {"Vorpostenkommandant To'arch im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr To'archs Sprengstoff benutzt, um den Sender der Legion: Bosheit, Sender der Legion: Zorn, Sender der Legion: Hass und Sender der Legion: Furcht zu zerstören."}},
		[10126] = {"Kriegsboss Nekroggs Befehle",nil, {"Reist entlang der westlichen Seite des Südlichen Bollwerks nach Süden und durchsucht das nächste Lager nach einer Spur von Gefreiter Imarion."}},
		[10127] = {"Mission: Das Band zerreißen",nil, {"Vorpostenkommandant To'arch am Häschersturz auf de Höllenfeuerhalbinsel möchte, dass Ihr 4 Teufelstreiber, 8 Zornwächter und Unterwerferin Shi'aziv tötet."}},
		[10128] = {"Der Soldat Imarion",nil, {"Setzt Euren Weg entlang des Südlichen Bollwerks fort und sucht nach dem Ort, an dem Kriegsboss Nekrogg Gefreiter Imarion gefangen hält."}},
		[10129] = {"Mission: Tore Murketh und Shaadraz",nil, {"Sprecht mit Schwadronskommandant Brack, bombardiert Tor Murketh und Tor Shaadrez und kehrt dann zu Vorpostenkommandant To'arch im Häschersturz zurück."}},
		[10130] = {"Die westliche Flanke",nil, {"Tötet 10 Wachen der Zerschmetterten Hand, 10 Berserker der Zerschmetterten Hand und 5 Akolythen der Zerschmetterten Hand und meldet Euch dann bei Leutnant Amadi im westlichen Wachturm der Ehrenfeste."}},
		[10131] = {"Fluchtplan",nil, {"Tötet einen Grenadier des Lachenden Schädels und bringt seine primitive Sprengladung zu Gefreiter Imarion im Außenposten von Gor'gaz."}},
		[10132] = {"Eine kolossale Bedrohung",nil, {"Tötet 5 tobende Kolosse und kehrt dann zu Tola'thion im Posten des Cenarius auf der Höllenfeuerhalbinsel zurück."}},
		[10133] = {"Mission: Tötet den Boten",nil, {"Vorpostenkommandant To'arch im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr Arix'maladish findet und tötet und den Schriftwechsel der Legion besorgt."}},
		[10134] = {"Hinweise des purpurnen Kristalls",nil, {"Bringt den Purpurkristallsplitter zu Tola'thion im Posten des Cenarius auf der Höllenfeuerhalbinsel."}},
		[10135] = {"Mission: Seid der Bote",nil, {"Bringt den Schriftwechsel der Legion zu Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel."}},
		[10136] = {"Grausame Pläne",nil, {"Tötet Arazzius den Grausamen und kehrt danach zu Nazgrel in Thrallmar zurück."}},
		[10137] = {"Den Kriegsboss provozieren",nil, {"Benutzt die primitiven Sprengladungen um einen Tumult zu veranstalten und Kriegsboss Nekrogg aus seinem Turm zu locken. Tötet ihn, besorgt Nekroggs Schlüssel und öffnet damit Gefreiter Imarions Käfig. Wenn Ihr Gefreiter Imarion befreit habt, meldet Euch bei Leutnant Amadi."}},
		[10138] = {"Auf wessen Befehl hin?",nil, {"Tötet 20 Grunzer der Zerschmetterten Hand, 10 Hexenmeister der Zerschmetterten Hand und besorgt Euch Drillmeister Zurok's Anweisungen. Kehrt dann zu Leutnant Amadi im westlichen Wachturm der Ehrenfeste zurück."}},
		[10139] = {"Die Beseitigung des Kommandanten",nil, {"Tötet Truppenkommandant Gorax und meldet Euch dann bei Leutnant Amadi im westlichen Turm der Ehrenfeste zurück."}},
		[10140] = {"Reise zur Ehrenfeste",nil, {"Fliegt zur Ehrenfeste und übergebt Marschall Isildor Durons Bericht."}},
		[10141] = {"Wiedergeburt der Brennenden Legion",nil, {"Sprecht mit Unteroffizier Altumus bei der östlichen Versorgungskarawane außerhalb der Ehrenfeste im Osten."}},
		[10142] = {"Der Pfad der Pein",nil, {"Unteroffizier Altumus bei der östlichen Versorgungskarawane östlich der Ehrenfeste auf der Höllenfeuerhalbinsel möchte, dass Ihr 4 Flammenschürerwichtel, 6 höllische Kriegshetzer und 1 Schreckensrufer tötet."}},
		[10143] = {"Expeditionsposten",nil, {"Meldet euch bei Vorpostenkommandant Wa'ppun im Expeditionsposten."}},
		[10144] = {"Die Kommunikation unterbrechen",nil, {"Besiegt die Feinde um Portal Grimh und Portal Kaalez herum, um dämonische Runensteine zu sammeln. Benutzt Wa'ppuns Zünder in Portal Grimh und Portal Kaalez. Kehrt danach mit dem Zünder zu Vorpostenkommandant Wa'ppun zurück."}},
		[10145] = {"Mission: Sever the Tie UNUSED",nil, {"Vorpostenkommandant Wa'ppun beim Expeditionsposten auf de Höllenfeuerhalbinsel möchte, dass Ihr 4 Teufelstreiber, 8 Zornwächter und Unterwerferin Yalqiz tötet."}},
		[10146] = {"Mission: Tor Murketh und Tor Shaadraz",nil, {"Sprecht mit Schwadronskommandant Dabir'ee, bombardiert Tor Murketh und Tor Shaadraz und kehrt dann zu Vorpostenkommandant Wa'ppun am Expeditionsposten zurück."}},
		[10147] = {"Mission: Tötet den Boten",nil, {"Vorpostenkommandant Königstein im Expeditionsposten auf der Höllenfeuerhalbinsel möchte, dass Ihr Arix'maladish findet und tötet und den Schriftwechsel der Legion besorgt."}},
		[10148] = {"Mission: Seid der Bote",nil, {"Bringt den Schriftwechsel der Legion zu Truppenkommandant Danath Trollbann in der Ehrenfeste auf der Höllenfeuerhalbinsel."}},
		[10149] = {"Mission: Beendet alles, seid alles",nil, {"Truppenkommandant Danath Trollbann in der Ehrenfeste auf der Höllenfeuerhalbinsel möchte, dass Ihr Arazzius den Grausamen tötet."}},
		[10150] = {"Die Erstreckung der Zitadelle",nil, {"Durchsucht das nördlichste Lager entlang des Nördlichen Bollwerks nach einer Spur von Späherin Makha."}},
		[10151] = {"Kriegsboss Nekroggs Befehle",nil, {"Reist entlang der westlichen Seite des Nördlichen Bollwerks nach Süden und durchsucht das nächste Lager nach einer Spur von Späherin Makha."}},
		[10152] = {"Die westliche Flanke",nil, {"Tötet 10 Wachen der Zerschmetterten Hand, 10 Berserker der Zerschmetterten Hand und 5 Akolythen der Zerschmetterten Hand und meldet Euch dann bei Steingardist Ambelan im Rückenbrecherposten in der Nähe von Zeth'Gor."}},
		[10153] = {"Die Späherin Makha",nil, {"Setzt Euren Weg über den Pfad des Ruhms fort und sucht nach dem Ort, an dem Kriegsboss Nekrogg Späherin Makha gefangen hält."}},
		[10154] = {"Fluchtplan",nil, {"Tötet einen Grenadier des Lachenden Schädels und bringt seine primitive Sprengladung zu Späherin Makha im Außenposten von Gor'gaz."}},
		[10155] = {"Den Kriegsboss provozieren",nil, {"Benutzt die primitive Sprengladung um einen Tumult zu veranstalten und Kriegsboss Nekrogg aus seinem Turm zu locken. Tötet ihn, besorgt Nekroggs Schlüssel und öffnet damit Späherin Makhas Käfig. Wenn Ihr Späherin Makha befreit habt, meldet Euch bei Steingardist Ambelan im Rückenbrecherposten in der Nähe von Zeth'Gor."}},
		[10156] = {"Auf wessen Befehl hin?",nil, {"Tötet 20 Grunzer der Zerschmetterten Hand, 10 Hexenmeister der Zerschmetterten Hand und besorgt Euch Drillmeister Zurok's Anweisungen. Kehrt dann zu Steingardist Ambelan im Rückenbrechergrat zurück."}},
		[10157] = {"Die Beseitigung des Kommandanten",nil, {"Tötet Truppenkommandant Gorax und meldet Euch dann bei Schattenjäger Ty'jin in Thrallmar zurück."}},
		[10158] = {"Vorräte des Blutenden Auges",nil, {"Zerstört 6 Stapel Vorräte des Blutenden Auges und meldet Euch dann bei Steingardist Ambelan im Rückenbrecherposten zurück."}},
		[10159] = {"Haltet den Dornnebelhügel sauber!",nil, {"Tötet 8 Dornenfangfelshetzer und 8 Dornenfanggiftspucker und kehrt dann zu Mahuram Starkhuf im Posten des Cenarius auf der Höllenfeuerhalbinsel zurück."}},
		[10160] = {"Den Feind kennen",nil, {"Sprecht mit Leutnant Amadi im alten Wachturm im Westen der Ehrenfeste."}},
		[10161] = {"Im Notfall...",nil, {"Sammelt 30 Zeppelintrümmer und bringt sie zu \"Schreihals\" Quack Lufthans bei der Zeppelinabsturzstelle."}},
		[10162] = {"Mission: Die Abyssische Untiefe",nil, {"Vorpostenkommandant To'arch im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen vernichtet."}},
		[10163] = {"Mission: Die Abyssische Untiefe",nil, {"Schwadronskommandant Greifgar im Trümmerposten auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen vernichtet."}},
		[10164] = {"Alles wird gut",nil, {"Großvater Aldrimus außerhalb der Auchenaikrypta von Auchindoun möchte, dass Ihr in die Auchenaikrypta geht und Exarch Maladaar vernichtet."}},
		[10165] = {"Die Konkurrenz untergraben",nil, {"Nexusprinz Haramad außerhalb der Managruft möchte, dass Ihr Nexusprinz Shaffar tötet und ihm Shaffars Bandagen bringt."}},
		[10166] = {"Weißborkes Erinnerung",nil, {"Larianna Flusswind möchte, dass Ihr Weißborkes Anhänger beim Runenstein im versengten Hain vergrabt."}},
		[10167] = {"Auchindoun...",nil, {"Reist zur Auchenaikrypta in der Knochenwüste der Wälder von Terokkar und tötet Exarch Maladaar um den Geist von D'ore zu befreien."}},
		[10168] = {"Was die Seele sieht",nil, {"Sucht einen Seelenspiegel in der Auchenaikrypta und benutzt ihn, um einen getrübten Geist eines uralten Orcahnen von Nagrand zu rufen. Zerstört 15 getrübte Geister, damit die Ahnen in Frieden ruhen können. Kehrt zu Mutter Kashur im Ahnengrund zurück, wenn Ihr fertig seid."}},
		[10169] = {"In Würde verlieren",nil, {"Magister Falris in Shattrath möchte, dass Ihr die Phiole mit Beruhigungsserum zur Terrasse des Lichts bringt und sie in den Kochtopf von Haronems Regiment schüttet."}},
		[10170] = {"Rückkehr zur Großmutter",nil, {"Kehrt nach Garadar zurück und sprecht mit Großmutter Geyah."}},
		[10171] = {"Der untröstliche Häuptling",nil, {"Großmutter Geyah in Garadar möchte, dass Ihr mit Garrosh sprecht."}},
		[10172] = {"Es gibt keine Hoffnung",nil, {"Sprecht mit Großmutter Geyah und erzählt ihr, was Garrosh Euch gesagt hat."}},
		[10173] = {"Der Stab des Erzmagiers",nil, {"Besorgt Erzmagier Vargoths Stab von Ekkorash der Inquisitorin und bringt ihn zu Ravandwyr in Area 52. Um Ekkorash aus ihrem Versteck hervorzulocken, streut das Beschwörungspulver auf die Kohlenpfanne in der Mitte der Ruinen."}},
		[10174] = {"Der Fluch des violetten Turms",nil, {"Benutzt Erzmagier Vargoths Stab um Kontakt mit Erzmagier Vargoth aufzunehmen. Falls Ihr Erzmagier Vargoths Stab verlieren solltet, bittet Ravandwyr in Area 52 um Ersatz."}},
		[10175] = {"Thrall, Sohn Durotans",nil, {"Reist nach Orgrimmar und überbringt Thrall Geyahs Nachricht."}},
		[10176] = {"Ar'kelos der Wächter",nil, {"Betretet den violetten Turm in der Mitte von Kirin'Var und besiegt Ar'kelos. Sprecht danach mit Erzmagier Vargoth."}},
		[10177] = {"Ärger in Auchindoun",nil, {"Meldet Euch bei Feldkommandeur Mahfuun beim Eingang zum Schattenlabyrinth in Auchindoun in den Wäldern von Terokkar."}},
		[10178] = {"Findet Spion To'gun",nil, {"Findet Spion To'gun im Schattenlabyrinth von Auchindoun."}},
		[10179] = {"Der Hüter von Kirin'Var",nil, {"Sprecht mit Hüter Dierwert in Kirin'Var im Nethersturm."}},
		[10180] = {"Kann es einfach nicht lassen",nil, {"Sprecht mit Isfar beim Eingang zu den Sethekkhallen in Auchindoun."}},
		[10182] = {"Kampfmagier Dathric",nil, {"Sucht die Wohnung von Kampfmagier Dathric in der Zaubergasse und bringt die Klinge zurück in sein Waffenregal. Sprecht danach mit Hüter Dierwert in Kirin'Var."}},
		[10183] = {"Auf nach Area 52",nil, {"Bringt die Notizen des Netheorologen zu Raketenchef Doppeldecker in Area 52 im Nethersturm."}},
		[10184] = {"Bösartige Überbleibsel",nil, {"Hüter Dierwert in Kirin'Var möchte, dass Ihr 20 abtrünnige Geister auf dem Ratsplatz tötet."}},
		[10185] = {"Schlimmer als der Tod",nil, {"Tötet 10 Magiertöter und 10 Manasucher und kehrt zu Hüter Dierwert in Kirin'Var zurück."}},
		[10186] = {"Ihr seid eingestellt!",nil, {"Sammelt 10 Astrallithiummatrixkristalle und kehrt dann zu Raketenchef Doppeldecker in Area 52 im Nethersturm zurück."}},
		[10187] = {"Eine Nachricht für den Erzmagier",nil, {"Sprecht mit Erzmagier Vargoth im Violetten Turm im Nethersturm."}},
		[10188] = {"Das Siegel von Krasus",nil, {"Holt Euch das Siegel von Krasus von Zauberbinderin Maryana in der Manaschmiede Coruu, nordwestlich vom violetten Turm. Wenn Ihr das Siegel bekommen habt, benutzt Erzmagier Vargoths Stab, um ein Bild von ihm zu rufen und ihn über Eure Fortschritte zu informieren. Falls Ihr Erzmagier Vargoths Stab verlieren solltet, bittet Ravandwyr in Area 52 um Ersatz."}},
		[10189] = {"Manaschmiede B'naar",nil, {"Spionagemeister Thalodien in Area 52 möchte, dass Ihr zum Rand der Manaschmiede B'naar geht und die Stammrolle von B'naar von Hauptmann Arathyn besorgt. Das Abschließen von Aufgaben für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10190] = {"Batterien aufladen",nil, {"Benutzt den Batterieladeblaster bei genügend Phasenjägern, um den Batterieladestatus auf 10 zu bringen. Bringt die Batterie danach zu Bot-Spezialistin Alley in den Ruinen von Enkaat im Nethersturm zurück."}},
		[10191] = {"Nummer V lebt!",nil, {"Begleitet Maxx E. Million Nr. V durch die Ruinen von Enkaat und wieder in Sicherheit. Sprecht dann mit Bot-Spezialistin Alley außerhalb der Ruinen von Enkaat im Nethersturm."}},
		[10192] = {"Krasus' Kompendium",nil, {"Holt Euch Krasus' Kompendium - Kapitel 1, Krasus' Kompendium - Kapitel 2 und Krasus' Kompendium - Kapitel 3 vom Ratsplatz von Kirin'Var. Benutzt Erzmagier Vargoths Stab um den Erzmagier zu kontaktieren, wenn Ihr die Kapitel zusammen habt. Falls Ihr Erzmagier Vargoths Stab verlieren solltet, bittet Ravandwyr in Area 52 um Ersatz."}},
		[10193] = {"Hochrangige Ziele",nil, {"Spionagemeister Thalodien in Area 52 möchte, dass Ihr in die Manaschmiede B'naar geht und 2 Warpmeister des Sonnenzorns, 6 Warpingenieure des Sonnenzorns und 8 Geologen des Sonnenzorns tötet. Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10194] = {"Heimlicher Flug",nil, {"Sprecht mit Veronia außerhalb von Area 52 und fliegt zur Manaschmiede Coruu. Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10195] = {"Söldner sieht, Söldner handelt",nil, {"Bringt 30 Arakkoafedern zu Ros'eleth in Allerias Feste."}},
		[10196] = {"Mehr Arakkoafedern",nil, {"Bringt 30 Arakkoafedern zu Malukaz in der Steinbrecherfeste."}},
		[10197] = {"Eine überzeugende Verkleidung",nil, {"Bringt ein Set Arkanistenroben des Sonnenzorns, ein Paar Forscherhandschuhe des Sonnenzorns und ein Gardistenmedaillon des Sonnenzorns zu Caledis Morgenhell bei der Manaschmiede Coruu im Nethersturm. Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10198] = {"Informationen sammeln",nil, {"Benutzt die Sonnenzornverkleidung, geht in die Manaschmiede Coruu und belauscht das Gespräch zwischen Sonnenzornkommandant Morgenschmied und Arkanist Ardonis. Meldet Euch bei Caledis Morgenhell, wenn Ihr die Aufgabe abgeschlossen habt. Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10199] = {"Der kleine Extrakick",nil, {"Besorgt 5 Netherrochenstachel und bringt sie zu Stiefels in Area 52 im Nethersturm."}},
		[10200] = {"Rückkehr zu Thalodien",nil, {"Kehrt zu Spionagemeister Thalodien in Area 52 im Nethersturm zurück."}},
		[10201] = {"Der Moment der Wahrheit",nil, {"Geht zu Grek und bittet ihn, das Öl an seiner Waffe auszuprobieren. Bringt das Ergebnis zu Rakoria, wenn er fertig ist."}},
		[10202] = {"Ein Abtrünniger",nil, {"Geht zur Manaschmiede Ara und sprecht mit Magister Theledorn. Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10203] = {"Unschätzbare Ausrüstung beamen",nil, {"Benutzt den ultrastarken Dekonsolidierungsstrahler, um den Hyperdreh-Grab-o-Matik, die servopneumatische Baggerklaue, den multispektralen Geländeabtaster und den großen Wagen voller Sprengstoff zurück nach Area 52 zu beamen. Meldet Euch dann beim leitenden Ingenieur Zischeldruck beim östlichen Ende des Risses in Area 52 im Nethersturm."}},
		[10204] = {"Blutjuwelenkristalle",nil, {"Magistrix Larynna möchte, dass Ihr zur Manaschmiede B'naar geht und einen Blutjuwelensplitter von einem Magister des Sonnenzorns besorgt. Benutzt den Splitter in der Nähe von größeren Blutjuwelenkristallen und kehrt zu ihr in Area 52 zurück. Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10205] = {"Sphärenräuber Nesaad",nil, {"Tötet Sphärenräuber Nesaad und kehrt dann zu Netherpirscher Khay'ji in Area 52 im Nethersturm zurück."}},
		[10206] = {"Wählt Eure Rolle",nil, {"Sammelt 10 Stück Astraltechnologie und bringt sie zu Papa Mauschel in Area 52 im Nethersturm."}},
		[10207] = {"Forward Base: Reaver's Fall REUSE",nil, {"Meldet Euch bei Vorpostenkommandant To'arch im Häschersturz."}},
		[10208] = {"Die Verstärkung abschneiden",nil, {"Besiegt die Feinde um Portal Xilus und Portal Kruul herum, um dämonische Runensteine zu sammeln. Benutzt To'archs Zünder in Portal Xilus und Portal Kruul. Kehrt danach mit To'archs Zünder zu To'arch im Häschersturz auf der Höllenfeuerhalbinsel zurück."}},
		[10209] = {"Beschwörer Kanthins Preis",nil, {"Besiegt Beschwörer Kanthin in der Manaschmiede Duro auf der Insel nördlich des Sonnenzornpostens und holt den Stein von Gletscharius zurück. Wenn Ihr den Stein besorgt habt, benutzt Erzmagier Vargoths Stab, um den Erzmagier zu kontaktieren. Falls Ihr Erzmagier Vargoths Stab verlieren solltet, bittet Ravandwyr in Area 52 um Ersatz."}},
		[10210] = {"A'dal",nil, {"Sprecht mit A'dal, Anführer der Sha'tar in der Mitte der Terrasse des Lichts."}},
		[10211] = {"Stadt des Lichts",nil, {"Folgt Khadgars Diener und hört Euch an, was er zu erzählen hat. Kehrt nach dieser Aufgabe zu Khadgar zurück."}},
		[10212] = {"Held der Mag'har",nil, {"Kehrt zu Großmutter Geyah in Garadar in Nagrand zurück."}},
		[10213] = {"Untersuchung der Absturzstelle",nil, {"Durchsucht die Absturzstelle nach Überlebenden."}},
		[10214] = {"Bringt diese Mine zum beben!",nil, {"Tötet 15 Pioniere der Gan'arg und kehrt dann zu Großknecht Razelcraz zurück."}},
		[10216] = {"Sicherheit geht vor",nil, {"Konstrukteur Morphalius möchte, dass Ihr 10 astrale Grufträuber, 5 astrale Zauberer, 5 Nexuswandler und 5 astrale Zauberbinder tötet. Wenn Ihr damit fertig seid, sucht den astralen Transporter in der Kammer vor Shaffars Kammern und aktiviert ihn."}},
		[10218] = {"Jemandes Arbeit zahlt sich aus",nil, {"Begleitet Kryoingenieur Sha'heen sicher durch die Managruft, damit er die Astralmaterie aus Shaffars Astralsammlern entnehmen kann. Meldet Euch bei Nexusprinz Haramad außerhalb der Managruft beim Lager des Konsortiums, wenn Ihr erfolgreich wart."}},
		[10219] = {"Gassi gehen",nil, nil},
		[10220] = {"Bringt sie zum Zuhören",nil, {"Mordiba möchte, dass Ihr 12 unerschütterliche Fußsoldaten, 8 unerschütterliche Krieger und 6 unerschütterliche Zauberhexer beim Expeditionsrüstlager westlich des Rückenbrecherpostens tötet."}},
		[10221] = {"Dr. Bumm!",nil, {"Tötet Dr. Bumm und kehrt dann mit den guten Nachrichten zu dem leitenden Ingenieur Zischeldruck in Area 52 im Nethersturm zurück."}},
		[10222] = {"Die Garnison des Sonnenzornpostens",nil, {"Hüter Dierwert in Kirin'Var möchte, dass Ihr 10 Bogenschützen des Sonnenzorns tötet."}},
		[10223] = {"Nieder mit Daellis",nil, {"Hüter Dierwert in Kirin'Var möchte, dass Ihr Meister Daellis Morgenschlag tötet."}},
		[10224] = {"Essenzen für die Motoren",nil, {"Sammelt 7 Essenzen eines Managespensts und bringt sie zu Chefingenieur Trep bei Area 52 im Nethersturm."}},
		[10225] = {"Meldung beim Chefingenieur",nil, {"Bringt die Astrallithiummatrixkristalle zu Chefingenieur Trep in Area 52 im Nethersturm."}},
		[10226] = {"Elementarenergieextraktor",nil, {"Extrahiert 5 Einheiten Elementarenergie und bringt sie zu Chefingenieur Trep in Area 52 im Nethersturm."}},
		[10227] = {"Ich sehe tote Draenei",nil, {"Sprecht mit Ramdor dem Wahnsinnigen auf dem Knochenhaufen außerhalb der Auchenaikrypta."}},
		[10228] = {"Ezekiel",nil, {"Sprecht mit Ezekiel von Shattrath in Shattrath."}},
		[10229] = {"Den Folianten entziffern",nil, {"Bringt den geheimnisvollen Folianten zu Althen dem Historiker im Rückenbrecherposten."}},
		[10230] = {"Das Kampfhorn",nil, {"Besorgt Euch das Kampfhorn der Unerschütterlichen von Feldkommandant Thalvos und bringt es zu Althen dem Historiker im Rückenbrecherposten."}},
		[10231] = {"Welches Buch? Ich sehe kein Buch.",nil, {"Ezekiel sagt, dass das Buch gestohlen wurde. Eure einzige Spur führt Euch zu den Grobianen. Schaut nach, ob sie irgendwelche Informationen haben."}},
		[10232] = {"Legion in Trümmern",nil, {"Tötet 5 Schicksalsschmiede der Mo'arg und 15 Ingenieure der Gan'arg. Kehrt danach zu Papa Mauschel in Area 52 im Nethersturm zurück."}},
		[10233] = {"Den Sonnenzornposten anzünden",nil, {"Benutzt eine flammende Fackel, zerstört 4 Sonnenzornballisten und 4 Sonnenzornzelte und kehrt dann zu Zauberhexerleutnant Morran in Kirin'Var zurück."}},
		[10234] = {"Der Müll eines Dämons",nil, {"Sammelt 10 Teile eines Teufelshäschers und bringt sie zu Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
		[10235] = {"Verdammnisklaue die Krallen stutzen",nil, {"Besorgt Euch Verdammnisklaues Hand und bringt sie zu Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
		[10236] = {"Die Scherbenwelt ist doof",nil, {"Großknecht Razelcraz möchte, dass Ihr ihm 6 Schredderteile besorgt. Kehrt danach zu ihm bei der Mine nordwestlich von Thrallmar zurück."}},
		[10237] = {"Warnt Area 52!",nil, {"Überbringt Raketenchef Doppeldecker in Area 52 in Nethersturm die schlechten Nachrichten."}},
		[10238] = {"Wie man Goblins dient",nil, {"Rettet Manni, Moh und Jakk aus den Lagern der Höllenorcs und bringt sie zu Großknecht Razelcraz bei der Mine nordwestlich von Thrallmar zurück."}},
		[10239] = {"Mögliche Energiequelle",nil, {"Zauberhexerleutnant Morran in Kirin'Var möchte, dass Ihr ihm 10 Energieisolationswürfel von den Blutelfen der Manaschmiede Coruu bringt."}},
		[10240] = {"Eine Verteidigung errichten",nil, {"Benutzt das Gerät zur Aktivierung von Runen bei der östlichen Rune, der nordöstlichen Rune und der westlichen Rune. Die Runen befinden sich entlang der Verteidigung der Zaubergasse und des Ratsplatz von Kirin'Var. Kehrt zu Zauberhexerleutnant Morran zurück, wenn Ihr die Aufgabe erfüllt habt."}},
		[10241] = {"Ablenkung in der Manaschmiede B'naar",nil, {"Tötet 8 Magister des Sonnenzorns und 8 Blutwärter des Sonnenzorns und kehrt dann zu Exarch Orelis in Area 52 zurück. Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Ruf bei den Sehern."}},
		[10242] = {"Der Rückenbrecherposten",nil, {"Apothekerin Zelana beim Häschersturz möchte, dass Ihr mit Schwadronskommandant Brack sprecht, um auf einem Flügeldrachen zum Rückenbrecherposten zu fliegen. Bringt die Blutprobe des Blutenden Auges zu Apotheker Albreck im Rückenbrecherposten."}},
		[10243] = {"Die Technologie der Naaru",nil, {"Geht in die Manaschmiede B'naar und sucht die Steuerkonsole von B'naar. Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10244] = {"Liesmich",nil, {"Bringt die Bauanleitung für Teufelshäscher zu Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
		[10245] = {"Steuerungsabschrift von B'naar",nil, {"Bringt die Steuerungsabschrift von B'naar zu Anachoret Karja in Area 52 im Nethersturm. Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10246] = {"Angriff auf Manaschmiede Coruu",nil, {"Reist zur Manaschmiede Coruu und tötet 8 Arkanisten des Sonnenzorns und 5 Forscher des Sonnenzorns. Kehrt zu Exarch Orelis zurück, wenn Ihr Eure Aufgabe erledigt habt. Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10247] = {"Dr. h. c. tüft. Vomisa",nil, {"Sprecht mit Dr. h. c. tüft. Vomisa in den Versuchsgründen im Nethersturm."}},
		[10248] = {"Du, der Roboter",nil, {"Aktiviert die Steuerung für Schrotthäscher X6000 und testet seine Fähigkeiten aus. Gebt Dr. h. c. tüft. Vomisa bei den Versuchsgründen im Nethersturm danach Eure Rückmeldung."}},
		[10249] = {"Zurück zum Chef!",nil, {"Meldet Euch sofort bei Raketenchef Doppeldecker in Area 52 im Nethersturm!"}},
		[10250] = {"Blutige Rache",nil, {"Stoßt am alten Belagerungsturm der Allianz in das Kampfhorn der Unerschütterlichen. Tötet Urtrak und kehrt danach zu Althen dem Historiker im Rückenbrecherposten zurück."}},
		[10251] = {"Der große Plan des Meisters?",nil, {"Sucht Nitrin den Gelehrten im verlassenen Rüstlager in Nagrand."}},
		[10252] = {"Vision der Toten",nil, {"Nitrin im verlassenen Rüstlager in Nagrand möchte, dass Ihr ihm 1 Augapfel eines Berggronns, 1 fehlerloser Schnabel eines großen Windrocs und 1 Tran eines gealterten Grollhufs bringt."}},
		[10253] = {"Levixus der Seelenrufer",nil, {"Vernichtet Levixus und nehmt Euch das Buch der Toten aus seinem Besitz. Bringt das Buch der Toten zu Ramdor dem Wahnsinnigen außerhalb der Auchenaikrypta am Eingang zu Auchindoun. Ihr hattet eine Vereinbarung, erinnert Ihr Euch?"}},
		[10254] = {"Truppenkommandant Danath",nil, {"Marschall Isildor möchte, dass Ihr Euch bei Truppenkommandant Danath Trollbann in der Ehrenfeste meldet."}},
		[10255] = {"Das Gegengift testen",nil, {"Probiert das cenarische Gegengift an einem titanischen Hölleneber aus und beobachtet die Folgen. Sobald Ihr die Ergebnisse des Experiments habt, kehrt zu Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel zurück."}},
		[10256] = {"Den Schlüsselmeister finden",nil, {"Sucht Apex und besorgt Euch Apex' Kristallfokus. Kehrt dann zum violetten Turm zurück und benutzt Apex' Kristallfokus in der Nähe von Erzmagier Vargoths Kugel um herauszufinden, wer den Schlüssel zum Fluch des Turms hat."}},
		[10257] = {"Den Schlüsselstein erobern",nil, {"Reist zur Botanika der Festung der Stürme und besorgt Euch den Schlüsselstein von Kommandant Sarannis. Bringt ihn danach zu Erzmagier Vargoth im violetten Turm."}},
		[10258] = {"Die Gefallenen ehren",nil, {"Meldet Euch bei Hogarth, dem Kommandanten des Expeditionsrüstlagers."}},
		[10259] = {"Auf in den Kampf!",nil, {"Tötet 6 einfallende Teufelswachen und kehrt dann zum Argentumabgesandten Stolzbrunn zurück."}},
		[10260] = {"Netheorologe Kupfernickel",nil, {"Sprecht mit Netheorologe Kupfernickel bei der Brücke, die vom Nethersturm zum Schergrat führt."}},
		[10261] = {"Gesucht: Vernichter Servo!",nil, {"Besorgt einen Vernichterservo und bringt ihn zu Papa Mauschel in Area 52 im Nethersturm."}},
		[10262] = {"Ein Hügel voll Astraler",nil, {"Sammelt 10 Insignien der Zaxxis und kehrt dann zu Netherpirscher Khay'ji in Area 52 im Nethersturm zurück."}},
		[10263] = {"Dem Konsortium helfen",nil, {"Sprecht mit Netherpirscher Khay'ji in Area 52 im Nethersturm."}},
		[10264] = {"Dem Konsortium helfen",nil, {"Sprecht mit Netherpirscher Khay'ji in Area 52 im Nethersturm."}},
		[10265] = {"Kristallsammlung des Konsortiums",nil, {"Besorgt ein Kristallartefakt von Arklon und bringt es zu Netherpirscher Khay'ji in Area 52 im Nethersturm."}},
		[10266] = {"Bitte um Unterstützung",nil, {"Sucht Gahruj und bietet ihm Eure Hilfe an. Er befindet sich im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
		[10267] = {"Rechtmäßiger Besitz",nil, {"Sammelt 10 Kästen mit Vermessungsausrüstung und bringt sie zu Gahruj im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
		[10268] = {"Eine Audienz beim Prinzen",nil, {"Bringt die Vermessungsausrüstung zum Bild von Nexusprinz Haramad in der Sturmsäule im Nethersturm."}},
		[10269] = {"Triangulationspunkt Eins",nil, {"Benutzt das Triangulationsgerät, um den Weg zum ersten Triangulationspunkt zu bestimmen. Wenn Ihr in gefunden habt, meldet die Position an Händler Hazzin beim Wachposten des Protektorats auf der Insel der Manaschmiede Ultris im Nethersturm."}},
		[10270] = {"Ein nicht-so-bescheidenes Angebot",nil, {"Besorgt Euch die Teleporterenergiezelle und bringt sie zur Astralteleportplattform im Sternensturz östlich der Manaschmiede Ultris. Aktiviert die Plattform und bringt die Teleporterenergiezelle zum Bild von Windhändler Marid, damit er sie einbauen kann."}},
		[10271] = {"Zur Sache",nil, {"Sammelt 8 Einheiten Netherdrachenessenz und kehrt dann zur Gegend bei der Astralteleporterplattform zurück. Die Netherdrachenessenz könnt Ihr von Netherdrachen oder Nethergroßdrachen sammeln."}},
		[10272] = {"Ein viel versprechender Anfang",nil, {"Sammelt 15 Netherdrachkineier und kehrt dann zu Tyri und Jorad im Sternensturz zurück."}},
		[10273] = {"Störende Ablenkung",nil, {"Sprecht mit Windhändler Marid und lockt ihn von seinem Handelsposten fort. Vernichtet ihn und kehrt dann zu Tyri und Jorad im Sternensturz zurück."}},
		[10274] = {"Den Sternensturz sichern",nil, {"Benutzt die Herausforderung des blauen Drachenschwarms um Veraku am südlichen Ende des Sternensturzes zu provozieren. Besiegt ihn und kehrt dann zu Tyri und Jorad im Sternesturz zurück."}},
		[10275] = {"Triangulationspunkt Zwei",nil, {"Benutzt das Triangulationsgerät, um den Weg zum zweiten Triangulationspunkt zu bestimmen. Wenn Ihr in gefunden habt, meldet die Position an Windhändler Tuluman bei Tulumans Landeplatz auf der anderen Seite der Brücke zu der Insel der Manaschmiede Ara im Nethersturm."}},
		[10276] = {"Das volle Dreieck",nil, {"Besorgt Euch den Kristall von Ata'mal und bringt ihn zum Bild von Nexusprinz Haramad in der Sturmsäule im Nethersturm."}},
		[10277] = {"Die Höhlen der Zeit",nil, {"Andormu in den Höhlen der Zeit möchte, dass Ihr der Bewahrerin der Zeit durch die Höhle folgt."}},
		[10278] = {"Sphärenrisse",nil, {"Benutzt den instabilen Sphärenrissgenerator in den Sphärenfeldern. Sammelt 3 Einheiten Sphärennether von instabilen Leerwandlern und kehrt dann zu Ogath dem Verrückten im Rückenbrecherposten zurück."}},
		[10279] = {"Zum Hort des Meisters",nil, {"Sprecht mit Andormu in den Höhlen der Zeit."}},
		[10280] = {"Sonderlieferung nach Shattrath",nil, {"Bringt den Kristall von Ata'mal zu A'dal auf der Terrasse des Lichts in Shattrath."}},
		[10281] = {"Formelle Vorstellung",nil, {"Sucht Tyri am nördlichen Ende des Sternensturzes und sprecht mit ihr."}},
		[10282] = {"Das alte Hügelland",nil, {"Andormu in den Höhlen der Zeit bittet Euch, ins Alte Hügelland zu reisen und mit Erozion zu sprechen."}},
		[10283] = {"Tarethas Ablenkungsmanöver",nil, {"Reist zur Burg Durnholde und platziert mit Hilfe des Bündels mit Brandbomben, das Ihr von Erozion erhalten habt, 5 Brandsätze auf den Fässern in jeder Internierungsbaracke. Sprecht mit Thrall im Kellergefängnis der Burg Durnholde, wenn Ihr die Internierungsbaracken angezündet habt."}},
		[10284] = {"Flucht aus Durnholde",nil, {"Gebt Thrall Bescheid, wenn Ihr bereit seid. Folgt Thrall aus der Burg Durnholde und helft ihm, Taretha zu befreien und sein Schicksal zu erfüllen. Sprecht mit Erozion im Alten Hügelland, wenn Ihr diese Aufgabe erfüllt habt."}},
		[10285] = {"Rückkehr zu Andormu",nil, {"Kehrt zu dem jungen Andormu in den Höhlen der Zeit in Tanaris zurück."}},
		[10286] = {"Arelions Geheimnis",nil, {"Magistrix Carinda möchte, dass Ihr Magister Aledis befragt. Aledis wurde zuletzt auf einer Reise von der Falkenwacht zu den Zangarmarschen gesehen."}},
		[10287] = {"Die enttarnte Geliebte",nil, {"Kehrt zu Magistrix Carinda in der Falkenwacht zurück."}},
		[10288] = {"Ankunft in der Scherbenwelt",nil, {"Kommandant Duron beim Dunklen Portal auf der Höllenfeuerhalbinsel möchte, dass Ihr Durons Bericht zu Flugmeister Amish Wildhammer bringt."}},
		[10289] = {"Reise nach Thrallmar",nil, {"Nehmt Vlagga Freyfeders Windreiter nach Thrallmar. Bringt Orions Bericht zu General Krakork."}},
		[10290] = {"Auf der Suche nach Farahlit",nil, {"Sammelt 4 Stück Rohfarahlit und kehrt dann zu Zuben Elgenubi in der Sturmsäule zurück."}},
		[10291] = {"Bericht an Nazgrel",nil, {"General Krakork in Thrallmar möchte, dass Ihr mit Nazgrel sprecht."}},
		[10292] = {"Mehr Energie!",nil, {"Zuben Elgenubi möchte, dass Ihr 8 Schusswaffen der Legion besorgt und sie zur Sturmsäule bringt."}},
		[10293] = {"Die Kristallader anzapfen",nil, {"Zuben Elgenubi in der Sturmsäule möchte, dass Ihr einen Farahlitkern besorgt."}},
		[10294] = {"Der Leerengrat",nil, {"Geht zum Leerengrat und tötet die Kreaturen, die Ihr dort findet. Sammelt 40 Seelensplitter des Leerengrats und kehrt dann zu Ogath dem Verrückten im Rückenbrecherposten zurück."}},
		[10295] = {"Aus dem Abgrund",nil, {"Tötet Galaxis und sammelt seinen Seelensplitter. Bringt den Splitter zu Ogath dem Verrückten im Rückenbrecherposten."}},
		[10296] = {"Der schwarze Morast",nil, {"Reist durch die Höhlen der Zeit in den schwarzen Morast während der Öffnung des Dunklen Portals und sprecht mit Sa'at."}},
		[10297] = {"Die Öffnung des Dunklen Portals",nil, {"Sa'at im schwarzen Morast der Höhlen der Zeit möchte, dass Ihr Medivh verteidigt, bis er das Dunkle Portal geöffnet hat. Kehrt zu Sa'at zurück, wenn Ihr erfolgreich wart."}},
		[10298] = {"Held des Schwarms",nil, {"Kehrt zum Kind, Andormu, in den Höhlen der Zeit in Tanaris zurück."}},
		[10299] = {"Manaschmiede B'naar abschalten",nil, {"Anachoret Karja möchte, dass Ihr in die Manaschmiede B'naar geht und einen Zugangskristall von Vorarbeiter Theredis besorgt. Benutzt ihn bei der Steuerkonsole von B'naar, um sie abzuschalten und meldet Euch danach bei Anachoret Karja zurück. Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10300] = {"Den Stab wiederherstellen",nil, {"Besorgt 4 fehlerlose Kristallsplitter von den Geistern in den Ruinen von Enkaat und bringt sie zu Ravandwyr in Area 52."}},
		[10301] = {"Das Kompendium öffnen",nil, {"Besorgt Euch das heliotrope Oculus von Zauberhäscherin Marathelle im Sonnenzornposten. Benutzt Erzmagier Vargoths Stab, um ihn zu kontaktieren, wenn Ihr fertig seid. Falls Ihr Erzmagier Vargoths Stab verlieren solltet, bittet Ravandwyr in Area 52 um Ersatz."}},
		[10302] = {"Unberechenbare Mutanten",nil, {"Tötet 8 unberechenbare Mutanten und kehrt dann zu Botanikerin Taerix bei der Absturzstelle im Am'mental zurück."}},
		[10303] = {"Die Blutelfen",nil, {"Tötet 10 Blutelfenspäher und kehrt dann zu Tolaan im Schattenkamm im Am'mental zurück."}},
		[10304] = {"Verteidiger Aldar",nil, {"Sprecht mit Verteidiger Aldar bei der Absturzstelle im Am'mental."}},
		[10305] = {"Bannzauberin Belmara",nil, {"Sucht die Wohnung von Bannzauberin Belmara in der Zaubergasse und bringt das Buch zurück auf seinen Platz im Bücherregal. Sprecht danach mit Hüter Dierwert in Kirin'Var."}},
		[10306] = {"Beschwörer Luminrath",nil, {"Sucht die Wohnung von Beschwörer Luminrath in der Zaubergasse und bringt den Mantel zurück in seine Kommode. Sprecht danach mit Hüter Dierwert in Kirin'Var."}},
		[10307] = {"Cohlien Frostwirker",nil, {"Sucht die Wohnung von Cohlien Frostwirker in der Zaubergasse und bringt die Kappe zurück in seine Schließkiste. Sprecht danach mit Hüter Dierwert in Kirin'Var."}},
		[10308] = {"Noch ein Hügel voller Astraler",nil, {"Das Konsortium ist sehr daran interessiert, alle Rebellen der Zaxxis beim Hügel im Süden auszulöschen. <Klasse>, wenn Ihr Euch bei uns noch beliebter machen wollt, dann geht zurück und besorgt noch mehr ihrer Insignien."}},
		[10309] = {"Teufelshäscher mit Herz",nil, {"Besorgt das Herz des Teufelshäschers und bringt es zu N. D. Mieskamp beim Hügel im Nethersturm."}},
		[10310] = {"Sabotage am Warptor!",nil, {"Begleitet Drijya zum Warptor beim Invasionspunkt: Zerstörer und sorgt für seine Sicherheit, während er das Tor sabotiert. Sprecht danach mit Gahruj im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
		[10311] = {"Drijya braucht Eure Hilfe",nil, {"Sprecht mit Drijya in der Nähe des Invasionspunkts: Zerstörer im Nethersturm."}},
		[10312] = {"Die Annalen von Kirin'Var",nil, {"Besorgt die Annalen von Kirin'Var aus dem Rathaus von Kirin'Var und bringt es zu Hüter Dierwert."}},
		[10313] = {"Warpenergien messen",nil, {"Exarch Orelis in Area 52 möchte, dass Ihr die Warpmesskugel zur Manaschmiede B'naar bringt und sie in der Nähe der vier Rohrleitungen benutzt. Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eurer Ehre bei den Sehern."}},
		[10314] = {"Ein hartnäckiger Verdacht",nil, {"Sammelt 10 abscheuliche Überreste von den wahnsinnigen Geistern auf dem Kapellenhof in Kirin'Var und bringt sie zu Hüter Dierwert."}},
		[10315] = {"Die Nethermanten neutralisieren",nil, {"Tötet 15 Nethermanten des Sonnenzorns und kehrt dann zu Netherpirscher Oazul in Tulumans Landeplatz im Nethersturm zurück."}},
		[10316] = {"Auf der Suche nach Beweisen",nil, {"Durchsucht die Häuser auf dem Kapellenhof von Kirin'Var nach Hinweisen darauf, wer sich hinter der Beeinflussung der Geister verbirgt."}},
		[10317] = {"Den Großknecht erledigen",nil, {"Vernichtet Großknecht Sonnenend in der Trelleummine, die sich unter der Manaschmiede Ara im Nethersturm befindet."}},
		[10318] = {"Den Übermeister erledigen",nil, {"Tötet Übermeister Grindgarr. Kehrt dann zu Windhändler Tuluman in Tulumans Landeplatz im Nethersturm."}},
		[10319] = {"Das Phylakterium entwenden",nil, {"Holt Naberius' Phylakterium vom Kapellenhof in Kirin'Var und bringt es zu Hüter Dierwert."}},
		[10320] = {"Vernichtet Naberius!",nil, {"Hüter Dierwert in Kirin'Var möchte, dass Ihr Naberius auf dem Kapellenhof des Dorfes besiegt."}},
		[10321] = {"Manaschmiede Coruu abschalten",nil, {"Reist zur Manaschmiede Coruu, östlich von Area 52, und besorgt den Zugangskristall von Coruu von Vorarbeiterin Seylanna. Benutzt ihn, um die Manaschmiede abzuschalten, und kehrt dann zu Anachoret Karja zurück. Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10322] = {"Manaschmiede Duro abschalten",nil, {"Anachoret Karja möchte, dass Ihr zur Manaschmiede Duro geht und den Zugangskristall von Vorarbeiter Athanel besorgt. Benutzt ihn, um die Steuerungskonsole von Duro abzuschalten. Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10323] = {"Manaschmiede Ara abschalten",nil, {"Reist zur Manaschmiede Ara und besorgt Euch einen Zugangskristall von Vorarbeiter Azarad. Benutzt ihn, um die Steuerungskonsole von Ara abzuschalten. Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10324] = {"Die große Mondweidenjagd",nil, {"Bringt 6 Bälge eines Mondweidenbocks zu Acteon in der Azurwacht."}},
		[10325] = {"Male von Kil'jaeden",nil, {"Bringt 10 Male von Kil'jaeden zu Adyen dem Lichtwächter in Shattrath. Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10326] = {"Mehr Male von Kil'jaeden",nil, {"Tragt Ihr noch mehr Insignien des Feindes bei Euch, <Name>? Die Brennende Legion ist auch weiterhin eine Bedrohung, und wir verfolgen sie überall, wo wir sie finden können."}},
		[10327] = {"Einzelne Male von Kil'jaeden",nil, {"Ich nehme Euch alle übrigen Male, die Ihr habt, ab. Keine Sorge, Eure Taten werden nicht unbemerkt bleiben."}},
		[10328] = {"Anweisungen des Sonnenzorns",nil, {"Reist zur Manaschmiede Duro und beschafft die Truppenbefehle des Sonnenzorns und die Arkanbefehle des Sonnenzorns von den Einheiten des Sonnenzorns, die dort postiert sind. Kehrt zu Exarch Orelis zurück, wenn Ihr Eure Aufgabe erledigt habt. Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10329] = {"Manaschmiede B'naar abschalten",nil, {"Geht zurück zur Manaschmiede B'naar und besorgt Euch den Zugangskristall von Vorarbeiter Theredis. Benutzt ihn bei der Steuerkonsole von B'naar um sie abzuschalten und meldet Euch danach bei Spionagemeister Thalodien zurück. Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10330] = {"Manaschmiede Coruu abschalten",nil, {"Besorgt den Zugangskristall von Coruu von Vorarbeiterin Seylanna. Benutzt ihn, um die Steuerkonsole der Manaschmiede abzuschalten und meldet Euch dann bei Caledis Morgenhell. Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10331] = {"Unverzichtbare Werkzeuge",nil, {"Besorgt den Schmiedehammer von den Lehrlingen von Kirin'Var und bringt ihn zurück zu Lehrling Andrethan in Kirin'Var."}},
		[10332] = {"Meisterschmied Rhonsus",nil, {"Lehrling Andrethan in Kirin'Var möchte, dass Ihr den Geist von Rhonsus, dem Meisterschmied des Dorfes, vernichtet."}},
		[10333] = {"Helft Mama Mauschel",nil, {"Sprecht mit Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
		[10334] = {"Etwas mehr Klang bitte",nil, {"Sucht Bessy in den Feldern nordöstlich vom Kapellenhügel von Kirin'Var."}},
		[10335] = {"Die Ruinen beobachten",nil, {"Platziert die drei Vermessungsmarkierungen und kehrt dann zu Zephirion in der Sturmsäule im Nethersturm zurück."}},
		[10336] = {"Diener von Culuthas",nil, {"Tötet 10 Hunde von Culuthas und 5 Augen von Culuthas. Kehrt danach zu Netherpirscher Nauthis in der Sturmsäule im Nethersturm zurück."}},
		[10337] = {"Almabtrieb",nil, {"Begleitet Bessy sicher zu ihrem Besitzer Thadell zurück."}},
		[10338] = {"Manaschmiede Duro abschalten",nil, {"Kehrt zur Manaschmiede Duro zurück und besorgt Euch den Zugangskristall von Vorarbeiter Athanel. Benutzt ihn, um die Steuerkonsole von Duro abzuschalten, und meldet Euch danach bei Spionagemeister Thalodien. Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10339] = {"Das Astraleum",nil, {"Kommandant Ameer möchte, dass Ihr 5 Auftragsmörder des Astraleums, 5 Stoßtruppsoldaten des Astraleums, 2 Forscher des Astraleums und Hauptmann Zovax tötet und dann den Transponder des Astraleums Zeta aktiviert."}},
		[10340] = {"Trümmerposten",nil, {"Vorpostenkommandant Wa'ppun möchte, dass Ihr mit Schwadronskommandant Dabir'ee sprecht, auf einem Greifen zum Trümmerposten fliegt und Euch bei Runetog Wildhammer meldet."}},
		[10341] = {"Tretet sie, solange sie am Boden liegen",nil, {"Tötet 8 Beschwörer des Sonnenzorns, 6 Jadgschützen des Sonnenzorns und 4 Zenturios des Sonnenzorns. Kehrt zu Magistrix Larynna in Area 52 zurück, wenn Ihr die Aufgabe erledigt habt. Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10342] = {"Schieferhautschiefer",nil, {"Sammelt 5 Stück Schieferhautschiefer und bringt sie zu Stiefels in Area 52 im Nethersturm."}},
		[10343] = {"Die unendliche Invasion",nil, {"Sucht die von Manakreaturen heimgesuchte Gegend in Kirin'Var nach einem Manabombenfragment ab und bringt sie zu Zauberhexerleutnant Morran."}},
		[10344] = {"Schwadronskommandant Greifgar",nil, {"Sprecht mit Schwadronskommandant Greifgar im Trümmerposten."}},
		[10345] = {"Fleisch geworden",nil, {"Benutzt den Anzünder des Protektorats um 12 siechende Leichen zu zerstören. Bringt den Anzünder des Protektorats zu Agent Araxes außerhalb des Zugangsschachts Zeon zurück, wenn Ihr erfolgreich wart."}},
		[10346] = {"Rückkehr zur abyssischen Untiefe",nil, {"Greifnerin Heulwind im Trümmerposten auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen zerstört."}},
		[10347] = {"Rückkehr zur abyssischen Untiefe",nil, {"Schwadronskommandant Brack im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen zerstört."}},
		[10348] = {"Neue Möglichkeiten",nil, {"Sammelt 15 Elfenbeinglocken und kehrt dann zu Mehrdad im Mittelreichposten zurück."}},
		[10349] = {"Die Erdbinderin",nil, {"Bringt den Purpurkristallsplitter zu Erdbinderin Galandria Nachtluft im Posten des Cenarius auf der Höllenfeuerhalbinsel."}},
		[10350] = {"Behomat",nil, {"Sprecht mit Behomat bei der Händlertreppe in der Exodar."}},
		[10351] = {"Natürliche Heilmittel",nil, {"Benutzt den Samen der Wiederbelebung beim Zirkel des Erdbinders, um das Land um den Kristall herum zu heilen. Kehrt dann zu Erdbinderin Galandria Nachtluft im Posten des Cenarius auf der Höllenfeuerhalbinsel mit Euren Beobachtungen zurück."}},
		[10352] = {"Spendet Wolle",nil, {"Wir nehmen zurzeit Spenden für Wolle entgegen. Spendet Ihr 60 Stücke, so wird Euch diese großzügige Tat die volle Anerkennung von Darnassus einbringen. Unsere Vorräte würden lediglich 60 Stücke Wollstoff von Euch benötigen; mit der Unterstützung anderer sollten wir unsere Sammelaktion dann erfolgreich vervollständigen können. Wenn Ihr die 60 Stücke Wollstoff bei Euch habt und zu deren Spende bereit seid, dann kann ich sie jetzt entgegennehmen."}},
		[10353] = {"Arconus der Unersättliche",nil, {"Agent Sechs-Uhr beim Zugangsschacht Zeon im Nethersturm möchte, dass Ihr Arconus den Unersättlichen tötet und dann zu Kommandant Ameer im Wachposten des Protektorats im Nethersturm zurückkehrt."}},
		[10354] = {"Spendet Seide",nil, {"Unsere Vorräte an Seide sind, wie fast alle anderen Stoffe auch, so knapp wie nie zuvor. Unterstützen uns noch andere, so bräuchten wir derzeit eine Spende von nicht mehr als 60 Stücken Seide von Euch, um unser Ziel umsetzen zu können. Eine wohlwollende Gabe, sagen wir Seide, könnte Euren hiesigen Ruf in der Gemeinschaft mit Sicherheit verbessern! Wenn Ihr die 60 Stücke Seide bei Euch habt und zu deren Spende bereit seid, dann kann ich sie jetzt entgegennehmen."}},
		[10355] = {"Siechendes Fleisch",nil, {"Sammelt 5 Proben einer verdurstenden Hydra und 5 Proben eines siechenden Sumpflords und bringt sie zu Ruam in Telredor."}},
		[10356] = {"Spendet Magiestoff",nil, nil},
		[10357] = {"Spendet Runenstoff",nil, nil},
		[10358] = {"Noch mehr Runenstoff",nil, nil},
		[10359] = {"Spendet Wolle",nil, {"Wir nehmen zurzeit Spenden für Wolle entgegen. Spendet Ihr 60 Stücke, so wird Euch diese großzügige Tat die volle Anerkennung von Silbermond einbringen. Wenn Ihr die 60 Stücke Wollstoff bei Euch habt und zu deren Spende bereit seid, dann kann ich sie jetzt entgegennehmen."}},
		[10360] = {"Spendet Seide",nil, {"Unsere Vorräte an Seide sind, wie fast alle anderen Stoffe auch, so knapp wie nie zuvor. Unterstützen uns noch andere, so bräuchten wir derzeit eine Spende von nicht mehr als 60 Stücken Seide von Euch, um unser Ziel umsetzen zu können. Eine wohlwollende Gabe, sagen wir Seide, könnte Euren hiesigen Ruf in Silbermond mit Sicherheit verbessern! Wenn Ihr die 60 Stücke Seide bei Euch habt und zu deren Spende bereit seid, dann kann ich sie jetzt entgegennehmen."}},
		[10361] = {"Spendet Magiestoff",nil, {"Unsere Vorräte an Magiestoff gehen zur Neige und wir könnten Eure Hilfe gebrauchen, um unsere Bestände wieder aufzufüllen! Wenn wir uns auf die Gemeinschaft im Ganzen verlassen, bräuchten wir lediglich eine Spende von 60 Stücken Magiestoff von Euch, um unser Ziel umsetzen zu können. Ich versichere Euch, dass eine derartige Großzügigkeit in Silbermond nicht unbeachtet bleiben würde!nnWenn Ihr die 60 Stücke Magiestoff bei Euch habt und zu deren Spende bereit seid, dann kann ich sie jetzt entgegennehmen."}},
		[10362] = {"Spendet Runenstoff",nil, {"<Name>, Ihr wart eine enorme Unterstützung bei unserer Stoffsammelaktion. Da wir sehr hart am Aufstocken unserer Vorräte arbeiten, muss ich Euch wohl auch von unserem neusten und schwerwiegendsten Mangel berichten: Runenstoff. Wir brauchen ihn ganz dringend und hoffen, dass Ihr uns hierbei, wie schon zuvor, wieder unterstützen könnt. Solltet Ihr gewillt sein, so bringt mir bitte all den Runenstoff, den Ihr entbehren könnt. Für den Anfang wären wir mit 60 Stücken einverstanden, dann sehen wir weiter."}},
		[10363] = {"Noch mehr Runenstoff",nil, {"Seid gegrüßt, <Name>! Wir benötigen auch weiterhin noch Runenstoff. Zusätzliche Mittel sind daher jederzeit willkommen, solltet Ihr welche entbehren können. Liefert Ihr uns eine weitere Spende von 20 Stück Runenstoff, so werde ich dafür sorgen, dass Ihr die gebührende Anerkennung für Eure anhaltenden Bemühungen gegenüber Silbermond erhaltet. Wann auch immer Ihr die erforderte Menge abgeben könnt, ich werde sie jederzeit entgegennehmen."}},
		[10364] = {"Caedmos",nil, {"Sprecht mit Caedmos in der Halle des Lichts der Exodar."}},
		[10365] = {"Manaschmiede Ara abschalten",nil, {"Reist zur Manaschmiede Ara und besorgt Euch einen Zugangskristall von Vorarbeiter Azarad. Benutzt ihn, um die Steuerungskonsole von Ara abzuschalten. Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10366] = {"Jol",nil, {"Sprecht mit Jol im Sanktum der Verteidiger in der Exodar."}},
		[10367] = {"Ein Verräter unter uns",nil, {"Naladu in den Ruinen von Sha'naar möchte, dass Ihr den Schlüssel von Sha'naar aus einer Hütte in der Nähe besorgt."}},
		[10368] = {"Die Ältesten des Lumpenpacks",nil, {"Befreit Morod den Windtreiber, Akoru den Feuerrufer und Aylaan den Wasserrufer aus den Ruinen von Sha'naar. Kehrt zu Naladu zurück, wenn Ihr diese Aufgabe erledigt habt."}},
		[10369] = {"Arzeths Untergang",nil, {"Benutzt den Ältestenstab des Lumpenpacks gegen Arzeth den Erbarmungslosen und tötet ihn, nachdem er seine Macht verloren hat. Kehrt danach zu Naladu in den Ruinen von Sha'naar zurück."}},
		[10370] = {"Nazgrels Befehl",nil, nil},
		[10371] = {"Yorus Gerstenbräu",nil, {"Sprecht mit Yorus Gerstenbräu."}},
		[10372] = {"Eine diskrete Anfrage",nil, {"Sprecht mit Eralan in Tristessa."}},
		[10373] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Kommandant Ashlam Ehrenschlag auf. Sein Stützpunkt befindet sich am Zugwindlager, nördlich des Alteracgebirges."}},
		[10374] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Hochexekutor Derrington auf. Sein Stützpunk befindet sich am Bollwerk, östlich von Tirisfal und Unterstadt."}},
		[10375] = {"Obsidiankriegsperlen",nil, nil},
		[10377] = {"Keine Angst",nil, nil},
		[10379] = {"Berührung der Schwäche",nil, nil},
		[10380] = {"Ein dunkler Pakt",nil, {"Kaylaan bei Tulumans Landeplatz in Nethersturm möchte, dass Ihr 6 Warptüftler der Gan'arg, 3 Töchter des Schicksals und 3 Warpmeister der Mo'arg tötet. Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10381] = {"Nie mehr Aldor",nil, {"Meldet Euch bei Exarch Orelis in Area 52. Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10382] = {"Geht zur Front",nil, {"Nehmt einen Greifen zum Ehrenposten und sprecht mit Feldmarschall Brock."}},
		[10383] = {"Dies ist ein Brotkrumen!",nil, {"Sprecht mit Jonathan LeCraft über die Kaninchen."}},
		[10384] = {"Daten der Astralen",nil, {"Das Bild von Kommandant Ameer beim Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr die Datenzelle des Astraleums besorgt."}},
		[10385] = {"Hirnschadengefahr = hoch",nil, {"Das Bild von Kommandant Ameer beim Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr 15 Melderdaten des Astraleums besorgt."}},
		[10386] = {"Der Teufelshäschertöter",nil, {"Sprecht mit Oberanführer Hun Stümmelfaust."}},
		[10387] = {"Der Teufelshäschertöter",nil, {"Sprecht mit Oberanführer Hun Stümmelfaust."}},
		[10388] = {"Rückkehr nach Thrallmar",nil, {"Kehrt nach Thrallmar zurück und sprecht mit Nazgrel."}},
		[10389] = {"Das Leid und die Dunkelheit",nil, {"Magister Blutfalke möchte, dass Ihr 10 Terrorbestien bei den Teichen von Aggonar tötet."}},
		[10390] = {"Konstruktionslager: Mageddon",nil, {"Nazgrel in Thrallmar möchte, dass Ihr das Konstruktionslager: Mageddon angreift, 10 Diener der Gan'arg tötet und mit Schmiedebestie Sägeklinges Kopf zurückkehrt."}},
		[10391] = {"Kanonen des Zorns",nil, {"Zerstört 3 Teufelskanonen V1 im Konstruktionslager: Zorn und kehrt dann zu Nazgrel in Thrallmar zurück."}},
		[10392] = {"Die Schwelle zum Abgrund",nil, {"Tötet Arix'Amal und nehmt Euch den Torschlüssel der Brennenden Legion. Benutzt den Schlüssel bei der Rune der Bosheit und kehrt danach zu Nazgrel in Thrallmar zurück."}},
		[10393] = {"Boshafte Pläne",nil, {"Bringt den Schriftwechsel der Legion zu Magister Blutfalke in Thrallmar."}},
		[10394] = {"Unterbrechung beim Konstruktionslager: Mageddon",nil, {"Tötet 10 Diener der Gan'arg und Schmiedebestie Sägeklinge im Konstruktionslager: Mageddon und kehrt danach zu Feldmarschall Brock beim Ehrenposten zurück."}},
		[10395] = {"Der dunkle Schriftwechsel",nil, {"Bringt den Schriftwechsel zu Warpseher Kryv in der Haupthalle der Ehrenfeste."}},
		[10396] = {"Der Feind meines Feindes...",nil, {"Zerstört 3 Teufelskanonen V1 im Konstruktionslager: Zorn und kehrt dann zu Feldmarschall Brock beim Ehrenposten zurück."}},
		[10397] = {"Invasionspunkt: Vernichter",nil, {"Tötet Arix'Amal. Nehmt den Torschlüssel der Brennenden Legion und benutzt ihn bei der Rune der Bosheit."}},
		[10398] = {"Rückkehr zur Ehrenfeste",nil, {"Geht zu Ehrenfeste."}},
		[10399] = {"Das Herz der Dunkelheit",nil, {"Tötet 10 Terrorbestien bei den Teichen von Aggonar und kehrt dann zu Warpseher Kryv in der Haupthalle der Ehrenfeste zurück."}},
		[10400] = {"Oberanführer",nil, {"Tötet Arazzius den Grausamen bei den Teichen von Aggonar und kehrt danach zu Truppenkommandant Danath Trollbann in der Ehrenfeste zurück."}},
		[10401] = {"Mission: Beendet alles, seid alles",nil, {"Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel möchte, dass Ihr Arazzius den Grausamen tötet."}},
		[10403] = {"Naladu",nil, {"Sucht Naladu in den Ruinen von Sha'naar."}},
		[10404] = {"Gegen die Legion",nil, {"Exarch Orelis in Area 52 möchte, dass Ihr zur Konstruktionsbasis: Vergessenheit und zur Konstruktionsbasis: Gehenna geht und 12 Feuerfäuste der Gan'arg, 6 Konstruktionsherren der Gan'arg und 6 Zornschaffer tötet. Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10405] = {"S-A-B-O-T-A-G-E",nil, {"Das Bild von Kommandant Ameer bei dem Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr eine vorbereitete Bandage des Astraleums besorgt."}},
		[10406] = {"Die Nachricht überbringen",nil, {"Das Bild von Kommandant Ameer bei dem Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr den Demolierer des Protektorats zur Energieleitung der Manaschmiede Ultris begleitet und ihn beschützt, während er die Leitung sabotiert."}},
		[10407] = {"Socrethars Schatten",nil, {"Anachoret Karja in Area 52 möchte, dass Ihr die erste Hälfte von Socrethars Stein von Konstruktionsmeister Morug in der Konstruktionsbasis: Vergessenheit und die zweite Hälfte des Steins von Konstruktionsmeister Silroth in der Konstruktionsbasis: Gehenna besorgt. Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10408] = {"Nexuskönig Salhadaar",nil, {"Das Bild von Kommandant Ameer beim Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr den Störer des Protektorats benutzt, um Salhadaar Kraftfelder zu senken und Nexuskönig Salhadaar zu vernichten."}},
		[10409] = {"Todesstoß für die Legion",nil, {"Hohepriesterin Ishanah möchte, dass Ihr zum Transporter der Legion am nordwestlichsten Ende des Nethersturms geht und Euch zu Socrethars Sitz teleportiert. Wenn Ihr dort seid, besiegt Socrethar. Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10410] = {"Ishanahs Hilfe",nil, {"Bringt Socrethars Teleportationsstein zu Hohepriesterin Ishanah beim Schrein des endlosen Lichts auf der Aldorhöhe in Shattrath. Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10411] = {"Elektroschock!",nil, {"Forscher Navuud im Wachposten des Protektorats im Nethersturm möchte, dass Ihr 30 Leerabschaumkugeln und 30 Sickerschlammkugeln zerstört."}},
		[10412] = {"Siegel der Feuerschwingen",nil, {"Magistrix Fyalenn in Shattrath möchte, dass Ihr ihr 10 Siegel der Feuerschwingen bringt. Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10413] = {"Die Schrecken der Verschmutzung",nil, {"Bringt die Phiole mit Schreckensschleim zu Forscher Navuud im Wachposten des Protektorakts im Nethersturm."}},
		[10414] = {"Einzelne Siegel der Feuerschwingen",nil, {"Sogar der kleinste Beitrag zu unserer Sache wird bemerkt, <Name>. Unsere Feinde sind stark, aber wir werden siegen!"}},
		[10415] = {"Mehr Siegel der Feuerschwingen",nil, {"Was habt Ihr in letzter Zeit für uns getan, <Name>? Der Krieg gegen Kael'thas kämpft sich nicht von alleine, verstanden?"}},
		[10416] = {"Bündelung der Macht",nil, {"Voren'thal der Seher in Shattrath möchte, dass Ihr einen arkanen Folianten besorgt. Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10417] = {"Führt eine Diagnose durch!",nil, {"Beschafft die Diagnoseergebnisse und bringt sie zu Mehrdad im Mittelreichposten."}},
		[10418] = {"Die Saboteure ausschalten",nil, {"Mehrdad beim Mittelreichposten möchte, dass Ihr 8 Stachelschuppenkrokilisken tötet."}},
		[10419] = {"Arkane Folianten",nil, {"Habt Ihr noch mehr arkane Folianten besorgt, <Name>?"}},
		[10420] = {"Ein reinigendes Licht",nil, {"Bringt eine Ladung teuflische Waffen zu Ishanah in Shattrath. Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10421] = {"Teuflische Waffen",nil, {"Habt Ihr in letzter Zeit ein paar teuflische Waffen gefunden? Lasst das Licht seine Wirkung tun, <Name>. Erlaubt diesen verderbten Dingen nicht, in dieser Welt zu verweilen."}},
		[10422] = {"Hauptmann Tyralius",nil, {"Fleischtreiber Viridius im Wachposten des Protektorats im Nethersturm möchte, dass Ihr Hauptmann Tyralius findet und befreit."}},
		[10423] = {"Zur Sturmsäule",nil, {"Sprecht mit Ghabar in der Sturmsäule."}},
		[10424] = {"Diagnose: kritisch",nil, {"Benutzt das Diagnosegerät in der Nähe des Generators der Biokuppel Sutheron und bringt die Diagnoseergebnisse zu Ghabar in der Sturmsäule zurück."}},
		[10425] = {"Ausbruch aus dem Stützpunkt",nil, {"Begleitet den gefangenen Vorposten des Protektorats zurück zum Wachposten des Protektorats im Nethersturm."}},
		[10426] = {"Flora der Biokuppel",nil, {"Benutzt den Energiefeldmodulator bei den Farahlonpeitschern. Beobachtet die Ergebnisse und meldet Euch bei Aurine Mondflamm in der Sturmsäule zurück, wenn Ihr 10 Tests durchgeführt habt."}},
		[10427] = {"Fauna der Biokuppel",nil, {"Markiert 12 Talbuks für Aurine Mondflamm in der Sturmsäule. Ihr könnt mit dem Talbukmarkierer sowohl Talbukböcke als auch Talbukkühe markieren."}},
		[10428] = {"Der vermisste Fischer",nil, {"Sucht Cowlen bei den südöstlichen Docks der Silbermythosinsel vor der Küste der Azurmythosinsel."}},
		[10429] = {"Wenn die Natur zu weit geht",nil, {"Aurine Mondflamm in der Sturmsäule möchte, dass Ihr ihr ein titanisches Hydraherz bringt."}},
		[10430] = {"Den Prototyp testen",nil, {"Bringt das Testreparaturgerät zu Tashar bei der Biokuppel Fernfeld."}},
		[10431] = {"Hilfe von außen",nil, {"Geht zu Tulumans Landeplatz, nördlich von Area 52, und sprecht mit Kaylaan. Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
		[10432] = {"Eindeutige Beweise",nil, {"Besorgt Euch 8 Befehle von Kael'thas von den Dämonen bei der Manaschmiede Ara und bringt sie zu Spionagemeister Thalodien in Area 52. Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
		[10433] = {"Den Schein wahren",nil, {"Bringt 10 Reißzahnpelze zu Shauly Pore im Mittelreichposten."}},
		[10434] = {"Das dynamische Duo",nil, {"Sprecht mit Audi der Nadel bei der Sturmsäule."}},
		[10435] = {"Die Waren zurückholen",nil, {"Audi die Nadel bei der Sturmsäule möchte, dass Ihr 10 Kuppelgeneratorsegmente besorgt."}},
		[10436] = {"Freie Bahn!",nil, {"Tashar bei der Biokuppel Fernfeld möchte, dass Ihr 12 Sichelzahnraptoren tötet."}},
		[10437] = {"Anleitung zur Zerstörung",nil, {"Professor Dabiri im Wachposten des Protektorats im Nethersturm möchte, dass Ihr 8 Fragmente von Dimensius besorgt."}},
		[10438] = {"Auf netherischen Schwingen",nil, {"Professor Dabiri im Wachposten des Protektorats im Nethersturm möchte, dass Ihr den Phasenstörer bei der Leerenleitung auf der Manaschmiede Ultris benutzt. Sendet so viele Störsignale wie möglich aus, um eine komplette Zerstörung zu gewährleisten. Die Entladungen des Phasenstörers werden automatisch das Ziel treffen, solange Ihr nahe genug seid, um ihn zu benutzen. Kehrt zu Professor Dabiri zurück, wenn Ihr erfolgreich wart!"}},
		[10439] = {"Dimensius der alles Verschlingende",nil, {"Professor Dabiri im Wachposten des Protektorats im Nethersturm möchte, dass Ihr mit Hauptmann Saeed sprecht, seiner Armee zur Manaschmiede Ultris folgt und Dimensius den alles Verschlingenden tötet!"}},
		[10440] = {"Erfolg!",nil, {"Kehrt zur Sturmsäule zurück und sprecht mit Ghabar."}},
		[10441] = {"Die Waren unters Volk bringen.",nil, {"Sprecht mit Aurine Mondflamm unten in der Sturmsäule im Nethersturm."}},
		[10442] = {"Dem Posten des Cenarius helfen",nil, {"Falknerin Drenna Flusswind möchte, dass Ihr Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel Eure Dienste anbietet."}},
		[10443] = {"Dem Posten des Cenarius helfen",nil, {"Amaan der Weise möchte, dass Ihr Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel Eure Dienste anbietet."}},
		[10444] = {"Meldung bei Allerias Posten",nil, {"Jenai Sternwisper möchte, dass Ihr Euch bei Leutnant Meridian in Allerias Posten in den Wäldern von Terokkar meldet."}},
		[10445] = {"Die Phiolen der Ewigkeit",nil, {"Soridormi in den Höhlen der Zeit möchte, dass Ihr die Überreste von Vashjs Phiole von Lady Vashj im Echsenkessel und die Überreste von Kaels Phiole von Kael'thas Sonnenwanderer in der Festung der Stürme besorgt."}},
		[10446] = {"Der finale Code",nil, {"Leutnant Meridian möchte, dass Ihr den finalen Code eingebt, um die Manabombe auszulösen. Meldet euch dann bei Jenai Sternwisper in Allerias Feste in den Wäldern von Terokkar zurück."}},
		[10447] = {"Der finale Code",nil, {"Unteroffizier Chawni möchte, dass Ihr den finalen Code eingebt, um die Manabombe auszulösen. Meldet euch dann bei Tooki in der Steinbrecherfeste in den Wäldern von Terokkar zurück."}},
		[10448] = {"Meldung beim Steinbrecherlager",nil, {"Tooki möchte, dass Ihr Euch bei Unteroffizier Chawni im Steinbrecherlager in den Wäldern von Terokkar meldet."}},
		[10449] = {"Apothekerin Zelana",nil, {"Vurtok Axtbrecher in Thrallmar möchte, dass Ihr die Blutprobe eines Knochenmalmers zu Apothekerin Zelana im Häschersturz bringt."}},
		[10450] = {"Blut der Knochenmalmer",nil, {"Bringt 12 Phiolen mit Knochenmalmerblut zu Vurtok Axtbrecher in Thrallmar."}},
		[10451] = {"Flucht aus der Zisterne der Echsennarbe",nil, {"Begleitet Erdheilerin Wilda aus der Zisterne der Echsennarbe hinaus und sucht dann Erdheiler Torlok beim Altar der Verdammnis am westlichen Rang der Hand von Gul'dan auf."}},
		[10454] = {"FLAG - OFF THE RAILS",nil, nil},
		[10455] = {"Die nahende Wildnis",nil, {"Rina Mondquell möchte, dass Ihr so schnell wie möglich 12 Hainpirscherluchse erlegt und dann zu ihr in Sylvanaar im Schergrat zurückkehrt."}},
		[10456] = {"Marodierende Wölfe",nil, {"Rina Mondquell möchte, dass Ihr 4 Schwänze eines Terrorwolfs der Donnerfürsten beschafft. Kehrt danach zu ihr in Sylvanaar im Schergrat zurück."}},
		[10457] = {"Die Eigenen schützen",nil, {"Benutzt Rinas Zweig bei 5 Hainsetzlingen, um Verteidigung für den lebenden Hain herbeizurufen. Kehrt dann zu Rina Mondquell in Sylvanaar im Schergrat zurück."}},
		[10458] = {"Wütende Geister des Feuers und der Erde",nil, {"Erdheiler Torlok beim Altar der Verdammnis im Schattenmondtal möchte, dass Ihr das Totem der Geister benutzt, um 8 Erdenseelen und 8 Feuerseelen einzufangen."}},
		[10459] = {"Von der Expedition des Cenarius verehrt",nil, {"Ysiel Windsänger bei der Zuflucht des Cenarius möchte, dass Euch einen respektvollen Ruf bei der Expedition des Cenarius verdient."}},
		[10460] = {"Das Versprechen des Verteidigers",nil, nil},
		[10461] = {"Das Versprechen des Bewahrers",nil, nil},
		[10462] = {"Das Versprechen des Helden",nil, {"Seid Ihr sicher, dass Ihr diesen Pfad gehen wollt, <Name>? Das ist keine Entscheidung, die leichtfertig getroffen werden sollte. Dieser Ring wurde mit einem Tropfen aus dem Brunnen der Ewigkeit verstärkt. Wenn Ihr Euch den Wächtern der Sande gegenüber beweist, wird er an Macht gewinnen."}},
		[10463] = {"Das Versprechen des Weisen",nil, {"Seid Ihr sicher, dass Ihr diesen Pfad gehen wollt, <Name>? Das ist keine Entscheidung, die leichtfertig getroffen werden sollte. Dieser Ring wurde mit einem Tropfen aus dem Brunnen der Ewigkeit verstärkt. Wenn Ihr Euch den Wächtern der Sande gegenüber beweist, wird er an Macht gewinnen."}},
		[10464] = {"Das Gelöbnis des Weisen",nil, {"Bringt Euer Band der Ewigkeit zu Soridormi in den Höhlen der Zeit, wenn Ihr einen wohlwollenden Ruf bei den Wächtern der Sande erlangt habt."}},
