		[10720] = {"Die kleinsten Wesen",nil, {"Begebt Euch zum Murmeltierbau und schlagt die Kodobalgtrommel. Vergiftet die drei Fässer im Lager der Blutschläger, während Ihr das Murmeltier kontrolliert. Kehrt zu Rexxar in der Donnerfeste im Schergrat zurück, wenn Ihr Eure Aufgabe erledigt habt."}},
		[10721] = {"Grulloc einen Eber aufbinden",nil, {"Beschafft Grullocs Sack und bringt ihn zu Baron Zobelmähne beim Zirkel des Blutes im Schergrat."}},
		[10722] = {"Treffen am Pechschwingenkoven",nil, {"Sammelt genügend Gewandfetzen von verschiedenen Wyrmkultisten, um eine Aufseherverkleidung herzustellen. Benutzt die Verkleidung, um am Treffen mit Kolphis Dunkelschuppe teilzunehmen. Meldet Euch mit Euren Erkenntnissen bei Baumhüter Chawn im ewigen Hain im Schergrat zurück."}},
		[10723] = {"Gorgrom der Drachenfresser",nil, {"Geht nach Fels'mok und sammelt 3 grausige Totems von den Ogern von Fels'mok. Stellt danach Zobelmähnes Falle bei Gorgroms Altar auf, um Gorgrom den Drachenfresser zu rufen und zu töten. Wenn Grogrom tot ist, platziert die drei grausigen Totems in der Nähe seiner Leiche. Kehrt danach zu Rexxar in der Donnerfeste im Schergrat zurück."}},
