   [693] = {"Seelenstein herstellen", "Rang 1", "Lässt einen schwachen Seelenstein entstehen. Der Seelenstein kann zum Speichern der Seele eines Ziels benutzt werden. Wenn das Ziel stirbt, während seine Seele gespeichert ist, kann es mit $3026s1 Gesundheit und $3026q1 Mana wiederbelebt werden.\r\n\r\n\r\n\r\nHerbeigezauberte Gegenstände verschwinden, wenn Ihr Euch länger als 15 Minuten ausloggt.", "", 0, 50, 10485760, 0,},
   [694] = {"Spöttischer Schlag", "Rang 1", "Ein spottender Angriff, der $s1 Schaden und ein mittleres Maß an Bedrohung verursacht. Zwingt das Ziel $d lang dazu, Angriffe auf Euch zu fokussieren.", "Verspottet.", 0, 40, 1342177280, 0,},
   [695] = {"Schattenblitz", "Rang 2", "Sendet einen schattigen Blitz zum Gegner, der $s1 Schattenschaden verursacht.", "", 0, 50, 10, 0,},
   [697] = {"Leerwandler beschwören", "Beschwören", "Beschwört einen Leerwandler, der unter dem Befehl des Hexenmeisters steht.", "", 0, 50, 5368709120, 0,},
   [699] = {"Blutsauger", "Rang 2", "Überträgt alle $t1 Sek. $s1 Gesundheit vom Ziel auf den Zaubernden. Hält $d lang an.", "Entzieht alle $t1 Sek. $s1 Gesundheit und überträgt sie auf den Zaubernden.", 0, 50, 80, 0,},
   [700] = {"Schlaf", "Rang 1", "Versetzt das feindliche Ziel bis zu $d lang in Schlaf. Jeglicher zugefügte Schaden wird das Ziel wieder aufwecken. Es kann immer nur jeweils ein Ziel schlafen. Unzuverlässig gegen Ziele über Stufe 30.", "Schlafend.", 0, 30, 00, 0,},
   [702] = {"Fluch der Schwäche", "Rang 1", "Die Nahkampfangriffskraft des Ziels wird $d lang um $s1 reduziert. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "Nahkampfangriffskraft um $s1 verringert.", 0, 50, 327680, 0,},
   [703] = {"Erdrosseln", "Rang 1", "Erdrosselt den Feind und verursacht $d lang insgesamt $o1 Schaden, erhöht durch Eure Angriffskraft. Muss verstohlen und hinter dem Ziel sein. Gewährt $s2 $lCombopunkt:Combopunkte;.", "Alle $t1 Sek. $s1 Schaden.", 0, 80, 2560, 0,},
   [704] = {"Fluch der Tollkühnheit", "Rang 1", "Verflucht das Ziel mit Tollkühnheit, wodurch sich die Nahkampfangriffskraft $d lang um $s1 erhöht, jedoch die Rüstung um $s2 verringert. Mit einem Fluch belegte Feinde fliehen nicht und ignorieren Furcht- und Entsetzenseffekte. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "Nahkampfangriffskraft um $s1 erhöhen und Rüstung um $s2 verringern. Das Ziel flieht nicht und ignoriert Furcht- und Entsetzenseffekte.", 0, 50, -21474836480, 0,},
   [705] = {"Schattenblitz", "Rang 3", "Sendet einen schattigen Blitz zum Gegner, der $s1 Schattenschaden verursacht.", "", 0, 50, 10, 0,},
   [706] = {"Dämonenrüstung", "Rang 1", "Schützt den Zaubernden und erhöht die Rüstung um $s1, den Schattenwiderstand um $s2 und stellt alle 5 Sek. $s3 Gesundheit wieder her. Es kann immer nur ein Rüstungszauber zur selben Zeit auf dem Hexenmeister aktiv sein. Hält $d an.", "Erhöht Rüstung um $s1, Schattenwiderstand um $s2 und stellt alle 5 Sek. $s3 Gesundheit wieder her.", 0, 50, 00, 32,},
   [707] = {"Feuerbrand", "Rang 2", "Verbrennt den Feind und fügt $s2 Feuerschaden sowie $d lang insgesamt $o1 zusätzlichen Feuerschaden zu.", "Alle $t1 Sek. $s1 Feuerschaden.", 0, 50, 40, 0,},
   [709] = {"Blutsauger", "Rang 3", "Überträgt alle $t1 Sek. $s1 Gesundheit vom Ziel auf den Zaubernden. Hält $d lang an.", "Entzieht alle $t1 Sek. $s1 Gesundheit und überträgt sie auf den Zaubernden.", 0, 50, 80, 0,},
   [710] = {"Verbannen", "Rang 1", "Verbannt das feindliche Ziel und verhindert damit jegliche Aktion, macht es aber bis zu $d lang unverwundbar. Es kann immer nur jeweils ein Ziel verbannt werden. Funktioniert nur bei Dämonen und Elementaren.", "Unverwundbar, aber kann nicht agieren.", 0, 50, 00, 0,},
   [712] = {"Sukkubus beschwören", "Beschwören", "Beschwört einen Sukkubus, der unter dem Befehl des Hexenmeisters steht.", "", 0, 50, 5368709120, 0,},
   [724] = {"Brunnen des Lichts", "Rang 1", "Erschafft einen heiligen Brunnen des Lichts. Mitglieder Eurer Gruppe oder Schlachtgruppe können den Brunnen des Lichts anklicken, um im Verlauf von $7001d insgesamt $7001o1 Gesundheit wiederherzustellen. Jeglicher erlittene Schaden bricht den Effekt ab. Der Brunnen des Lichts bleibt $d lang bestehen oder bis alle 5 Aufladungen verbraucht sind.", "", 0, 60, 10737418240, 0,},
   [740] = {"Gelassenheit", "Rang 1", "Heilt bei allen in der Nähe befindlichen Gruppenmitgliedern $d lang alle $t2 Sek. $s1 Gesundheit. Der Druide muss seine Kräfte kanalisieren, um den Zauber aufrechtzuerhalten.", "Heilt in der Nähe befindliche Gruppenmitglieder alle $t2 Sek. um $s1.", 0, 70, 1280, 0,},
   [755] = {"Lebenslinie", "Rang 1", "Gibt dem Begleiter des Zaubernden $d lang in jeder Sekunde $s1 Gesundheit, solange der Zaubernde seine Kräfte kanalisiert.", "Überträgt Leben.", 0, 50, 167772160, 0,},
   [759] = {"Manaachat herbeizaubern", "", "Zaubert einen Manaachat herbei, der zum sofortigen Wiederherstellen von $5405s1 Mana verwendet werden kann.\r\n\r\n\r\n\r\nHerbeigezauberte Gegenstände verschwinden, wenn Ihr Euch länger als 15 Minuten ausloggt.", "", 0, 30, 10737418240, 0,},
   [768] = {"Katzengestalt", "Gestaltwandel", "Gestaltwandlung in Katzengestalt, erhöht Nahkampfangriffskraft um $3025s1 plus Beweglichkeit. Beschützt den Zaubernden auch vor Verwandlungseffekten und erlaubt den Einsatz von verschiedenen Katzenfähigkeiten.\r\n\r\n\r\n\r\nDer Akt des Gestaltwandelns befreit den Zaubernden von verwandlungs- und bewegungseinschränkenden Effekten.", "Immun gegen Verwandlungseffekte. Erhöht die Nahkampfangriffskraft um $3025s1 plus Beweglichkeit.", 0, 70, -21474836480, 0,},
   [769] = {"Prankenhieb", "Rang 3", "Schlägt $x1 in der Nähe befindliche Feinde und verursacht $s1 Schaden, erhöht durch Angriffskraft.", "", 0, 70, 00, 1048576,},
   [770] = {"Feenfeuer", "Rang 1", "Verringert die Rüstung des Ziels $d lang um $s1. Während der Wirkungsdauer kann das Ziel weder Verstohlenheit anwenden noch unsichtbar werden.", "Verringert Rüstung um $s1. Kann weder Verstohlenheit anwenden noch unsichtbar werden.", 0, 70, 10240, 0,},
   [772] = {"Verwunden", "Rang 1", "Verwundet das Ziel, lässt es $d lang bluten und fügt damit $o1 Schaden plus ${0.00743*3*(($MWB+$mwb)/2+$AP/14*$MWS)} zusätzlichen Schaden (basierend auf Waffenschaden) zu.", "Das Bluten fügt alle $t1 Sek. $s1 Schaden plus zusätzlichen Waffenschaden zu.", 0, 40, 320, 0,},
   [774] = {"Verjüngung", "Rang 1", "Heilt beim Ziel $d lang insgesamt $o1 Schaden.", "Heilt alle $t1 Sek. $s1 Schaden.", 0, 70, 160, 0,},
   [778] = {"Feenfeuer", "Rang 2", "Verringert die Rüstung des Ziels $d lang um $s1. Während der Wirkungsdauer kann das Ziel weder Verstohlenheit anwenden noch unsichtbar werden.", "Verringert Rüstung um $s1. Kann weder Verstohlenheit anwenden noch unsichtbar werden.", 0, 70, 10240, 0,},
   [779] = {"Prankenhieb", "Rang 1", "Schlägt $x1 in der Nähe befindliche Feinde und verursacht $s1 Schaden, erhöht durch Angriffskraft.", "", 0, 70, 00, 1048576,},
   [780] = {"Prankenhieb", "Rang 2", "Schlägt $x1 in der Nähe befindliche Feinde und verursacht $s1 Schaden, erhöht durch Angriffskraft.", "", 0, 70, 00, 1048576,},
   [781] = {"Rückzug", "Rang 1", "Versucht, sich vom Ziel zurückzuziehen und verringert dadurch die Bedrohung. Charakter verlässt Kampfmodus.", "", 0, 90, 00, 16384,},
   [782] = {"Dornen", "Rang 2", "Aus dem befreundeten Ziel sprießen Dornen, die jedem, der es schlägt, $s1 Naturschaden zufügen. Hält $d lang an.", "Verursacht $s1 Naturschaden bei Angreifern.", 0, 70, 2560, 0,},
   [783] = {"Reisegestalt", "Gestaltwandel", "Verwandelt Euch in eine Reisegestalt und erhöht dadurch Euer Bewegungstempo um $5419s1%. Beschützt den Zaubernden auch vor Verwandlungseffekten. Kann nur draußen verwendet werden.\r\n\r\n\r\n\r\nDer Akt des Gestaltwandelns befreit den Zaubernden von verwandlungs- und bewegungseinschränkenden Effekten.", "Immun gegen Verwandlungseffekte. Bewegungstempo um $5419s1% erhöht.", 0, 70, 00, 16384,},
   [834] = {"Geringe Rüstung", "", "Erhöht die Rüstung $d lang um $s1. Wächterelixier.", "Erhöht die Rüstung um $s1. Wächterelixier.", 0, 130, 00, 0,},
   [837] = {"Frostblitz", "Rang 3", "Schleudert einen Frostblitz auf den Feind, der $s2 Frostschaden verursacht und $d lang das Bewegungstempo um $s1% reduziert.", "Bewegungstempo um $s1% verringert.", 0, 30, 15728960, 0,},
   [845] = {"Spalten", "Rang 1", "Ein weitreichender Angriff, der dem Ziel und seinem nächsten Verbündeten Euren Waffenschaden sowie $s1 zusätzlichen Schaden zufügt.", "", 0, 40, 41943040, 0,},
   [853] = {"Hammer der Gerechtigkeit", "Rang 1", "Betäubt das Ziel $d lang.", "Betäubt.", 0, 100, 20480, 0,},
   [857] = {"Khadgars Öffnung", "Rang 2", "Schließt auf magische Weise Schatztruhen und andere verschlossene Gegenstände auf.", "", 0, 30, 00, 0,},
   [865] = {"Frostnova", "Rang 2", "Fügt in der Nähe des Zaubernden befindlichen Feinden $s1 Frostschaden zu und friert sie bis zu $d lang ein. Erlittener Schaden kann den Effekt unterbrechen.", "Festgefroren.", 0, 30, 5243520, 0,},
   [871] = {"Schildwall", "", "Verringert den durch Nahkampfangriffe, Distanzangriffe und Zauber erlittenen Schaden $d lang um $s1%.", "Schaden um $s1% verringert.", 0, 40, 81920, 0,},
   [879] = {"Exorzismus", "Rang 1", "Verursacht $s1 Heiligschaden an einem untoten oder dämonischen Ziel.", "", 0, 100, 00, 2,},
   [905] = {"Blitzschlagschild", "Rang 3", "Der Zaubernde ist von $n Kugelblitzen umgeben. Wenn der Zaubernde von einem Nahkampf-, Fernkampf- oder Zauberangriff getroffen wird, erleidet der Angreifende $26366s1 Naturschaden. Dies kostet einen Kugelblitz. Es wird immer nur ein einzelner Kugelblitz alle paar Sekunden abgefeuert. Hält $d lang an. Es kann immer nur ein Elementarschild auf dem Schamanen aktiv sein.", "Verursacht beim Angreifer bei einem Treffer $26366s1 Naturschaden.", 0, 110, 10240, 0,},
   [913] = {"Welle der Heilung", "Rang 4", "Heilt ein befreundetes Ziel um $s1.", "", 0, 110, 640, 0,},
   [915] = {"Blitzschlag", "Rang 4", "Schleudert einen Blitz auf das Ziel und fügt ihm $s1 Naturschaden zu.", "", 0, 110, 10, 0,},
   [921] = {"Taschendiebstahl", "", "Einen Taschendiebstahl am Ziel begehen.", "", 0, 80, -21474836480, 0,},
   [930] = {"Kettenblitzschlag", "Rang 2", "Schleudert einen Blitzschlag auf den Feind, der $s1 Naturschaden verursacht und zu in der Nähe befindlichen Feinden weiterspringt. Jeder Sprung verringert den Schaden um 30%. Wirkt auf insgesamt $x1 Ziele.", "", 0, 110, 20, 0,},
   [939] = {"Welle der Heilung", "Rang 5", "Heilt ein befreundetes Ziel um $s1.", "", 0, 110, 640, 0,},
   [943] = {"Blitzschlag", "Rang 5", "Schleudert einen Blitz auf das Ziel und fügt ihm $s1 Naturschaden zu.", "", 0, 110, 10, 0,},
   [945] = {"Blitzschlagschild", "Rang 4", "Der Zaubernde ist von $n Kugelblitzen umgeben. Wenn der Zaubernde von einem Nahkampf-, Fernkampf- oder Zauberangriff getroffen wird, erleidet der Angreifende $26367s1 Naturschaden. Dies kostet einen Kugelblitz. Es wird immer nur ein einzelner Kugelblitz alle paar Sekunden abgefeuert. Hält $d lang an. Es kann immer nur ein Elementarschild auf dem Schamanen aktiv sein.", "Verursacht beim Angreifer bei einem Treffer $26367s1 Naturschaden.", 0, 110, 10240, 0,},
   [959] = {"Welle der Heilung", "Rang 6", "Heilt ein befreundetes Ziel um $s1.", "", 0, 110, 640, 0,},
   [970] = {"Schattenwort: Schmerz", "Rang 3", "Ein Wort der Dunkelheit, das $d lang insgesamt $o1 Schattenschaden zufügt.", "Alle $t1 Sek. $s1 Punkte Schattenschaden.", 0, 60, 327680, 0,},
   [974] = {"Erdschild", "Rang 1", "Schützt das Ziel mit einem Erdschild, der eine Chance von $s2% gewährt, Zauberunterbrechungen durch Schaden zu vermeiden. Angriffe heilen das geschützte Ziel um $s1, dieser Effekt tritt aber nur einmal alle paar Sekunden auf. $n Aufladungen. Hält $d lang an. Dieser Schild kann immer nur auf einem Ziel gleichzeitig aktiv sein, und auf jedem Ziel kann nur ein Elementarschild aktiv sein.", "$s2% Chance, Zauberunterbrechung durch Schaden zu vermeiden. Angriffe heilen das geschützte Ziel um $s1.", 0, 110, 00, 1024,},
   [976] = {"Schattenschutz", "Rang 1", "Erhöht $d lang die Widerstandskraft des Ziels gegen alle Schattenzauber um $s1.", "Schattenwiderstand erhöht um $s1.", 0, 60, 2560, 0,},
   [980] = {"Fluch der Pein", "Rang 1", "Verflucht das Ziel mit Pein und fügt $d lang insgesamt $o1 Schattenschaden zu. Zuerst wird der Schaden langsam zugefügt und nimmt dann zu, bis der Fluch seine Gesamtdauer erreicht hat. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "$d lang insgesamt $o1 Schattenschaden.", 0, 50, 10240, 0,},
   [982] = {"Tier wiederbeleben", "", "Bringt Euer totes Tier mit $s1% seiner Grundgesundheit wieder ins Leben zurück.", "", 0, 90, 167772160, 0,},
   [984] = {"Göttliche Pein", "Rang 4", "Zerschmettert einen Feind und fügt ihm $s1 Heiligschaden zu.", "", 0, 60, 1280, 0,},
   [988] = {"Magiebannung", "Rang 2", "Bannt auf dem Ziel liegende Magie und entfernt $s1 $lschädlichen Zauber:schädliche Zauber; von einem Freund oder $s1 $lnützlichen Zauber:nützliche Zauber; von einem Feind.", "", 0, 60, 00, 1,},
   [990] = {"Essen herbeizaubern", "Rang 3", "Zaubert $s1 $lLaib:Laibe; Roggenbrot herbei und versorgt den Magier sowie seine Verbündeten mit etwas Essbarem.\r\n\r\n\r\n\r\nHerbeigezauberte Gegenstände verschwinden, wenn Ihr Euch länger als 15 Minuten ausloggt.", "", 0, 30, 10737418240, 0,},
   [992] = {"Schattenwort: Schmerz", "Rang 4", "Ein Wort der Dunkelheit, das $d lang insgesamt $o1 Schattenschaden zufügt.", "Alle $t1 Sek. $s1 Punkte Schattenschaden.", 0, 60, 327680, 0,},
   [996] = {"Gebet der Heilung", "Rang 2", "Ein wirkungsvolles Gebet heilt alle Gruppenmitglieder in einem Umkreis von $a1 Metern um $s1.", "", 0, 60, 5120, 0,},
   [1002] = {"Augen des Wildtiers", "", "Lässt Euch die direkte Kontrolle über Euer Tier übernehmen und $d lang durch seine Augen sehen.", "Tier direkt kontrollieren.", 0, 90, 41943040, 0,},
   [1004] = {"Göttliche Pein", "Rang 5", "Zerschmettert einen Feind und fügt ihm $s1 Heiligschaden zu.", "", 0, 60, 1280, 0,},
   [1006] = {"Inneres Feuer", "Rang 4", "Der Zaubernde wird von einem Schub heiliger Energie erfüllt, der seine Rüstung um $s1 erhöht. Ein erlittener Nahkampf- oder Distanzangriffstreffer verbraucht eine Aufladung. Hält $d lang an oder bis alle $n Aufladungen verbraucht sind.", "Erhöht die Rüstung um $s1.", 0, 60, 20, 0,},
   [1008] = {"Magie verstärken", "Rang 1", "Verstärkt gegen das anvisierte Gruppenmitglied gerichtete Magie, wobei der von Zaubern zugefügte Schaden um bis zu $s1 und Heilzauber um bis zu $s2 erhöht werden. Hält $d lang an.", "Erhöht erlittenen Magieschaden um bis zu $s1 und Heilung um bis zu $s2.", 0, 30, 81920, 0,},
   [1010] = {"Fluch der Torheit", "Rang 1", "Verflucht das Ziel mit Torheit, verringert Intelligenz und Willenskraft alle $t3 Sek. um $s1, bis beides um insgesamt $*15;s1 verringert worden ist. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "Intelligenz und Willenskraft verringert.", 0, 50, -21474836480, 0,},
   [1014] = {"Fluch der Pein", "Rang 2", "Verflucht das Ziel mit Pein und fügt $d lang insgesamt $o1 Schattenschaden zu. Zuerst wird der Schaden langsam zugefügt und nimmt dann zu, bis der Fluch seine Gesamtdauer erreicht hat. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "$d lang insgesamt $o1 Schattenschaden.", 0, 50, 10240, 0,},
   [1020] = {"Gottesschild", "Rang 2", "Schützt den Paladin $d lang vor allen Schäden und Zaubern, erhöht jedoch die Zeit zwischen seinen Angriffen um $s2%. Einmal geschützt, kann der Paladin $25771d lang nicht erneut durch Gottesschild, Göttlicher Schutz oder Segen des Schutzes unverwundbar gemacht werden oder Zornige Vergeltung nutzen.", "Immun gegen alle Angriffe und Zauber, die Zeit zwischen Angriffen ist jedoch um $s2% erhöht.", 0, 100, 41943040, 0,},
   [1022] = {"Segen des Schutzes", "Rang 1", "Ein anvisiertes Gruppenmitglied ist $d lang vor allen körperlichen Angriffen geschützt, allerdings kann es dabei weder angreifen noch körperliche Fähigkeiten einsetzen. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein. Einmal geschützt, kann das Ziel $25771d lang nicht erneut durch Gottesschild, Göttlicher Schutz oder Segen des Schutzes unverwundbar gemacht werden.", "Immun gegen körperliche Angriffe. Kann weder angreifen noch körperliche Fähigkeiten verwenden.", 0, 100, 1280, 0,},
   [1026] = {"Heiliges Licht", "Rang 4", "Heilt ein befreundetes Ziel um $s1.", "", 0, 100, -21474836480, 0,},
   [1032] = {"Aura der Hingabe", "Rang 5", "Gibt Gruppenmitgliedern in einem Umkreis von $a1 Metern $s1 zusätzliche Rüstung. Spieler können pro Paladin immer nur jeweils eine Aura besitzen.", "Erhöht die Rüstung um $s1.", 0, 100, 640, 0,},
   [1038] = {"Segen der Rettung", "", "Belegt ein Gruppenmitglied mit einem Segen und verringert dabei jegliche erzeugte Bedrohung $d lang um $s1%. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein.", "Verringert Bedrohung um $s1%.", 0, 100, 2560, 0,},
   [1042] = {"Heiliges Licht", "Rang 5", "Heilt ein befreundetes Ziel um $s1.", "", 0, 100, -21474836480, 0,},
   [1044] = {"Segen der Freiheit", "", "Belegt ein befreundetes Ziel mit einem Segen und befreit es $d lang von allen bewegungseinschränkenden Effekten. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein.", "Immun gegen bewegungseinschränkende Effekte.", 0, 100, 2684354720, 0,},
   [1058] = {"Verjüngung", "Rang 2", "Heilt beim Ziel $d lang insgesamt $o1 Schaden.", "Heilt alle $t1 Sek. $s1 Schaden.", 0, 70, 160, 0,},
   [1062] = {"Wucherwurzeln", "Rang 2", "Lässt das Ziel an Ort und Stelle festwurzeln und verursacht $d lang insgesamt $o2 Naturschaden. Erlittener Schaden kann den Effekt unterbrechen. Kann nur draußen verwendet werden.", "Unbeweglich. Alle $t2 Sek. $s2 Naturschaden verursacht.", 0, 70, 5120, 0,},
   [1064] = {"Kettenheilung", "Rang 1", "Heilt das befreundete Ziel um $s1 und springt dann weiter, um weitere in der Nähe befindliche Ziele zu heilen. Wenn es auf ein Gruppenmitglied gewirkt wird, springt die Heilung nur auf andere Gruppenmitglieder über. Nach jedem Sprung verringert sich die Heilwirkung um 50%. Heilt $x1 Ziele insgesamt.", "", 0, 110, 2560, 0,},
   [1066] = {"Wassergestalt", "Gestaltwandel", "Gestaltwandlung in eine Wassergestalt. Das Schwimmtempo wird um $5421s1% erhöht und der Druide kann unter Wasser atmen. Beschützt den Zaubernden auch vor Verwandlungseffekten.\r\n\r\n\r\n\r\nDer Akt des Gestaltwandelns befreit den Zaubernden von verwandlungs- und bewegungseinschränkenden Effekten.", "Immun gegen Verwandlungseffekte. Erhöht das Schwimmtempo um $5421s1% und ermöglicht Unterwasseratmung.", 0, 70, 5368709120, 0,},
   [1075] = {"Dornen", "Rang 3", "Aus dem befreundeten Ziel sprießen Dornen, die jedem, der es schlägt, $s1 Naturschaden zufügen. Hält $d lang an.", "Verursacht $s1 Naturschaden bei Angreifern.", 0, 70, 2560, 0,},
   [1079] = {"Zerfetzen", "Rang 1", "Finishing-Move, der Schaden im Lauf der Zeit verursacht. Der Schaden erhöht sich pro Combopunkt sowie durch Eure Angriffskraft:\r\n\r\n   1 Punkt: $d lang 42 Schaden.\r\n\r\n   2 Punkte: $d lang 66 Schaden.\r\n\r\n   3 Punkte: $d lang 90 Schaden.\r\n\r\n   4 Punkte: $d lang 114 Schaden.\r\n\r\n   5 Punkte: $d lang 138 Schaden.", "Alle $t1 Sek. Blutungsschaden.", 0, 70, 83886080, 0,},
   [1082] = {"Klaue", "Rang 1", "Den Feind mit Klauen beharken und ihm so zusätzlich $s1 Schaden zufügen. Gewährt $s2 $lCombopunkt:Combopunkte;.", "", 0, 70, 40960, 0,},
   [1086] = {"Dämonenrüstung", "Rang 2", "Schützt den Zaubernden und erhöht die Rüstung um $s1, den Schattenwiderstand um $s2 und stellt alle 5 Sek. $s3 Gesundheit wieder her. Es kann immer nur ein Rüstungszauber zur selben Zeit auf dem Hexenmeister aktiv sein. Hält $d an.", "Erhöht Rüstung um $s1, Schattenwiderstand um $s2 und stellt alle 5 Sek. $s3 Gesundheit wieder her.", 0, 50, 00, 32,},
   [1088] = {"Schattenblitz", "Rang 4", "Sendet einen schattigen Blitz zum Gegner, der $s1 Schattenschaden verursacht.", "", 0, 50, 10, 0,},
   [1090] = {"Schlaf", "Rang 2", "Versetzt das feindliche Ziel bis zu $d lang in Schlaf. Jeglicher zugefügte Schaden wird das Ziel wieder aufwecken. Es kann immer nur jeweils ein Ziel schlafen.", "Schlafend.", 0, 30, 00, 0,},
   [1094] = {"Feuerbrand", "Rang 3", "Verbrennt den Feind und fügt $s2 Feuerschaden sowie $d lang insgesamt $o1 zusätzlichen Feuerschaden zu.", "Alle $t1 Sek. $s1 Feuerschaden.", 0, 50, 40, 0,},
   [1098] = {"Dämonensklave", "Rang 1", "Versklavt den Zieldämon bis zur Stufe $m1 und zwingt ihn, Euren Befehlen Folge zu leisten. Während er versklavt ist, wird die Zeit zwischen Angriffen des Dämons um $s2% erhöht und sein Zaubertempo um $s3% verlangsamt. Hält bis zu $d lang an. Wenn Ihr denselben Dämon mehrmals versklavt, wird es mit jedem Versuch schwieriger, ihn zu kontrollieren.", "Versklavt.", 0, 50, 20480, 0,},
   [1106] = {"Schattenblitz", "Rang 5", "Sendet einen schattigen Blitz zum Gegner, der $s1 Schattenschaden verursacht.", "", 0, 50, 10, 0,},
   [1108] = {"Fluch der Schwäche", "Rang 2", "Die Nahkampfangriffskraft des Ziels wird $d lang um $s1 reduziert. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "Nahkampfangriffskraft um $s1 verringert.", 0, 50, 327680, 0,},
   [1120] = {"Seelendieb", "Rang 1", "Entzieht dem Ziel die Seele und verursacht $d lang insgesamt $o2 Schattenschaden. Wenn das Ziel beim Entziehen der Seele stirbt und Erfahrung oder Ehrenpunkte abgibt, erhält der Zaubernde einen Seelensplitter. Seelensplitter werden für andere Zauber benötigt.", "Alle $t2 Sek. $s2 Schattenschaden.", 0, 50, 163840, 0,},
   [1126] = {"Mal der Wildnis", "Rang 1", "Erhöht $d lang die Rüstung des befreundeten Ziels um $s1.", "Erhöht die Rüstung um $s1.", 0, 70, 2621440, 0,},
   [1130] = {"Mal des Jägers", "Rang 1", "Legt das Mal des Jägers auf das Ziel und erhöht dabei die Distanzangriffskraft aller Angreifer gegen dieses Ziel um $s2 sowie jedesmal, wenn es durch einen Distanzangriff getroffen wird, um zusätzliche ${$m2/10} bis zu einem Höchstbetrag von ${$m2*4}. Zusätzlich ist mit dieser Fähigkeit das Ziel immer sichtbar, auch wenn es in den Verstohlenheitsmodus geht oder unsichtbar wird. Das Ziel wird außerdem auf der Minikarte angezeigt. Hält $d lang an.", "Alle Angreifer erhalten $s2 Distanzangriffskraft gegenüber diesem Ziel.", 0, 90, 10240, 0,},
   [1152] = {"Läutern", "", "Läutert ein befreundetes Ziel und hebt dabei $s1 Krankheits$leffekt:effekte; und $s2 Gift$leffekt:effekte; auf.", "", 0, 100, 40960, 0,},
   [1160] = {"Demoralisierender Ruf", "Rang 1", "Verringert $d lang die Nahkampfangriffskraft von Feinden innerhalb eines Radius von $a1 Metern um $s1.", "Verringert Nahkampfangriffskraft um $s1.", 0, 40, 1310720, 0,},
   [1161] = {"Herausforderungsruf", "", "Zwingt alle Feinde in einem Umkreis von $a1 Metern, $d lang ihre Angriffe auf Euch zu konzentrieren.", "Verspottet.", 0, 40, 5242880, 0,},
   [1243] = {"Machtwort: Seelenstärke", "Rang 1", "Macht erfüllt das Ziel und erhöht $d lang seine Ausdauer um $s1.", "Erhöht Ausdauer um $w1.", 0, 60, 80, 0,},
   [1244] = {"Machtwort: Seelenstärke", "Rang 2", "Macht erfüllt das Ziel und erhöht $d lang seine Ausdauer um $s1.", "Erhöht Ausdauer um $w1.", 0, 60, 80, 0,},
   [1245] = {"Machtwort: Seelenstärke", "Rang 3", "Macht erfüllt das Ziel und erhöht $d lang seine Ausdauer um $s1.", "Erhöht Ausdauer um $w1.", 0, 60, 80, 0,},
   [1329] = {"Verstümmeln", "Rang 1", "Greift sofort mit beiden Waffen an und fügt mit jeder Waffe zusätzlich $5374s1 Schaden zu. Der Schaden gegen vergiftete Ziele ist um 50% erhöht. Muss hinter dem Ziel sein. Gewährt 2 Combopunkte.", "", 0, 80, 00, 0,},
   [1430] = {"Verjüngung", "Rang 3", "Heilt beim Ziel $d lang insgesamt $o1 Schaden.", "Heilt alle $t1 Sek. $s1 Schaden.", 0, 70, 160, 0,},
   [1449] = {"Arkane Explosion", "Rang 1", "Verursacht eine Explosion arkaner Magie um den Zaubernden herum und fügt allen Zielen in einem Umkreis von $a1 Metern $s1 Arkanschaden zu.", "", 0, 30, 40960, 0,},
   [1454] = {"Aderlass", "Rang 1", "Wandelt $s1 Gesundheit in $s1 Mana um.", "", 0, 50, 2621440, 0,},
   [1455] = {"Aderlass", "Rang 2", "Wandelt $s1 Gesundheit in $s1 Mana um.", "", 0, 50, 2621440, 0,},
   [1456] = {"Aderlass", "Rang 3", "Wandelt $s1 Gesundheit in $s1 Mana um.", "", 0, 50, 2621440, 0,},
   [1459] = {"Arkane Intelligenz", "Rang 1", "Erhöht $d lang die Intelligenz des Ziels um $s1.", "Erhöht Intelligenz um $s1.", 0, 30, 10240, 0,},
   [1460] = {"Arkane Intelligenz", "Rang 2", "Erhöht $d lang die Intelligenz des Ziels um $s1.", "Erhöht Intelligenz um $s1.", 0, 30, 10240, 0,},
   [1461] = {"Arkane Intelligenz", "Rang 3", "Erhöht $d lang die Intelligenz des Ziels um $s1.", "Erhöht Intelligenz um $s1.", 0, 30, 10240, 0,},
   [1463] = {"Manaschild", "Rang 1", "Absorbiert $s1 Schaden und entzieht stattdessen Mana. Entzieht pro absorbiertem Schaden $e Mana. Hält $d lang an.", "Absorbiert Schaden und entzieht stattdessen Mana.", 0, 30, 327680, 0,},
   [1464] = {"Zerschmettern", "Rang 1", "Zerschmettert den Gegner und verursacht Waffenschaden plus $s1.", "", 0, 40, 20971520, 0,},
   [1490] = {"Fluch der Elemente", "Rang 1", "Verflucht das Ziel und verringert $d lang seinen Arkan-, Feuer-, Frost- und Schattenwiderstand um $s1. Erhöht außerdem den erlittenen Arkan-, Feuer-, Frost- und Schattenschaden des Ziels um $s2%. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "Verringert Arkan-, Feuer-, Frost- und Schattenwiderstand um $s1. Erhöht den erlittenen Arkan-, Feuer-, Frost- und Schattenschaden um $s2%.", 0, 50, 00, 512,},
   [1495] = {"Mungobiss", "Rang 1", "Ein Gegenangriff auf den Feind fügt ihm ${$AP*0.2+$m1} Schaden zu. Kann nur eingesetzt werden, nachdem Ihr ausgewichen seid.", "", 0, 90, 20, 0,},
   [1499] = {"Eiskältefalle", "Rang 1", "Eine Eiskältefalle platzieren, die den ersten sich nähernden Feind einfriert und $3355d lang jegliche Aktion des Feindes verhindert. Jeglicher erlittener Schaden wird das Eis wieder brechen. Die Falle bleibt $d lang bestehen. Es kann immer nur jeweils eine Falle aktiv sein.", "", 0, 90, 1280, 0,},
   [1510] = {"Salve", "Rang 1", "Feuert kontinuierlich eine Salve Munition auf das Zielgebiet und verursacht $d lang pro Sekunde $42243s1 Arkanschaden bei feindlichen Zielen im Umkreis von $a1 Metern.", "", 0, 90, 81920, 0,},
   [1513] = {"Wildtier ängstigen", "Rang 1", "Jagt einem Wildtier Angst ein, woraufhin es bis zu $d lang voller Furcht wegläuft. Erlittener Schaden kann den Effekt unterbrechen. Es kann immer nur jeweils ein Wildtier mit dem Furchteffekt versehen werden.", "Furchterfüllt.", 0, 90, 00, 65536,},
   [1535] = {"Totem der Feuernova", "Rang 1", "Beschwört ein Totem der Feuernova mit $s1 Gesundheit, das $d lang anhält. Wenn es nicht innerhalb von $8443t1 Sek. zerstört wird, fügt das Totem Feinden in einem Umkreis von $8349a1 Metern $8349s1 Feuerschaden zu.", "", 0, 110, 6710886400, 0,},
   [1543] = {"Leuchtfeuer", "", "Gibt $d lang alle verborgenen und unsichtbaren Feinde innerhalb von $a1 Metern im Zielgebiet preis.", "Versteckte und unsichtbare Gegner werden sichtbar.", 0, 90, 00, 32768,},
   [1608] = {"Heldenhafter Stoß", "Rang 4", "Ein heftiger Angriff, der den Nahkampfschaden um $s1 erhöht und ein hohes Maß an Bedrohung erzeugt.", "", 0, 40, 640, 0,},
   [1671] = {"Schildhieb", "Rang 2", "Schlägt mit Eurem Schild auf das Ziel ein und verursacht $s2 Schaden. Unterbricht außerdem das Wirken von Zaubern und verhindert $d lang, dass diese Art Zauber gewirkt werden kann.", "", 0, 40, 20480, 0,},
   [1672] = {"Schildhieb", "Rang 3", "Schlägt mit Eurem Schild auf das Ziel ein und verursacht $s2 Schaden. Unterbricht außerdem das Wirken von Zaubern und verhindert $d lang, dass diese Art Zauber gewirkt werden kann.", "", 0, 40, 20480, 0,},
   [1680] = {"Wirbelwind", "", "In einem stählernen Wirbel greift Ihr bis zu $i Feinde in einem Umkreis von $a1 Metern an und verursacht Waffenschaden beider Nahkampfwaffen bei jedem Feind.", "", 0, 40, 00, 4,},
   [1706] = {"Levitieren", "", "Ermöglicht dem Zaubernden zu levitieren und damit wenige Schritte über dem Boden zu fliegen. Beim Levitieren fallt Ihr mit reduziertem Tempo und könnt wasserähnliche Oberflächen überwinden. Jeglicher erlittene Schaden hebt den Effekt wieder auf. Hält $d lang an.", "Schweben.", 0, 60, -21474836480, 4096,},
   [1714] = {"Fluch der Sprachen", "Rang 1", "Zwingt das Ziel, Dämonisch zu sprechen, und verlangsamt die Zauberzeit um $s1%. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem Ziel aktiv sein. Hält $d lang an.", "Dämonisch sprechen und Zauberzeit um $s1% erhöhen.", 0, 50, -21474836480, 0,},
   [1715] = {"Kniesehne", "Rang 1", "Verstümmelt den Feind, wobei $s1 Schaden verursacht werden und das Bewegungstempo des Feindes $d lang um $s2% verlangsamt wird.", "Bewegungstempo um $s2% verringert.", 0, 40, 20, 0,},
   [1719] = {"Tollkühnheit", "", "Der Krieger verursacht für die nächsten $d mit den meisten Angriffen kritischen Schaden und ist gegen Furchteffekte immun. Jeglicher erlittene Schaden wird allerdings um $s2% erhöht.", "Immun gegen Furchteffekte, die meisten Angriffe führen zu kritischen Treffern, allerdings wird jeglicher erlittene Schaden um $s2% erhöht.", 0, 40, 160, 0,},
   [1725] = {"Ablenken", "", "Bewirkt eine Ablenkung, die die Aufmerksamkeit aller in der Nähe befindlichen Monster $s1 Sek. lang auf sich zieht. Lässt Verstohlenheit weiterbestehen.", "", 0, 80, 40960, 0,},
   [1735] = {"Demoralisierendes Gebrüll", "Rang 2", "Der Druide brüllt und verringert damit die Nahkampfangriffskraft von in der Nähe befindlichen Feinden um $s1. Hält $d lang an.", "Nahkampfangriffskraft um $s1 verringert.", 0, 70, 80, 0,},
   [1752] = {"Finsterer Stoß", "Rang 1", "Ein sofortiger Schlag, der zusätzlich zu Eurem normalen Waffenschaden noch $s1 Schaden zufügt. Gewährt $s2 $lCombopunkt:Combopunkte;.", "", 0, 80, 83886100, 0,},
   [1757] = {"Finsterer Stoß", "Rang 2", "Ein sofortiger Schlag, der zusätzlich zu Eurem normalen Waffenschaden noch $s1 Schaden zufügt. Gewährt $s2 $lCombopunkt:Combopunkte;.", "", 0, 80, 83886100, 0,},
