   [1766] = {"Tritt", "Rang 1", "Ein schneller Tritt, der einen einzelnen Gegner verletzt und ihm $s2 Schaden zufügt. Unterbricht außerdem das Wirken von Zaubern und verhindert $d lang, dass diese Art Zauber gewirkt werden kann.", "", 0, 80, 160, 0,},
   [1767] = {"Tritt", "Rang 2", "Ein schneller Tritt, der einen einzelnen Gegner verletzt und ihm $s2 Schaden zufügt. Unterbricht außerdem das Wirken von Zaubern und verhindert $d lang, dass diese Art Zauber gewirkt werden kann.", "", 0, 80, 160, 0,},
   [1768] = {"Tritt", "Rang 3", "Ein schneller Tritt, der einen einzelnen Gegner verletzt und ihm $s2 Schaden zufügt. Unterbricht außerdem das Wirken von Zaubern und verhindert $d lang, dass diese Art Zauber gewirkt werden kann.", "", 0, 80, 160, 0,},
   [1769] = {"Tritt", "Rang 4", "Ein schneller Tritt, der einen einzelnen Gegner verletzt und ihm $s2 Schaden zufügt. Unterbricht außerdem das Wirken von Zaubern und verhindert $d lang, dass diese Art Zauber gewirkt werden kann.", "", 0, 80, 160, 0,},
   [1776] = {"Solarplexus", "Rang 1", "Fügt dem Gegner $s1 Schaden zu, macht ihn $d lang handlungsunfähig und stoppt Euren Angriff. Das Ziel muss Euch zugewandt sein. Jeglicher zugefügter Schaden lässt das Ziel wieder zu sich kommen. Gewährt $s2 $lCombopunkt:Combopunkte;.", "Handlungsunfähig.", 0, 80, 80, 0,},
   [1777] = {"Solarplexus", "Rang 2", "Fügt dem Gegner $s1 Schaden zu, macht ihn $d lang handlungsunfähig und stoppt Euren Angriff. Das Ziel muss Euch zugewandt sein. Jeglicher zugefügter Schaden lässt das Ziel wieder zu sich kommen. Gewährt $s2 $lCombopunkt:Combopunkte;.", "Handlungsunfähig.", 0, 80, 80, 0,},
   [1784] = {"Verstohlenheit", "Rang 1", "Ermöglicht dem Schurken das Herumschleichen, verringert jedoch das Bewegungstempo um $s3%. Hält an, bis die Aktion abgebrochen wird.", "Verstohlen. Bewegung um $s3% verlangsamt.", 0, 80, 41943040, 0,},
   [1785] = {"Verstohlenheit", "Rang 2", "Ermöglicht dem Schurken das Herumschleichen, verringert jedoch das Bewegungstempo um $s3%. Hält an, bis die Aktion abgebrochen wird.", "Verstohlen. Bewegung um $s3% verlangsamt.", 0, 80, 41943040, 0,},
   [1786] = {"Verstohlenheit", "Rang 3", "Ermöglicht dem Schurken das Herumschleichen, verringert jedoch das Bewegungstempo um $s3%. Hält an, bis die Aktion abgebrochen wird.", "Verstohlen. Bewegung um $s3% verlangsamt.", 0, 80, 41943040, 0,},
   [1787] = {"Verstohlenheit", "Rang 4", "Ermöglicht dem Schurken das Herumschleichen, verringert jedoch das Bewegungstempo um $s3%. Hält an, bis die Aktion abgebrochen wird.", "Verstohlen. Bewegung um $s3% verlangsamt.", 0, 80, 41943040, 0,},
   [1822] = {"Krallenhieb", "Rang 1", "Attackiert das Ziel mit Krallen, fügt ${$AP/100+$m1} Blutungsschaden sowie $d lang insgesamt ${$m2*3+$AP*0.06} zusätzlichen Schaden zu. Gewährt $s3 $lCombopunkt:Combopunkte;.", "Das Bluten fügt alle $t2 Sek. $s2 Schaden zu.", 0, 70, 40960, 0,},
   [1823] = {"Krallenhieb", "Rang 2", "Attackiert das Ziel mit Krallen, fügt ${$AP/100+$m1} Blutungsschaden sowie $d lang insgesamt ${$m2*3+$AP*0.06} zusätzlichen Schaden zu. Gewährt $s3 $lCombopunkt:Combopunkte;.", "Das Bluten fügt alle $t2 Sek. $s2 Schaden zu.", 0, 70, 40960, 0,},
   [1824] = {"Krallenhieb", "Rang 3", "Attackiert das Ziel mit Krallen, fügt ${$AP/100+$m1} Blutungsschaden sowie $d lang insgesamt ${$m2*3+$AP*0.06} zusätzlichen Schaden zu. Gewährt $s3 $lCombopunkt:Combopunkte;.", "Das Bluten fügt alle $t2 Sek. $s2 Schaden zu.", 0, 70, 40960, 0,},
   [1833] = {"Fieser Trick", "", "Betäubt das Ziel $d lang. Muss im Verstohlenheitsmodus sein. Gewährt $s2 $lCombopunkt:Combopunkte;.", "Betäubt.", 0, 80, 10240, 0,},
   [1842] = {"Falle entschärfen", "", "Schleicht Euch an die Falle heran, um sie zu entschärfen. Kommt ihr aber nicht zu nahe, sonst wird die Falle ausgelöst.", "", 0, 80, -21474836480, 0,},
   [1856] = {"Verschwinden", "Rang 1", "Ermöglicht dem Schurken, sich außer Sicht zu stehlen und $11327d lang in einem verbesserten Verstohlenheitsmodus zu agieren. Durchbricht außerdem die Bewegung einschränkenden Effekte.", "Verbesserte Verstohlenheit.", 0, 80, 20480, 0,},
   [1857] = {"Verschwinden", "Rang 2", "Ermöglicht dem Schurken, sich außer Sicht zu stehlen und $11329d lang in einem verbesserten Verstohlenheitsmodus zu agieren. Durchbricht außerdem die Bewegung einschränkende Effekte. Wirkungsvoller als 'Verschwinden' (Rang 1).", "Verbesserte Verstohlenheit.", 0, 80, 20480, 0,},
   [1943] = {"Blutung", "Rang 1", "Finishing-Move, der Schaden im Lauf der Zeit verursacht, erhöht durch Angriffskraft. Hält pro Combopunkt länger an:\r\n\r\n   1 Punkt: 40 Schaden über 8 Sekunden.\r\n\r\n   2 Punkte: 60 Schaden über 10 Sekunden.\r\n\r\n   3 Punkte: 84 Schaden über 12 Sekunden.\r\n\r\n   4 Punkte: 112 Schaden über 14 Sekunden.\r\n\r\n   5 Punkte: 144 Schaden über 16 Sekunden", "Verursacht alle $t1 Sek. Schaden.", 0, 80, 10485760, 0,},
   [1949] = {"Höllenfeuer", "Rang 1", "Entzündet den Bereich um den Zaubernden herum, fügt ihm selbst alle $t2 Sek. $s2 Feuerschaden zu sowie im gleichen Rythmus allen in der Nähe befindlichen Feinden $5857s1 Feuerschaden. Hält $d lang an.", "Fügt sich selbst und allen in der Nähe befindlichen Feinden Schaden zu.", 0, 50, 640, 0,},
   [1953] = {"Blinzeln", "", "Teleportiert den Zaubernden $a1 Meter nach vorn, es sei denn, etwas steht im Weg. Befreit den Zaubernden auch von Betäubungen und Banden.", "Blinzeln", 0, 30, 655360, 0,},
   [1966] = {"Finte", "Rang 1", "Ein Täuschungsmanöver wird durchgeführt, das zwar keinen Schaden verursacht, aber Eure Bedrohung um ein kleines Maß verringert und die Wahrscheinlichkeit senkt, dass der Feind Euch angreift.", "", 0, 80, 1342177280, 0,},
   [1978] = {"Schlangenbiss", "Rang 1", "Beißt das Ziel und verursacht $d lang ${$RAP*0.1+$m1*5} Naturschaden. Es kann immer nur ein Biss oder Stich pro Jäger auf einem einzelnen Ziel aktiv sein.", "Verursacht alle $t1 Sek. $s1 Naturschaden.", 75, 975, 1638475, 0,},
   [2006] = {"Auferstehung", "Rang 1", "Bringt einen Spieler mit $s1 Gesundheit und $q1 Mana ins Leben zurück. Kann nicht während des Kampfes verwendet werden.", "", 0, 60, 10737418240, 0,},
   [2008] = {"Geist der Ahnen", "Rang 1", "Lässt den Geist in den Körper zurückkehren und bringt ein totes Ziel mit $s1 Gesundheit und $q1 Mana wieder ins Leben zurück. Kann nicht während des Kampfes verwendet werden.", "", 0, 110, 00, 0,},
   [2010] = {"Auferstehung", "Rang 2", "Bringt einen Spieler mit $s1 Gesundheit und $q1 Mana ins Leben zurück. Kann nicht während des Kampfes verwendet werden.", "", 0, 60, 10737418240, 0,},
   [2023] = {"Mana wiederherstellen", "", "Stellt $s1 Mana wieder her.", "", 0, 130, 00, 0,},
   [2024] = {"Heiltrank", "", "Stellt $s1 Gesundheit wieder her.", "", 0, 130, 00, 0,},
   [2048] = {"Schlachtruf", "Rang 8", "Der Krieger lässt einen wilden Schlachtruf ertönen, der bei allen Gruppenmitgliedern in einem Umkreis von $a1 Metern die Nahkampfangriffskraft um $s1 erhöht. Hält $d lang an.", "Nahkampfangriffskraft um $s1 erhöht.", 0, 40, 655360, 0,},
   [2050] = {"Geringes Heilen", "Rang 1", "Euer Ziel um $s1 heilen.", "", 0, 60, 2621440, 0,},
   [2052] = {"Geringes Heilen", "Rang 2", "Euer Ziel um $s1 heilen.", "", 0, 60, 2621440, 0,},
   [2053] = {"Geringes Heilen", "Rang 3", "Euer Ziel um $s1 heilen.", "", 0, 60, 2621440, 0,},
   [2054] = {"Heilen", "Rang 1", "Euer Ziel um $s1 heilen.", "", 0, 60, 10240, 0,},
   [2055] = {"Heilen", "Rang 2", "Euer Ziel um $s1 heilen.", "", 0, 60, 10240, 0,},
   [2060] = {"Große Heilung", "Rang 1", "Ein langsam zu wirkender Zauber, der ein einzelnes Ziel um $s1 heilt.", "", 0, 60, 40960, 0,},
   [2061] = {"Blitzheilung", "Rang 1", "Heilt ein befreundetes Ziel um $s1.", "", 0, 60, 20480, 0,},
   [2062] = {"Totem des Erdelementars", "Rang 1", "Beschwört ein Elementartotem, das einen großen Erdelementar ruft, um $gden Zaubernden:die Zaubernde; und $gseine:ihre; Verbündeten zu beschützen. Hält $d lang an.", "", 0, 110, 5368709120, 0,},
   [2070] = {"Kopfnuss", "Rang 2", "Macht ein Ziel bis zu $d lang handlungsunfähig. Muss im Verstohlenheitsmodus sein. Wirkt nur bei Humanoiden, die sich nicht im Kampf befinden. Jeglicher erlittene Schaden wird das Ziel wiederbeleben. Es kann immer nur ein Ziel von der Fähigkeit 'Kopfnuss' betroffen sein.", "Handlungsunfähig.", 0, 80, 1280, 0,},
   [2090] = {"Verjüngung", "Rang 4", "Heilt beim Ziel $d lang insgesamt $o1 Schaden.", "Heilt alle $t1 Sek. $s1 Schaden.", 0, 70, 160, 0,},
   [2091] = {"Verjüngung", "Rang 5", "Heilt beim Ziel $d lang insgesamt $o1 Schaden.", "Heilt alle $t1 Sek. $s1 Schaden.", 0, 70, 160, 0,},
   [2094] = {"Blenden", "", "Blendet das Ziel und lässt es bis zu $d lang desorientiert umherlaufen. Jeglicher erlittene Schaden wird den Effekt aufheben.", "Desorientiert.", 0, 80, 167772160, 0,},
   [2096] = {"Gedankensicht", "Rang 1", "Ermöglicht dem Zaubernden, $d lang mit den Augen des Ziels zu sehen.", "Sicht mit den Augen des Ziels gewährt.", 0, 60, 671088640, 0,},
