   [6192] = {"Schlachtruf", "Rang 3", "Der Krieger schreit, wodurch sich die Nahkampfangriffskraft der Gruppe innerhalb eines Radius von $a1 Metern um $s1 erhöht. Hält $d lang an.", "Erhöht Eure Nahkampfangriffskraft um $s1.", 0, 40, 655360, 0,},
   [6201] = {"Gesundheitsstein herstellen", "Rang 1", "Lässt einen schwachen Gesundheitsstein entstehen, der verwendet werden kann, um sofort $6262s1 Gesundheit wiederherzustellen.\r\n\r\n\r\n\r\nHerbeigezauberte Gegenstände verschwinden, wenn Ihr Euch länger als 15 Minuten ausloggt.", "", 0, 50, 10485760, 0,},
   [6202] = {"Gesundheitsstein herstellen", "Rang 2", "Lässt einen geringen Gesundheitsstein entstehen, der verwendet werden kann, um $6263s1 Gesundheit wiederherzustellen.\r\n\r\n\r\n\r\nHerbeigezauberte Gegenstände verschwinden, wenn Ihr Euch länger als 15 Minuten ausloggt.", "", 0, 50, 10485760, 0,},
   [6205] = {"Fluch der Schwäche", "Rang 3", "Die Nahkampfangriffskraft des Ziels wird $d lang um $s1 reduziert. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "Nahkampfangriffskraft um $s1 verringert.", 0, 50, 327680, 0,},
   [6213] = {"Furcht", "Rang 2", "Erfüllt den Feind mit Furcht, woraufhin er bis zu $d lang voller Furcht flüchtet. Erlittener Schaden kann den Effekt unterbrechen. Es kann immer nur jeweils ein einzelnes Ziel mit Furcht erfüllt werden.", "Furchterfüllt.", 0, 50, 00, 1024,},
   [6215] = {"Furcht", "Rang 3", "Erfüllt den Feind mit Furcht, woraufhin er bis zu $d lang voller Furcht flüchtet. Erlittener Schaden kann den Effekt unterbrechen. Es kann immer nur jeweils ein einzelnes Ziel mit Furcht erfüllt werden.", "Furchterfüllt.", 0, 50, 00, 1024,},
   [6217] = {"Fluch der Pein", "Rang 3", "Verflucht das Ziel mit Pein und fügt $d lang insgesamt $o1 Schattenschaden zu. Zuerst wird der Schaden langsam zugefügt und nimmt dann zu, bis der Fluch seine Gesamtdauer erreicht hat. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "$d lang insgesamt $o1 Schattenschaden.", 0, 50, 10240, 0,},
   [6219] = {"Feuerregen", "Rang 2", "Lässt einen feurigen Regen niedergehen, der $d lang Feinde im Wirkungsbereich mit insgesamt ${$42224m1*4} Feuerschaden verbrennt.", "Alle $42224t1 Sek. $42224s1 Feuerschaden.", 0, 50, 320, 0,},
   [6222] = {"Verderbnis", "Rang 2", "Verdirbt das Ziel und verursacht $d lang insgesamt $o1 Schattenschaden.", "Alle $t1 Sek. $s1 Schattenschaden.", 0, 50, 20, 0,},
   [6223] = {"Verderbnis", "Rang 3", "Verdirbt das Ziel und verursacht $d lang insgesamt $o1 Schattenschaden.", "Alle $t1 Sek. $s1 Schattenschaden.", 0, 50, 20, 0,},
   [6226] = {"Mana entziehen", "Rang 2", "Überträgt alle $t1 Sek. $s1 Mana vom Ziel auf den Zaubernden. Hält $d lang an.", "Entzieht jede Sekunde $s1 Mana für den Zaubernden.", 0, 50, 160, 0,},
   [6262] = {"Schwacher Gesundheitsstein", "", "Stellt sofort $s1 Gesundheit wieder her.", "", 0, 50, 655360, 0,},
   [6263] = {"Geringer Gesundheitsstein", "", "Stellt sofort $s1 Gesundheit wieder her.", "", 0, 50, 655360, 0,},
   [6307] = {"Blutpakt", "Rang 1", "Erhöht die Ausdauer der Gruppenmitglieder um $s1.", "Erhöht Ausdauer um $s1.", 0, 50, 83886080, 0,},
   [6343] = {"Donnerknall", "Rang 1", "Dröhnt in der Nähe befindliche Feinde mit Donner zu, erhöht die Zeit zwischen ihren Angriffen $d lang um $s2% und fügt ihnen $s1 Schaden zu. Diese Fähigkeit verursacht zusätzliche Bedrohung und wirkt auf maximal $i Ziele.", "Zeit zwischen Angriffen um $s2% erhöht.", 0, 40, 1280, 0,},
   [6346] = {"Furchtzauberschutz", "", "Schützt das befreundete Ziel gegen Furcht. Der nächste Furchteffekt gegen das Ziel ist erfolglos, dabei wird der Zauberschutz verbraucht. Hält $d lang an.", "Geschützt gegen Furcht.", 0, 60, -21474836480, 4096,},
