   [6353] = {"Seelenfeuer", "Rang 1", "Verbrennt die Seele eines Feindes und verursacht $s1 Feuerschaden.", "", 0, 50, 00, 128,},
   [6358] = {"Verführung", "", "Verführt das Ziel und verhindert dadurch bis zu $d lang alle Aktionen. Jeglicher erlittene Schaden wird den Effekt aufheben. Funktioniert nur bei Humanoiden.", "Bezaubert.", 0, 50, 10737418240, 0,},
   [6360] = {"Besänftigender Kuss", "Rang 1", "Besänftigt das Ziel und erhöht die Chance, dass es etwas anderes angreift.", "", 0, 50, 10737418240, 0,},
   [6363] = {"Totem der Verbrennung", "Rang 2", "Beschwört $d lang ein Totem der Verbrennung mit $s1 Gesundheit zu Euren Füßen, das alle 2 Sekunden einen Feind im Umkreis von $6350r Metern angreift und $6350s1 Punkt(e) Feuerschaden anrichtet.", "", 0, 110, 160, 0,},
   [6364] = {"Totem der Verbrennung", "Rang 3", "Beschwört $d lang ein Totem der Verbrennung mit $s1 Gesundheit zu Euren Füßen, das alle 2 Sekunden einen Feind im Umkreis von $6351r Metern angreift und $6351s1 Punkt(e) Feuerschaden anrichtet.", "", 0, 110, 160, 0,},
   [6365] = {"Totem der Verbrennung", "Rang 4", "Beschwört $d lang ein Totem der Verbrennung mit $s1 Gesundheit zu Euren Füßen, das alle 2 Sekunden einen Feind im Umkreis von $6352r Metern angreift und $6352s1 Punkt(e) Feuerschaden anrichtet.", "", 0, 110, 160, 0,},
   [6366] = {"Feuerstein herstellen", "Rang 1", "Stellt einen geringen Feuerstein her, der angelegt werden kann. Wenn angelegt, wird die Waffenhandwaffe mit Feuer verzaubert und gewährt jedem Angriff die Chance, $17809s1 zusätzlichen Feuerschaden zuzufügen. Außerdem erhöht das Anlegen eines geringen Feuersteins den Schaden von Feuerzaubern um $23480s1.\r\n\r\n\r\n\r\nHerbeigezauberte Gegenstände verschwinden, wenn Ihr Euch länger als 15 Minuten ausloggt.", "", 0, 50, 10485760, 0,},
   [6371] = {"Heilstrom", "Rang 2", "", "Heilt alle $t1 Sek. $s1 Schaden.", 0, 110, 81920, 0,},
   [6372] = {"Heilstrom", "Rang 3", "", "Heilt alle $t1 Sek. $s1 Schaden.", 0, 110, 81920, 0,},
   [6375] = {"Totem des heilenden Flusses", "Rang 2", "Beschwört $d lang ein Totem des heilenden Flusses mit $s1 Gesundheit zu Füßen des Zaubernden, das Gruppenmitglieder im Umkreis von $6371a1 Metern alle $6371t1 Sek. um $6371s1 heilt.", "", 0, 110, 5242880, 0,},
   [6377] = {"Totem des heilenden Flusses", "Rang 3", "Beschwört $d lang ein Totem des heilenden Flusses mit $s1 Gesundheit zu Füßen des Zaubernden, das Gruppenmitglieder im Umkreis von $6372a1 Metern alle $6372t1 Sek. um $6372s1 heilt.", "", 0, 110, 5242880, 0,},
   [6390] = {"Totem der Steinklaue", "Rang 2", "Beschwört $d lang ein Totem der Steinklaue mit $s1 Gesundheit zu Füßen des Zaubernden, das Kreaturen im Umkreis von $6393a1 Metern durch Spott dazu bringt, es anzugreifen. Es besteht eine Chance von $6397h%, dass Gegner, die das Totem angreifen, $39796d lang betäubt werden.", "", 0, 110, 80, 0,},
   [6391] = {"Totem der Steinklaue", "Rang 3", "Beschwört $d lang ein Totem der Steinklaue mit $s1 Gesundheit zu Füßen des Zaubernden, das Kreaturen im Umkreis von $6394a1 Metern durch Spott dazu bringt, es anzugreifen. Es besteht eine Chance von $6398h%, dass Gegner, die das Totem angreifen, $39796d lang betäubt werden.", "", 0, 110, 80, 0,},
   [6392] = {"Totem der Steinklaue", "Rang 4", "Beschwört $d lang ein Totem der Steinklaue mit $s1 Gesundheit zu Füßen des Zaubernden, das Kreaturen im Umkreis von $6395a1 Metern durch Spott dazu bringt, es anzugreifen. Es besteht eine Chance von $6399h%, dass Gegner, die das Totem angreifen, $39796d lang betäubt werden.", "", 0, 110, 80, 0,},
   [6495] = {"Totem des Wachens", "", "Beschwört $d lang ein unbewegliches Totem des Wachens mit $s1 Gesundheit zu Euren Füßen, das die Sicht auf die in der Nähe befindlichen Bereiche ermöglicht und vor Feinden warnt, die es angreifen. Mit einem Rechtsklick auf den Stärkungszauber kann zwischen dem Blickwinkel des Totems und dem Blickwinkel des Schamanen gewechselt werden.", "Ermöglicht Euch die Sicht aus dem Blickwinkel des Totems des Wachens. Rechtsklick auf den Stärkungszauber wechselt zwischen dem Blickwinkel des Totems und dem Blickwinkel des Schamanen.", 0, 110, 5368709120, 0,},
   [6512] = {"Geringe Unsichtbarkeit entdecken", "", "Gewährt $d lang das Entdecken geringer Unsichtbarkeit.", "Geringe Unsichtbarkeit entdecken.", 0, 130, 00, 0,},
   [6546] = {"Verwunden", "Rang 2", "Verwundet das Ziel, lässt es $d lang bluten und fügt damit $o1 Schaden plus ${0.00743*4*(($MWB+$mwb)/2+$AP/14*$MWS)} zusätzlichen Schaden (basierend auf Waffenschaden) zu.", "Das Bluten fügt alle $t1 Sek. $s1 Schaden plus zusätzlichen Waffenschaden zu.", 0, 40, 320, 0,},
   [6547] = {"Verwunden", "Rang 3", "Verwundet das Ziel, lässt es $d lang bluten und fügt damit $o1 Schaden plus ${0.00743*5*(($MWB+$mwb)/2+$AP/14*$MWS)} zusätzlichen Schaden (basierend auf Waffenschaden) zu.", "Das Bluten fügt alle $t1 Sek. $s1 Schaden plus zusätzlichen Waffenschaden zu.", 0, 40, 320, 0,},
   [6548] = {"Verwunden", "Rang 4", "Verwundet das Ziel, lässt es $d lang bluten und fügt damit $o1 Schaden plus ${0.00743*6*(($MWB+$mwb)/2+$AP/14*$MWS)} zusätzlichen Schaden (basierend auf Waffenschaden) zu.", "Das Bluten fügt alle $t1 Sek. $s1 Schaden plus zusätzlichen Waffenschaden zu.", 0, 40, 320, 0,},
   [6552] = {"Zuschlagen", "Rang 1", "Schlägt auf das Ziel ein und fügt ihm $s2 Schaden zu. Unterbricht außerdem das Wirken von Zaubern und verhindert $d lang, dass diese Art Zauber gewirkt werden kann.", "", 0, 40, 80, 0,},
   [6554] = {"Zuschlagen", "Rang 2", "Schlägt auf das Ziel ein und fügt ihm $s2 Schaden zu. Unterbricht außerdem das Wirken von Zaubern und verhindert $d lang, dass diese Art Zauber gewirkt werden kann.", "", 0, 40, 80, 0,},
   [6560] = {"LeCraft Test Spell", "Rang 1", "Verringert den Koeffizienten des Zaubers 'Frostblitz' um $s1%.", "Koeffizient des Zaubers 'Frostblitz' um $s1% verringert.", 0, 60, 00, 0,},
   [6572] = {"Rache", "Rang 1", "Führt einen sofortigen Gegenangriff gegen den Feind aus, der $s1 Schaden verursacht und dabei ein hohes Maß an Bedrohung generiert. 'Rache' muss auf Blocken, Ausweichen oder Parieren folgen.", "", 0, 40, 10240, 0,},
   [6574] = {"Rache", "Rang 2", "Führt einen sofortigen Gegenangriff gegen den Feind aus, der $s1 Schaden verursacht und dabei ein hohes Maß an Bedrohung generiert. 'Rache' muss auf Blocken, Ausweichen oder Parieren folgen.", "", 0, 40, 10240, 0,},
