   [6615] = {"Freie Aktion", "", "Macht Euch $d lang immun gegen betäubungs- und bewegungseinschränkende Effekte. Bereits bestehende Effekte dieser Art werden nicht entfernt.", "Freie Aktion.", 0, 130, 00, 0,},
   [6660] = {"Schießen", "", "Schießt auf den Feind und fügt körperlichen Schaden zu.", "", 0, 90, 10, 0,},
