   [6756] = {"Mal der Wildnis", "Rang 3", "Erhöht $d lang die Rüstung des befreundeten Ziels um $s1 und alle Attribute um $s2.", "Erhöht die Rüstung um $s1 und alle Attribute um $s2.", 0, 70, 2621440, 0,},
   [6760] = {"Ausweiden", "Rang 2", "Finishing-Move, der Schaden pro Combopunkt verursacht:\r\n\r\n   1 Punkt: ${$m1+($b1*1)+$AP*0.03}-${$M1+($b1*1)+$AP*0.03} Schaden\r\n\r\n   2 Punkte: ${$m1+($b1*2)+$AP*0.06}-${$M1+($b1*2)+$AP*0.06} Schaden\r\n\r\n   3 Punkte: ${$m1+($b1*3)+$AP*0.09}-${$M1+($b1*3)+$AP*0.09} Schaden\r\n\r\n   4 Punkte: ${$m1+($b1*4)+$AP*0.12}-${$M1+($b1*4)+$AP*0.12} Schaden\r\n\r\n   5 Punkte: ${$m1+($b1*5)+$AP*0.15}-${$M1+($b1*5)+$AP*0.15} Schaden", "", 0, 80, 85196800, 0,},
   [6761] = {"Ausweiden", "Rang 3", "Finishing-Move, der Schaden pro Combopunkt verursacht:\r\n\r\n   1 Punkt: ${$m1+($b1*1)+$AP*0.03}-${$M1+($b1*1)+$AP*0.03} Schaden\r\n\r\n   2 Punkte: ${$m1+($b1*2)+$AP*0.06}-${$M1+($b1*2)+$AP*0.06} Schaden\r\n\r\n   3 Punkte: ${$m1+($b1*3)+$AP*0.09}-${$M1+($b1*3)+$AP*0.09} Schaden\r\n\r\n   4 Punkte: ${$m1+($b1*4)+$AP*0.12}-${$M1+($b1*4)+$AP*0.12} Schaden\r\n\r\n   5 Punkte: ${$m1+($b1*5)+$AP*0.15}-${$M1+($b1*5)+$AP*0.15} Schaden", "", 0, 80, 85196800, 0,},
