   [6768] = {"Finte", "Rang 2", "Ein Täuschungsmanöver wird durchgeführt, das zwar keinen Schaden verursacht, aber Eure Bedrohung um ein mittleres Maß verringert und die Wahrscheinlichkeit senkt, dass der Feind Euch angreift.", "", 0, 80, 1342177280, 0,},
   [6770] = {"Kopfnuss", "Rang 1", "Macht ein Ziel bis zu $d lang handlungsunfähig. Muss im Verstohlenheitsmodus sein. Wirkt nur bei Humanoiden, die sich nicht im Kampf befinden. Jeglicher erlittene Schaden wird das Ziel wiederbeleben. Es kann immer nur ein Ziel von der Fähigkeit 'Kopfnuss' betroffen sein.", "Handlungsunfähig.", 0, 80, 1280, 0,},
   [6774] = {"Zerhäckseln", "Rang 2", "Finishing-Move, der das Nahkampfangriffstempo um $s2% erhöht. Hält pro Combopunkt länger an:\r\n\r\n 1 Punkt: 9 Sekunden\r\n\r\n 2 Punkte: 12 Sekunden\r\n\r\n 3 Punkte: 15 Sekunden\r\n\r\n 4 Punkte: 18 Sekunden\r\n\r\n 5 Punkte: 21 Sekunden", "Nahkampfangriffstempo um $s2% erhöht.", 0, 80, 2621440, 0,},
