   [6783] = {"Schleichen", "Rang 2", "Ermöglicht dem Druiden das Schleichen, verringert jedoch Euer Bewegungstempo um $s2%. Hält an, bis die Aktion abgebrochen wird.", "Verstohlen. Bewegungstempo um $s2% verlangsamt.", 0, 70, 163840, 0,},
   [6785] = {"Verheeren", "Rang 1", "Verheert das Ziel, wobei dem Ziel $s2% Schaden plus 147 zugefügt wird. Muss schleichen und hinter dem Ziel sein. Gewährt $s3 $lCombopunkt:Combopunkte;.", "", 0, 70, 655360, 0,},
   [6787] = {"Verheeren", "Rang 2", "Verheert das Ziel, wobei dem Ziel $s2% Schaden plus 217 zugefügt wird. Muss schleichen und hinter dem Ziel sein. Gewährt $s3 $lCombopunkt:Combopunkte;.", "", 0, 70, 655360, 0,},
