   [11721] = {"Fluch der Elemente", "Rang 2", "Verflucht das Ziel und verringert $d lang seinen Arkan-, Feuer-, Frost- und Schattenwiderstand um $s1. Erhöht außerdem den erlittenen Arkan-, Feuer-, Frost- und Schattenschaden des Ziels um $s2%. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "Verringert Arkan-, Feuer-, Frost- und Schattenwiderstand um $s1. Erhöht den erlittenen Arkan-, Feuer-, Frost- und Schattenschaden um $s2%.", 0, 50, 00, 512,},
   [11722] = {"Fluch der Elemente", "Rang 3", "Verflucht das Ziel und verringert $d lang seinen Arkan-, Feuer-, Frost- und Schattenwiderstand um $s1. Erhöht außerdem den erlittenen Arkan-, Feuer-, Frost- und Schattenschaden des Ziels um $s2%. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "Verringert Arkan-, Feuer-, Frost- und Schattenwiderstand um $s1. Erhöht den erlittenen Arkan-, Feuer-, Frost- und Schattenschaden um $s2%.", 0, 50, 00, 512,},
   [11725] = {"Dämonensklave", "Rang 2", "Versklavt den Zieldämon bis zur Stufe $m1 und zwingt ihn, Euren Befehlen Folge zu leisten. Während er versklavt ist, wird die Zeit zwischen Angriffen des Dämons um $s2% erhöht und sein Zaubertempo um $s3% verlangsamt. Hält bis zu $d lang an. Wenn Ihr denselben Dämon mehrmals versklavt, wird es mit jedem Versuch schwieriger, ihn zu kontrollieren.", "Versklavt.", 0, 50, 20480, 0,},
