   [20154] = {"Siegel der Rechtschaffenheit", "Rang 1", "Erfüllt den Paladin $d lang mit heiligem Geist, der jedem Nahkampfangriff zusätzlichen Heiligschaden in Höhe von ${$cond($eq($HND,1),0.85*($m1*1.2*1.03*$MWS/100)+0.03*($MW+$mw)/2-1,1.2*($m1*1.2*1.03*$MWS/100)+0.03*($MW+$mw)/2+1)} verleiht. Es kann immer nur jeweils ein Siegel auf dem Paladin aktiv sein.", "Nahkampfangriffe verursachen zusätzlichen Heiligschaden in Höhe von ${$cond($eq($HND,1),0.85*($m1*1.2*1.03*$MWS/100)+0.03*($MW+$mw)/2-1,1.2*($m1*1.2*1.03*$MWS/100)+0.03*($MW+$mw)/2+1)}.", 0, 100, 1342177280, 0,},
   [20162] = {"Siegel des Kreuzfahrers", "Rang 2", "Erfüllt den Paladin $d lang mit dem Geist eines Kreuzfahrers und verleiht $s1 Nahkampfangriffskraft. Außerdem greift der Paladin um $s3% schneller an, verursacht jedoch bei jedem Angriff weniger Schaden. Es kann immer nur jeweils ein Siegel auf dem Paladin aktiv sein.\r\n\r\n\r\n\r\nDie Entfesselung der Energie dieses Siegels richtet einen Feind $20188d lang und erhöht den erlittenen Heiligschaden um bis zu $20188s1. Eure Nahkampfangriffe frischen die Dauer dieses Siegels auf. Es kann immer nur jeweils ein Richturteil pro Paladin aktiv sein.", "Nahkampfangriffskraft um $s1 erhöht. Nahkampfangriffe sind $s3% schneller, aber verursachen weniger Schaden.", 0, 100, 5120, 0,},
   [20164] = {"Siegel der Gerechtigkeit", "Rang 1", "Erfüllt den Paladin $d lang mit dem Geist der Gerechtigkeit, der jedem Nahkampfangriff eine Chance verleiht, $20170d lang zu betäuben. Es kann immer nur jeweils ein Siegel auf dem Paladin aktiv sein.\r\n\r\n\r\n\r\nDie Entfesselung der Energie dieses Siegels richtet einen Feind $20184d lang und hindert ihn an der Flucht. Eure Nahkampfangriffe frischen die Dauer dieses Siegels auf. Es kann immer nur jeweils ein Richturteil pro Paladin aktiv sein.", "Nachkampfangriffe haben eine Chance, den Gegner $20170d lang zu betäuben.", 0, 100, 1342177280, 0,},
   [20165] = {"Siegel des Lichts", "Rang 1", "Erfüllt den Paladin $d lang mit göttlichem Licht, das jedem Nahkampfangriff eine Chance verleiht, den Paladin um $20167s1 zu heilen. Es kann immer nur jeweils ein Siegel auf dem Paladin aktiv sein.\r\n\r\n\r\n\r\nDie Entfesselung der Energie dieses Siegels richtet einen Feind $20185d lang und verleiht Nahkampfangriffen, die gegen den gerichteten Feind ausgeführt werden, eine Chance den Angreifer um $20267s1 zu heilen. Eure Nahkampfangriffe erneuern die Dauer dieses Effekts. Es kann immer nur jeweils ein Richturteil pro Paladin aktiv sein.", "Nahkampfangriffe haben eine Chance, Euch um $20167s1 zu heilen.", 0, 100, 1342177280, 0,},
   [20166] = {"Siegel der Weisheit", "Rang 1", "Erfüllt den Paladin $d lang mit göttlicher Weisheit, die jedem Nahkampfangriff eine Chance verleiht, $20168s1 Mana des Paladins wiederherzustellen. Es kann immer nur jeweils ein Siegel auf dem Paladin aktiv sein.\r\n\r\n\r\n\r\nDie Entfesselung der Energie dieses Siegels richtet einen Feind $20186d lang und verleiht Angriffen und Zaubern, die gegen den gerichteten Feind ausgeführt werden, eine Chance beim Angreifer $20268s1 Mana wiederherzustellen. Eure Nahkampfangriffe erneuern die Dauer dieses Effekts. Es kann immer nur jeweils ein Richturteil pro Paladin aktiv sein.", "Nahkampfangriffe haben eine Chance $20168s1 Mana wiederherzustellen.", 0, 100, 1342177280, 0,},
