   [26679] = {"Tödlicher Wurf", "Rang 1", "Finishing-Move, der das Bewegungstempo des Ziels $d lang um $s2% verringert und Wurfwaffenschaden plus zusätzlichen Schaden pro Combopunkt verursacht:\r\n\r\n   1 Punkt  : ${$m1+($b1*1)+$rwb} - ${$M1+($b1*1)+$RWB} Schaden\r\n\r\n   2 Punkte: ${$m1+($b1*2)+$rwb} - ${$M1+($b1*2)+$RWB} Schaden\r\n\r\n   3 Punkte: ${$m1+($b1*3)+$rwb} - ${$M1+($b1*3)+$RWB} Schaden\r\n\r\n   4 Punkte: ${$m1+($b1*4)+$rwb} - ${$M1+($b1*4)+$RWB} Schaden\r\n\r\n   5 Punkte: ${$m1+($b1*5)+$rwb} - ${$M1+($b1*5)+$RWB} Schaden", "Bewegungstempo um $s2% verlangsamt.", 0, 80, 83886080, 1,},
   [26688] = {"Beruhigendes Gift", "Rang 1", "", "", 0, 80, 00, 16,},
   [26748] = {"Stich des Flügeldrachen", "Rang 1", "", "Alle $t1 Sek. $s1 Naturschaden.", 0, 90, 655360, 0,},
   [26785] = {"Beruhigendes Gift", "Rang 1", "Überzieht eine Waffe mit Gift, das eine Stunde lang wirkt. Bei jedem Schlag besteht eine Chance von $h%, den Gegner zu vergiften und sofort $26688s1 Naturschaden zuzufügen, ohne zusätzliche Bedrohung zu verursachen.", "", 0, 80, 00, 16,},
   [26839] = {"Erdrosseln", "Rang 7", "Erdrosselt den Feind, bringt ihn für $1330d zum Schweigen und verursacht $d lang $o1 Schaden, erhöht durch Angriffskraft. Muss verstohlen und hinter dem Ziel sein. Gewährt $s2 $lCombopunkt:Combopunkte;.", "Alle $t1 Sek. $s1 Schaden.", 0, 80, 2560, 0,},
   [26861] = {"Finsterer Stoß", "Rang 9", "Ein sofortiger Schlag, der zusätzlich zu Eurem normalen Waffenschaden noch $s1 Schaden zufügt. Gewährt $s2 $lCombopunkt:Combopunkte;.", "", 0, 80, 83886100, 0,},
   [26862] = {"Finsterer Stoß", "Rang 10", "Ein sofortiger Schlag, der zusätzlich zu Eurem normalen Waffenschaden noch $s1 Schaden zufügt. Gewährt $s2 $lCombopunkt:Combopunkte;.", "", 0, 80, 83886100, 0,},
   [26863] = {"Meucheln", "Rang 10", "Das Ziel meucheln und ihm $s2% Waffenschaden plus 255 zufügen. Muss hinter dem Ziel sein. In der Waffenhand muss sich ein Dolch befinden. Gewährt $s3 $lCombopunkt:Combopunkte;.", "", 0, 80, 83886120, 0,},
   [26864] = {"Blutsturz", "Rang 4", "Ein sofortiger Stoß, der $s2% Waffenschaden sowie einen Blutsturz beim Ziel verursacht und so den dem Ziel zugefügten körperlichen Schaden um bis zu $s3 erhöht. Hält $n Aufladungen oder $d lang an. Gewährt 1 Combopunkt.", "Erhöht erlittenen Schaden um $s3.", 0, 80, 419430400, 0,},
   [26865] = {"Ausweiden", "Rang 10", "Finishing-Move, der Schaden pro Combopunkt verursacht:\r\n\r\n   1 Punkt: ${$m1+($b1*1)+$AP*0.03}-${$M1+($b1*1)+$AP*0.03} Schaden\r\n\r\n   2 Punkte: ${$m1+($b1*2)+$AP*0.06}-${$M1+($b1*2)+$AP*0.06} Schaden\r\n\r\n   3 Punkte: ${$m1+($b1*3)+$AP*0.09}-${$M1+($b1*3)+$AP*0.09} Schaden\r\n\r\n   4 Punkte: ${$m1+($b1*4)+$AP*0.12}-${$M1+($b1*4)+$AP*0.12} Schaden\r\n\r\n   5 Punkte: ${$m1+($b1*5)+$AP*0.15}-${$M1+($b1*5)+$AP*0.15} Schaden", "", 0, 80, 85196800, 0,},
   [26866] = {"Rüstung schwächen", "Rang 6", "Finishing-Move, der eine Schwachstelle in der Rüstung des Ziels ausfindig macht und $d lang Rüstung pro Combopunkt verringert:\r\n\r\n   1 Punkt: ${0-$b1*1} Rüstung\r\n\r\n   2 Punkte: ${0-$b1*2} Rüstung\r\n\r\n   3 Punkte: ${0-$b1*3} Rüstung\r\n\r\n   4 Punkte: ${0-$b1*4} Rüstung\r\n\r\n   5 Punkte: ${0-$b1*5} Rüstung", "Rüstung verringert.", 0, 80, 5242880, 0,},
   [26867] = {"Blutung", "Rang 7", "Finishing-Move, der Schaden im Lauf der Zeit verursacht, erhöht durch Angriffskraft. Hält pro Combopunkt länger an:\r\n\r\n   1 Punkt: 324 Schaden über 8 Sekunden.\r\n\r\n   2 Punkte: 460 Schaden über 10 Sekunden.\r\n\r\n   3 Punkte: 618 Schaden über 12 Sekunden.\r\n\r\n   4 Punkte: 798 Schaden über 14 Sekunden.\r\n\r\n   5 Punkte: 1000 Schaden über 16 Sekunden.", "Verursacht alle $t1 Sek. Schaden.", 0, 80, 10485760, 0,},
   [26884] = {"Erdrosseln", "Rang 8", "Erdrosselt den Feind, bringt ihn für $1330d zum Schweigen und verursacht $d lang $o1 Schaden, erhöht durch Angriffskraft. Muss verstohlen und hinter dem Ziel sein. Gewährt $s2 $lCombopunkt:Combopunkte;.", "Alle $t1 Sek. $s1 Schaden.", 0, 80, 2560, 0,},
   [26888] = {"Verschwinden", "Rang 3", "", "Verbesserte Verstohlenheit", 0, 80, 20480, 0,},
   [26889] = {"Verschwinden", "Rang 3", "Ermöglicht dem Schurken, sich außer Sicht zu stehlen und $11329d lang in einem verbesserten Verstohlenheitsmodus zu agieren. Durchbricht außerdem die Bewegung einschränkende Effekte. Wirkungsvoller als 'Verschwinden' (Rang 2).", "Verbesserte Verstohlenheit", 0, 80, 20480, 0,},
   [26890] = {"Sofort wirkendes Gift VII", "Rang 7", "", "", 0, 80, 81920, 0,},
   [26891] = {"Sofort wirkendes Gift VII", "Rang 7", "Überzieht eine Waffe mit Gift, das eine Stunde lang wirkt.\r\n\r\nBei jedem Schlag besteht eine Chance von $h%, den Gegner zu vergiften, was unmittelbar $26890s1 Naturschaden verursacht.", "", 0, 80, 81920, 0,},
   [26967] = {"Tödliches Gift VI", "Rang 6", "Überzieht eine Waffe mit Gift, das eine Stunde lang wirkt.\r\n\r\nBei jedem Schlag besteht eine Chance von $h%, den Gegner $26968d lang für $26968o1 Naturschaden zu vergiften. Kann bis zu 5-mal auf ein einzelnes Ziel angewendet werden.", "", 0, 80, 655360, 0,},
   [26968] = {"Tödliches Gift VI", "Rang 6", "", "Ziel nimmt alle $t1 Sek. $s1 Naturschaden.", 0, 80, 655360, 0,},
   [26978] = {"Heilende Berührung", "Rang 12", "Heilt ein befreundetes Ziel um $s1.", "", 0, 70, 320, 0,},
   [26979] = {"Heilende Berührung", "Rang 13", "Heilt ein befreundetes Ziel um $s1.", "", 0, 70, 320, 0,},
   [26980] = {"Nachwachsen", "Rang 10", "Heilt ein befreundetes Ziel um $s1 und über $d um weitere $o2.", "Heilt alle $t2 Sek. $s2 Gesundheit.", 0, 70, 640, 0,},
   [26981] = {"Verjüngung", "Rang 12", "Heilt beim Ziel $d lang $o1 Schaden.", "Heilt alle $t1 Sek. $s1 Schaden.", 0, 70, 160, 0,},
   [26982] = {"Verjüngung", "Rang 13", "Heilt beim Ziel $d lang $o1 Schaden.", "Heilt alle $t1 Sek. $s1 Schaden.", 0, 70, 160, 0,},
   [26983] = {"Gelassenheit", "Rang 5", "Heilt bei allen in der Nähe befindlichen Gruppenmitgliedern $d lang alle $t2 Sek. $s1 Gesundheit. Der Druide muss seine Kräfte kanalisieren, um den Zauber aufrechtzuerhalten.", "Heilt in der Nähe befindliche Gruppenmitglieder alle $t2 Sek. um $s1.", 0, 70, 1280, 0,},
   [26984] = {"Zorn", "Rang 9", "Verursacht $s1 Naturschaden am Ziel.", "", 0, 70, 10, 0,},
   [26985] = {"Zorn", "Rang 10", "Verursacht $s1 Naturschaden am Ziel.", "", 0, 70, 10, 0,},
   [26986] = {"Sternenfeuer", "Rang 8", "Verursacht $s1 Arkanschaden am Ziel.", "", 0, 70, 40, 0,},
   [26987] = {"Mondfeuer", "Rang 11", "Verbrennt den Feind und fügt ihm $s2 Arkanschaden sowie $d lang $o1 zusätzlichen Arkanschaden zu.", "Alle $t1 Sek. $s1 Arkanschaden.", 0, 70, 20, 0,},
   [26988] = {"Mondfeuer", "Rang 12", "Verbrennt den Feind und fügt ihm $s2 Arkanschaden sowie $d lang $o1 zusätzlichen Arkanschaden zu.", "Alle $t1 Sek. $s1 Arkanschaden.", 0, 70, 20, 0,},
   [26989] = {"Wucherwurzeln", "Rang 7", "Lässt das Ziel an Ort und Stelle festwurzeln und verursacht $d lang insgesamt $o2 Naturschaden. Erlittener Schaden kann den Effekt unterbrechen. Kann nur draußen verwendet werden.", "Unbeweglich. Alle $t2 Sek. $s2 Naturschaden verursacht.", 0, 70, 5120, 0,},
   [26990] = {"Mal der Wildnis", "Rang 8", "Erhöht $d lang die Rüstung des befreundeten Ziels um $s1, alle Attribute um $s2 und alle Widerstandsarten um $s3.", "Erhöht die Rüstung um $s1, alle Attribute um $s2 und alle Widerstandsarten um $s3.", 0, 70, 2621440, 0,},
   [26991] = {"Gabe der Wildnis", "Rang 3", "Erhöht $d lang die Rüstung der Gruppenmitglieder um $s1, alle Attribute um $s2 und alle Widerstandsarten um $s3.", "Erhöht die Rüstung um $s1, alle Attribute um $s2 und alle Widerstandsarten um $s3.", 0, 70, 2621440, 0,},
   [26992] = {"Dornen", "Rang 7", "Aus dem befreundeten Ziel sprießen Dornen, die jedem, der es schlägt, $s1 Naturschaden zufügen. Hält $d lang an.", "Verursacht $s1 Naturschaden bei Angreifern.", 0, 70, 2560, 0,},
   [26993] = {"Feenfeuer", "Rang 5", "Verringert die Rüstung des Ziels $d lang um $s1. Während der Wirkungsdauer kann das Ziel weder Verstohlenheit anwenden noch unsichtbar werden.", "Verringert Rüstung um $s1. Kann weder Verstohlenheit anwenden noch unsichtbar werden.", 0, 70, 10240, 0,},
   [26994] = {"Wiedergeburt", "Rang 6", "Lässt den Geist in den Körper zurückkehren und bringt ein totes Ziel mit $s1 Gesundheit und $q Mana wieder ins Leben zurück.", "", 0, 70, 2852126720, 0,},
   [26995] = {"Tier besänftigen", "Rang 4", "Besänftigt das Ziel und verringert die Reichweite, über die es Euch angreift, um $s1 Meter. Wirkt sich nur auf Wildtiere der Stufe 85 oder niedriger aus. Hält $d lang an.", "Verringert die Entfernung, aus der das Ziel angreift.", 0, 70, 167772160, 131072,},
   [26996] = {"Zermalmen", "Rang 8", "Erhöht den nächsten Angriffsschaden des Druiden um $s1.", "", 0, 70, 20480, 0,},
   [26997] = {"Prankenhieb", "Rang 6", "Schlägt $x1 in der Nähe befindliche Feinde und verursacht $s1 Schaden, erhöht durch Angriffskraft.", "", 0, 70, 00, 1048576,},
   [26998] = {"Demoralisierendes Gebrüll", "Rang 6", "Der Druide brüllt und verringert damit die Nahkampfangriffskraft von in der Nähe befindlichen Feinden um $s1. Hält $d lang an.", "Verringert Nahkampfangriffskraft um $s1.", 0, 70, 80, 0,},
   [26999] = {"Rasende Regeneration", "Rang 4", "Wandelt $d lang bis zu 10 Wutpunkte pro Sekunde in Gesundheit um. Jeder Wutpunkt wird in $s1 Gesundheit umgewandelt.", "Wandelt Wut in Gesundheit um.", 0, 70, 00, 0,},
   [27000] = {"Klaue", "Rang 6", "Den Feind mit Klauen beharken und ihm so zusätzlich $s1 Schaden zufügen. Gewährt $s2 $lCombopunkt:Combopunkte;.", "", 0, 70, 40960, 0,},
   [27001] = {"Schreddern", "Rang 6", "Schreddert das Ziel, wobei dem Ziel $s3% Schaden plus 236 zugefügt wird. Muss hinter dem Ziel sein. Gewährt $s2 $lCombopunkt:Combopunkte;.", "", 0, 70, 327680, 0,},
   [27002] = {"Schreddern", "Rang 7", "Schreddert das Ziel, wobei dem Ziel $s3% Schaden plus 405 zugefügt wird. Muss hinter dem Ziel sein. Gewährt $s2 $lCombopunkt:Combopunkte;.", "", 0, 70, 327680, 0,},
   [27003] = {"Krallenhieb", "Rang 5", "Attackiert das Ziel mit Krallen, fügt ${$AP/100+$m1} Blutungsschaden sowie $d lang insgesamt ${$m2*3+$AP*0.06} zusätzlichen Schaden zu. Gewährt $s3 $lCombopunkt:Combopunkte;.", "Das Bluten fügt alle $t2 Sek. $s2 Schaden zu.", 0, 70, 40960, 0,},
   [27005] = {"Verheeren", "Rang 5", "Verheert das Ziel, wobei dem Ziel $s2% Schaden plus 514 zugefügt wird. Muss schleichen und hinter dem Ziel sein. Gewährt $s3 $lCombopunkt:Combopunkte;.", "", 0, 70, 655360, 0,},
   [27006] = {"Anspringen", "Rang 4", "Anspringen betäubt das Ziel $d lang und verursacht $27007d lang insgesamt $27007o1 Schaden. Erfordert, dass der Druide schleicht. Gewährt $s3 $lCombopunkt:Combopunkte;.", "Betäubt.", 0, 70, 1310720, 0,},
   [27008] = {"Zerfetzen", "Rang 7", "Finishing-Move, der Schaden im Lauf der Zeit verursacht. Der Schaden erhöht sich pro Combopunkt sowie durch Eure Angriffskraft:\r\n\r\n   1 Punkt: $d lang 300 Schaden.\r\n\r\n   2 Punkte: $d lang 498 Schaden.\r\n\r\n   3 Punkte: $d lang 696 Schaden.\r\n\r\n   4 Punkte: $d lang 894 Schaden.\r\n\r\n   5 Punkte: $d lang 1092 Schaden.", "Alle $t1 Sek. Blutungsschaden.", 0, 70, 83886080, 0,},
   [27009] = {"Griff der Natur", "Rang 7", "Bei Aktivierung hat ein Feind jedes Mal, wenn er den Zaubernden schlägt, eine Chance von $h%, von 'Wucherwurzeln' (Rang 7) betroffen zu werden. Kann nur draußen verwendet werden. $n Aufladung(en). Hält $d lang an.", "Nahkampfschaden, den Ihr erleidet, hat die Chance, den Feind zu durchwuchern.", 0, 70, 10485760, 0,},
   [27010] = {"Wucherwurzeln", "Rang 7", "Lässt das Ziel an Ort und Stelle festwurzeln und verursacht $d lang insgesamt $o2 Naturschaden. Erlittener Schaden kann den Effekt unterbrechen. Kann nur draußen verwendet werden.", "Unbeweglich. Alle $t2 Sek. $s2 Naturschaden verursacht.", 0, 70, 5120, 0,},
   [27011] = {"Feenfeuer (Tiergestalt)", "Rang 5", "Verringert die Rüstung des Ziels $d lang um $s1. Während der Wirkungsdauer kann das Ziel weder Verstohlenheit anwenden noch unsichtbar werden.", "Verringert Rüstung um $s1. Kann weder Verstohlenheit anwenden noch unsichtbar werden.", 0, 70, 10240, 0,},
   [27012] = {"Hurrikan", "Rang 4", "Erzeugt einen wütenden Sturm im Zielgebiet, der Feinden alle $t3 Sek. $42230s1 Naturschaden zufügt und die Zeit zwischen Angriffen um $s2% verringert. Hält $d lang an. Ein Druide muss seine Kräfte kanalisieren, um den Zauber aufrechtzuerhalten.", "Alle $t3 Sek. $42230s1 Schaden, Zeit zwischen Angriffen um $s2% erhöht.", 0, 70, 41943040, 0,},
   [27013] = {"Insektenschwarm", "Rang 6", "Das feindliche Ziel wird von Insekten umschwärmt, die dessen Trefferchance um $s2% verringern und beim Betroffenen über $d $o1 Naturschaden verursachen.", "Trefferchance um  $s2% verringert. Fügt alle $t1 Sek. $s1 Naturschaden zu.", 0, 70, 20971520, 0,},
   [27014] = {"Raptorstoß", "Rang 9", "Ein heftiger Angriff, der den Nahkampfschaden um $s1 erhöht.", "", 0, 90, 20, 0,},
   [27015] = {"Rückzug", "Rang 4", "Versucht, sich vom Ziel zurückzuziehen und verringert dadurch die Bedrohung. Wirkungsvoller als 'Rückzug' (Rang 3). Charakter verlässt Kampfmodus.", "", 0, 90, 00, 16384,},
   [27016] = {"Schlangenbiss", "Rang 10", "Beißt das Ziel und verursacht $d lang ${$RAP*0.1+$m1*5} Naturschaden. Es kann immer nur ein Biss oder Stich pro Jäger auf einem einzelnen Ziel aktiv sein.", "Verursacht alle $t1 Sek. $s1 Naturschaden.", 75, 975, 1638475, 0,},
   [27018] = {"Vipernbiss", "Rang 4", "Beißt das Ziel und zieht $d lang $o1 Mana ab. Es kann immer nur jeweils ein Biss oder Stich pro Jäger auf einem beliebigen Ziel aktiv sein.", "Zieht alle $t1 Sek. $s1 Mana ab.", 75, 975, 075, 128,},
   [27019] = {"Arkaner Schuss", "Rang 9", "Ein sofortiger Schuss, der ${$rap*0.15+$m2} Arkanschaden verursacht und $s1 $lmagischen Effekt:magische Effekte; bannt.", "", 75, 975, 204875, 0,},
   [27020] = {"Ablenkender Schuss", "Rang 7", "Lenkt das Ziel ab und verursacht Bedrohung. Wirkungsvoller als 'Ablenkender Schuss' (Rang 6).", "", 75, 975, 6553675, 131072,},
   [27021] = {"Mehrfachschuss", "Rang 6", "Feuert mehrere Geschosse ab, trifft $x1 Ziele und fügt zusätzliche $s1 Schaden zu.", "", 75, 975, 409675, 0,},
   [27022] = {"Salve", "Rang 4", "Feuert kontinuierlich eine Salve Munition auf das Zielgebiet und verursacht $d lang pro Sekunde $42234s1 Arkanschaden bei feindlichen Zielen im Umkreis von $a1 Metern.", "", 0, 90, 81920, 0,},
   [27023] = {"Feuerbrandfalle", "Rang 6", "Eine Feuerfalle platzieren, die dem ersten sich nähernden Feind $27024d lang insgesamt ${$RAP*0.1+$27024m1*5} Feuerschaden zufügt. Die Falle bleibt $d lang bestehen. Es kann immer nur jeweils eine Falle aktiv sein.", "", 0, 90, 1280, 0,},
   [27024] = {"Feuerbrandfalle", "Rang 6", "", "Alle $t1 Sek. $s1 Feuerschaden.", 0, 90, 40, 0,},
   [27025] = {"Sprengfalle", "Rang 4", "Eine Feuerfalle platzieren, die explodiert, wenn sich ein Feind nähert. Die Falle verursacht ${$RAP*0.1+$27026m1} bis ${$RAP*0.1+$27026M1} Feuerschaden und verbrennt alle Feinde in einem Umkreis von $27026a1 Metern, indem sie ihnen $27026d lang insgesamt $27026o2 zusätzlichen Feuerschaden zufügt. Die Falle bleibt $d lang bestehen. Es kann immer nur jeweils eine Falle aktiv sein.", "", 0, 90, 1280, 0,},
   [27026] = {"Sprengfalle", "Rang 4", "", "Alle $t2 Sek. $s2 Feuerschaden.", 0, 90, 40, 0,},
   [27044] = {"Aspekt des Falken", "Rang 8", "Der Jäger nimmt die Aspekte eines Falken an: Die Distanzangriffskraft wird um $s1 erhöht. Es kann immer nur jeweils ein Aspekt aktiv sein.", "Distanzangriffskraft um $s1 erhöht.", 0, 90, 10485760, 0,},
   [27045] = {"Aspekt der Wildnis", "Rang 3", "Der Jäger und alle Gruppenmitglieder in einem Umkreis von $a1 Metern nehmen die Aspekte der Wildnis an: Der Naturwiderstand wird um $s1 erhöht. Es kann immer nur jeweils ein Aspekt aktiv sein.", "Erhöht Naturwiderstand um $s1.", 0, 90, 00, 4194304,},
   [27046] = {"Tier heilen", "Rang 8", "Heilt Euer Tier $d lang für $o1 Gesundheit.", "Heilt alle $t1 Sek. $s1 Gesundheit.", 0, 90, 83886080, 0,},
   [27063] = {"Donnerstampfer", "Rang 4", "Lässt den Boden mit donnernder Macht erbeben und verursacht bei allen Feinden in einem Umkreis von $a1 Metern $s1 Naturschaden. Diese Fähigkeit verursacht zusätzliche Bedrohung.", "", 0, 40, 1280, 0,},
   [27065] = {"Gezielter Schuss", "Rang 7", "Ein gezielter Schuss, der den Distanzschaden um $s1 erhöht und auf das Ziel gewirkte Heileffekte um $s2% verringert. Hält $d lang an.", "Heileffekte um $s2% verringert.", 75, 975, 13107275, 0,},
   [27066] = {"Aura des Volltreffers", "Rang 4", "Erhöht die Angriffskraft von Gruppenmitgliedern in einem Umkreis von $a1 Metern um $s1. Hält $d an.", "Angriffskraft um $s1 erhöht.", 0, 90, 00, 2097152,},
   [27067] = {"Gegenangriff", "Rang 4", "Ein Schlag, der aktiv wird, nachdem ein gegnerischer Angriff pariert wurde. Dieser Angriff fügt $s1 Schaden zu und macht das Ziel $d lang bewegungsunfähig. Auf 'Gegenangriff' kann nicht mit 'Blocken', 'Ausweichen' oder 'Parieren' reagiert werden.", "Unbeweglich.", 0, 90, 00, 524288,},
   [27068] = {"Stich des Flügeldrachen", "Rang 4", "Verschießt einen Stachel, der das Ziel $d lang einschläfert. Jeglicher erlittene Schaden hebt den Effekt wieder auf. Wenn das Ziel wieder aufwacht, verursacht der Stich $27069d lang insgesamt $27069o1 Naturschaden. Auf dem Ziel kann gleichzeitig nur ein Biss oder Stich pro Jäger aktiv sein.", "Schlafend.", 0, 90, 00, 4096,},
   [27069] = {"Stich des Flügeldrachen", "Rang 4", "", "Alle $t1 Sek. $s1 Naturschaden.", 0, 90, 00, 256,},
   [27070] = {"Feuerball", "Rang 13", "Schleudert eine glühende Kugel hinaus, die $s1 Feuerschaden sowie $d lang $o2 zusätzlichen Feuerschaden verursacht.", "Alle $t2 Sek. $s2 Feuerschaden.", 0, 30, 10, 0,},
   [27071] = {"Frostblitz", "Rang 12", "Schleudert einen Frostblitz auf den Feind, der $s2 Frostschaden verursacht und $d lang das Bewegungstempo um $s1% reduziert.", "Bewegungstempo um $s1% verringert.", 0, 30, 15728960, 0,},
   [27072] = {"Frostblitz", "Rang 13", "Schleudert einen Frostblitz auf den Feind, der $s2 Frostschaden verursacht und $d lang das Bewegungstempo um $s1% reduziert.", "Bewegungstempo um $s1% verringert.", 0, 30, 15728960, 0,},
   [27073] = {"Versengen", "Rang 8", "Versengt den Feind und fügt ihm $s1 Feuerschaden zu.", "", 0, 30, 160, 0,},
   [27074] = {"Versengen", "Rang 9", "Versengt den Feind und fügt ihm $s1 Feuerschaden zu.", "", 0, 30, 160, 0,},
