   [27124] = {"Eisrüstung", "Rang 5", "Erhöht die Rüstung um $s1 und den Frostwiderstand um $s3. Wenn ein Feind den Zaubernden schlägt, kann $7321d lang sein Bewegungstempo um $7321s1% verlangsamt und die Zeit zwischen seinen Angriffen um $7321s2% erhöht werden. Es kann immer nur ein Rüstungszauber zur selben Zeit auf dem Magier aktiv sein. Hält $d lang an.", "Erhöht die Rüstung um $s1, Frostwiderstand um $s3 und kann Angreifer verlangsamen.", 0, 30, 340787200, 0,},
   [27125] = {"Magische Rüstung", "Rang 4", "Erhöht den Widerstand gegen alle Arten von Magie um $s1 und gewährt dem Zaubernden jederzeit $s2% seiner Manaregeneration. Es kann immer nur ein Rüstungszauber zur selben Zeit auf dem Magier aktiv sein. Hält $d lang an.", "Um $s1 erhöhter Widerstand gegen alle Arten von Magie und jederzeit $s2% der normalen Manaregeneration.", 0, 30, 2684354560, 0,},
