   [27130] = {"Magie verstärken", "Rang 5", "Verstärkt gegen das anvisierte Gruppenmitglied gerichtete Magie, wobei der von Zaubern zugefügte Schaden um bis zu $s1 und Heilzauber um bis zu $s2 erhöht werden. Hält $d lang an.", "Erhöht erlittenen Magieschaden um bis zu $s1 und Heilung um bis zu $s2.", 0, 30, 81920, 0,},
   [27131] = {"Manaschild", "Rang 7", "Absorbiert $s1 Schaden und entzieht stattdessen Mana. Entzieht pro absorbiertem Schaden $e Mana. Hält $d lang an.", "Absorbiert Schaden und entzieht stattdessen Mana.", 0, 30, 327680, 0,},
   [27132] = {"Pyroschlag", "Rang 9", "Schleudert einen riesigen feurigen Felsen hinaus, der $s1 Feuerschaden sowie zusätzlich $d lang insgesamt $o2 Feuerschaden verursacht.", "Alle $t2 Sek. $s2 Feuerschaden.", 0, 30, 41943040, 0,},
