   [27140] = {"Segen der Macht", "Rang 8", "Belegt ein befreundetes Ziel mit einem Segen und erhöht $d lang die Angriffskraft um $s1. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein.", "Erhöht Angriffskraft um $s1.", 0, 100, 2684354580, 0,},
   [27141] = {"Großer Segen der Macht", "Rang 3", "Belegt alle Mitglieder einer Gruppe oder eines Schlachtzuges, die dieselbe Klasse wie das Ziel haben, mit dem großen Segen der Macht. Der große Segen der Macht erhöht die Angriffskraft $d lang um $s1. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein.", "Erhöht Angriffskraft um $s1.", 0, 100, 2684354580, 0,},
   [27142] = {"Segen der Weisheit", "Rang 7", "Belegt ein befreundetes Ziel mit einem Segen und stellt $d lang alle 5 Sek. $s1 Mana wieder her. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein.", "Stellt alle 5 Sek. $s1 Mana wieder her.", 0, 100, 2685009920, 0,},
