   [27144] = {"Segen des Lichts", "Rang 4", "Belegt ein befreundetes Ziel mit einem Segen und verstärkt die Effekte von den Zaubern 'Heiliges Licht', die auf dieses Ziel angewandt werden, um bis zu $s1 sowie die Effekte der Zauber 'Lichtblitz', die auf das Ziel angewandt werden, um bis zu $s2. Hält $d lang an. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein.", "Erhält bis zu $s1 Extraheilung durch 'Heiliges Licht' und bis zu $s2 Extraheilung durch den Zauber 'Lichtblitz'.", 0, 100, 2684354560, 0,},
   [27145] = {"Großer Segen des Lichts", "Rang 2", "Belegt alle Mitglieder einer Gruppe oder eines Schlachtzuges, die dieselbe Klasse wie das Ziel haben, mit dem großen Segen des Lichts. Der große Segen des Lichts erhöht die Effekte der auf das Ziel gewirkten Zauber 'Heiliges Licht' um $s1 und 'Lichtblitz' um $s2. Hält $d lang an. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein.", "Erhält bis zu $s1 Extraheilung durch 'Heiliges Licht' und bis zu $s2 Extraheilung durch den Zauber 'Lichtblitz'.", 0, 100, 2684354560, 0,},
   [27147] = {"Segen der Opferung", "Rang 3", "Wendet einen Segen auf das Gruppenmitglied an und überträgt $s1 des erlittenen Schadens pro Treffer auf den Zaubernden. Hält $d lang an. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein.", "Überträgt $s1 Schaden pro Treffer auf den Paladin.", 0, 100, 2684354560, 0,},
   [27148] = {"Segen der Opferung", "Rang 4", "Wendet einen Segen auf das Gruppenmitglied an und überträgt $s1 des erlittenen Schadens pro Treffer auf den Zaubernden. Hält $d lang an. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein.", "Überträgt $s1 Schaden pro Treffer auf den Paladin.", 0, 100, 2684354560, 0,},
   [27149] = {"Aura der Hingabe", "Rang 8", "Gibt Gruppenmitgliedern in einem Umkreis von $a1 Metern $s1 zusätzliche Rüstung. Spieler können pro Paladin immer nur jeweils eine Aura besitzen.", "Erhöht die Rüstung um $s1.", 0, 100, 640, 0,},
   [27150] = {"Aura der Vergeltung", "Rang 6", "Verursacht $s1 Heiligschaden für jede Kreatur, die ein Gruppenmitglied schlägt, das sich im Umkreis von $a1 Metern befindet. Die Spieler können pro Paladin nur jeweils eine Aura besitzen.", "Fügt Euren Angreifern $s1 Punkte heiligen Schaden zu.", 0, 100, 80, 0,},
   [27151] = {"Aura des Schattenwiderstands", "Rang 4", "Erhöht den Schattenwiderstand aller sich im Umkreis von $a1 Metern befindlichen Gruppenmitglieder um $s1. Spieler können pro Paladin immer nur jeweils eine Aura besitzen.", "Erhöht Schattenwiderstand um $s1.", 0, 100, 671088640, 16,},
   [27152] = {"Aura des Frostwiderstands", "Rang 4", "Erhöht den Frostwiderstand aller sich im Umkreis von $a1 Metern befindlichen Gruppenmitglieder um $s1. Spieler können pro Paladin immer nur jeweils eine Aura besitzen.", "Erhöht Frostwiderstand um $s1.", 0, 100, 671088640, 16,},
   [27153] = {"Aura des Feuerwiderstands", "Rang 4", "Erhöht den Feuerwiderstand aller sich im Umkreis von $a1 Metern befindlichen Gruppenmitglieder um $s1. Spieler können pro Paladin immer nur jeweils eine Aura besitzen.", "Feuerwiderstand um $s1 erhöht.", 0, 100, 671088640, 16,},
   [27154] = {"Handauflegung", "Rang 4", "Heilt ein befreundetes Ziel um einen Betrag, der der maximalen Gesundheit des Paladins entspricht, und stellt $s2 seines Manas wieder her. Entzieht dem Paladin bei Verwendung sämtliches noch vorhandenes Mana.", "", 0, 100, 327680, 0,},
   [27155] = {"Siegel der Rechtschaffenheit", "Rang 9", "Erfüllt den Paladin $d lang mit heiligem Geist, der jedem Nahkampfangriff zusätzlichen Heiligschaden in Höhe von ${$cond($eq($HND,1),0.85*($m1*1.2*1.03*$MWS/100)+0.03*($MW+$mw)/2-1,1.2*($m1*1.2*1.03*$MWS/100)+0.03*($MW+$mw)/2+1)} verleiht. Es kann immer nur jeweils ein Siegel auf dem Paladin aktiv sein.\r\n\r\n\r\n\r\nDie Entfesselung der Energie dieses Siegels fügt einem Feind $27157s1 Heiligschaden zu.", "Nahkampfangriffe verursachen zusätzlichen Heiligschaden in Höhe von ${$cond($eq($HND,1),0.85*($m1*1.2*1.03*$MWS/100)+0.03*($MW+$mw)/2-1,1.2*($m1*1.2*1.03*$MWS/100)+0.03*($MW+$mw)/2+1)}.", 0, 100, 1342177280, 0,},
   [27156] = {"Siegel der Rechtschaffenheit", "Rang 9", "", "", 0, 100, 00, 0,},
   [27157] = {"Richturteil der Rechtschaffenheit", "Rang 9", "", "", 0, 100, 10240, 0,},
   [27158] = {"Siegel des Kreuzfahrers", "Rang 7", "Erfüllt den Paladin $d lang mit dem Geist eines Kreuzfahrers und verleiht $s1 Nahkampfangriffskraft. Außerdem greift der Paladin um $s3% schneller an, verursacht jedoch bei jedem Angriff weniger Schaden. Es kann immer nur jeweils ein Siegel auf dem Paladin aktiv sein.\r\n\r\n\r\n\r\nDie Entfesselung der Energie dieses Siegels richtet einen Feind $27159d lang und erhöht den erlittenen Heiligschaden um bis zu $27159s1. Eure Nahkampfangriffe frischen die Dauer dieses Siegels auf. Es kann immer nur jeweils ein Richturteil pro Paladin aktiv sein.", "Nahkampfangriffskraft um $s1 erhöht. Nahkampfangriffe sind $s3% schneller, aber verursachen weniger Schaden.", 0, 100, 5120, 0,},
   [27159] = {"Richturteil des Kreuzfahrers", "Rang 7", "", "Erhöht erlittenen Heiligschaden um bis zu $w1.", 0, 100, 5368709120, 0,},
   [27160] = {"Siegel des Lichts", "Rang 5", "Erfüllt den Paladin $d lang mit göttlichem Licht, das jedem Nahkampfangriff eine Chance verleiht, den Paladin um $27161s1 zu heilen. Es kann immer nur jeweils ein Siegel auf dem Paladin aktiv sein.\r\n\r\n\r\n\r\nDie Entfesselung der Energie dieses Siegels richtet einen Feind $27162d lang und verleiht Nahkampfangriffen, die gegen den gerichteten Feind ausgeführt werden, eine Chance den Angreifer um $27163s1 zu heilen. Eure Nahkampfangriffe erneuern die Dauer dieses Effekts. Es kann immer nur jeweils ein Richturteil pro Paladin aktiv sein.", "Nahkampfangriffe haben eine Chance, Euch um $27161s1 zu heilen.", 0, 100, 1342177280, 0,},
   [27161] = {"Siegel des Lichts", "Rang 5", "", "", 0, 100, 2621440, 0,},
