   [27163] = {"Richturteil des Lichts", "Rang 5", "", "", 0, 100, 00, 1,},
   [27164] = {"Richturteil der Weisheit", "Rang 4", "", "Angriffe und Zauber gegen Euch haben eine Chance beim Angreifer $27165s1 Mana wiederherzustellen.", 0, 100, 5242880, 0,},
   [27166] = {"Siegel der Weisheit", "Rang 4", "Erfüllt den Paladin $d lang mit göttlicher Weisheit, die jedem Nahkampfangriff eine Chance verleiht, $27167s1 Mana des Paladins wiederherzustellen. Es kann immer nur jeweils ein Siegel auf dem Paladin aktiv sein.\r\n\r\n\r\n\r\nDie Entfesselung der Energie dieses Siegels richtet einen Feind $27164d lang und verleiht Angriffen und Zaubern, die gegen den gerichteten Feind ausgeführt werden, eine Chance beim Angreifer $27165s1 Mana wiederherzustellen. Eure Nahkampfangriffe erneuern die Dauer dieses Effekts. Es kann immer nur jeweils ein Richturteil pro Paladin aktiv sein.", "Nahkampfangriffe haben eine Chance $27167s1 Mana wiederherzustellen.", 0, 100, 1342177280, 0,},
   [27167] = {"Siegel der Weisheit", "Rang 4", "", "", 0, 100, 2621440, 0,},
   [27168] = {"Segen des Refugiums", "Rang 5", "Belegt ein befreundetes Ziel mit einem Segen und verringert dabei jeglichen zugefügten Schaden von allen Quellen $d lang um bis zu $s1. Zusätzlich nimmt ein Angreifer $s2 Heiligschaden, wenn das Ziel einen Nahkampfangriff blockt. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein.", "Erlittener Schaden um bis zu $s1 verringert. Geblockte Nahkampfangriffe verursachen beim Angreifer $s2 Heiligschaden.", 0, 100, 2684354560, 0,},
   [27169] = {"Großer Segen des Refugiums", "Rang 2", "Belegt alle Mitglieder einer Gruppe oder eines Schlachtzuges, die dieselbe Klasse wie das Ziel haben, mit dem großen Segen des Refugiums. Der große Segen des Refugiums reduziert sämtlichen beim Ziel verursachten Schaden $d lang um $s1. Zusätzlich verursacht ein Ziel, wenn es einen Angriff blockt, beim Angreifer $s2 Heiligschaden. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein.", "Erlittener Schaden um bis zu $s1 verringert. Geblockte Nahkampfangriffe verursachen beim Angreifer $s2 Heiligschaden.", 0, 100, 2684354560, 0,},
   [27170] = {"Siegel des Befehls", "Rang 6", "Erfüllt den Paladin $d lang mit dem Geist des Befehls und gewährt ihm die Chance, zusätzlich zum erzielten Waffenschaden, Heiligschaden in Höhe von $20424s1% des normalen Waffenschadens zuzufügen. Es kann immer nur jeweils ein Siegel auf dem Paladin aktiv sein.\r\n\r\n\r\n\r\nDie Entfesselung der Energie dieses Siegels richtet einen Feind und verursacht sofortigen Heiligschaden in Höhe von $/2;27171s1, oder $27171s1 Heiligschaden, sollte das Ziel betäubt oder handlungsunfähig sein.", "Nahkampfangriffe haben eine Chance, zusätzlichen Heiligschaden zu verursachen.", 0, 100, 335544320, 0,},
