   [27179] = {"Heiliger Schild", "Rang 4", "Erhöht die Blockchance $d lang um $s1% und fügt, solange aktiv, für jeden geblockten Angriff $s2 Heiligschaden zu. Der durch 'Heiliger Schild' verursachte Schaden erzeugt 35% zusätzliche Bedrohung. Jedes Blocken verbraucht eine der $n Aufladungen.", "Blockchance um $s1% erhöht. $s2 Heiligschaden auf Angreifer beim Blocken. Der durch 'Heiliger Schild' verursachte Schaden erzeugt 35% zusätzliche Bedrohung. $n Aufladungen.", 0, 100, 00, 64,},
   [27180] = {"Hammer des Zorns", "Rang 4", "Schleudert einen Hammer gegen einen Feind und fügt $s1 Heiligschaden zu. Nur auf  Ziele anwendbar, die über 20% oder weniger Gesundheit verfügen.", "", 0, 100, 00, 128,},
   [27186] = {"Tödliches Gift VII", "Rang 7", "Überzieht eine Waffe mit Gift, das eine Stunde lang wirkt.\r\n\r\nBei jedem Schlag besteht eine Chance von $h%, den Gegner $27187d lang für $27187o1 Naturschaden zu vergiften. Kann bis zu 5-mal auf ein einzelnes Ziel angewendet werden.", "", 0, 80, 655360, 0,},
