   [27212] = {"Feuerregen", "Rang 5", "Lässt einen feurigen Regen niedergehen, der $d lang Feinde im Wirkungsbereich mit insgesamt ${$42218m1*4} Feuerschaden verbrennt.", "Alle $42218t1 Sek. $42218s1 Feuerschaden.", 0, 50, 320, 0,},
   [27213] = {"Höllenfeuer", "Rang 4", "Entzündet den Bereich um den Zaubernden herum, fügt ihm selbst alle $t2 Sek. $s2 Feuerschaden zu sowie im gleichen Rythmus allen in der Nähe befindlichen Feinden $27214s1 Feuerschaden. Hält $d lang an.", "Fügt sich selbst und allen in der Nähe befindlichen Feinden Schaden zu.", 0, 50, 640, 0,},
   [27214] = {"Höllenfeuer", "Rang 4", "", "", 0, 50, 640, 0,},
   [27215] = {"Feuerbrand", "Rang 9", "Verbrennt den Feind und fügt $s2 Feuerschaden sowie $d lang $o1 zusätzlichen Schaden zu.", "Alle $t1 Sek. $s1 Feuerschaden.", 0, 50, 40, 0,},
   [27216] = {"Verderbnis", "Rang 8", "Verdirbt das Ziel und verursacht $d lang $o1 Schattenschaden.", "Alle $t1 Sek. $s1 Schattenschaden.", 0, 50, 20, 0,},
   [27217] = {"Seelendieb", "Rang 5", "Entzieht dem Ziel die Seele und verursacht $d lang $o2 Schattenschaden. Wenn das Ziel beim Entziehen der Seele stirbt und Erfahrung oder Ehrenpunkte abgibt, erhält der Zaubernde einen Seelensplitter. Seelensplitter werden für andere Zauber benötigt.", "Alle $t2 Sek. $s2 Schattenschaden.", 0, 50, 163840, 0,},
   [27218] = {"Fluch der Pein", "Rang 7", "Verflucht das Ziel mit Pein und fügt $d lang $o1 Schattenschaden zu. Zuerst wird der Schaden langsam zugefügt und nimmt dann zu, bis der Fluch seine Gesamtdauer erreicht hat. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "$d lang insgesamt $o1 Schattenschaden.", 0, 50, 10240, 0,},
   [27219] = {"Blutsauger", "Rang 7", "Überträgt alle $t1 Sek. $s1 Gesundheit vom Ziel auf den Zaubernden. Hält $d lang an.", "Entzieht alle $t1 Sek. $s1 Gesundheit und überträgt sie auf den Zaubernden.", 0, 50, 80, 0,},
   [27220] = {"Blutsauger", "Rang 8", "Überträgt alle $t1 Sek. $s1 Gesundheit vom Ziel auf den Zaubernden. Hält $d lang an.", "Entzieht alle $t1 Sek. $s1 Gesundheit und überträgt sie auf den Zaubernden.", 0, 50, 80, 0,},
   [27221] = {"Mana entziehen", "Rang 5", "Überträgt alle $t1 Sek. $s1 Mana vom Ziel auf den Zaubernden. Hält $d lang an.", "Entzieht jede Sekunde $s1 Mana und überträgt sie auf den Zaubernden.", 0, 50, 160, 0,},
   [27222] = {"Aderlass", "Rang 7", "Wandelt $s1 Gesundheit in $s1 Mana um.", "", 0, 50, 2621440, 0,},
   [27223] = {"Todesmantel", "Rang 4", "Verursacht $s1 Schattenschaden und das betroffene Ziel läuft $d lang voller Entsetzen davon. Der Zaubernde erhält ${100*$e1}% des verursachten Schadens in Gesundheit.", "Von Entsetzen erfüllt.", 0, 50, 5242880, 0,},
   [27224] = {"Fluch der Schwäche", "Rang 7", "Die Nahkampfangriffskraft des Ziels wird $d lang um $s1 reduziert. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "Nahkampfangriffskraft um $s1 verringert.", 0, 50, 327680, 0,},
   [27226] = {"Fluch der Tollkühnheit", "Rang 5", "Verflucht das Ziel mit Tollkühnheit, wodurch sich die Nahkampfangriffskraft $d lang um $s1 erhöht, jedoch die Rüstung um $s2 verringert. Mit einem Fluch belegte Feinde fliehen nicht und ignorieren Furcht- und Entsetzenseffekte. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "Nahkampfangriffskraft um $s1 erhöhen und Rüstung um $s2 verringern. Das Ziel flieht nicht und ignoriert Furcht- und Entsetzenseffekte.", 0, 50, -21474836480, 0,},
   [27228] = {"Fluch der Elemente", "Rang 4", "Verflucht das Ziel und verringert $d lang seinen Arkan-, Feuer-, Frost- und Schattenwiderstand um $s1. Erhöht außerdem den erlittenen Arkan-, Feuer-, Frost- und Schattenschaden des Ziels um $s2%. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "Verringert Arkan-, Feuer-, Frost- und Schattenwiderstand um $s1. Erhöht den erlittenen Arkan-, Feuer-, Frost- und Schattenschaden um $s2%.", 0, 50, 00, 512,},
   [27229] = {"Fluch der Schatten", "Rang 3", "Verflucht das Ziel und verringert $d lang seinen Schatten- und Arkanwiderstand um $s1. Erhöht außerdem den erlittenen Schatten- und Arkanschaden um $s2%. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein.", "Verringert Schatten- und Arkanwiderstand um $s1. Erhöht den erlittenen Schatten- und Arkanschaden um $s2%.", 0, 50, 00, 512,},
   [27230] = {"Gesundheitsstein herstellen", "Rang 6", "Lässt einen meisterlichen Gesundheitsstein entstehen, der verwendet werden kann, um sofort $27235s1 Gesundheit wiederherzustellen.\r\n\r\n\r\n\r\nHerbeigezauberte Gegenstände verschwinden, wenn Ihr Euch länger als 15 Minuten ausloggt.", "", 0, 50, 10485760, 0,},
   [27235] = {"Meisterlicher Gesundheitsstein", "", "Stellt sofort $s1 Gesundheit wieder her.", "", 0, 50, 655360, 0,},
   [27236] = {"Meisterlicher Gesundheitsstein", "", "Stellt sofort $s1 Gesundheit wieder her.", "", 0, 50, 655360, 0,},
   [27237] = {"Meisterlicher Gesundheitsstein", "", "Stellt sofort $s1 Gesundheit wieder her.", "", 0, 50, 655360, 0,},
   [27238] = {"Seelenstein herstellen", "Rang 6", "Lässt einen meisterlichen Seelenstein entstehen. Der Seelenstein kann zum Speichern der Seele eines Ziels benutzt werden. Wenn das Ziel stirbt, während seine Seele gespeichert ist, kann es mit $27240s1 Gesundheit und $27240q1 Mana wiederbelebt werden.\r\n\r\n\r\n\r\nHerbeigezauberte Gegenstände verschwinden, wenn Ihr Euch länger als 15 Minuten ausloggt.", "", 0, 50, 10485760, 0,},
   [27243] = {"Saat der Verderbnis", "Rang 1", "Pflanzt dem feindlichen Ziel einen Samen der Verderbnis ein, der $d lang insgesamt $o1 Schattenschaden verursacht. Wenn das Ziel insgesamt $s2 Schaden nimmt oder stirbt, verursacht der Samen bei allen anderen Feinden im Umkreis von $27285a1 Metern um das Ziel $27285s1 Schattenschaden. Auf jedem Ziel kann pro Hexenmeister nur ein Verderbniszauber aktiv sein.", "Verursacht alle $t1 Sek. $s1 Schattenschaden. Nachdem insgesamt $s2 Schaden erlitten wurden, oder das betroffene Ziel stirbt, fügt 'Saat der Verderbnis' allen anderen Feinden, mit Ausnahme des Ziels selbst, im Umkreis von $27285a1 Metern $27285s1 Schattenschaden zu.", 0, 50, 00, 16,},
   [27250] = {"Feuerstein herstellen", "Rang 5", "Stellt einen meisterlichen Feuerstein her, der angelegt werden kann. Wenn angelegt, wird die Waffenhandwaffe mit Feuer verzaubert und gewährt jedem Angriff die Chance, $27253s1 zusätzlichen Feuerschaden zuzufügen. Außerdem erhöht das Anlegen eines meisterlichen Feuersteins den Schaden von Feuerzaubern um $27256s1.\r\n\r\n\r\n\r\nHerbeigezauberte Gegenstände verschwinden, wenn Ihr Euch länger als 15 Minuten ausloggt.", "", 0, 50, 10485760, 0,},
   [27253] = {"Meisterlicher Feuerstein", "", "Feuerschaden.", "", 0, 50, 20971520, 0,},
   [27256] = {"Feuerschaden erhöhen 30", "Rang 5", "Erhöht den von Feuerzaubern und -effekten zugefügten Schaden um bis zu $s1.", "", 0, 50, 20971520, 0,},
   [27259] = {"Lebenslinie", "Rang 8", "Gibt dem Begleiter des Zaubernden $d lang in jeder Sekunde $s1 Gesundheit, solange der Zaubernde seine Kräfte kanalisiert.", "Überträgt Leben.", 0, 50, 167772160, 0,},
   [27260] = {"Dämonenrüstung", "Rang 6", "Schützt den Zaubernden und erhöht die Rüstung um $s1, den Schattenwiderstand um $s2 und stellt alle 5 Sek. $s3 Gesundheit wieder her. Es kann immer nur ein Rüstungszauber zur selben Zeit auf dem Hexenmeister aktiv sein. Hält $d an.", "Erhöht Rüstung um $s1, Schattenwiderstand um $s2 und stellt alle 5 Sek. $s3 Gesundheit wieder her.", 0, 50, 00, 32,},
   [27263] = {"Schattenbrand", "Rang 7", "Überzieht das Ziel sofort mit $s2 Schattenschaden. Wenn das Ziel innerhalb von $29341d nach 'Schattenbrand' stirbt und Erfahrung oder Ehrenpunkte abgibt, erhält der Zaubernde einen Seelensplitter.", "", 0, 50, 1280, 0,},
   [27264] = {"Lebensentzug", "Rang 5", "Überträgt alle $t1 Sek. $s1 Gesundheit vom Ziel auf den Zaubernden. Hält $d lang an.", "Zieht alle $t1 Sek. $s1 Gesundheit auf den Zaubernden ab.", 0, 50, 00, 1,},
   [27265] = {"Dunkler Pakt", "Rang 4", "Zieht $s1 vom Mana Eures Begleiters ab und gibt 100% an Euch zurück.", "", 0, 50, -21474836480, 0,},
   [27266] = {"Feuersbrunst", "Rang 5", "Entzündet ein Ziel, das bereits mit Eurem 'Feuerbrand' infiziert ist, fügt $s1 Feuerschaden zu und verzehrt den Zauber 'Feuerbrand'.", "", 0, 50, 5120, 0,},
   [27267] = {"Feuerblitz", "Rang 8", "Verursacht $s1 Feuerschaden am Ziel.", "", 0, 50, 40960, 0,},
   [27268] = {"Blutpakt", "Rang 6", "Erhöht die Ausdauer der Gruppenmitglieder um $s1.", "Erhöht Ausdauer um $s1.", 0, 50, 83886080, 0,},
   [27269] = {"Feuerschild", "Rang 6", "Umgibt das Ziel mit einem Schild aus Feuer. Der Feuerwiderstand des Zieles wird um $s2 erhöht und jeder Schlag gegen das Ziel verursacht beim Angreifer $s1 Feuerschaden. Hält $d lang an. Der Zaubernde kann 'Feuerschild' nicht auf sich selbst wirken.", "Erhöht Feuerwiderstand um $s2 und fügt dem Angreifer bei einem Treffer $s1 Punkte Feuerschaden zu.", 0, 50, 83886080, 0,},
   [27270] = {"Qual", "Rang 7", "Verspottet die Kreatur und erhöht damit die Chance, dass sie den Leerwandler angreift. Wirkungsvoller als 'Qual' (Rang 6).", "", 0, 50, 335544320, 0,},
   [27271] = {"Leiden", "Rang 5", "Verspottet alle Gegner im Umkreis von $a1 Metern, erhöht damit die Chance, dass sie den Leerwandler angreifen, und verringert die Trefferchance $d lang um $s2%. Wirkungsvoller als 'Leiden' (Rang 4).", "Trefferchance um $s2% verringert.", 0, 50, 335544320, 0,},
   [27272] = {"Schatten verzehren", "Rang 7", "Der Leerwandler verzehrt in der Nähe befindliche Schatten, um seine Gestalt zu vergrößern, und stellt im Laufe von $d $o1 Gesundheit wieder her. Zusätzlich erhöht sich die Angriffskraft des Leerwandlers $27599d lang um $27599s1. Kann nicht während eines Kampfes verwendet werden.", "Heilt alle $t1 Sek. $s1 Schaden.", 0, 50, 335544320, 0,},
   [27273] = {"Opferung", "Rang 7", "Opfert den Leerwandler und gewährt seinem Besitzer einen Schild, der $d lang $s1 Schaden absorbiert. Solange der Schild hält, wird das Zauberwirken durch erlittenen Schaden nicht unterbrochen.", "Absorbiert allen Schaden.", 0, 50, 335544320, 0,},
   [27274] = {"Schmerzenspeitsche", "Rang 7", "Ein sofortiger Angriff, der das Ziel peitscht und $s1 Schattenschaden zufügt.", "", 0, 50, 81920, 0,},
   [27275] = {"Besänftigender Kuss", "Rang 5", "Besänftigt das Ziel und erhöht die Chance, dass es etwas anderes angreift. Verringert zusätzlich das Nahkampfangriffstempo $d lang um $s2%. Effektiver als Besänftigender Kuss (Rang 4).", "Nahkampfangriffstempo um $s2% verringert.", 0, 50, 10737418240, 0,},
   [27276] = {"Magie verschlingen", "Rang 5", "Reinigt und entzieht $s1 $lschädlichen Magieeffekt:schädliche Magieeffekte; von einem Freund oder $s1 $lnützlichen Magieeffekt:nützliche Magieeffekte; von einem Feind. Wird ein Effekt verschlungen, wird der Teufelsjäger um $27278s1 geheilt.", "", 0, 50, 00, 0,},
   [27277] = {"Magie verschlingen", "Rang 6", "Reinigt und entzieht $s1 $lschädlichen Magieeffekt:schädliche Magieeffekte; von einem Freund oder $s1 $lnützlichen Magieeffekt:nützliche Magieeffekte; von einem Feind. Wird ein Effekt verschlungen, wird der Teufelsjäger um $27279s1 geheilt.", "", 0, 50, 00, 0,},
