   [27286] = {"Schattenwut", "", "", "", 0, 60, 83886080, 0,},
   [27441] = {"Hinterhalt", "Rang 7", "Das Ziel aus dem Hinterhalt angreifen und ihm $s2% Waffenschaden plus 335 zufügen. Muss verstohlen und hinter dem Ziel sein. In der Waffenhand muss sich ein Dolch befinden. Gewährt $s3 $lCombopunkt:Combopunkte;.", "", 0, 80, 83891200, 0,},
   [27448] = {"Finte", "Rang 6", "Ein Täuschungsmanöver wird durchgeführt, das zwar keinen Schaden verursacht, aber Eure Bedrohung um ein großes Maß verringert und die Wahrscheinlichkeit senkt, dass der Feind Euch angreift.", "", 0, 80, 1342177280, 0,},
   [27498] = {"Zorn des Kreuzfahrers", "", "Bei einem Nahkampfangriff besteht eine Chance, dass $27499d lang Euer Zauberschaden und Eure Heilung um $27499s1 erhöht werden.", "", 0, 100, 00, 0,},
   [27514] = {"Manareduzierung 3%", "", "Reduziert die Manakosten Eurer Zauber um $s1%.", "", 0, 30, 00, 0,},
   [27530] = {"Hurrikan", "", "Erzeugt einen wütenden Sturm im Zielgebiet, der allen in der Nähe befindlichen Feinden alle $t1 Sek. $s1 Naturschaden zufügt und die Zeit zwischen Angriffen der betroffenen Feinde um $s2% erhöht. Hält $d lang an. Ein Druide muss seine Kräfte kanalisieren, um den Zauber aufrechtzuerhalten.", "Alle $t1 Sek. $s1 Schaden, Zeit zwischen Angriffen um $s2% erhöht.", 0, 70, 41943040, 0,},
   [27533] = {"Feuerwiderstand", "", "Absorbiert $s1 Feuerschaden. Hält $d lang an.", "Absorbiert $s1 Feuerschaden.", 0, 130, 00, 0,},
