   [27556] = {"Krallenhieb", "", "Attackiert das Ziel mit Krallen, fügt $s1 Schaden sowie $o2 zusätzlichen Schaden im Verlauf von $d zu.", "Das Bluten fügt alle $t2 Sek. $s2 Schaden zu.", 0, 70, 40960, 0,},
