   [27576] = {"Verstümmeln", "Rang 1", "Greift sofort mit beiden Waffen an und fügt mit jeder Waffe zusätzlich $5374s1 Schaden zu. Der Schaden gegen vergiftete Ziele ist um 50% erhöht. Muss hinter dem Ziel sein. Gewährt 2 Combopunkte.", "", 0, 80, 00, 4,},
   [27577] = {"Abfangen", "Rang 3", "Stürmt auf einen Gegner zu, verursacht $20615s2 Schaden und betäubt ihn $20615d lang.", "", 0, 40, 00, 0,},
   [27578] = {"Schlachtruf", "Rang 6", "Der Krieger schreit, wodurch sich die Angriffskraft der Gruppe innerhalb eines Radius von $a1 Metern um $s1 erhöht. Hält $d lang an.", "Erhöht Eure Angriffskraft um $s1.", 0, 40, 655360, 0,},
   [27579] = {"Demoralisierender Ruf", "Rang 5", "Verringert $d lang die Angriffskraft von Feinden innerhalb eines Radius von $a1 Metern um $s1.", "Verringert Angriffskraft um $s1.", 0, 40, 1310720, 0,},
   [27580] = {"Tödlicher Stoß", "Rang 4", "Ein heimtückischer Stoß, der Waffenschaden plus $s2 verursacht und das Ziel verwundet sowie die Wirksamkeit jeglicher Heilung $d lang um $s1% verringert.", "Heileffekte um $s1% verringert.", 0, 40, 335544320, 0,},
   [27581] = {"Entwaffnen", "", "Entwaffnet den Gegner $d lang.", "Entwaffnet!", 0, 40, 00, 0,},
   [27584] = {"Kniesehne", "Rang 3", "Verstümmelt den Feind, wobei $s1 Schaden verursacht werden und die Bewegung des Feindes $d lang um $s2% verlangsamt wird.", "Bewegungstempo um $s2% verringert.", 0, 40, 00, 0,},
   [27599] = {"Verzehrte Schatten", "Rang 7", "", "Angriffskraft um $s1 erhöht.", 0, 50, 335544320, 0,},
   [27605] = {"Schattenwort: Schmerz", "Rang 8", "Ein Wort der Dunkelheit, das $d lang insgesamt $o1 Schattenschaden zufügt.", "Alle $t1 Sek. $s1 Punkte Schattenschaden.", 0, 60, 327680, 0,},
   [27606] = {"Erneuerung", "Rang 9", "Heilt das Ziel $d lang um $o1 Schaden.", "Heilt alle $t1 Sek. $s1 Schaden.", 0, 60, 640, 0,},
   [27607] = {"Machtwort: Schild", "Rang 10", "Nutzt die Seelenkraft des Gruppenmitglieds zum Abschirmen und absorbiert dabei $s1 Schaden. Hält $d lang an. Solange der Schild hält, wird das Zauberwirken durch erlittenen Schaden nicht unterbrochen. Nachdem das Ziel einmal abgeschirmt wurde, kann es $6788d lang nicht wieder abgeschirmt werden.", "Absorbiert Schaden.", 0, 60, 10, 0,},
   [27608] = {"Blitzheilung", "Rang 7", "Heilt ein befreundetes Ziel um $s1.", "", 0, 60, 20480, 0,},
   [27609] = {"Magiebannung", "Rang 2", "Bannt auf dem Ziel liegende Magie und entfernt $s1 $lschädlichen Zauber:schädliche Zauber; von einem Freund oder $s1 $lnützlichen Zauber:nützliche Zauber; von einem Feind.", "", 0, 60, -21474836480, 0,},
   [27610] = {"Psychischer Schrei", "Rang 4", "Stößt einen psychischen Schrei aus, der $d lang $i Feinde in einem Umkreis von $a1 Metern in die Flucht schlägt. Erlittener Schaden kann den Effekt unterbrechen.", "Läuft vor Furcht weg.", 0, 60, 655360, 0,},
   [27611] = {"Ausweiden", "", "", "", 0, 80, 85196800, 0,},
   [27615] = {"Nierenhieb", "Rang 2", "Finishing-Move, der das Ziel betäubt.", "Betäubt.", 0, 80, 20971520, 0,},
   [27617] = {"Verschwinden", "Rang 2", "Ermöglicht dem Schurken, sich außer Sicht zu stehlen und $11329d lang in einem verbesserten Verstohlenheitsmodus zu agieren. Durchbricht außerdem die Bewegung einschränkende Effekte. Wirkungsvoller als 'Verschwinden' (Rang 1).", "Verbesserte Verstohlenheit.", 0, 80, 20480, 0,},
   [27618] = {"Blizzard", "Rang 6", "Eisbrocken prasseln auf das Zielgebiet nieder und verursachen $d lang insgesamt $o1 Frostschaden.", "Alle $t1 $lSek.:Sek.; $s1 Frostschaden.", 0, 30, 5244160, 0,},
   [27619] = {"Eisblock", "", "Ihr werdet in einen Eisblock eingehüllt, der Euch $d lang vor allen körperlichen Angriffen und Zaubern schützt, allerdings könnt Ihr während dieser Zeit weder angreifen noch Euch bewegen und auch keine Zauber wirken.", "Immun gegen alle Angriffe und Zauber. Kann weder angreifen, Zauber verwenden noch sich bewegen.", 0, 30, 5242880, 0,},
   [27621] = {"Totem des Windzorns", "Rang 3", "Beschwört ein Totem des Windzorns mit $s1 Gesundheit zu Füßen des Zaubernden. Das Totem verzaubert die Waffenhandwaffen aller Gruppenmitglieder mit Wind, wenn sie sich im Umkreis von $10611a1 Metern aufhalten. Bei jedem Treffer besteht eine Chance von 20%, dass der Angreifer $10610s2 $lzusätzlichen Angriff:zusätzliche Angriffe; mit $10610s1 zusätzlicher Angriffskraft erhält. Hält $d lang an.", "", 0, 110, 5368709120, 0,},
   [27623] = {"Totem der Feuernova", "Rang 5", "Beschwört ein Totem der Feuernova mit $s1 Gesundheit, das $d lang anhält. Wenn es nicht innerhalb von $11311t1 Sek. zerstört wird, fügt das Totem Feinden in einem Umkreis von $11307a1 Metern $11307s1 Feuerschaden zu.", "", 0, 110, 6710886400, 0,},
   [27624] = {"Geringe Welle der Heilung", "Rang 6", "Heilt ein befreundetes Ziel um $s1.", "", 0, 110, 1280, 0,},
   [27626] = {"Reinigen", "Rang 2", "Reinigt das feindliche Ziel und entzieht $m1 $lnützlichen magischen Effekt:nützliche magische Effekte;.", "", 0, 110, 00, 0,},
   [27632] = {"Gezielter Schuss", "Rang 6", "Ein gezielter Schuss, der den Distanzschaden um $s1 erhöht.", "", 75, 975, 13107275, 0,},
   [27633] = {"Zurechtstutzen", "Rang 3", "Verursacht $s2 Schaden und reduziert $d lang das Bewegungstempo des feindlichen Ziels um $s1%.", "Bewegungstempo um $s1% verringert.", 0, 90, 640, 0,},
   [27634] = {"Erschütternder Schuss", "", "Macht einen Feind benommen und verringert sein Bewegungstempo $d lang um $s1%.", "Bewegungstempo um $s1% verringert.", 75, 075, 075, 0,},
   [27635] = {"Blitzschlagschild", "", "Schleudert einen Blitz auf das Ziel und fügt ihm $s1 Naturschaden zu.", "", 0, 110, 10240, 0,},
   [27636] = {"Sternensplitter", "", "Lässt Sternensplitter auf den Kopf des feindlichen Ziels regnen und verursacht $d lang insgesamt $o1 Arkanschaden.", "Alle $t1 Sek. $s1 Arkanschaden.", 0, 60, 20971520, 0,},
   [27638] = {"Krallenhieb", "", "Attackiert das Ziel mit Krallen, fügt $s1 Schaden sowie $o2 zusätzlichen Schaden im Verlauf von $d zu.", "Das Bluten fügt alle $t2 Sek. $s2 Schaden zu.", 0, 70, 40960, 0,},
