   [40442] = {"Druidenschmuckstück", "", "Bei 'Zerfleischen' besteht eine Chance von 40%, $40452d lang $40452s1 Stärke zu gewähren, bei 'Sternenfeuer' besteht eine Chance von 25%, $40445d lang bis zu $40445s1 Zauberschaden zu gewähren und bei 'Verjüngung' besteht eine Chance von 25%, $40446d lang bis zu $40446s1 Heilung zu gewähren.", "", 0, 70, 00, 0,},
